            allItems.Add(new Items ( 274, "Pièce du Puzzle de la Griffe", "Une pièce très rare du trésor de Dodge, l'un des plus grands Ecaflip d'Amakna." ));
            allItems.Add(new Items ( 276, "Pièce du Trésor du Badoul", "Une pièce très rare d'un trésor caché par le célèbre Dodge, l'un des plus grands guerriers Ecaflip d'Amakna." ));
            allItems.Add(new Items ( 278, "Anneau des Rocheuses", "Anneau serti d'une pierre rare. Il doit son nom aux différentes pierres nécessaires à sa confection." ));
            allItems.Add(new Items ( 279, "La Larme du Bouftou", "Selon un illustre aventurier, cette amulette contient une larme de Bouftou, à l'odeur et aux propriétés semblables à celles des décoctions de cuir de bouftou." ));
            allItems.Add(new Items ( 280, "Collier de l\'Arc en Ciel", "Ce collier est recherché par les collectionneurs, vous en tirerez un bon prix." ));
            allItems.Add(new Items ( 281, "Pomme", "Cette pomme restaure quelques points de vie perdus. Posée sur la tête d'un ami, elle pourra vous servir de cible." ));
            allItems.Add(new Items ( 282, "Fiole de Soin supérieure", "Cette fiole restaure quelques points de vie perdus." ));
            allItems.Add(new Items ( 283, "Fiole de Soin", "Cette fiole restaure quelques points de vie perdus." ));
            allItems.Add(new Items ( 284, "Sel", "Le sel est un minéral, moulu, il vous permet de donner du goût à vos pains." ));
            allItems.Add(new Items ( 285, "Farine de Blé", "La farine s'obtient en écrasant des grains de blé, elle est très utile pour faire du pain, des crêpes, des gaufres et de délicieux gâteaux, mais surtout pour enfariner vos ennemis d'enfance." ));
            allItems.Add(new Items ( 286, "Levure de Boulanger", "C'est un ingrédient indispensable pour la fermentation du pain." ));
            allItems.Add(new Items ( 287, "Graine de Sésame", "Cette graine extrêmement riche en protéines permet de réaliser de délicieux pains, mais la culture du sésame est délicate sous le climat d'Amakna, ce qui en fait une graine rare et chère." ));
            allItems.Add(new Items ( 288, "Graine de Tournesol sauvage", "Cette graine riche en protéines permet de donner une saveur très particulière aux pains. La seule façon d'en trouver c'est de tuer un Tournesol sauvage ou de l'acheter !" ));
            allItems.Add(new Items ( 289, "Blé", "Le Blé est depuis la nuit des temps un ingrédient indispensable dans l'alimentation des habitants d'Amakna et des Tofus." ));
            allItems.Add(new Items ( 290, "Champignon", "Avec ou sans crème, les champignons sont toujours aussi bons. La manière la plus simple de s'en procurer reste de tuer un Champ-Champ." ));
            allItems.Add(new Items ( 291, "Poils du Mulou", "Ces poils ne sont pas comme tous les poils, ils disposent de propriétés particulières qui les rendent très appréciés des alchimistes." ));
            allItems.Add(new Items ( 292, "Testicules du Mulou", "Ces énormes testicules sont recouverts d'un étrange duvet particulièrement doux et chaud. Il est presque impossible de les récupérer intacts après un combat face à un Mulou, ce qui explique en partie leur prix scandaleux." ));
            allItems.Add(new Items ( 293, "Petite Pelle du Craqueleur", "La seule chose qui soit aussi solide qu'un Craqueleur, c'est un Craqueleur, voilà pourquoi cette pelle, destinée à la chasse aux craqueleurs, est en Pierre de Craqueleur." ));
            allItems.Add(new Items ( 294, "Pelle du Craqueleur", "La seule chose qui soit aussi solide qu'un Craqueleur, c'est un Craqueleur, voilà pourquoi cette pelle destinée à la chasse aux craqueleurs est en Pierre de Craqueleur." ));
            allItems.Add(new Items ( 295, "Grande Pelle du Craqueleur", "La seule chose qui soit aussi solide qu'un Craqueleur, c'est un Craqueleur, voilà pourquoi cette pelle destinée à la chasse aux craqueleurs est en Pierre de Craqueleur." ));
            allItems.Add(new Items ( 296, "Puissante Pelle du craqueleur", "La seule chose qui soit aussi solide qu'un Craqueleur, c'est un Craqueleur, voilà pourquoi cette pelle destinée à la chasse aux craqueleurs est en Pierre de Craqueleur." ));
            allItems.Add(new Items ( 297, "Bottes du Petit Bouftou", "Ces bottes sont confectionnées en cuir de Bouftou, elles permettent aux éleveurs de s'approcher plus facilement des Bouftous pour les tuer, grâce à l'odeur qu'elles dégagent." ));
            allItems.Add(new Items ( 298, "Sandales Tofuesques", "Ces sandales sont faites à partir de plumes de Tofu bouilli, et la semelle est en Bec de Tofu inusable. Pour des raisons encore inexpliquées, elles rendent parfois leur porteur un peu débile...comme un Tofu." ));
            allItems.Add(new Items ( 299, "Bottes de Faillete", "La meilleure défense c'est l'attaque, ces bottes anti-agression vous permettront d'exterminer un groupe d'agresseurs sans aucune difficulté." ));
            allItems.Add(new Items ( 300, "Graine de Pavot", "Cette graine confère des vertus hallucinogènes aux pains prisés des aventuriers qui désirent se reposer après un combat difficile." ));
            allItems.Add(new Items ( 301, "Plumes de Tofu", "Les plumes de Tofu sont très utilisées en couture pour les rembourrages divers. Et surtout, n'oubliez pas, un kilo de plumes de Tofu pèse autant qu'un kilo de plomb." ));
            allItems.Add(new Items ( 302, "Citrouille", "Les citrouilles poussent dans les champs, à peine plus grosses que les testicules de mulou, elles ont en revanche bien meilleur goût." ));
            allItems.Add(new Items ( 303, "Bois de Frêne", "Le Bois de Frêne n'est pas un bois de qualité, mais il est abondant et pousse relativement vite. Attention tout de même à ne pas le stocker près d'une forte source de chaleur, il est inflammable." ));
            allItems.Add(new Items ( 304, "Cuir de Bouftou", "Ce cuir très résistant constitue la principale ressource des tanneurs qui en font des bottes, des ceintures et même des lanières de fouet. Son seul inconvénient, c'est d'avoir l'odeur de la sueur de Bouftou en rut... ce qui ne favorise pas les rencontres." ));
            allItems.Add(new Items ( 305, "Dent de Wabbit", "Abîmées à force de ronger des cawottes, les dents de Wabbit sont tout de même très utiles lors de la confection de potions grâce à leurs propriétés revigorantes." ));
            allItems.Add(new Items ( 306, "Fleur de Pissenlit Diabolique", "Correctement bouillie puis séchée, cette fleur perd ses propriétés toxiques et peut se consommer en tisane, mais la fleur de Pissenlit Diabolique est surtout utilisée pour amuser la jeunesse en quête de romantisme. En effet, qui n'a jamais soufflé sur une fleur de Pissenlit Diabolique pour envoyer dans le cou de l'être tant convoité, ces magnifiques graines, douces comme la caresse d'une éclaircie sur les feuilles d'un charme encore imprégnées de rosée ? Peut-être les Iops, qui au lieu de souffler, ont tendance à aspirer les graines et à s'étouffer lamentablement dans d'atroces convulsions." ));
            allItems.Add(new Items ( 307, "Ailes de Moskito", "Ces ailes très fines ont la particularité de favoriser la réflexion des personnes qui osent les mâcher. Leur goût est assez proche des oeufs de Kwak pourris, c'est pour cela qu'elles sont essentiellement utilisées pour les potions de sagesse." ));
            allItems.Add(new Items ( 308, "Coquille de Dragoeuf Blanc", "Cette coquille est presque incassable, cependant, en la polissant pendant des générations, les meilleurs Bijoutiers peuvent utiliser ses facultés magiques pour confectionner des amulettes aux pouvoirs exceptionnels." ));
            allItems.Add(new Items ( 309, "Pétale de Rose Démoniaque", "Ces pétales ont des vertus aphrodisiaques très puissantes, ce qui en fait le cadeau idéal pour conquérir l'élu(e) de votre coeur. Éviter de les consommer en infusion est une sage décision dans la mesure où leurs effets se retrouvent être inversés." ));
            allItems.Add(new Items ( 310, "Os de Chafer", "Cet os est presque incassable, mais au contact de l'eau, il devient tout mou, ce qui facilite son utilisation par les alchimistes." ));
            allItems.Add(new Items ( 311, "Eau", "L'eau est utile pour de nombreuses choses, et plus elle est trouble, plus elle donnera de goût à la bière (méfiez-vous tout de même, l'eau bue explose)." ));
            allItems.Add(new Items ( 312, "Fer", "Le fer est très utile pour les forgerons et pour ceux qui utilisent du métal, certains s'en servent même pour repasser." ));
            allItems.Add(new Items ( 313, "Or", "L'or est un métal rare et précieux, ses qualités intrinsèques en font un métal de choix pour la conception d'armes ou d'objets particulièrement puissants et d'esthétique raffinée." ));
            allItems.Add(new Items ( 315, "Diamant", "Tous les bijoutiers d'Amakna raffolent de cette pierre qui demande une excellente dextérité pour être travaillée. Il faut souvent s'aventurer dans de dangereuses mines pour les trouver, mais heureusement, les diamants sont éternels." ));
            allItems.Add(new Items ( 316, "Emeraude", "Avec son vert intense, cette magnifique pierre précieuse peut facilement transformer un thon en princesse, sans l'utilisation d'une quelconque magie occulte." ));
            allItems.Add(new Items ( 317, "Bâton du Petit Tabi", "Ce bâton servait à l'origine aux éleveurs d'Amakna qui voulaient contrôler plus facilement leurs troupeaux de Tabis, ce bâton est en effet si léger, qu'il permet à son porteur de courir après les Tabis égarés sans se fatiguer." ));
            allItems.Add(new Items ( 318, "Bâton du Tabi", "Ce bâton servait à l'origine aux éleveurs d'Amakna qui voulaient contrôler plus facilement leurs troupeaux de Tabis, ce bâton est en effet si léger, qu'il permet à son porteur de courir après les Tabis égarés sans se fatiguer." ));
            allItems.Add(new Items ( 319, "Bâton du Grand Tabi", "Ce bâton servait à l'origine aux éleveurs d'Amakna qui voulaient contrôler plus facilement leurs troupeaux de Tabis, ce bâton est en effet si léger, qu'il permet à son porteur de courir après les Tabis égarés sans se fatiguer." ));
            allItems.Add(new Items ( 320, "Bâton du Puissant Tabi", "Ce bâton servait à l'origine aux éleveurs d'Amakna qui voulaient contrôler plus facilement leurs troupeaux de Tabis, ce bâton est en effet si léger, qu'il permet à son porteur de courir après les Tabis égarés sans se fatiguer." ));
            allItems.Add(new Items ( 323, "Amulette du Kam Assutra", "Cette amulette donne des facultés très particulières à son porteur, en effet ce dernier se voit comme transporté par une aura mystérieuse parcourant son corps, ce qui généralement a pour effet de décupler ses capacités physiques." ));
            allItems.Add(new Items ( 324, "Amulette de Krokette", "Cette amulette a la faculté de devenir rose les jours où son porteur est d'humeur guillerette. On raconte qu'elle décuple également vos chances de trouver de bons objets." ));
            allItems.Add(new Items ( 325, "Amulette du Sram", "Cette amulette confectionnée à partir de petits crânes taillés dans de l'os, par un des plus grands bijoutiers Sram, est unique en son genre. Elle permet à son porteur de s'évader plus facilement lors des situations périlleuses." ));
            allItems.Add(new Items ( 326, "Amulette Deulegarnoulle", "Cette amulette provient des régions nordiques, elle confère a son porteur la capacité d'invoquer plus facilement des créatures." ));
            allItems.Add(new Items ( 327, "Lame du Chevaucheur de Karne", "Cette lame forgée à l'origine pour les Chevaucheurs de Karne, était en fait bien trop lourde pour ces faibles gobelins. Elle n'en reste pas moins très légère pour un individu normal, ce qui lui permet de frapper très vite." ));
            allItems.Add(new Items ( 328, "Koupe-koupe", "Cette lame double à la forme bien étrange servait à l'origine à élaguer les arbres pour progresser dans les forêts denses. Les bûcherons se sont vite aperçus qu'elle constituait une arme de choix pour exterminer toute forme de menace." ));
            allItems.Add(new Items ( 329, "Arc de Triomphe", "Comme son nom l'indique, cet arc permet de gagner facilement un combat. Un grand architecte Crâ sans imagination a même utilisé le nom de cet arc pour nommer une de ses constructions." ));
            allItems.Add(new Items ( 331, "Arc Hancihaile", "C'est l'arc le plus recherché de tout Amakna, on raconte que là où ses flèches retombent, on peut trouver un trésor. Étrangement, il ne semble être efficace que lorsque pluie et rayons de soleil se mêlent." ));
            allItems.Add(new Items ( 332, "Arc de Jeanne", "Jeanne était un peu folle, elle entendait des Tabis roter dans sa tête. Pour éviter qu'elle ne se blesse avec son arc, il lui a été confisqué et on a décidé de la brûler vive pour réchauffer l'ambiance lors de l'inauguration de la taverne." ));
            allItems.Add(new Items ( 333, "Baguette Deuh-Pain", "A l'origine destinée à être mangée, cette baguette est devenue une arme redoutée. En effet, Xavier le Boulanger l'a oubliée dans son four pendant 234 ans alors qu'il était parti distribuer des pains aux monstres autour de sa boulangerie." ));
            allItems.Add(new Items ( 334, "Baguette de Liriel", "Cette baguette appartenait à l'Eniripsa Liriel qui mourût d'une overdose de fraises dans d'atroces souffrances, perdant ses ailes dans un râle de douleur à vous glacer le sang. Les collectionneurs s'arrachent cette baguette réputée maudite." ));
            allItems.Add(new Items ( 335, "Bâton du Bwork Mage", "Sans leur bâton, les Bwork Mages sont aussi inoffensifs que les Tofus sans leur Bécos. Voilà qui en dit long sur la puissance de ce bâton." ));
            allItems.Add(new Items ( 336, "Bâton du Grand Pa Wabbit", "Ce bâton a la faculté de pouvoir faire léviter les Cawottes, on se demande bien à quoi cela peut servir en combat." ));
            allItems.Add(new Items ( 337, "Arc du Bwork Archer", "C'est un arc très simple à utiliser, il a été conçu pour s'adapter à l'intelligence d'un Bwork Archer, ce qui le rend presque utilisable par un Tofu. En revanche, il nécessite une force de Craqueleur pour être bandé." ));
            allItems.Add(new Items ( 338, "Lame du Petit Bwork", "Cette épée d'apparence rudimentaire est très appréciée des Bworks les plus débiles, qui ne savent pas se servir d'un arc ou d'un bâton, certains confondent même la lame et le manche ce qui ne manque pas de faire rire leurs adversaires." ));
            allItems.Add(new Items ( 339, "Lame du Chafer", "Cette lame appartenait à Metzger le boucher, elle avait été forgée pour découper les os. Le Chafer a cependant réussi à lui dérober son outil, devenu maintenant une arme très redoutée qui est à l'origine de nombreuses amputations." ));
            allItems.Add(new Items ( 340, "Leurnettes", "Ces dagues permettent selon la légende de voler tout et n'importe quoi, mais surtout n'importe quoi à n'importe qui, où l'inverse, mais plus personne ne se souvient de la légende avec exactitude." ));
            allItems.Add(new Items ( 341, "Dagues Eurfolles", "Ces dagues légendaires constituent de vraies pièces de collection pour tout Sram qui se respecte, elles sont tellement vénérées, que certains Srams se refusent à les voler." ));
            allItems.Add(new Items ( 342, "Marteau Pickeur", "Ce marteau très particulier a été confectionné à partir de becs de Kwaks, ce qui explique pourquoi il pique très fort lorsque l'on se prend un coup de ce marteau." ));
            allItems.Add(new Items ( 345, "Petite Pelle Teuze", "Après une soirée un peu trop arrosée à la taverne qui a débouché sur une bagarre, un Enutrof s'est aperçu que cette pelle conçue à l'origine pour les grands travaux de construction était une excellente arme." ));
            allItems.Add(new Items ( 346, "Anneau Rak", "Selon certaines légendes, cet anneau protège son porteur des intempéries. Mais ceci n'a jamais encore pu être prouvé, puisqu'il ne pleut jamais en Amakna." ));
            allItems.Add(new Items ( 347, "Rhizome Doré", "Cette pelle de jardinier est particulièrement efficace pour couper les rhizomes, des mauvaises herbes. C'est également une excellente arme dès qu'il s'agit de couper autre chose que des rhizomes." ));
            allItems.Add(new Items ( 348, "Bottes de Céleri-Thé", "Ces bottes appartenaient à un cuisinier très pressé, qui allait chercher son céleri et son thé à toute vitesse à travers la campagne d'Amakna." ));
            allItems.Add(new Items ( 350, "Argent", "L'argent est un métal brillant très apprécié des bijoutiers, contrairement à ce que certaines personnes essaient de vous faire croire, il n'a aucun effet particulier sur les vampires." ));
            allItems.Add(new Items ( 351, "Bière d\'Amakna", "Cette bière vous permettra de restaurer vos points de vie. Et comme le dit un brillant dicton paternel : " ));
            allItems.Add(new Items ( 352, "Petite Epée Sacrée", "Une épée sainte distribuée aux jeunes guerriers durant leur formation." ));
            allItems.Add(new Items ( 353, "Epée du Petit Chevalier", "L'épée du  Petit Chevalier est une épée solide et efficace, bien utilisée elle peut causer de gros dommages à vos ennemis." ));
            allItems.Add(new Items ( 354, "La Klebik", "Ce type d'épée était forgé pour les gardes d'un commerçant aussi riche que réputé. Plutôt destinée à effrayer les voleurs, elle n'en reste pas moins redoutable pour achever les voleurs ou les clients honnêtes, mais un peu usants." ));
            allItems.Add(new Items ( 355, "L\'Anneau de Valeur", "Cet anneau apporte force et courage à son porteur." ));
            allItems.Add(new Items ( 356, "Ceinture d\'Endurance", "Cette ceinture rend son porteur plus résistant et endurant." ));
            allItems.Add(new Items ( 357, "La Queue", "Epée symbolisant la partie de leur corps dont les Ecaflips sont le plus fier. Cette épée résume bien la façon de faire des joueurs." ));
            allItems.Add(new Items ( 358, "La Queue Infernale", "La Queue Infernale est vraiment infernale... De quoi rendre votre adversaire ou vos coéquipiers complètement fous, et surtout de transformer vos coéquipiers en adversaires si vous vous y prenez mal. En revanche, le cas inverse ne semble jamais se produire." ));
            allItems.Add(new Items ( 359, "L\'Ecaliseur", "Cet anneau a été offert il y bien longtemps à Isidor, un chat malin, par le roi Allister, qui reconnu l'agilité inégalable de cet Ecaflip." ));
            allItems.Add(new Items ( 360, "Oreilles du Tiwabbit", "Ces petites oreilles recouvertes de poils bruns sentent le mulou mort une fois bouillies. Elles sont en revanche très appréciées lorsqu'elles sont fourrées avec de la gelée royale." ));
            allItems.Add(new Items ( 361, "Cawotte", "C'est l'unique nourriture du Tiwabbit Kiafin, ce qui explique peut-être l'origine de sa débilité profonde." ));
            allItems.Add(new Items ( 362, "Peau de Larve Bleue", "Cette peau toute lisse constitue un très bon ingrédient pour les potions magiques." ));
            allItems.Add(new Items ( 363, "Peau de Larve Orange", "Cette peau toute lisse constitue un excellent ingrédient pour les potions magiques." ));
            allItems.Add(new Items ( 364, "Peau de Larve Verte", "Cette peau toute lisse constitue un excellent ingrédient pour les potions magiques." ));
            allItems.Add(new Items ( 365, "Patte d\'Arakne", "Une patte très fine, mais très poilue. Très prisée des alchimistes pour ses propriétés euphorisantes, c'est la petite faiblesse qui les perdra." ));
            allItems.Add(new Items ( 366, "Bec du Tofu", "Ce ridicule petit bec de Tofu, d'apparence anodine, est cependant très aiguisé. Les archers d'Amakna les utilisent depuis des lustres comme pointes de flèches." ));
            allItems.Add(new Items ( 367, "Oeuf de Tofu", "Cet oeuf a la particularité de sentir l'oeuf pourri même lorsqu'il n'est pas pourri, imaginez un peu ce qu'il sent lorsqu'il est réellement pourri. Personne ne sait en revanche, qui du Tofu ou de l'Oeuf de Tofu est arrivé en premier en Amakna." ));
            allItems.Add(new Items ( 368, "Gelée à la Fraise", "Cette délicieuse gelée à la fraise a rendu plus d'un aventurier un peu trop gourmand malade. Comme le dit si bien le fameux dicton, " ));
            allItems.Add(new Items ( 369, "Gelée à la Menthe", "Cette délicieuse gelée à la menthe a rendu plus d'un aventurier un peu trop gourmand malade. Elle a un effet apaisant sur le moral de toute votre équipe si vous la consommez en cas de mauvaise haleine." ));
            allItems.Add(new Items ( 370, "Gelée Bleutée Royale", "Cette délicieuse gelée est issue de la digestion et de la fermentation de miel par une Gelée Royale. Immangeable sous sa forme brute, cette gelée n'en reste pas moins, un ingrédient de choix pour les confiseries." ));
            allItems.Add(new Items ( 371, "Sourcil de Moskito", "Le Moskito dispose de longs et fins sourcils qui mettent en valeur son regard de grand charmeur. La grande sensibilité du moskito fait que ses sourcils sont souvent imprégnés de larmes aux propriétés méconnues." ));
            allItems.Add(new Items ( 372, "Bandeau du Black Tiwabbit", "Ce bandeau donne une allure de guerrier au Black Tiwabbit qui le porte. Cette tradition de porter un bandeau vient paraît-il du Wabbit Wambo." ));
            allItems.Add(new Items ( 373, "Tige de Pissenlit Diabolique", "Cette tige, souple comme le roseau, se plie, mais ne casse pas, ce qui en fait un composant de choix pour la réalisation des fouets et autres martinets, indispensables à l'éducation d'un enfant." ));
            allItems.Add(new Items ( 374, "Langue du Pissenlit Diabolique", "La couleur verte de cette langue provient de la consommation abusive de gelée à la menthe dont le Pissenlit Diabolique est friand." ));
            allItems.Add(new Items ( 375, "Dent du Tofu Maléfique", "Le Tofu Maléfique a troqué son ridicule petit bec de Tofu contre de ridicules grandes dents. Il est difficile de savoir ce qui est le plus dangereux sans tester soi-même." ));
            allItems.Add(new Items ( 376, "Aile du Tofu Maléfique", "Cette ridicule petite aile ne sert pas à grand-chose au Tofu Maléfique, si ce n'est à brasser de l'air pour déséquilibrer les Moskitos." ));
            allItems.Add(new Items ( 377, "Epine du Champ Champ", "Cette épine venimeuse protège le Champ Champ des attaques au corps à corps, mais l'empêche en revanche de faire des câlins sans tuer ceux qu'il aime. Heureusement pour lui, le Champ Champ utilise du Sporme qu'il envoie à distance pour se reproduire." ));
            allItems.Add(new Items ( 378, "Sporme du Champ Champ", "Cette sporme est la " ));
            allItems.Add(new Items ( 379, "Pince du Crabe", "Cette pince de taille démesurée est presque creuse, elle permet juste au Crabe d'épater la galerie et de se faire passer pour un boss. Elle est en revanche réputée incassable." ));
            allItems.Add(new Items ( 380, "Feuille de Menthe Sauvage", "C'est l'aliment de base de la Gelée Verte, si vous sentez une odeur de menthe, n'en déduisez donc pas obligatoirement qu'il y a de la menthe près de vous." ));
            allItems.Add(new Items ( 381, "Fraise", "C'est l'aliment de base de la Gelée à la fraise, si vous sentez une odeur de fraise, vous êtes peut-être sur son territoire de chasse." ));
            allItems.Add(new Items ( 382, "Ongle de Chevaucheur de Karne", "Cet ongle sale et usé a traîné n'importe où, mais surtout dans des endroits peu recommandables. Il est donc préférable de ne pas à votre tour le mettre n'importe où et encore moins dans des endroits peu recommandables." ));
            allItems.Add(new Items ( 383, "Corne de Bouftou", "Les Cornes de Bouftou sont luisantes et douces comme du cuir, pour des raisons inconnues, les Osamodas les affectionnent particulièrement." ));
            allItems.Add(new Items ( 384, "Laine de Bouftou", "Cette laine épaisse sent le Bwork qui se néglige, elle est cependant très chaude et surtout, elle ne démange pas les peaux sensibles, ce que les Eniripsa ne manquent pas d'apprécier pour leurs sous-vêtements." ));
            allItems.Add(new Items ( 385, "Bave de Bouftou", "Cette bave purulente tire son odeur du fait que le Bouftou passe son temps à se lécher la laine, ce qui est également à l'origine de l'haleine exécrable du Bouftou." ));
            allItems.Add(new Items ( 386, "Groin du Sanglier", "Ce groin est un ustensile de choix pour débusquer les plus gros glands qui traînent dans les sous-bois. Contrairement au groin du Dragon Cochon, celui-ci est immangeable du fait de son odeur répugnante." ));
            allItems.Add(new Items ( 387, "Défense du Sanglier", "Cette défense permet au Sanglier de se défendre et accessoirement de curer son groin débusqueur de glands. N'oubliez donc pas de les nettoyer à l'eau vive avant d'en faire de la poudre." ));
            allItems.Add(new Items ( 388, "Poils du Sanglier", "Ces poils particulièrement résistants sont utilisés entre autres pour la confection de pinceaux et de balais. Certaines personnes subissent d'importantes allergies au contact de ces poils, il est donc recommandé de ne pas s'y frotter avec vigueur." ));
            allItems.Add(new Items ( 389, "Huile de Tournesol Sauvage", "Cette huile est fortement recommandée par les diététiciens en raison de la forte proportion d'acides gras essentiels qu'elle contient. De plus, son contenu élevé en gras polyinsaturé et faible en gras saturé en fait un choix judicieux." ));
            allItems.Add(new Items ( 390, "Huile de Noix", "Huile au goût très fruité, utilisée en pâtisserie pour parfumer certains gâteaux. Cette huile faible en gras saturé se conserve peu de temps, même dans une bouteille qui n'a pas été ouverte." ));
            allItems.Add(new Items ( 391, "Noix de Cajou", "Cette Noix de Cajou permet de faire de l'huile si elle est récoltée encore verte. Une fois séchée, elle peut toujours servir de jouet pour les enfants de Iop un peu simplets." ));
            allItems.Add(new Items ( 392, "Noix de Pécan", "Cette Noix de Pécan permet de faire de l'huile, mais il est préférable de la consommer séchée au regard des effets laxatifs de son huile." ));
            allItems.Add(new Items ( 393, "Gland", "Les sangliers adorent les glands, et tout le monde ou presque se demande bien pourquoi." ));
            allItems.Add(new Items ( 394, "Noisette", "Cette petite Noisette est délicieuse lorsqu'elle est associée à une barre chocolatée. Dommage que le chocolat n'existe pas à Amakna." ));
            allItems.Add(new Items ( 395, "Trèfle à 5 feuilles", "Ce trèfle constitue un élément essentiel de la nourriture des Tabis. Les éleveurs font tout de même attention dans la mesure où le Trèfle à 5 feuilles provoque des ballonnements chez les tabis." ));
            allItems.Add(new Items ( 396, "Huile de Palme", "De goût et parfum légers, cette huile convient bien pour tous les usages, de la friture à la préparation de vinaigrettes." ));
            allItems.Add(new Items ( 397, "Huile de Koode", "Cette huile très rare est un vrai élixir. Elle est si lubrifiante qu'elle permet aux rouages et aux engrenages de ne jamais s'user. Elle est issue d'un savant mélange de diverses huiles." ));
            allItems.Add(new Items ( 398, "Fruit de Palme", "Ce fruit étrange permet d'obtenir une huile raffinée une fois pressé. Dommage que ce fruit soit presque impressable à cause de sa coquille aussi dure que la semelle des bottes du Bowisse." ));
            allItems.Add(new Items ( 399, "Huile de Sésame", "Cette huile extraite des grains de sésame se distingue par son goût riche et délicat de noisettes grillées. Dommage que son odeur se rapproche plus de celle du Bouftou grillé que de la feuille de menthe sauvage." ));
            allItems.Add(new Items ( 400, "Orge", "L'orge est une céréale utilisée pour la bière mais aussi pour le bétail et parfois pour les bétails buveurs de bière." ));
            allItems.Add(new Items ( 401, "Houblon", "La fleur du plant femelle de Houblon renferme les principes actifs utiles au brasseur, notamment les résines amères et les huiles essentielles. " ));
            allItems.Add(new Items ( 405, "Malt", "C'est le nom donné à l'Orge germée. C'est un ingrédient de choix pour les bières de qualité. Attention cependant, le malt est vicieux et a tendance à absorber la bière lors de sa germination, un peu comme le Tot après une dure journée de chasse aux Tofus." ));
            allItems.Add(new Items ( 406, "Estomac de Wo Wabbit", "Cet estomac a la faculté de se dilater très facilement, ce qui simplifie grandement les digestions difficiles du Wo Wabbit qui a tendance à abuser des liqueurs de Cawottes fermentées." ));
            allItems.Add(new Items ( 407, "Pic du Prespic", "Pour se curer les dents ou se créer une nouvelle pointe de flèche, rien de mieux que le Pic du Prespic." ));
            allItems.Add(new Items ( 408, "Dentier du Chafer d\'élite", "Le Chafer d'élite a les dents moisies par l'alcool, c'est pour cela qu'il utilise un dentier. Ce dentier coûte une fortune car ses dents sont en or et l'haleine fétide du Chafer d'élite a doté ses dents de pouvoirs imprévisibles." ));
            allItems.Add(new Items ( 409, "Poils de Barbe du Bwork Mage", "Ces poils roux sentent très mauvais lorsqu'il pleut, pour des raisons assez obscures. Cependant, les Bworks Mages semblent tirer une partie de leurs pouvoirs magiques de ces poils de barbe, ce qui pourrait intéresser certains alchimistes." ));
            allItems.Add(new Items ( 410, "Livre du Bwork Mage", "Ce livre est une vraie pièce de collection, c'est de là que le Bwork Mage puise toute son inspiration et sa vigueur. Chaque page de ce livre recèle de nombreux secrets quant à l'anatomie de certaines créatures d'Amakna." ));
            allItems.Add(new Items ( 411, "Bec du Kwak de Glace", "Ce bec extraordinairement solide permet au Kwak de Glace de faire des trous dans la glace, juste pour le plaisir de faire des trous dans la glace. Les kwaks de Glace sont décidément bien étranges..." ));
            allItems.Add(new Items ( 412, "Bec du Kwak de Flamme", "Ce bec extraordinairement solide permet au Kwak de Flamme d'éteindre des braises entre son bec, juste pour le plaisir d'éteindre des braises entre son bec. Les kwaks de Flamme sont décidément bien étranges..." ));
            allItems.Add(new Items ( 413, "Bec du Kwak du Vent", "Ce bec extraordinairement solide permet au Kwak du Vent de se prendre des vents sans broncher, juste pour le plaisir de se prendre des vents sans broncher. Les kwaks du vent sont décidément bien étranges..." ));
            allItems.Add(new Items ( 414, "Plume du Kwak de Glace", "Cette plume a la particularité de résister au givre. C'est pour cela qu'on ne rencontre jamais de Kwak de Glace givré." ));
            allItems.Add(new Items ( 415, "Plume du Kwak de Flamme", "Cette plume a la particularité de résister aux flammes. C'est pour cela qu'on ne rencontre jamais de Kwak de Flamme enflammés." ));
            allItems.Add(new Items ( 416, "Plume du Kwak de Vent", "Cette plume a la particularité de résister aux bourrasques de vent. C'est pour cela que le Kwak de Vent peut encaisser de multiples vents jour après jour sans jamais voir son moral ni son ardeur affaiblis." ));
            allItems.Add(new Items ( 417, "Boue du Boo", "La Boo est très agressive, elle est constituée de morceaux de boue mis bout à bout, le seul moyen d'en venir à bout c'est de la dessécher, mais elle est tellement humide que vous n'êtes pas près d'en voir le bout." ));
            allItems.Add(new Items ( 418, "Poils de Barbe du Grand Pa Wabbit", "Le Grand Pa Wabbit possède une barbe postiche qui sent très mauvais, un peu comme le Père Nowel, mais en pire. A force de baver sans cesse devant les Grand Ma Wabbit, ses poils de barbe ont fini par s'imprégner de sa bave aux pouvoirs méconnus." ));
            allItems.Add(new Items ( 419, "Oreille du Grand Pa Wabbit", "Les oreilles du Grand Pa Wabbit sont pointues et rigides, mais sont recouvertes d'une épaisse fourrure zébrée du plus bel effet. C'est sans conteste une des fourrures les plus recherchées, ne serait-ce que parce qu'elle ne sent absolument rien." ));
            allItems.Add(new Items ( 420, "Ficelle en Lin", "La ficelle de lin est très solide, elle permet de faire de jolies coutures bien solides ou alors de ligoter ses amis pour leur montrer qui est réellement le chef de la guilde." ));
            allItems.Add(new Items ( 421, "Fleur de Lin", "La Fleur de Lin peut être bleue ou blanche, suivant la période de l'année à laquelle la graine a été semée. En décoction ou en infusion, elle procure une sensation de bien-être peu commune." ));
            allItems.Add(new Items ( 422, "Graine de Lin", "Cette toute petite graine contient énormément de bonnes choses. Les chasseurs de Kwaks savent que ces derniers en sont friands et n'hésitent pas à les attirer dans leurs pièges avec ces graines luisantes." ));
            allItems.Add(new Items ( 423, "Lin", "La tige de Lin n'a rien de spécial, si ce n'est qu'elle est capable de se redresser 7 fois après avoir été pliée par les tempêtes. C'est sûrement pour cela que sa fibre est si souple." ));
            allItems.Add(new Items ( 424, "Fibre de Lin", "C'est la fibre idéale pour confectionner les plus belles étoffes, elle est rare et chère mais tellement supérieure aux fibres classiques qu'aucun tisserand ne pourrait s'en passer." ));
            allItems.Add(new Items ( 425, "Chanvre", "La Tige de Chanvre peut être utilisée lors de la recherche d'effets hallucinogènes, mais également par les tisserands qui exploitent leurs fibres pour des raisons toutes autres." ));
            allItems.Add(new Items ( 426, "Fibre de Chanvre", "Cette fibre n'est pas très belle, mais elle est particulièrement résistante. De plus, dans des moments d'introspection sur soi-même, il est toujours possible de la fumer." ));
            allItems.Add(new Items ( 427, "Graine de Chanvre", "La Graine de Chanvre est très utilisée pour calmer les troupeaux de Tabis en rut, il suffit de leur en administrer quelques-unes et ces derniers deviennent soudainement moins farouches." ));
            allItems.Add(new Items ( 428, "Fleur de Chanvre", "Cette fleur permet de faire des potions aux effets aléatoires, qui peuvent aller de la paralysie totale à l'invulnérabilité aux gaz de Wabbits." ));
            allItems.Add(new Items ( 429, "Pointe de Flèche du Bwork Archer", "Cette pointe de flèche rouillée est faite à partir d'un métal rare, qu'on ne trouve que sur ces pointes de flèche." ));
            allItems.Add(new Items ( 430, "Os Invisible du Chafer Invisible", "Cet os est invisible, grâce aux propriétés magiques qui lui ont été conférées. Il doit sûrement pouvoir être utilisé pour des potions magiques..." ));
            allItems.Add(new Items ( 431, "Pierre du Craqueleur", "Cette pierre est presque vivante, elle tire sa vie des pierres précieuses qu'elle renferme. Elle est quasiment incassable, sauf par un Craqueleur. Il n'y a pas mieux pour se faire un bon gros marteau en pierre." ));
            allItems.Add(new Items ( 432, "Côtes du Rib", "Les côtes du Rib sont vraiment très bonnes une fois grillées, il n'y a pas beaucoup à manger autour, mais c'est tellement fin, que ça se mange sans fin." ));
            allItems.Add(new Items ( 433, "Fémur du Chafer Archer", "Voilà un os bien étrange, il est particulièrement friable, et dégage une odeur de curry éventé lorsqu'il est brûlé, voilà qui pourrait bien arranger certains cuisiniers peu scrupuleux." ));
            allItems.Add(new Items ( 434, "Ecorce d\'Abraknyde", "Ce morceau d'écorce est rugueux, crevassé, recouvert de petites aspérités et sent étrangement bon, un peu comme la peau des pieds d'un Enutrof." ));
            allItems.Add(new Items ( 435, "Racine d\'Abraknyde", "Ce morceau de racine a connu divers substrats à force d'accompagner l'Abraknyde lors de promenades dans les sous-bois. Mais ça n'explique pas pourquoi ce morceau de racine est magique." ));
            allItems.Add(new Items ( 436, "Bâton en Racine d\'Abraknyde", "Ce bâton taillé dans une grosse racine d'Abraknyde est très efficace au corps à corps, attention à ne pas le laisser au contact de l'eau, il pourrait reprendre racines et se retourner contre son maître." ));
            allItems.Add(new Items ( 437, "Bourgeon d\'Abraknyde", "Lors de la puberté, les Abraknydes se recouvrent de vilains bourgeons gorgés de sève juteuse riche en vitamines. Plus tard, ces bourgeons donnent naissance à de nouvelles pousses qui elles-mêmes donneront naissance à des bourgeons." ));
            allItems.Add(new Items ( 438, "Queue du Mulou", "La queue du Mulou ressemble à s'y méprendre à une énorme bûche velue, et pourtant, il n'en est rien, ne vous méprenez pas." ));
            allItems.Add(new Items ( 439, "Canine du Mulou", "Cette canine tranchante est surtout recherchée en tant que porte bonheur, pourtant, elle n'apporte à son porteur que des mauvaises odeurs." ));
            allItems.Add(new Items ( 440, "Griffe du Mulou", "Cette griffe bien sale permet de faire des armes redoutables." ));
            allItems.Add(new Items ( 441, "Cuivre", "Le Cuivre est un métal très utile pour confectionner les armes bon marché, c'est presque un ersatz de l'or si on fait abstraction de toutes les qualités de l'or." ));
            allItems.Add(new Items ( 442, "Bronze", "Ce métal entre souvent dans la fabrication d'articles ornementaux, pour embellir vos armes à moindre frais, c'est le métal idéal." ));
            allItems.Add(new Items ( 443, "Kobalte", "Le nom « Kobalte » signifie « cuivre du diable » en Sadida ancien, c'est un métal adapté à la forge d'armes légères et élégantes, mais avant tout mortelles." ));
            allItems.Add(new Items ( 444, "Etain", "L'Etain est un métal mou et lourd, un peu comme le Wo Wabbit, mais en pire." ));
            allItems.Add(new Items ( 445, "Manganèse", "Contrairement à ce que tout le monde pense, ce métal ne sert pas à faire des gouttières, mais plutôt à faire des armes terrifiantes, comme le redoutable " ));
            allItems.Add(new Items ( 446, "Bauxite", "Ce métal est très apprécié pour sa couleur argentée et pour son tranchant lorsqu'il est correctement aiguisé." ));
            allItems.Add(new Items ( 447, "Charbon", "Le Charbon ne dispose d'aucune propriété magique particulière, contrairement à ce que l'on pourrait croire. Cependant, il a une fâcheuse tendance à attirer la foudre, à la contenir, et à la libérer au mauvais moment." ));
            allItems.Add(new Items ( 448, "Silex", "Ce caillou est particulièrement tranchant, il a égorgé plus d'une taupe imprudente." ));
            allItems.Add(new Items ( 449, "Bois d\'Ebène", "Ce bois tient son nom d'un malentendu, il y a bien longtemps, la seule famille de Bûcherons qui savait où se trouvaient ces arbres, s'appelait Baine, et partout, on parlait du " ));
            allItems.Add(new Items ( 450, "Pierre de Granit", "Ce minéral est presque inusable, mais aussi très lourd, ce qui limite son utilisation dans la forge d'armes. En le travaillant longtemps, on peut parfois en extraire des pierres précieuses." ));
            allItems.Add(new Items ( 454, "Hache de Bûcheron", "Cette hache est indispensable au bûcheron s'il veut correctement exercer son métier. Le cas échéant, elle peut également lui permettre de se débarrasser de quelques monstres..." ));
            allItems.Add(new Items ( 455, "Marteau de l\'Aurore", "Ce ridicule petit marteau servait à Zokrim le paysan, qui s'en servait pour planter ses piquets de clôtures à l'aurore. Personne ne sait pourquoi Zokrim plantait ses piquets à l'aurore, mais la légende raconte qu'il avait de bonnes raisons." ));
            allItems.Add(new Items ( 456, "Tueuse de Chênes", "Cette hache permet de couper facilement les arbres, même les plus gros. Elle est surtout appréciée pour son poids important qui lui permet de couper un Bwork en trois facilement, pour peu que son porteur soit capable de la soulever." ));
            allItems.Add(new Items ( 458, "Amulette du Chafer", "Le Chafer est quelqu'un de sensible et distingué, il garde toujours avec lui une amulette faite des crânes de ses cousins tombés au combat. Le plus étrange, c'est que le Chafer n'a jamais eu de cousins." ));
            allItems.Add(new Items ( 459, "Planche en Frêne", "Cette planche en Frêne permet aux bricoleurs de construire des trucs en bois, comme un cercueil en planches par exemple." ));
            allItems.Add(new Items ( 460, "Bois de Chêne", "Ce bois d'excellente qualité est particulièrement résistant, il permet de faire presque tout, des gourdins, des maisons ou encore des bateaux, mais la meilleure façon d'utiliser ce bois reste d'en faire des fûts à bière (Hips !)." ));
            allItems.Add(new Items ( 461, "Bois d\'If", "Ce bois particulièrement souple ne casse jamais, ce qui en fait un bois de choix pour la réalisation d'arcs. Il est en revanche difficile à trouver, les paysans d'Amakna ayant tendance à tuer les jeunes pousses d'If qui rendent malades leurs Bouftous." ));
            allItems.Add(new Items ( 462, "Arc en Racine d\'Abraknyde", "Le bois d'Abraknyde étant toujours vivant une fois coupé, cet arc est très difficile à utiliser. Cependant, il n'y a pas mieux pour ridiculiser un Abraknyde, que de le tuer avec un arc issu des racines de ses propres frères." ));
            allItems.Add(new Items ( 463, "Ambre", "Cette magnifique pierre précieuse est constituée de résine d'arbre fossilisée. On y trouve parfois à l'intérieur, un petit Moskito pris au piège il y a des lustres, ce qui ne manque pas de rajouter de la valeur à cette pierre." ));
            allItems.Add(new Items ( 464, "Ambre d\'Abraknyde", "Cette magnifique pierre précieuse est constituée de résine d'Abraknyde fossilisée. On y trouve parfois à l'intérieur, une Arakne prise au piège il y a des lustres dans la sève de son Maître. Voilà une mort bien méritée pour ce genre de Monstre." ));
            allItems.Add(new Items ( 465, "Cristal", "Le Cristal est une pierre translucide, mais très cassante. Et oui, même chez les pierres précieuses, rien n'est parfait." ));
            allItems.Add(new Items ( 466, "Saphir", "Cette pierre précieuse toute bleue est bien adaptée à la fabrication d'armes aux effets magiques, peut-être parce que les créateurs de jeu en mal d'inspiration utilisent toujours le bleu pour représenter le mana." ));
            allItems.Add(new Items ( 467, "Rubis", "Cette pierre précieuse aux couleurs rougeoyantes comme les plumes du Kwak de Flammes est froide au toucher comme les plumes du Kwak de Glace." ));
            allItems.Add(new Items ( 468, "Pain d\'Amakna", "Ce petit pain tout chaud à la mie bien molle et à la croûte dorée permet de regagner des points de vie perdus au combat. C'est le pain le plus populaire, car généralement le moins cher." ));
            allItems.Add(new Items ( 469, "Baguette en Racine d\'Abraknyde", "Cette baguette utilise les propriétés magiques de la Racine d'Abraknyde qui est encore vivante, méfiez-vous des effets néfastes de cette baguette, il lui arrive parfois de se retourner contre son maître." ));
            allItems.Add(new Items ( 470, "Bois d\'Orme", "Le Bois d'Orme est un des bois les plus recherchés, courant autrefois, il a été décimé il y a des lustres par la Grafioze, une sorte d'envoûtement terrible lancé par un Puissant Bwork Mage des Cavernes de la pire espèce." ));
            allItems.Add(new Items ( 471, "Bois d\'Erable", "Les feuilles d'Erable ont permis à des gens peu inspirés de se faire un drapeau original, le sirop d'Erable a permis de donner du goût aux crêpes. Quant à son bois, on cherche encore à quoi il peut bien servir." ));
            allItems.Add(new Items ( 472, "Bois de Charme", "Le Bois de Charme doit son nom aux Sadidas, qui s'accouplent toujours sous les Charmes, pour des raisons qui ne sont révélées que dans le livre du Bwork Mage." ));
            allItems.Add(new Items ( 473, "Bois de Châtaignier", "L'odeur du Bois de Châtaignier repousse les mouches, sauf celles qui aiment les châtaignes et celles qui ont perdu l'odorat, ce qui explique pourquoi l'odeur du bois de Châtaignier ne repousse en fait aucune mouche." ));
            allItems.Add(new Items ( 474, "Bois de Merisier", "Ce bois est recherché en ébénisterie du fait de sa texture compacte et de sa couleur brun rougeâtre qu'il acquiert avec le temps." ));
            allItems.Add(new Items ( 476, "Bois de Noyer", "Le Bois de Noyer est très utilisé pour confectionner des meubles. Pour des raisons obscures, certains charpentiers se refusent à l'utiliser pour leurs bateaux." ));
            allItems.Add(new Items ( 477, "Sang de Dragon Cochon", "Cette fiole est remplie d'un sang qui conférait au Dragon Cochon des pouvoirs diaboliques. A consommer avec modération, ou a faire cuire si vous aimez le boudin noir de Dragon Cochon." ));
            allItems.Add(new Items ( 478, "Elagueuse de Charme", "Cette hache doit son nom à Woodee la Bûcheronne, qui savait user de ses charmes pour détourner l'attention des autres Bûcherons et ainsi garder secret l'emplacement des rares Charmes dont elle voulait préserver l'existence." ));
            allItems.Add(new Items ( 479, "Ivoire", "L'Ivoire est très recherché par les collectionneurs, pour sa blancheur légendaire, et par les Forgerons, pour sa capacité à rendre les armes mortelles. Ce qui est certain, c'est que dans la région d'Amakna, l'Ivoire a vraiment la côte." ));
            allItems.Add(new Items ( 480, "Minerai Etrange", "Ce Minerai a été extrait d'une mystérieuse carrière inconnue. Contrairement à tous les objets étranges d'Amakna, celui-ci n'est pas magique du tout." ));
            allItems.Add(new Items ( 481, "Crâne du Dragon Cochon", "Malgrès sa faible contenance, ce crâne est une vraie pièce de collection, sa relative ressemblance avec un crâne de Iop, a permis à de nombreux étudiants en médecine d'expliquer l'origine des faiblesses intellectuelles de certains Iop." ));
            allItems.Add(new Items ( 482, "Pelle Teuze", "Après une soirée un peu trop arrosée à la taverne qui a débouché sur une bagarre, un Enutrof s'est aperçu que cette pelle conçue à l'origine pour les grands travaux de construction était une excellente arme." ));
            allItems.Add(new Items ( 483, "Grande Pelle Teuze", "Après une soirée un peu trop arrosée à la taverne qui a débouché sur une bagarre, un Enutrof s'est aperçu que cette pelle conçue à l'origine pour les grands travaux de construction était une excellente arme." ));
            allItems.Add(new Items ( 484, "Puissante Pelle Teuze", "Après une soirée un peu trop arrosée à la taverne qui a débouché sur une bagarre, un Enutrof s'est aperçu que cette pelle conçue à l'origine pour les grands travaux de construction était une excellente arme." ));
            allItems.Add(new Items ( 485, "Slip en cuir du Chafer Lancier", "Lorsque vient l'hiver, le Chafer Lancier ne sort jamais sans son slip en cuir, qui lui permet de garder ses articulations bien au chaud ou de distraire ses adversaires le cas échéant." ));
            allItems.Add(new Items ( 486, "Cuir de Sanglier", "Ce cuir est particulièrement résistant, mais sent le sanglier mâle en rût, ce qui explique pourquoi les paysans locaux s'en servent pour piéger les femelles sangliers, qui sont trop stupides pour déjouer un stratagème si fourbe." ));
            allItems.Add(new Items ( 487, "Cuir du Dragon Cochon", "A force de se prendre pour un Dragon, le Dragon Cochon s'est brûlé les poils de la couenne, ce qui explique en partie l'odeur de cochon grillé que dégage ce cuir." ));
            allItems.Add(new Items ( 489, "Loupe", "La loupe permet d'examiner avec attention les éléments du décor pour y déceler des trésors." ));
            allItems.Add(new Items ( 490, "Fiole", "Cette fiole, vitale à tout alchimiste qui se respecte, permet de réaliser les meilleures potions." ));
            allItems.Add(new Items ( 491, "Sertisseur", "Cet objet permet au Bijoutier de travailler ses joailleries." ));
            allItems.Add(new Items ( 492, "Le Pétrisseur", "Outil indispensable si vous voulez exercer le métier de boulanger-pâtissier." ));
            allItems.Add(new Items ( 493, "Marteau de Forgeur de Marteaux", "Outil indispensable à la création de marteaux." ));
            allItems.Add(new Items ( 494, "Marteau de Forgeur d\'Epées", "Outil indispensable à la création d'épées." ));
            allItems.Add(new Items ( 495, "Marteau de Forgeur de Dagues", "Outil indispensable à la création de dagues." ));
            allItems.Add(new Items ( 496, "Marteau de Forgeur de Pelles", "Outil indispensable à la création de pelles." ));
            allItems.Add(new Items ( 497, "Pioche du Mineur", "Outil indispensable à la collecte des minerais." ));
            allItems.Add(new Items ( 498, "Tailladeuse de Bâton", "Outil indispensable à la création des bâtons." ));
            allItems.Add(new Items ( 499, "Tailladeuse de Baguette", "Outil indispensable à la création des baguettes." ));
            allItems.Add(new Items ( 500, "Tailladeuse d\'Arc", "Outil indispensable à la création des arcs." ));
            allItems.Add(new Items ( 501, "Sève d\'Abraknyde Retravaillée", "Cette sève sirupeuse a été retravaillée par un Alchimiste de renom, pour lui permettre de redonner vie aux Racines d'Abraknydes. De toute évidence, ce genre de sève ne doit être manipulée que par des mains expertes." ));
            allItems.Add(new Items ( 502, "Trancheuse d\'If", "Cette hache est une des seules à pouvoir couper des arbres envoûtés, on peut donc par extension, affirmer qu'elle peut trancher tout ce qui est envoûté, vivant ou non." ));
            allItems.Add(new Items ( 515, "Boulgourde des Clairières", "Cette hache a appartenu à Brouce Boulgoure le fameux Bûcheron, marié jadis avec Kassiopée Baine, fille d'un autre grand Bûcheron. Cette arme lui permit de se défendre maintes fois lorsqu'il partait la nuit couper des forêts entières." ));
            allItems.Add(new Items ( 519, "Poudre de Perlinpainpain", "Comme son nom l'indique cette poudre est faite à partir de graines de Lin et de Pain. Mais ça n'explique pas pourquoi cette poudre est magique." ));
            allItems.Add(new Items ( 520, "Pain aux Graines de Pavot", "La confection de ce pain est délicate, car les boulangers qui en respirent les fumées lors de la cuisson, sont victimes de symptômes étranges et se mettent à danser autour de leur boulangerie." ));
            allItems.Add(new Items ( 521, "Pain aux Graines de Sésame", "Les Kwaks les plus audacieux n'hésitent pas à se déguiser en boulanger pour subtiliser les Graines de Sésame, mais cette supercherie ne fonctionne jamais sauf chez les Iop Boulangers." ));
            allItems.Add(new Items ( 522, "Pain aux Graines de Lin", "Ce pain est riche en protéines, mais pauvre en goût. Et oui, la vie est toujours une question de choix." ));
            allItems.Add(new Items ( 524, "Pain aux Noisettes", "Ce pain au goût subtil est garanti sans colorants, ni conservateurs, tout le contraire de la Gelée Royale." ));
            allItems.Add(new Items ( 526, "Pain aux Céréales", "Ce pain composé d'une variété de céréales sans précédent, est suffisamment riche en protéines pour rendre la vie à un Chafer." ));
            allItems.Add(new Items ( 527, "Pain aux Noix", "Ce pain est cuit avec du bois de Noyer et les noix sont grillées avant d'être fourrées dans la mie." ));
            allItems.Add(new Items ( 528, "Pain au Blé Complet", "Ce pain est enrichi en blé complet, ce qui en fait un pain de qualité pour les gens exigeants." ));
            allItems.Add(new Items ( 529, "Farine d\'Orge", "Cette farine s'obtient au moulin, en écrasant des grains d'orge, elle permet de donner une légèreté à la mie de pain qui n'a pas d'équivalent, à moins, bien entendu, de faire emploi d'une magie occulte lors de la cuisson de vos pains." ));
            allItems.Add(new Items ( 530, "Farine de Seigle", "Cette farine hérite de tous les bienfaits du Seigle. Selon la légende, le Seigle qui germe à l'aurore, ne mûrit jamais. Par extension, certains paysans font leur enfants à l'aurore en espérant qu'ils ne vieillissent jamais." ));
            allItems.Add(new Items ( 531, "Farine d\'Avoine", "Cette farine ne lève pas à la cuisson, ce qui permet de faire des pains vraiment denses, particulièrement utiles lorsqu'il faut distribuer des pains aux monstres." ));
            allItems.Add(new Items ( 532, "Seigle", "Cette céréale sombre a la faculté de pousser dans des conditions difficiles, c'est une des rares céréales à subsister après de fortes sécheresses." ));
            allItems.Add(new Items ( 533, "Avoine", "Cette céréale est utilisée pour nourrir les jeunes Tabis, lorsqu'ils n'ont pas encore les dents assez solides pour ruminer. Les paysans font tout de même attention dans la mesure où l'avoine cause parfois des crises d'aérophagie chez le bétail sensible." ));
            allItems.Add(new Items ( 534, "Farine de Malt", "Cette farine blanche est obtenue en écrasant des grains d'Orge germés, ce qui donne un goût parfois acidulé au pain, et dans le meilleur des cas, un goût de bière éventée, utile pour les sevrages progressifs des amateurs de bière. Une fois cuite, cette farine permet en effet de mieux supporter les cuites." ));
            allItems.Add(new Items ( 535, "Farine de Houblon", "Cette farine permet de donner un délicieux goût de bière au pain, sans avoir à utiliser de la bière pour faire son pain. Il faut parfois reconnaître que la nature est bien faite." ));
            allItems.Add(new Items ( 536, "Pain aux Flocons d\'Avoine", "Ce pain à la mie particulièrement légère est parfait pour les personnes qui n'ont pas été gâtées par Mère Nature, et qui n'ont qu'un tout petit estomac." ));
            allItems.Add(new Items ( 537, "Pomme de Terre", "De manière générale, lorsque vous tombez sur cet objet, c'est que vous êtes soit au milieu d'un champ, soit au fond d'un cachot." ));
            allItems.Add(new Items ( 538, "Pommes de Terre épluchées", "Cette Pomme de terre est dépourvue de sa pelure, ce qui en fait une denrée commestible." ));
            allItems.Add(new Items ( 539, "Pain de Seigle", "Ce pain est fait à partir de farine de Seigle, ce qui lui donne à la mie, une couleur brune particulière, mais surtout un léger goût sucré." ));
            allItems.Add(new Items ( 543, "Pierre de Diamant", "Correctement polie, cette pierre peut donner un magnifique Diamant." ));
            allItems.Add(new Items ( 544, "Pierre d\'Emeraude", "Correctement polie, cette pierre peut donner une magnifique Emeraude." ));
            allItems.Add(new Items ( 545, "Pierre de Cristal", "Correctement polie, cette pierre peut donner un magnifique Cristal." ));
            allItems.Add(new Items ( 546, "Pierre de Saphir", "Correctement polie, cette pierre peut donner un magnifique Saphir" ));
            allItems.Add(new Items ( 547, "Pierre de Rubis", "Correctement polie, cette pierre peut donner un magnifique Rubis." ));
            allItems.Add(new Items ( 548, "Potion de Rappel", "Cette potion permet de revenir au dernier point de sauvegarde." ));
            allItems.Add(new Items ( 577, "Faux du Paysan", "Cette faux n'est pas très aiguisée et surtout un peu rouillée, ce qui en fait une arme peu dangereuse. En revanche, pour faucher, c'est l'outil idéal." ));
            allItems.Add(new Items ( 578, "Pierre à Aiguiser", "Je ne vous jette pas la pierre Pierre !" ));
            allItems.Add(new Items ( 579, "Coupe Cuir", "Outil indispensable à la création de chaussures et de ceintures." ));
            allItems.Add(new Items ( 580, "Potion Ghetto Raide", "Cette potion permet de retrouver quelques points d'énergie." ));
            allItems.Add(new Items ( 582, "Farine Blanche", "Cette farine nécessite plusieurs céréales pour être obtenue, elle est cependant très appréciée pour sa qualité panifiable. Et pourtant au premier regard, rien ne la distingue d'une farine classique." ));
            allItems.Add(new Items ( 583, "Farine Bise", "Cette farine mi-blanche tire son nom de son inventeur, une jolie paysanne qui aimait faire des bises, les joues pleines de farine. Quelle coquine." ));
            allItems.Add(new Items ( 586, "Farine Paysanne", "C'est en affrontant la femme de Brouce Boulgoure à coups de sacs de farine, que Shika Ingalsse a eu l'idée de créer cette farine devenue populaire. Les combats de femmes apportent parfois beaucoup plus que de la distraction pour la plèbe." ));
            allItems.Add(new Items ( 587, "Farine Complète", "Cette farine est issue d'une multitude de céréales, ce qui en fait une farine particulièrement complète, très utile pour les pains complets." ));
            allItems.Add(new Items ( 592, "Corde d\'Ivan Nowé", "Cet arc a appartenu à Ivan Nowé, un Crâ téméraire, qui a pourfendu plus d'un Tofu égaré. Cet arc a la particularité de faire des coups pour le moins surprenants qui laissent perplexes puis agonisants les ennemis." ));
            allItems.Add(new Items ( 593, "Orchidée Freyesque", "Cette fleur est d'une rareté et d'une beauté inimaginable, mais elle est presque introuvable. En effet, les Crapauds ont tendance à les ramener dans leur campement pour nourrir leur progéniture." ));
            allItems.Add(new Items ( 594, "Edelweiss", "Cette fleur est d'une rareté et d'une beauté inimaginable, mais elle est presque introuvable. Pour cause, sa magie est si puissante qu'elle serait capable de faire revivre certains morts." ));
            allItems.Add(new Items ( 596, "Canne à Pêche Courte", "Cette petite canne à pêche est idéale pour s'amuser à pêcher les petits poissons dans les mares d'eau." ));
            allItems.Add(new Items ( 597, "Filet simple de capture", "Ce filet très résistant permet de capturer la plus sauvage des montures, à condition d'avoir réussi à l'hypnotiser ou à la distraire un peu avant." ));
            allItems.Add(new Items ( 598, "Greu-Vette", "Les Greu-Vettes sont appréciées pour leur couleur rose et leur goût raffiné. Elles sont cependant détestées pour leur mauvais caractère légendaire et leurs antennes gominées." ));
            allItems.Add(new Items ( 600, "Kralamoure", "Le Kralamoure est un grand romantique, ses yeux de petit Tofu lui donnent un air un peu niais, mais ne vous y méprenez pas, le Kralamoure est très agressif envers les étrangers." ));
            allItems.Add(new Items ( 602, "Requin Marteau-Faucille", "Ce Requin rouge est un requin militant, d'instinct grégaire, l'individu n'existe pas au sein du groupe, même si leur chef, le Requin Stakanoviou est porté au rang d'idole." ));
            allItems.Add(new Items ( 603, "Poisson Chaton", "Ce poisson a la particularité d'avoir des moustaches et de ronronner sous l'eau, ce qui ne manque pas de faire peur aux Poissons Volants." ));
            allItems.Add(new Items ( 607, "Truite Vaseuse", "La Truite Vaseuse est une cousine éloignée du Saumoon agressif de l'île de Moon. Cette truite est aussi bonne cuite que crue, qui l'eut cru ?" ));
            allItems.Add(new Items ( 610, "Amulette en Crabe", "Cette amulette blanchâtre est confectionnée avec du Crabe, ce qui en fait une amulette très fragile mais originale." ));
            allItems.Add(new Items ( 616, "Amulette du Marin", "Cette amulette, sertie d'écailles de poisson, est censée porter chance à son propriétaire lorsqu'il part en mer les jours de tempête." ));
            allItems.Add(new Items ( 617, "Amulette du Pirate", "Cette amulette terrifiante a impressionné plus d'un marin, la porter sur soi suffit à faire de vous une personne redoutée." ));
            allItems.Add(new Items ( 619, "Bâton du Maître des Bouftous", "Ce bâton permettait à Farle Ingalsse, éleveur de Bouftous, d'invoquer plus facilement des Tofus gardiens de troupeaux. Au fil du temps, ce bâton s'est révélé être une excellente arme de combat." ));
            allItems.Add(new Items ( 620, "Découpeuse de Kralamoure", "Cette épée a permis au célèbre marin Mobby Diqueriveurse, de découper en tranches, les tentacules du célèbre Kralamoure Géant de l'île de Moon, réputé pour étouffer les marins saouls et saouler les marins étouffés." ));
            allItems.Add(new Items ( 629, "Chapeau Pointu", "Ce magnifique chapeau permettra à votre héros d'être la plus belle pour aller danser au bal masqué (hohé hohé)." ));
            allItems.Add(new Items ( 638, "Amulette du Pêcheur de Somoon", "Pêcher un Somoon relève de l'exploit, mais avec cette amulette sur soi, plus rien n'est impossible." ));
            allItems.Add(new Items ( 639, "Amulette des Abîmes", "Cette amulette a été retrouvée dans l'épave échouée du célèbre pêcheur de Kralamoure, Moby Diqueriveurse, qui était également populaire pour ses boutades vaseuses au bar des pirates." ));
            allItems.Add(new Items ( 640, "Arc des Rivages", "Cet arc permettait aux pêcheurs de nettoyer les rivages de toute la vermine environnante sans avoir à se lever de leur siège, et cela depuis la fenêtre du bar des pirates." ));
            allItems.Add(new Items ( 641, "Coton Ancestral", "Ce Coton est doux comme le pelage d'une jeune Ecaflipette, blanc comme un nuage de printemps, mais fragile comme une plume de Tofu." ));
            allItems.Add(new Items ( 642, "Cocon de Ver à Soie", "Cet énorme cocon blanchâtre, peut être déroulé pour obtenir un magnifique fil de soie. nL'aventurier Harry Hane l'aurait même utilisé pour pouvoir sortir du labyrinthe du Dark Vlad." ));
            allItems.Add(new Items ( 643, "Fil de Soie", "Ce fil est d'une finesse et d'une souplesse sans équivalent. C'est de loin la matière préférée du Chafer dès qu'il s'agit de s'équiper en sous-vêtements de combat." ));
            allItems.Add(new Items ( 646, "Poils de Black Tiwabbit", "Ce poil tout noir à l'avantage ne de pas être salissant, ce qui ne manquera pas d'intéresser les héros peu portés sur l'hygiène et la coquetterie. Attention cependant, ce poil a tendance à déteindre au contact d'une sueur abondante et corrosive." ));
            allItems.Add(new Items ( 648, "Poils de Wabbit", "Ce poil n'est pas très prisé par les Tailleurs expérimentés qui lui préfèrent le poil des autres Wabbits, mais il a le mérite de faire fuir les Moskitos à cause de son odeur de Citronnelle Sauvage." ));
            allItems.Add(new Items ( 649, "Poils de Wo Wabbit", "Ce poil est si gras, qu'il permet de garder les étoffes imperméables aux intempéries." ));
            allItems.Add(new Items ( 650, "Poils de Grand Pa Wabbit", "Ce poil malodorant est délicat à utiliser en couture à cause de sa tendance à friser au contact du jus de Cawotte." ));
            allItems.Add(new Items ( 651, "Etoffe du Mulou", "Cette étoffe en Poils de Mulou est très rare, mais sa constitution lui permet de résister à toutes formes d'agressions physiques et parfois même magiques." ));
            allItems.Add(new Items ( 652, "Etoffe du Sanglier", "Cette étoffe en Poils de Sanglier est rugueuse et malodorante, mais protège efficacement des attaques physiques." ));
            allItems.Add(new Items ( 653, "Etoffe du Prespic", "Sous ses pics, le Prespic dispose d'une fourrure rousse très douce, qui dégage une odeur désagréable lorsqu'il pleut. Heureusement pour vous, il ne pleut jamais en Amakna, mais évitez d'utiliser cette étoffe pour vous confectionner un caleçon de bain." ));
            allItems.Add(new Items ( 654, "Etoffe du Wabbit", "Cette étoffe en Poils de Barbe du Grand Pa Wabbit est très touffue, mais également irritante, un peu comme une grosse chenille urticante." ));
            allItems.Add(new Items ( 657, "Bâton du Maître des Tofus", "Ce bâton permettait à Shika Ingalsse, éleveuse de Tofus, d'invoquer plus facilement des Tofus gardiens de troupeaux. Au fil du temps, ce bâton s'est révélé être une excellente arme de combat." ));
            allItems.Add(new Items ( 658, "Bâton du Maître des Tabis", "Ce bâton permettait à Gramine Ingalsse, éleveuse de Tabis, d'invoquer plus facilement des Tofus gardiens de troupeaux. Au fil du temps, ce bâton s'est révélé être une excellente arme de combat." ));
            allItems.Add(new Items ( 673, "Mangeuse de Châtaignier", "Cette hache n'a rien de particulier si ce n'est que la légèreté naturelle du Bois de Châtaignier, permet à cette hache d'avoir un manche aussi léger qu'une plume de jeune Tofu Maléfique." ));
            allItems.Add(new Items ( 674, "Fendeuse de Merisier", "" ));
            allItems.Add(new Items ( 675, "Trancheuse de Noyer", "Cette hache à l'aspect rudimentaire, n'en est pas moins efficace. C'est avec cet outil que Brouce Boulgoure le Bûcheron a gagné le concours de tranchage de Bworks nains. Voilà qui en dit long sur l'efficacité de cette hache." ));
            allItems.Add(new Items ( 676, "Mangeuse d\'Erable", "Cette hache a la particularité de ne jamais rouiller, pour éviter de désagréables surprises lorsque les Bûcherons gourmands décident de lécher le sirop d'érable sur la lame après une éprouvante journée d'abattage d'érables." ));
            allItems.Add(new Items ( 677, "Cape du Pirate", "Cette cape a appartenu à un vieux pirate du nom de Zokrim. Il devait être plutôt malchanceux..." ));
            allItems.Add(new Items ( 678, "Parchemin d\'Ivoire", "(Double clic pour gagner de l'expérience) nCe parchemin permet de gagner de l'expérience sans prendre de risques." ));
            allItems.Add(new Items ( 679, "Parchemin Blanc", "(Double clic pour gagner de l'expérience) nCe parchemin permet de gagner de l'expérience sans prendre de risques." ));
            allItems.Add(new Items ( 680, "Parchemin Doré", "(Double clic pour gagner de l'expérience) nCe parchemin permet de gagner de l'expérience sans prendre de risques." ));
            allItems.Add(new Items ( 683, "Petit Parchemin de Force", "(Double clic pour gagner de la force) nCe parchemin permet de gagner de la force de manière définitive sans prendre de risque. nAttention cependant, vous ne pourrez l'utiliser que si votre force est inférieure à 25." ));
            allItems.Add(new Items ( 684, "Parchemin de Sorts", "(Double clic pour gagner des Points de Sorts) nCe parchemin permet de gagner des Points de Sorts de manière définitive sans prendre de risques." ));
            allItems.Add(new Items ( 686, "Petit Parchemin d\'Intelligence", "(Double clic pour gagner des Points d'Intelligence) nCe parchemin permet de gagner des Points d'Intelligence de manière définitive sans prendre de risques. nAttention cependant, vous ne pourrez l'utiliser que si votre intelligence est inférieure à 25." ));
            allItems.Add(new Items ( 690, "Farine de Lin", "Cette farine est très étrange, car elle est n'est pas issue d'une céréale, ce qui explique pourquoi elle ne ressemble à aucune autre farine." ));
            allItems.Add(new Items ( 692, "Pain Consistant", "Ce pain est fait à partir de Farine de Lin, ce qui explique pourquoi il est si gras, et pourquoi sa mie est brune. Très riche en huiles essentielles, il faut cependant avouer qu'il a très mauvais goût." ));
            allItems.Add(new Items ( 694, "Dofus Pourpre", "C'est le Dragon du Feu Ignemikhal qui créa le Dofus Pourpre, un Œuf dont le moins qu'on puisse dire, c'est qu'il sent le soufre - mais il sent bien d'autres choses encore..." ));
            allItems.Add(new Items ( 695, "Parchemin de Bûcheron", "(Double clic pour gagner des Points d'expérience)" ));
            allItems.Add(new Items ( 696, "La Coiffe du Ploukosse", "La Coiffe du Ploukosse, sous ses dehors " ));
            allItems.Add(new Items ( 697, "Makroute", "Makroute est un chien très affectueux... Le problème, c'est que lorsqu'il trouve un nouveau copain, il ne le lâche plus... Il a bien dû se faire 30 millions d'amis de cette façon..." ));
            allItems.Add(new Items ( 698, "Choipo Podlard", "Le Choipo Podlard vient d'une grande école de magie. Créature vivante, il a rencontré les plus grands magiciens de notre temps, tels que Mme Magoa AlaGale, Mr Doublegore et le célèbre Remy Krokeur." ));
            allItems.Add(new Items ( 699, "Le Troma", "Le Troma est aussi craignos qu'un Rasboul en poil de Bitouf. (Pour ceux qui ne savent pas ce qu'est un Rasboul en poil de bitouf, référez-vous aux grands livres des Craps à Poules)." ));
            allItems.Add(new Items ( 700, "Pandawa", "Ah le Pandawa, vraiment quelle grosse tête il a. Comme son pote Makroute, il passe son temps sur la tête de son maître. Nul besoin de se déplacer, lorsqu'il est motivé, il arrive même à subtiliser la nourriture de son porteur." ));
            allItems.Add(new Items ( 701, "Le Oiram", "Le Oiram a fait de nombreux adeptes de part le monde, du fait de son côté très classieux... Ce chapeau a longtemps été porté par un plombier qui passait son temps à sauter et courir..." ));
            allItems.Add(new Items ( 702, "Kritter", "Ce crâne de démon reconstruit à partir d'ossements divers et variés apporte grâce et élégance à son porteur... Vos amies vont vous jalouser..." ));
            allItems.Add(new Items ( 703, "Dora", "Ce chapeau, en forme de corbeille à fruits en osier renversée, est en fait une corbeille à fruits renversée magique. Ce qui ne manquera pas d'impressionner votre entourage, surtout s'il est composé d'apprentis Bworks Mages." ));
            allItems.Add(new Items ( 704, "Dantgoule", "Ce chapeau, confectionné à partir de Slips en cuir de Chafer Lancier tressés entre eux, vous rendra de toute évidence irrésistible auprès des femelles Bwork Mages, réputées pour leur féminité débordante, accentuée par leur barbe rousse." ));
            allItems.Add(new Items ( 705, "Masque Tromatisant", "Si vous voulez être traumatisé, il vous faut absolument cette magnifique noix de coco trouée. Nous rappelons à l'utilisateur que l'intérieur est équipé d'une paille et de boissons rafraîchissantes." ));
            allItems.Add(new Items ( 706, "Crystal O\'Boul", "Hugne " ));
            allItems.Add(new Items ( 707, "Minotokorno", "Ce chapeau en Corne de Minotoror est très efficace pour impressionner ses adversaires en se faisant passer pour un Minotoror. Cette ruse ne semble fonctionner que sur les adversaires simplets. Un Iop ou un Tofu devrait faire l'affaire." ));
            allItems.Add(new Items ( 708, "Houde", "Ce chapeau en peau de larve verte magique donne à son porteur une allure guillerette, mais également un peu stupide. On ne peut pas tout avoir dans la vie, le porteur de ce chapeau en est d'ailleurs un excellent exemple." ));
            allItems.Add(new Items ( 709, "Lorko Kasko", "Ce chapeau en écorce enchantée appartenait à Lorko, un Bûcheron réputé qui n'a pas hésité à défier le célèbre Bûcheron Brouce Boulgoure, lors d'un fameux duel de déracinage d'Orme à mains nues dans la clairière de Liroye Merline." ));
            allItems.Add(new Items ( 710, "Korko Klako", "Lo Korko Klako ost ouno chapo kilo bo. Tros ostotiko, tros bolo, il so porto par los plus bo dos Sadida." ));
            allItems.Add(new Items ( 711, "Jouik Krampe", "Ce magnifique masque en cuir appartenait à Jouik Krampe, cousin de Zeurg. Tous deux étaient très portés sur les vêtements en cuir moulant. Un peu comme le Captain Chafer, la perversité en moins." ));
            allItems.Add(new Items ( 712, "Gulliver", "Ce chapeau confectionné à partir d'oreilles cousues entre elles, vous rendra tout simplement unique tant il est original. On raconte qu'il a jadis permis à Rakmar Bilgout de séduire la reine des Bouftous, pour ensuite l'égorger plus facilement." ));
            allItems.Add(new Items ( 713, "Parchemin de Forgeur d\'épée", "(Double clic pour gagner des Points d'expérience)" ));
            allItems.Add(new Items ( 714, "Parchemin de Forgeur de dagues", "(Double clic pour gagner des Points d'expérience)" ));
            allItems.Add(new Items ( 715, "Parchemin de Sculpteur d\'arcs", "(Double clic pour gagner des Points d'expérience)" ));
            allItems.Add(new Items ( 716, "Parchemin de Sculpteur de bâtons", "(Double clic pour gagner des Points d'expérience)" ));
            allItems.Add(new Items ( 717, "Parchemin de Sculpteur de baguettes", "(Double clic pour gagner des Points d'expérience)" ));
            allItems.Add(new Items ( 718, "Parchemin de Sort Flamiche", "(Double click pour apprendre le sort) nCe parchemin vous permettra d'apprendre le sort élémentaire Flamiche." ));
            allItems.Add(new Items ( 719, "Parchemin de Sort Libération", "(Double click pour apprendre le sort) nCe parchemin vous permettra d'apprendre le sort élémentaire Libération." ));
            allItems.Add(new Items ( 720, "Parchemin de Sort Foudroiement", "(Double click pour apprendre le sort) nCe parchemin vous permettra d'apprendre le sort élémentaire Foudroiement." ));
            allItems.Add(new Items ( 721, "Parchemin de Sort Invocation d\'Arakne", "(Double click pour apprendre le sort) nCe parchemin vous permettra d'apprendre le sort d'invocation d'Arakne." ));
            allItems.Add(new Items ( 724, "Sort Maîtrise du Bâton", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant un bâton." ));
            allItems.Add(new Items ( 725, "Sort Maîtrise de l\'Epée", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant une épée." ));
            allItems.Add(new Items ( 726, "Sort Maîtrise de l\'Arc", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant un arc." ));
            allItems.Add(new Items ( 727, "Sort Maîtrise du Marteau", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant un marteau." ));
            allItems.Add(new Items ( 728, "Sort Maîtrise de la Baguette", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant une baguette." ));
            allItems.Add(new Items ( 729, "Sort Maîtrise des Dagues", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant une dague." ));
            allItems.Add(new Items ( 730, "Sort Maîtrise de la Pelle", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant une pelle." ));
            allItems.Add(new Items ( 731, "Parchemin de Sort Spécial Brokle", "(Double clic pour apprendre le sort) nCe parchemin vous permet d'apprendre le sort Brokle... Réservé aux Iops uniquement." ));
            allItems.Add(new Items ( 732, "Alliance de Silimelle", "Cette magnifique alliance a été volée à Silimelle la Brasseuse de Bière, lors d'une soirée un peu trop arrosée à la taverne. Voilà ce qui arrive lorsque l'on paie des coups à des inconnus..." ));
            allItems.Add(new Items ( 733, "Bottes de Ragalde", "Ragalde était sûrement un des Guerriers Iop les plus téméraires, ces bottes magiques lui ont permis d'occire bon nombre d'indésirables." ));
            allItems.Add(new Items ( 734, "Ceinture de Sécurité", "Cette ceinture permettait aux gardes du Château d'Amakna de contenir les émeutes et autres débordements lors des fêtes populaires." ));
            allItems.Add(new Items ( 737, "Dofus Emeraude", "Plus vert qu'un Tofu malade, le Dofus Emeraude fut créé par Aerafal, le Dragon de l'Air. Pour créer quelque chose d'aussi vert, il devait avoir le mal de l'air. Un comble, non ?" ));
            allItems.Add(new Items ( 739, "Dofus Turquoise", "Le Dofus Turquoise fut créé par Aguabrial, le Dragon de l'Eau. Quand on y pense, c'est fou ce qu'un Dragon peut faire avec de l'eau..." ));
            allItems.Add(new Items ( 742, "Bâton Cornu", "" ));
            allItems.Add(new Items ( 743, "Brioche Dorée", "La recette de cette Brioche a été élaborée par Xavier le Boulanger en personne qui voulait en faire une brioche délicieuse et légère à la fois. On peut sans se tromper, dire qu'il a réussi." ));
            allItems.Add(new Items ( 744, "Cape de Bowisse", "Cette cape est comme son porteur, unique en son genre et trop souvent sous-estimée. nElle est constituée d'un assemblage de peaux de larves vertes, ce qui lui donne un aspect rugueux des plus sympathique." ));
            allItems.Add(new Items ( 745, "Lingot d\'Or", "Ce lingot est obtenu en faisant fondre de l'Or à l'état brut." ));
            allItems.Add(new Items ( 746, "Ebonite", "Cet alliage très rare permet de fabriquer des objets très rares et souvent mortels. En effet, l'Ebonite est un métal qui une fois rouillé, devient un véritable poison." ));
            allItems.Add(new Items ( 747, "Aluminite", "Cet alliage est d'une légèreté impressionnante, ce qui en fait un métal de choix pour les petites dagues ou les épées courtes." ));
            allItems.Add(new Items ( 748, "Magnésite", "Cet alliage original est presque magique, il brille et vibre la nuit, ce qui peut être suffisant pour effrayer un Bwork un peu trop crédule." ));
            allItems.Add(new Items ( 749, "Bakélélite", "Cet alliage n'est pas des plus solide, mais correctement aiguisé, il devient aussi tranchant que la Lame d'un Chafer." ));
            allItems.Add(new Items ( 750, "Kouartz", "Cet alliage très étrange est capable de renfermer une énergie impressionnante, le rendant très instable dans certaines conditions. Les meilleurs Forgerons raffolent de cet alliage, qui leur permet de forger des armes aux pouvoirs presque légendaires." ));
            allItems.Add(new Items ( 752, "Sang du Vampire", "Ce sang est magique, ce qui en fait un ingrédient indispensable pour le Boudin Noir magique. Les effets secondaires sont cependant encore inconnus." ));
            allItems.Add(new Items ( 754, "Cape du Vampire", "Cette cape rouge sang a baigné dans le sang pendant des années. Ce type de cape est très prisée lors des Fêtes Dalouhines." ));
            allItems.Add(new Items ( 756, "Slip en Cuir Moulant du Vampire", "Pour se protéger des coups critiques aux endroits critiques, le Vampire utilise parfois son Slip en Cuir lorsqu'il doit partir en chasse. Ce Slip lui permet également de protéger les endroits critiques des coups de soleil." ));
            allItems.Add(new Items ( 757, "Gelée Bleutée", "Cette gelée est particulièrement collante, un peu comme une Bloqueuse enragée. Étrangement, elle colle particulièrement bien les plumes de Tofus. On se demande bien à quoi cela peut bien servir..." ));
            allItems.Add(new Items ( 758, "Cape du Justicier", "Cette cape ne peut être portée que par les grands justiciers d'Amakna, c'est à dire presque personne." ));
            allItems.Add(new Items ( 759, "Cape du Poolay Chogrelotant", "Cette magnifique cape a été confectionnée avec les plumes du dernier Poolay d'Amakna. Véritable rareté, les collectionneurs rêvent de l'avoir dans leur collection." ));
            allItems.Add(new Items ( 760, "Pain au curry sans curry", "Un prototype de Kwaker Vans, son goût et son efficacité sont toujours une surprise, malheureusement l'odeur, elle, est toujours la même." ));
            allItems.Add(new Items ( 761, "Poils de souris", "Ces poils de souris sont très fins et doux, et sont de ce fait très recherchés pour la confection de manteaux. Le manteau en peau et poils de souris fait fureur dans la haute société d'Amakna, il paraît que ça fait classe d'en porter." ));
            allItems.Add(new Items ( 764, "Amulette du Petit Hibou", "Cette petite amulette ravira les héros débutants." ));
            allItems.Add(new Items ( 765, "Faux de Farle Ingalsse", "Cette Faux a appartenu à Farle Ingalsse en personne, elle est suffisamment aiguisée pour faucher tous les types de céréales et tous les types de monstres qui se cachent dans les champs de céréales." ));
            allItems.Add(new Items ( 766, "Koliet Aclou", "Cette amulette permet d'occasionner plus de dégâts pour les aventuriers qui débutent." ));
            allItems.Add(new Items ( 767, "Gantie Royal", "Malgré un nom pompeux, cet anneau rend juste un peu plus fort, et un peu plus bête." ));
            allItems.Add(new Items ( 768, "Camaïneux", "Cet anneau influe sur le cours de votre destinée, il vous rend plus résistant, mais moins chanceux. Vous augmentez vos chances de vivre longtemps en diminuant vos chances de vivre heureux." ));
            allItems.Add(new Items ( 769, "Einekeineux", "Les bottes qui portent chance. Le problème c'est qu'elles vous rendent moins résistant en combat." ));
            allItems.Add(new Items ( 770, "Fecaflip", "Cette magnifique paire de bottes convertit un peu de votre force en intelligence." ));
            allItems.Add(new Items ( 771, "Mangeuse de Frêne", "Cette hache n'a rien d'exceptionnel, mais elle reste tout de même appréciée des Bûcherons débutants, qui l'aiment pour sa simplicité d'utilisation ." ));
            allItems.Add(new Items ( 772, "Cape Edépée", "On prétend que cette cape légendaire permet de devenir presque invincible si on la combine à l'épée légendaire des escrimeurs, le Dard Tagnan et au chapeau légendaire des Six Rameaux de Bergers Rahks." ));
            allItems.Add(new Items ( 773, "Cape Ellinie", "Cette cape vient du pays des Patitaliennes, un endroit où l'on parle avec les mains. Cela explique la grande liberté de mouvement laissée par celle-ci. Elle est en outre d'une légèreté surprenante." ));
            allItems.Add(new Items ( 774, "Cape Ulais", "Venant du Mont Taigüe ou du monde Aigüe selon la manière dont vous le prononcez, cette cape a conduit à l'empoisonnement de son ancienne propriétaire, mais bizarrement elle augmente la résistance de son porteur." ));
            allItems.Add(new Items ( 775, "Cape Abilité", "Tissée magiquement avec un soin infini, la Cape Abilité permet à son porteur de discerner les points faibles de ses adversaires pour leur porter des coups destructeurs." ));
            allItems.Add(new Items ( 776, "Cape Sulcorpe", "Ayant appartenu à une certaine Mulba, qui s'en servait pour transporter des objets encombrants dans sa quête des Sept Poules de Cristal venues des étoiles, cette cape augmente considérablement la force." ));
            allItems.Add(new Items ( 777, "Cape du Coq Hû", "Le Coq ayant donné ses plumes pour la confection de cette cape s'est débattu pendant plusieurs jours et plusieurs nuits avant de finalement tomber à bout de force et de se laisser déplumer. " ));
            allItems.Add(new Items ( 778, "Cape de Mori Arty", "Cette authentique cape de méchant est une preuve de votre appartenance à la bande du diaboliquement génial Mori Arty, dont on raconte que seule sa malchance égalait sa stupéfiante intelligence. Ne peut être portée que par les plus grands méchants." ));
            allItems.Add(new Items ( 779, "Cape Maj\'Hic", "Cette cape d'apparence quelconque n'est en fait pas tout à fait quelconque. Elle rendrait invisible son porteur aux yeux de ceux qui ne le regardent pas. Impressionnant !" ));
            allItems.Add(new Items ( 781, "Cape Itou Lascione", "Cette superbe cape a appartenu au Grand Stratéquerre. Ce célèbre seigneur de guerre et fin stratège, avide de conquête de l'univers, a bien vite abandonné tous ses projets après avoir revêtu cette cape. Étrange !" ));
            allItems.Add(new Items ( 782, "Agride", "Cette hache est assez particulière, elle est à la base destinée à l'apprentissage du métier de Bûcheron. Correctement utilisée, elle peut sectionner en deux un jeune Abraknyde trop insouciant, ce qui avouons-le, est très rare." ));
            allItems.Add(new Items ( 783, "Amulette du Bûcheron", "Cette amulette, exclusivement fabriquée à partir de bois précieux, est très prisée des Bûcherons qui lui attribuent bien souvent des pouvoirs qu'elle n'a pas." ));
            allItems.Add(new Items ( 784, "Bottes du Chat Buté", "Le Chat Buté est le surnom donné à un célèbre Ecaflip. Aucun Ecaflip n'aura été plus têtu que lui, et en combat seule sa chance le sauvait de son inexistante intelligence.  n" ));
            allItems.Add(new Items ( 785, "Anneau Fortifiant", "Cet anneau, transmis de Grand père Enutrof en Père Enutrof, procure à son porteur une formidable force. Celui-ci n'en est qu'une pâle copie, mais il saura tout de même satisfaire les collectionneurs, qu'ils soient Enutrof ou non." ));
            allItems.Add(new Items ( 786, "Pendentif du Hibou Chétif", "Née d'une erreur de manipulation d'un bijoutier, cette amulette augmente l'intelligence de son porteur. En revanche, elle donne généralement un puissant mal de crâne qui a pour effet d'amoindrir la condition physique de son propriétaire." ));
            allItems.Add(new Items ( 787, "Anneau Rexik", "Cet étrange petit anneau a la faculté d'augmenter l'agilité de son porteur, pourtant étrangement frappé d'un manque de chance." ));
            allItems.Add(new Items ( 789, "Ceinture du Kobeer", "Cette ceinture fait fureur auprès de la jeune population d'Amakna. Un couturier Sadida en est à l'origine. Lors de sa première vente, il s'exclama : " ));
            allItems.Add(new Items ( 790, "Cape de Bonne Espérance", "Les effets magiques de cette cape sont souvent surestimés, ce qui a causé la perte de nombreux aventuriers, notamment, le tristement célèbre Ménestrel Francisque Kabroule." ));
            allItems.Add(new Items ( 791, "Abraknydi Vivitus", "C'est un Bâton en racines d'Abraknyde encore vivantes. Attention, ce dernier a une fâcheuse tendance à se retourner contre son maître. Il peut être très efficace lorsqu'il décide de s'attaquer à la même cible que celle de son maître." ));
            allItems.Add(new Items ( 792, "Sève d\'Abraknyde", "Cette sève sirupeuse ne sert à rien sauf si vous être prêt à attendre qu'elle se fossilise en Ambre. On raconte cependant, qu'une Enutrofette astucieuse et particulièrement séduisante a trouvé un moyen pour accélérer ce processus." ));
            allItems.Add(new Items ( 793, "Potion de Vieillesse", "Cette potion permet d'accélérer certains processus chimiques, comme la formation de précipités. C'est un peu comme une levure surpuissante, le mauvais goût en moins." ));
            allItems.Add(new Items ( 795, "Parchemin de Force", "(Double click pour gagner de la force) nCe parchemin permet de gagner de la force de manière définitive sans prendre de risques. nAttention cependant, vous ne pourrez l'utiliser que si votre force est inférieure à 50." ));
