            allItems.Add(new Items ( 818, "Clé en Roche de Ded Aléïcar", "Cette petite clé en roche vous ouvrira la porte de la grotte de Ded Aléïcar. Mais encore faut-il la trouver..." ));
            allItems.Add(new Items ( 819, "Epée Sacrée", "Une épée sainte distribuée aux jeunes guerriers durant leur formation." ));
            allItems.Add(new Items ( 820, "Grande Epée Sacrée", "Une épée sainte distribuée aux jeunes guerriers durant leur formation." ));
            allItems.Add(new Items ( 821, "Puissante Epée Sacrée", "Une épée sainte distribuée aux jeunes guerriers durant leur formation." ));
            allItems.Add(new Items ( 822, "Epée du Chevalier", "L'épée du Chevalier est une épée solide et efficace, bien utilisée elle peut causer de gros dommages à vos ennemis." ));
            allItems.Add(new Items ( 823, "Epée du Grand Chevalier", "L'épée du Grand Chevalier est une épée solide et efficace, bien utilisée elle peut causer de gros dommages à vos ennemis." ));
            allItems.Add(new Items ( 824, "Epée du Puissant Chevalier", "L'épée du Puissant Chevalier est une épée solide et efficace, bien utilisée elle peut causer de gros dommages à vos ennemis." ));
            allItems.Add(new Items ( 825, "Lame du Bwork", "Cette épée d'apparence rudimentaire est très appréciée des Bworks les plus débiles, qui ne savent pas se servir d'un arc ou d'un bâton, certains confondent même la lame et le manche ce qui ne manque pas de faire rire leurs adversaires." ));
            allItems.Add(new Items ( 826, "Lame du Grand Bwork", "Cette épée d'apparence rudimentaire est très appréciée des Bworks les plus débiles, qui ne savent pas se servir d'un arc ou d'un bâton, certains confondent même la lame et le manche ce qui ne manque pas de faire rire leurs adversaires." ));
            allItems.Add(new Items ( 827, "Lame du Puissant Bwork", "Cette épée d'apparence rudimentaire est très appréciée des Bworks les plus débiles, qui ne savent pas se servir d'un arc ou d'un bâton, certains confondent même la lame et le manche ce qui ne manque pas de faire rire leurs adversaires." ));
            allItems.Add(new Items ( 828, "Arc Sifflant", "Cet arc tire si rapidement ses flèches, que l'on entend ces dernières partir de très loin. Pensez donc à l'équiper d'un silencieux, si c'est la discrétion que vous recherchez." ));
            allItems.Add(new Items ( 829, "Arc Hade", "À cause de sa faible portée, cet arc n'est pas très efficace, mais il permet tout de même d'affaiblir suffisamment l'ennemi avant de l'achever avec une autre arme ou magie." ));
            allItems.Add(new Items ( 830, "Baguette d\'Intelligence", "Cette baguette permet à son porteur d'augmenter son intelligence." ));
            allItems.Add(new Items ( 831, "Grande Baguette d\'Intelligence", "Cette baguette permet à son porteur d'augmenter son intelligence." ));
            allItems.Add(new Items ( 832, "Puissante Baguette d\'Intelligence", "Cette baguette permet à son porteur d'augmenter son intelligence." ));
            allItems.Add(new Items ( 833, "Baguette de Sagesse", "Cette baguette permet à son porteur d'augmenter sa sagesse." ));
            allItems.Add(new Items ( 834, "Grande Baguette de Sagesse", "Cette baguette permet à son porteur d'augmenter sa sagesse." ));
            allItems.Add(new Items ( 835, "Puissante Baguette de Sagesse", "Cette baguette permet à son porteur d'augmenter sa sagesse." ));
            allItems.Add(new Items ( 836, "Abranneau", "Cet anneau, forgé dans de l'écorce d'Abraknyde possède de grands pouvoirs et implique donc de grandes responsabilités. Cependant, malgré sa valeur, des rumeurs laissent entendre qu'il est maudit." ));
            allItems.Add(new Items ( 837, "Bottes de Kluh", "Ces bottes augmentent la force de leur porteur, mais risquent de le rendre un peu idiot au bout d'un certain temps, voire de le transformer en Iop en cas d'utilisation régulière et prolongée" ));
            allItems.Add(new Items ( 838, "Sang d\'Oni", "Cette fiole de sang d'Oni est très recherchée par les alchimistes expérimentés qui désirent donner à certaines armes, des propriétés particulièrement effrayantes." ));
            allItems.Add(new Items ( 840, "Poils du Minotoror", "Ces poils sentent très mauvais, mais sont d'une résistance à toute épreuve, porter sur soi une étoffe en poils de Minotoror fera de vous une personne redoutée de vos adversaires, et appréciée des mouches." ));
            allItems.Add(new Items ( 841, "Anneau de Satisfaction", "Cet anneau permettra a votre héros d'augmenter ses PA, en contrepartie de sa force et de son intelligence." ));
            allItems.Add(new Items ( 842, "Coquille de Dragoeuf Doré", "Cette coquille est presque incassable, cependant, en la polissant pendant des générations, les meilleurs Bijoutiers peuvent utiliser ses facultés magiques pour confectionner des amulettes aux pouvoirs exceptionnels." ));
            allItems.Add(new Items ( 843, "Coquille de Dragoeuf Noir", "Cette coquille est presque incassable, cependant, en la polissant pendant des générations, les meilleurs Bijoutiers peuvent utiliser ses facultés magiques pour confectionner des amulettes aux pouvoirs exceptionnels." ));
