            allItems.Add(new Items ( 862, "Sandales d\'Intelligence", "Ces bottes permettent d'augmenter l'intelligence de leur porteur, tout en lui conférant un style vraiment à la mode par les temps qui courent grâce à leur semelle en Bois de Noyer qui offre une adhérence remarquable." ));
            allItems.Add(new Items ( 863, "Bottes Agilesques", "Ces bottes permettent d'augmenter l'agilité de leur porteur, afin qu'il puisse se prendre pour un Tofu plus facilement, lorsque l'envie lui prend de faire l'idiot." ));
