            allItems.Add(new Items ( 880, "Laine de Bouftou Royal", "Cette laine est royale, elle est douce et légère, mais a tendance à démanger les peaux sensibles. Cette laine est en revanche très usée donc très fragile, étant donné l'âge du Bouftou Royal." ));
            allItems.Add(new Items ( 881, "Laine de Boufton Blanc", "Cette laine est extrêmement douce, car elle provient du duvet d'un Boufton. Attention tout de même, cette laine est encore imprégnée d'une odeur qui pourrait inciter la mère du Boufton à vous lécher si vous entrez en contact avec cette laine." ));
            allItems.Add(new Items ( 882, "Laine du Chef de Guerre Bouftou", "Cette laine sent très mauvais, mais elle a appartenu à un Chef de Guerre Bouftou, ce qui lui confère une grande valeur sentimentale auprès des guerriers." ));
            allItems.Add(new Items ( 883, "Cuir de Boufton Blanc", "Ce cuir très lisse est particulièrement adapté à la confection de sous-vêtements en cuir, le Slip en Cuir du Chafer en est un exemple frappant." ));
