            allItems.Add(new Items ( 885, "Laine de Boufton Noir", "Cette laine rugueuse n'est pas très utilisée par les tailleurs, elle est en revanche très appréciée des Enutrofs, qui s'en servent pour cacher leur calvitie naissante." ));
            allItems.Add(new Items ( 886, "Cuir de Bouftou Royal", "Ce cuir est royal, il est d'une souplesse extraordinaire, et les crevasses qu'il arbore, sont les témoins de la longue vie du Bouftou Royal. Il n'existe pas meilleur cuir dans toute la province d'Amakna." ));
