            allItems.Add(new Items ( 910, "Bottes d\'Hogmeiser", "Ces bottes ont appartenu à Hogmeiser pendant des années, avant qu'il ne les perdent lors d'un pari stupide avec Grizmine. La morale de l'histoire, c'est qu'il ne faut jamais jouer avec un Ecaflip." ));
            allItems.Add(new Items ( 911, "Bottes Usées d\'Hogmeiser", "Ces bottes usées ont appartenu à Hogmeiser pendant des années, elles sont tellement usées, qu'elles sont surtout handicapantes. Un très bon Cordonnier devrait pouvoir les réparer avec de la chance." ));
            allItems.Add(new Items ( 912, "Bottes Dorées d\'Hogmeiser", "Ces magnifiques bottes d'Hogmeiser sont recouvertes d'une fine couche de pierres précieuses, pour les rendre insensibles à certaines attaques magiques. C'est avec ces bottes que Brouce Boulgoure a écrasé la tête du Roi des Abraknydes il y a très longtemps, pour animer l'ouverture de la foire du Trool." ));
            allItems.Add(new Items ( 916, "Amulette de Silicate", "Silicate a confectionné cette Amulette un matin d'hiver, pour un célèbre aventurier du nom de Rimb Vardi, qui pourchassait un vieux Bwork Mage, responsable de plusieurs catastrophes naturelles déclenchées par sa magie trop approximative." ));
            allItems.Add(new Items ( 917, "La Feuille d\'Automne", "Cette Broche a été confectionnée un matin d'automne au milieu d'une forêt de Hêtres et d'Ormes non loin de l'île d'Olag-lacustre. Elle est considérée par nombre de bûcherons comme une véritable relique." ));
            allItems.Add(new Items ( 918, "Ambre Ancestrale", "Cette ambre provient d'un Abraknyde Ancestral, elle a donc une valeur inestimable, et peut conférer des propriétés magiques à certains objets si elle est travaillée par des mains habiles." ));
            allItems.Add(new Items ( 919, "Dagues Ancestrales", "Ces puissantes dagues, forgées à l'ancienne à partir d'Ambre Ancestrale, ont servi pendant la colonisation de l'île de Moon, avant que cette dernière ne redevienne sauvage." ));
            allItems.Add(new Items ( 920, "Bois Ancestral", "Ce bois provient d'un Orme de l'île d'Olag-lacustre, qui a été foudroyé un matin d'automne, par un Bwork Mage qui maîtrisait assez mal ses sorts élémentaires." ));
            allItems.Add(new Items ( 922, "Marteau du Forgeur de Haches", "Outil indispensable à la création de haches." ));
            allItems.Add(new Items ( 923, "Fendeuse d\'Abraknyde", "Cette hache de guerre a été forgée par Kademlia Boulgoure, pour combattre les plus terrifiants Abraknydes. La légende raconte que cette arme légendaire a tué en une nuit plus d'Abraknydes qu'il n'existe d'arbres en Amakna." ));
            allItems.Add(new Items ( 924, "Puissantes Bottes de Klime", "L'agilité que confère ces bottes permettait à Klime le cordonnier de monter facilement sur le dos de n'importe quel Bouftou. Il est mort en confondant un Bouftou avec une Gelée, après une soirée trop arrosée passée à la taverne." ));
            allItems.Add(new Items ( 926, "Bois Envoûté", "Ce bois a été envoûté par un Mage Abraknyde, pour donner vie à un petit Abraknyde une fois trempé dans l'eau. Il va de soi que ce bois est extrêmement magique." ));
