            allItems.Add(new Items ( 928, "Lame d\'Ogralimde", "Ogralimde était un grand guerrier qui devait sa force à Adyol son maître, un puissant Bwork Mage d'une intelligence surprenante. Mais l'élève n'a jamais dépassé le maître, contrairement à ce que raconte la légende." ));
