            allItems.Add(new Items ( 1338, "Potion de Secousse", "Cette potion vibrante, grâce à la faible magie de terre qu'elle emprisonne, est généralement utilisée pour enchanter un peu toutes sortes d'objets." ));
            allItems.Add(new Items ( 1339, "Clochettes", "Ces fleurs de muguet sont vraiment toutes petites, fragiles et mignonnes. On dit de plus qu'elles ont le pouvoir de calmer l'esprit et de guérir certains maux. Elles ont sûrement été conçues par une divinité qui souhaitait voir une plante à l'image des Eniripsas." ));
            allItems.Add(new Items ( 1340, "Potion d\'Eboulement", "Cette potion tremblante, grâce à la magie de terre qu'elle emprisonne, est généralement utilisée pour enchanter correctement toutes sortes d'objets." ));
            allItems.Add(new Items ( 1341, "Potion d\'Averse", "Cette potion clapotante, grâce à la magie d'eau qu'elle emprisonne, est généralement utilisée pour enchanter correctement toutes sortes d'objets." ));
            allItems.Add(new Items ( 1342, "Potion de Rafale", "Cette potion sifflante, grâce à la magie d'air qu'elle emprisonne, est généralement utilisée pour enchanter correctement toutes sortes d'objets." ));
            allItems.Add(new Items ( 1343, "Potion de Flambée", "Cette potion crépitante, grâce à la magie de feu qu'elle emprisonne, est généralement utilisée pour enchanter correctement toutes sortes d'objets." ));
            allItems.Add(new Items ( 1344, "Verveine", "Cette plante spéciale permet de faire dormir n'importe qui bien plus facilement. Cela doit probablement être l'emblême des Sadida." ));
            allItems.Add(new Items ( 1345, "Potion d\'Incendie", "Cette potion fulminante, grâce à la puissante magie de feu qu'elle emprisonne, est généralement utilisée pour enchanter puissamment toutes sortes d'objets." ));
            allItems.Add(new Items ( 1346, "Potion de Tsunami", "Cette potion inondante, grâce à la puissante magie d'eau qu'elle emprisonne, est généralement utilisée pour enchanter puissamment toutes sortes d'objets." ));
            allItems.Add(new Items ( 1347, "Potion d\'Ouragan", "Cette potion hurlante, grâce à la puissante magie d'air qu'elle emprisonne, est généralement utilisée pour enchanter puissamment toutes sortes d'objets." ));
            allItems.Add(new Items ( 1348, "Potion de Séisme", "Cette potion embourbante, grâce à la puissante magie de terre qu'elle emprisonne, est généralement utilisée pour enchanter puissamment toutes sortes d'objets." ));
            allItems.Add(new Items ( 1350, "Arc Holic", "Cet arc a été créé par un magicien porté sur la bouteille. Afin de résoudre son problème, il a inventé cet arc spécialisé dans l'eau." ));
            allItems.Add(new Items ( 1351, "Arc du Xueluom", "" ));
            allItems.Add(new Items ( 1352, "Arc de Boo", "Cet arc prend sa force aussi bien de la terre que de l'eau. Malheur à ceux qui ne maîtrisent ni l'un ni l'autre..." ));
            allItems.Add(new Items ( 1353, "Arc à Poulie Sciée", "Cet arc, dont toutes les poulies ont été sciées, était, il y a fort longtemps, le symbole de la lutte du bien contre le mal. nEncore aujourd'hui, on peut sentir en lui la flamme des anciens justiciers." ));
            allItems.Add(new Items ( 1354, "L\'Arc à Hick", "Hick était un Crâ maudit : il n'arrivait jamais à se débarrasser de son hoquet. Cependant, il finit un jour par jeter son arc au feu pour le remplacer par une épée, et son mal disparu mystérieusement..." ));
            allItems.Add(new Items ( 1355, "Arc Hidsad", "Le créateur de cet arc, un certain Marc Hidsad, n'a jamais très bien su s'en servir. Il pouvait tout autant infliger de sévères blessures à ses ennemis que de se blesser lui-même." ));
            allItems.Add(new Items ( 1356, "Demi-Baguette", "Rika Zarouil la boulangère fut connue pour avoir été l'eniripsa la plus petite de la contrée. Elle faisait son pain dans des petits-fours en carton et se battait avec des demi-baguettes." ));
            allItems.Add(new Items ( 1357, "Daguette", "Un sculpteur un peu farceur tenta de faire passer ses baguettes pour des dagues. Son stratagème n'eu aucun succès mais ses baguettes, elles, en eurent." ));
            allItems.Add(new Items ( 1359, "Le Tison Fûté", "Les origines de cette baguette restent obscures. Cependant, elle a toujours aidé ses heureux possesseurs à trouver les chemins les moins encombrés d'Amakna, ce qui peut servir les jours de grande influence sur le domaine du bouftou royal." ));
            allItems.Add(new Items ( 1360, "L\'Hyldegarde", "Cette baguette fut créée il y a de nombreuses années dans les flammes de la forge de Mort Dur. Cette arme nécessite d'avoir toujours de l'eau à côté de soi pour pouvoir maitriser son feu intérieur..." ));
            allItems.Add(new Items ( 1361, "Baguette Sylvien", "La Baguette Sylvien a l'étrange pouvoir de conférer une vue perçante à son porteur. Charge ensuite à celui ci d'être suffisamment agile pour pouvoir fuir le danger." ));
            allItems.Add(new Items ( 1362, "Pieu Vampirique", "Le pieu vampirique est en fait la baguette d'un vampire depuis longtemps retiré de la vie sociale et de la boucherie. Cet étrange bout de bois a cependant gardé quelques uns des avantages de son ancien maître..." ));
            allItems.Add(new Items ( 1363, "Bâton Tont\'Ata", "Outre son nom insupportable, ce bâton possède bien d'autres moyens de rendre son propriétaire fou de rage." ));
            allItems.Add(new Items ( 1364, "Bâton d\'Oubli", "Ce bâton fait oublier tous les soucis et les mauvaises pensées de son propriétaire. Malheureusement, il perd aussi parfois une partie de sa mémoire et devient sujet à des pertes de concentration de plus en plus fréquentes. A réserver aux sages..." ));
            allItems.Add(new Items ( 1365, "La Canne Hête", "Hète était vraiment un guerrier redoutable avec son bâton. Il était tellement efficace qu'on prétendait que son bâton était magique et qu'il lançait des flammes..." ));
            allItems.Add(new Items ( 1366, "Bâton de Bouftier", "Bâton couramment utilisé lors de la transhumance par les bouftiers. Les bworks aiment déguster ces troupeaux de bouftous en fines tranches lors de l'apéritif (le plat principal étant constitué du bouftier lui-même)." ));
            allItems.Add(new Items ( 1367, "Bâton de Dina, dit \'le Mythe\'", "Ce bâton a appartenu à la très belle Dina 'Mythe', qui était surtout connue pour ses formes avantageuses et son caractère explosif... Bref c'était une vraie bombe." ));
            allItems.Add(new Items ( 1368, "La Lance à un ", "" ));
            allItems.Add(new Items ( 1369, "Les Lames en Table", "Ces couteaux étaient à l'origine censés servir dans un restaurant de luxe. Mais devant leur look plutôt... étrange, le patron du restaurant a préféré les revendre à un lanceur de couteaux ambulant." ));
            allItems.Add(new Items ( 1370, "L\'Ergot Mina", "La poignée de cette arme est grasse et glissante, mais elle a vraiment un look génial." ));
            allItems.Add(new Items ( 1371, "Dagues Eulasse", "Eulasse était un bouftier du nord d'Amakna qui trouva un jour, au milieu du fumier de ses bouftous, une paire de dagues légèrement salies. nQue celui qui les porte fasse attention : le manche glisse, il est facile de manquer son coup." ));
            allItems.Add(new Items ( 1372, "Dagues Aj\'Deh\'Là", "Qui ne connait pas Aj'Deh'Là, l'ermite, légendaire disciple de Sram qui avait passé sa vie à refuser tout contact avec les autres. À sa mort, on retrouva ses dagues fétiches à côté de son corps, et seuls les plus agiles purent les apprivoiser." ));
            allItems.Add(new Items ( 1373, "Le Rasoir Infernal", "Krys Razepa était un vieux disciple d'Enutrof qui portait la barbe, comme tous les disciples d'Enutrof dignes de ce nom. Lorsque madame Razepa, sa femme, lui offrit un rasoir, il préféra l'utiliser pour combattre..." ));
            allItems.Add(new Items ( 1374, "Dagues Tylo", "Tylo était un scribe amaknéen qui vécut il y a des siècles de cela. Il avait la particularité de graver ses cahiers de pierre non pas avec des burins mais avec des dagues. Nous reste de lui cette superbe dague, impressionnante de résistance aux chocs." ));
            allItems.Add(new Items ( 1375, "Hache Térophyle", "Térophyle était un disciple de Iop capable de soulever des masses impressionnantes. Mais malheureusement pour lui, comme tous les Iop trop musclés, l'intelligence n'était pas son fort..." ));
            allItems.Add(new Items ( 1376, "Hache Heuvante", "Cette hache, parfois lourde à manier, est parfaite pour terrasser les Trools : les runes magiques qu'elle contient permettent en effet de brûler les ennemis." ));
            allItems.Add(new Items ( 1377, "Hache Huré", "Huré était un jeune prêtre qui ne reculait pas devant les armes pour faire progresser la foi. Son arme de prédilection était une hache sur laquelle il avait dessiné de nombreux traits, dont la signification est perdue à jamais..." ));
            allItems.Add(new Items ( 1378, "Francisque à Brêles", "Cette hache de combat, réservée aux jeunes guerriers, comme son nom l'indique, a la particularité de produire un son strident particulièrement désagréable lorsqu'elle entrechoque une autre arme." ));
            allItems.Add(new Items ( 1379, "Le Scrounnchhh", "D'après la légende, ce marteau tirerait son nom du son qu'il produit lorsqu'il s'écrase sur un crâne ennemi. Malheureusement, nul ennemi n'est là pour confirmer ces dires." ));
