            allItems.Add(new Items ( 1403, "Marteau Tau", "Ce marteau a appartenu à Tau, un jeune disciple d'Osamodas qui passait son temps à raconter des blagues stupides. Son marteau, tout comme lui, ne rend pas très intelligent, mais peut parfois détendre l'atmosphère..." ));
            allItems.Add(new Items ( 1404, "Marteau Toh\'Lo", "Vous aimez les animaux ? Toh'Lo aussi. Il leur avait même voué sa vie. Ce marteau raconte un peu son histoire , une grande communion avec les bêtes..." ));
            allItems.Add(new Items ( 1405, "Potion Eau de fée", "Cette potion parvient à reproduire les mêmes pouvoirs de guérison que le sang d'une Eniripsa. Plus besoin de les saigner à blanc maintenant." ));
