            allItems.Add(new Items ( 1426, "Pelle à Thart\'", "Thart', le créateur de cette pelle, était un disciple de Iop. Un nom prédestiné, pour un Iop, n'est-ce pas ?" ));
            allItems.Add(new Items ( 1427, "Pelle Zébuth", "Cette pelle est utilisée par les grands prêtres Kannibouls de l'île de Moon pour invoquer Zébuth, une de leurs divinités souvent représentée sous une forme bovine. Seuls les personnages vils peuvent utiliser cette arme." ));
            allItems.Add(new Items ( 1428, "Immortelle", "Cette étrange petite plante pousse dans les milieux arides, et répand une odeur de curry aux alentours. Coriace, elle ne semble pas subir les assauts du temps... Si seulement l'on pouvait en dire autant de toutes les femmes..." ));
            allItems.Add(new Items ( 1429, "Petite Pelle Mechba", "Cette pelle est utilisée par les Enutrof pour cueillir des fruits dans les arbres, mais elle peut s'avérer une arme de combat redoutable. Comme dirait son créateur, Mechba : 'eche est beche, ma pelle'." ));
            allItems.Add(new Items ( 1430, "Pelle Mechba", "Cette pelle est utilisée par les Enutrof pour cueillir des fruits dans les arbres, mais elle peut s'avérer une arme de combat redoutable. Comme dirait son créateur, Mechba : 'eche est beche, ma pelle'." ));
            allItems.Add(new Items ( 1431, "Grande Pelle Mechba", "Cette pelle est utilisée par les Enutrof pour cueillir des fruits dans les arbres, mais elle peut s'avérer une arme de combat redoutable. Comme dirait son créateur, Mechba : 'eche est beche, ma pelle'." ));
            allItems.Add(new Items ( 1432, "Pelle Houze", "Gaston Houze, jardinier disciple d'Enutrof, avait tendance à toujours trop abuser des plantes. Cette arme en est la preuve : puissante, elle aura tendance à vous jouer des tours et à mettre à mal votre intelligence." ));
            allItems.Add(new Items ( 1433, "Pelle Sécutrice", "Cette pelle fut l'arme d'autodéfense d'un petit groupe d'Enutrofettes. Ces dames, une fois l'instrument de puissance entre leurs mains, changèrent du tout au tout et tentèrent de conquérir le monde." ));
