            allItems.Add(new Items ( 1435, "La Pelle du Large", "Pelle très lourde, donc réservée à l'usage des aventuriers les plus gr... larges." ));
            allItems.Add(new Items ( 1436, "Pelle Ikan", "Ikan etait le petit poisson de Chourros le guerrier. Il est important de préciser que le nom de cette pelle n'a aucun rapport avec ce poisson, mais plutôt avec son odeur..." ));
