            allItems.Add(new Items ( 1476, "Amulette du Craqueleur", "Cette puissante amulette contient toute la vie du Craqueleur, tous les objets qu'il a récoltés avant de mourir sous les coups de butoir d'aventuriers attirés par ses richesses." ));
