            allItems.Add(new Items ( 1619, "L\'Abramu", "L'Abramu est une amulette très élégante. Si vous avez l'intention de la porter nous vous conseillons de vous promener nu afin de bien mettre en valeur ce précieux bijoux. Vous verrez, les dames n'y résistent pas." ));
            allItems.Add(new Items ( 1620, "Abrarc", "Cet arc semble encore vivant et peut presque tirer des flèches tout seul, comme un grand. Il aurait pu être l'arc idéal des personnes dotées de deux mains gauches, mais malheureusement, il faut toujours encocher la flèche dans le bon sens." ));
            allItems.Add(new Items ( 1621, "Bottes de Maîtrise", "Les bottes de Maîtrise augmentent l'agilité et l'intelligence de leur lanceur au détriment de la force et de la chance. Contrairement à ce que l'on pourrait croire, elle ne vous permettent pas de maitriser votre adversaire." ));
            allItems.Add(new Items ( 1622, "Bottes de Puissance", "Les bottes de puissance augmentent la force et la chance de leur lanceur au détriment de l'intelligence et de l'agilité. Contrairement à ce que l'on pourrait croire, elle ne confèrent pas à leur porteur, une puissance démente." ));
            allItems.Add(new Items ( 1623, "Bottes d\'Anticipation", "Les bottes d'Anticipation augmentent la force et l'agilité de leur lanceur au détriment de l'intelligence et de la chance. Contrairement à ce que l'on pourrait croire, elles ne vous permettent pas d'anticiper les actions de vos adversaires." ));
