            allItems.Add(new Items ( 1652, "Patte d\'Arakne Majeure", "Une patte très fine, mais très poilue. Elle était autrefois utilisée lors de la confection des balais et des perruques au caractère un peu exotique." ));
            allItems.Add(new Items ( 1653, "Foi de Don Rouann", "Cette bague symbolise le fait que vous êtes à la recherche du secret de Don Rouann. Vous avez appris actuellement le courage." ));
            allItems.Add(new Items ( 1654, "Foi de Don Rouann", "Cette bague symbolise le fait que vous êtes à la recherche du secret de Don Rouann. Vous avez appris actuellement le courage, la communication et devez à présent être patient." ));
            allItems.Add(new Items ( 1655, "Foi de Don Rouann", "Cette bague symbolise le fait que vous êtes à la recherche du secret de Don Rouann." ));
            allItems.Add(new Items ( 1656, "Fécaliseur du Magicien", "Cet anneau est à même de protéger son porteur des petites attaques magiques et donc par extension, des petits monstres qui usent de la magie pour faire les malins et se rendre intéressants." ));
            allItems.Add(new Items ( 1657, "Fécaliseur du Guerrier", "Cet anneau est à même de protéger son porteur des petites attaques physiques et donc par extension, des petits monstres qui jouent sur leur physique pour vous surprendre." ));
            allItems.Add(new Items ( 1660, "Ambre d\'Abraknyde Sombre", "Cet ambre splendide contient une vilaine bestiole prisonnière dans la sève de son maître. Une sorte de sarcophage, qui permet à l'Abraknyde Sombre de garder de bons souvenirs de ses invocations, une fois celles-ci mortes." ));
            allItems.Add(new Items ( 1661, "Ambramulette Moskito", "Porter autour du cou un Moskito pris dans un ambre, c'est un peu montrer à vos adversaires, que vous êtes un redoutable chasseur... de Moskitos. De quoi vous rendre ridicule en dehors du Berceau." ));
            allItems.Add(new Items ( 1662, "Ambramulette Arackne", "Porter autour du coup une Arackne prise dans l'ambre, lorsque vous attaquez une de ses soeurs, peut suffire à la décontenancer. Pauvre bête, on appelle cela l'ironie du sort." ));
            allItems.Add(new Items ( 1663, "Ecaille de Crocodaille", "Cette écaille de Crocodaille constitue, pour tout chasseur débutant de Crocodaille qui se respecte, un petit trophée. N'hésitez pas à la montrer aux ivrognes de la taverne, ils seront de toute évidence impressionnés et vous paieront des coups à boire." ));
            allItems.Add(new Items ( 1664, "Dent de Crocodaille", "Quand un Crocodaille a une dent contre vous, la meilleure solution consiste à la lui arracher avec vigueur et détermination, tel un dentiste rigoureux." ));
            allItems.Add(new Items ( 1665, "Bottes d\'Apprentissage", "Ces bottes bien étranges étaient utilisées sur les jeunes disciples un peu limités intellectuellement, pour leur permettre de rattraper leur retard. Elles conviendront particulièrement aux Iops." ));
            allItems.Add(new Items ( 1666, "Bottes de l\'Apprenti Invocateur", "Ces bottes ont été spécialement conçues pour ceux qui aiment laisser à leurs créatures le plaisir de faire tout le travail pendant les combats." ));
            allItems.Add(new Items ( 1667, "Ceinture Luthuthu", "Cette ceinture porte le nom ridicule de son créateur, Luthuthu, un cordonnier beaucoup moins connu que Bowisse, mais encore très créatif malgré son âge avancé." ));
            allItems.Add(new Items ( 1668, "Ceinture du Dodu Trembleur", "Le Dodu était un Sadida qui s'obstinait à faire trembler ses ennemis, il a commandé à Bowisse cette ceinture, assortie à la couleur de ses poils, car ne l'oublions pas, le Dodu en plus d'être un trembleur était un charmeur." ));
            allItems.Add(new Items ( 1669, "Ceinture des Vents", "Cette ceinture a été créée pour la plus populaire des Crâette, qui aimait donner de son temps pour organiser tout un tas de fêtes et d'évènements hors du commun, histoire de divertir le peuple entre deux repassages de Slip en Cuir du Captain Chafer." ));
            allItems.Add(new Items ( 1670, "Plume Pointue de Kwak du Vent", "Parfois, un Kwak du Vent dispose d'une plume pointue différente de toutes les autres, elle est en règle générale magique et peut-être utilisée pour se déguiser en chef indien." ));
            allItems.Add(new Items ( 1671, "Avoine Magique", "Ce fétu de paille d'Avoine Magique est issu d'un modeste paysan ensorcelé par un apprenti Bwork Mage, qui tenait son livre à l'envers." ));
            allItems.Add(new Items ( 1672, "Poils Darits", "Ces poils constituent les derniers restes de Darit, un jeune magicien très prétentieux, qui avait oublié de vérifier si le Prespic avait lancé Contre, avant de lancer son sort Foudre Dépoteuse." ));
            allItems.Add(new Items ( 1673, "Patte d\'Arakne Magique", "Cette patte d'Arakne, n'est pas comme toutes les autres, elle bouge toute seule et semble pleine de vie, même une fois arrachée à sa maîtresse. Une nouvelle vie pourrait commencer pour cette patte qui semble attirée par la couture." ));
            allItems.Add(new Items ( 1674, "Champignon Luidegît", "Ce héros, a été envoûté par le Chêne Mou, mais la magie un peu aléatoire de ce dernier, a fait qu'il s'est transformé en champignon. Les Champs étant assez idiots, ils le vénèrent et le protègent jusqu'à la mort." ));
            allItems.Add(new Items ( 1675, "Os Surprise du Chafer Fantassin", "Cet Os magique, permet aux chafers fantassins de faire des coups surprenants, comme d'enlever et de mettre leurs slips sur la tête, sans les mains, en plein combat et en réussissant leurs jets d'esquive. On ne se lasse jamais de cette prestation." ));
            allItems.Add(new Items ( 1676, "Osier Enchanté", "Ce morceau d'Osier enchanté devrait permettre de fabriquer une magnifique corbeille à fruits magique, alliant élégance et puissance, tout le contraire d'une méchante Liche." ));
            allItems.Add(new Items ( 1677, "Potion Dantegoulesque", "Cette potion magique est la seule à même de rendre magiques des Slips en Cuir de Captain Chafer tressés entre eux, voilà qui en dit long sur la puissance d'une telle potion." ));
            allItems.Add(new Items ( 1678, "Noix de Kokoko Traumatisée", "Cette noix de Kokoko a été traumatisée durant son enfance, elle a accumulé une haine incroyable durant toutes ces années, ce qui en fait une noix de Kokoko très puissante." ));
            allItems.Add(new Items ( 1679, "Pierre de Crystaloboule", "Avec un peu de patience et de travail, cette pierre précieuse peut devenir toute ronde et brillante comme un pompon de chapeau magique. Sa forme lui permettra en outre de se glisser presque n'importe où." ));
            allItems.Add(new Items ( 1680, "Corne Brûlée du Minotoror", "Cette Corne de Minotoror a été brûlée par une magie très puissante, ce qui en fait un artéfact magique de choix. Malheureusement, comme beaucoup de choses, elles ne fonctionnent correctement que par paires..." ));
            allItems.Add(new Items ( 1681, "Peau de Larve Verte Magique", "Ne vous y trompez pas, derrière son apparence visqueuse, se cache en fait, un artefact qui s'il est porté près de la tête, peut vous rendre aussi chanceux, qu'un Chafer cocu." ));
            allItems.Add(new Items ( 1682, "Ecorce de Liroye Merline", "Ce morceau d'écorce provient d'un arbre de la clairière enchantée de Liroye Merline. nCe morceau s'est décroché lorsque Brouce Boulgoure le bûcheron a cassé sa Boulgourde en tentant d'abattre un arbre enchanté." ));
            allItems.Add(new Items ( 1683, "Sac à Patates Clair", "Ce sac magique permettait à un Sadida Roublard de transporter des quantités incroyables de patates déjà épluchées, pour les revendre aux prisonniers. Une fois mort, son sac est transmis de Roublard en Roublard comme une sorte de rituel." ));
            allItems.Add(new Items ( 1684, "Sac à Patates Sombre", "C'est à peu près le même sac que le Sac à Patates Clair, si ce n'est que celui-ci a été trempé dans de la sève d'Abraknyde Sombre, ce qui lui confère des pouvoirs supplémentaires." ));
            allItems.Add(new Items ( 1685, "Plume du Dernier Poolay", "Attention, vous avez en votre possession, la dernière plume arrachée au derrière, du dernier Poolay de tout Amakna. Cette plume constituera un très beau cadeau pour vos amis Tailleurs, ou pour les fanatiques du Poolay de plus en plus nombreux au fil du temps." ));
            allItems.Add(new Items ( 1686, "Teinture Magique de Rougeur", "Cette petite potion magique donne une couleur rouge magnifique aux poils sur lesquels elle est appliquée. Accessoirement, il semblerait qu'elle puisse apporter quelques effets magiques." ));
            allItems.Add(new Items ( 1687, "Teinture Magique Verdâtre", "Cette petite potion magique donne une couleur verte magnifique aux poils sur lesquels elle est appliquée. Accessoirement, il sembleraît qu'elle puisse apporter quelques effets magiques." ));
            allItems.Add(new Items ( 1688, "Teinture Magique Bleutée", "Cette petite potion magique donne une couleur bleue magnifique aux poils sur lesquels elle est appliquée. Accessoirement, il sembleraît qu'elle puisse apporter quelques effets magiques." ));
            allItems.Add(new Items ( 1689, "Teinture Magique Orange", "Cette petite potion magique donne une couleur orange magnifique aux poils sur lesquels elle est appliquée. Accessoirement, il semblerait qu'elle puisse apporter quelques effets magiques." ));
            allItems.Add(new Items ( 1690, "Poils du Milimulou", "Ces poils ressemblent à ceux de Mulou, en un peu plus courts, moins solides, mais plus doux. Malheureusement, l'odeur elle est la même." ));
            allItems.Add(new Items ( 1691, "Poils du Meulou", "Rares sont les aventuriers à avoir la chance de toucher ces poils vivants. nÇa ne fait pas de vous un héros pour autant." ));
            allItems.Add(new Items ( 1692, "Teinture Magique Sombre", "Cette petite potion magique donne une couleur noire magnifique aux poils sur lesquels elle est appliquée. Accessoirement, il sembleraît qu'elle puisse apporter quelques effets magiques." ));
            allItems.Add(new Items ( 1693, "Cape de Jules Yanos", "Jules Yanos, célèbre tailleur, était surtout célèbre pour avoir échangé sa femme contre un dé à coudre magique. Comme quoi, il existe encore des gens qui ont le sens des affaires." ));
            allItems.Add(new Items ( 1694, "Laine du Trool Furieux", "Ce morceau de laine provient du Trool le plus dangereux d'Amakna, qui aurait été transformé en couvre-lit, par un tailleur sans scrupules, le même qui aurait soi-disant échangé sa femme contre un dé à coudre magique. Le mystère reste entier." ));
            allItems.Add(new Items ( 1695, "Cape d\'Elya Wood", "On raconte que le Captain Chafer a tenté de voler cette cape pour remplacer ses célèbres sous-vêtements en cuir. Mais elle ne lui allait pas, elle n'incarnait pas assez la perversité et la perfidie pour lui." ));
            allItems.Add(new Items ( 1696, "Etoffe du Meulou", "Les tailleurs ont trouvé il y a des lustres un moyen de déterminer la valeur d'une étoffe, plus cette dernière sent mauvais, plus elle est puissante. Celle-ci sent plus mauvais qu'un pédiluve d'Enutrofs dans lequel un Chafer aurait lavé son slip." ));
            allItems.Add(new Items ( 1697, "Petit Sac en Laine de Boufton", "Ce petit sac ne fera pas de vous une célébrité, mais il aura au moins le mérite de vous permettre de ranger quelques effets personnels." ));
            allItems.Add(new Items ( 1698, "Sac du Petit Récolteur", "Ce sac est spécialement étudié pour contenir quelques ressources collectables. nSeuls les vrais collecteurs sont à même de pouvoir le porter. (niveau 20 de collecteur requis)." ));
            allItems.Add(new Items ( 1699, "Sac du Petit Aventurier", "Ce sac est destiné à ceux qui ne partent pas trop loin de chez eux, et ont besoin d'emmener des choses pas importantes du tout, comme des Planches de Frêne par exemple." ));
            allItems.Add(new Items ( 1700, "Ceinture de Célérité", "Cette ceinture aérodynamique conçue par Tengra Reufrus vous rend agile comme l'air, et libre comme un Tofu. A moins que ça ne soit l'inverse." ));
            allItems.Add(new Items ( 1701, "Ceinture de Poche", "Cette ceinture possède de petites poches permettant de stocker davantage d'objets dans votre inventaire." ));
            allItems.Add(new Items ( 1702, "Sac du Récolteur", "Ce sac est spécialement étudié pour contenir des ressources collectables. nSeuls les vrais collecteurs sont à même de pouvoir le porter. (niveau 40 de collecteur requis)." ));
            allItems.Add(new Items ( 1703, "Sac du Grand Récolteur", "Ce sac est spécialement étudié pour contenir de nombreuses ressources collectables. nSeuls les vrais collecteurs sont à même de pouvoir le porter. (niveau 60 de collecteur requis)." ));
            allItems.Add(new Items ( 1704, "Sac du Staca Noviste", "On raconte qu'un Mineur célèbre était capable de miner sans arrêt pendant des mois dans 4 mines différentes en même temps. Il s'agit en fait d'une légende inventée par la guilde du Marteau-Faucille pour inciter ses mineurs à récolter plus." ));
            allItems.Add(new Items ( 1705, "Sac de l\'Aventurier", "C'est le sac idéal pour ranger tous vos pains et potions lorsque vous partez pour plusieurs mois, dans le labyrinthe par exemple." ));
            allItems.Add(new Items ( 1707, "Sac du grand aventurier", "C'est le sac idéal pour ranger tous vos pains et potions lorsque vous partez en voyage vers des destinations exotiques, comme l'île de Moon." ));
            allItems.Add(new Items ( 1709, "Nonos", "" ));
            allItems.Add(new Items ( 1710, "Croissant de Lune", "Les origines de cette amulette, représentant un quartier de lune, sont inconnues de tout le monde...ou presque. Qui sait donc quels secrets ce bijou renferme..." ));
            allItems.Add(new Items ( 1711, "Chienchien", "Avec ce petit toutou, vous goûterez aux joies des petites promenades pipi. Fort de ses racines sauvages, il est déconseillé de lui donner à manger, sous peine de perdre une main.  n(CE FAMILIER N'AIME AUCUNE RESSOURCE)" ));
            allItems.Add(new Items ( 1712, "Potion Pahoa Raide", "Cette potion permet de retrouver quelques points d'énergie." ));
            allItems.Add(new Items ( 1713, "Potion Raide Boule", "Cette potion permet de retrouver quelques points d'énergie." ));
            allItems.Add(new Items ( 1715, "Le don de Nouwel", "Best seller de la fin d'année 634, ce livre se doit d'être lu. Un classique !" ));
            allItems.Add(new Items ( 1716, "La famille DuCiel", "Deuxième meilleure vente de l'hiver 634, ce livre est déjà une référence." ));
            allItems.Add(new Items ( 1717, "Premier Noël", "L'outsider du début d'année 635, ce livre mérite vraiment qu'on s'y intéresse." ));
            allItems.Add(new Items ( 1718, "Force de Crocoburio", "Cet anneau fut enchanté par le souffle noir de Grougalorasalar, lui conférant un immense pouvoir, hélas aujourd'hui amoindri. Cette relique était destinée à Crocoburio car lui seul était à même d'équiper cet anneau sans subir sa domination." ));
            allItems.Add(new Items ( 1719, "Esprit de Crocoburio", "Cette paire de bottes appartiendrait à Crocoburio. Le peu de personnes aptes à les chausser se sentent soudain envahis de pensées machiavéliques et ingénieuses. Ce maléfice étant, selon la légende, dû aux milliers de crânes qu'elles ont écrasés." ));
            allItems.Add(new Items ( 1720, "Puissance de Crocoburio", "Grougalorasalar aimait garder des fragments d'os appartenant aux ennemis vaincus les plus forts. Son affection pour Crocoburio le poussa à les utiliser pour fabriquer cette ceinture surpuissante." ));
            allItems.Add(new Items ( 1721, "Potion d\'oubli : \'Horloge\'", "Cette potion permet de désapprendre un niveau du sort des Xélor : 'Horloge'." ));
            allItems.Add(new Items ( 1722, "Transformation Abraknyde", "La malédiction du Chêne Mou vous a transformé en abraknyde. nPour rompre le charme, vous devez vaincre un aventurier égaré dans la forêt des Abraknydes." ));
