            allItems.Add(new Items ( 2157, "Clint", "Le Clint est un chapeau sobre mais qui en jette pas mal. La dernière version de ce chapeau convient aussi bien aux aventurières coquettes qu'aux aventuriers virils. Sûrement un gage de qualité." ));
