            allItems.Add(new Items ( 2169, "Raaga", "Amener Raaga dans une caverne magmatique." ));
            allItems.Add(new Items ( 2170, "Epée de Mercenaire", "Cette longue épée est lourde et peu maniable mais découpe à merveille, un peu comme une longue épée lourde et peu maniable." ));
            allItems.Add(new Items ( 2171, "Rapport de Raaga", "Ce rapport décrit les changements chaotiques des activités volcaniques de Brâkmar." ));
            allItems.Add(new Items ( 2172, "Liste d\'échantillons à trouver", "Raaga vous a noté vite fait les indications à suivre pour trouver de bons échantillons de lave : nChercher sur un périmètre de 5 lieux. Chercher des petits pics." ));
            allItems.Add(new Items ( 2173, "Les secrets du langage écureuil", "Ce dictionnaire vous apprend à dialoguer avec un écureuil, ou tout du moins essayer ..." ));
            allItems.Add(new Items ( 2174, "Laisser-Passer Quartiers d\'interrogation", "Ce bout de papier permet d'entrer librement dans les petits quartiers d'interrogation de la prison." ));
            allItems.Add(new Items ( 2175, "Menace de l\'intendant", "Une menace écrite de Danathor, pour ne pas oublier de surveiller ses arrières à l'avenir." ));
            allItems.Add(new Items ( 2176, "Adresse de Rimaraf", "Rimaraf habite une maison en brique rouge au 13 Ruelle de la Découpe dans le quartier de la Boucherie." ));
            allItems.Add(new Items ( 2177, "Broche de Danathor", "Cette petite broche vous a été donnée par Danathor." ));
            allItems.Add(new Items ( 2178, "Formulaire d\'enquête n°36", "Ce formulaire permet d'identifier d'éventuels suspects dans des actes criminels. n(Cliquer sur le parchemin, et puis sur quelqu'un pour l'utiliser)" ));
            allItems.Add(new Items ( 2179, "Dent de Dragodinde", "Cette Dent paraît bien tranchante. Elle pourrait devenir une arme redoutable entre les mains de n'importe qui. Heureusement que le dragodinde ne s'en sert que pour mâcher de la salade." ));
            allItems.Add(new Items ( 2180, "Compte-rendu interrogatoire", "L'alibi de cette personne semble irréprochable..." ));
            allItems.Add(new Items ( 2181, "Compte-rendu interrogatoire", "Cette personne est suspecte, son alibi ne tient pas debout." ));
            allItems.Add(new Items ( 2182, "Carte d\'un enqueteur", "Un enquêteur de la milice vous surveille et vous suspecte du meurtre de Rimaraf. nIl vous a laissé sa carte lors de l'interrogatoire ..." ));
            allItems.Add(new Items ( 2183, "Potion d\'oubli de métier : Pêcheur", "Cette potion vous fera oublier le métier de Pêcheur" ));
