            allItems.Add(new Items ( 2186, "Potion d\'oubli de métier : Boucher", "Cette potion vous fera oublier le métier de Boucher" ));
            allItems.Add(new Items ( 2187, "Pichon Eud\'Compet", "Ce poisson, pêchable uniquement dans la pichine de Sufokia, est un croisement entre tout plein d'autres poissons." ));
            allItems.Add(new Items ( 2188, "Canne à Pichon", "Seule cette canne permet de pêcher les pichons de la pichine de Sufokia." ));
            allItems.Add(new Items ( 2189, "Certificat de bon vote", "Ce petit bout de papier signé par Satirev Refibrom prouve que vous avez voté lors d'un jugement... Et que vous avez bien voté." ));
            allItems.Add(new Items ( 2190, "Formulaire d\'enquête n°33", "Ce formulaire permet habituellement de faire avouer un suspect dans des affaires criminels. Il ne reste plus qu'à le remplir vous-même pour créer un faux interrogatoire de 'Oni Tnec'. n(L'utiliser sur vous pour l'utiliser)" ));
            allItems.Add(new Items ( 2191, "Certificat de mauvais vote", "Ce petit bout de papier signé par Satirev Refibrom prouve que vous avez voté lors d'un jugement... Et que vous avez menti." ));
            allItems.Add(new Items ( 2192, "Documents volés", "Ces documents top secret sont scellés par une glyphe de mort. Si vous l'ouvrez vous ne pourrez pas les lire." ));
            allItems.Add(new Items ( 2193, "Touffe de cheveux de Puja", "Vous avez mis une raclée au fils d'Oto Mustam. Il n'appréciera sans-doute pas." ));
            allItems.Add(new Items ( 2194, "Pense-bête", "Ramener 10 écorces d'abraknyde." ));
            allItems.Add(new Items ( 2195, "Dent", "Cette dent doit probablement vous appartenir vu la raclé que Oto Mustam vous a mis. nIl serait peut-être sage d'attendre qu'il se calme avant de lui reparler à moins que vous ne vouliez vous faire massacrer à nouveau." ));
            allItems.Add(new Items ( 2196, "Connaissance", "Cette bénédiction permet de gagner de l'Intelligence pour les prochains combats." ));
            allItems.Add(new Items ( 2197, "La Force Divorce", "Ces anneaux permettent de briser les liens sacrés du mariage." ));
            allItems.Add(new Items ( 2198, "Parchemin de Connaissance", "Ce parchemin vous permet d'améliorer votre Intelligence, ce qui peut servir pour garder son calme face à deux Bworks." ));
            allItems.Add(new Items ( 2199, "Parchemin de Stupidité", "Ce parchemin vous permet de réduire l'Intelligence d'une cible unique, ce qui peut servir quand votre ennemi est prêt à paniquer à l'approche de deux Bworks." ));
            allItems.Add(new Items ( 2200, "Menace de Satirev Refibrom", "Le grand juge de Brâkmar ne veut plus vous voir. Il semble qu'il ne vous porte pas du tout dans son coeur...enfin il n'en a pas de toute façon." ));
            allItems.Add(new Items ( 2201, "Sérénité", "Cette bénédiction permet de gagner de la Sagesse pour les prochains combats." ));
            allItems.Add(new Items ( 2202, "Sénilité", "Cette malédiction vous enlève de la Sagesse pour le prochain combat." ));
            allItems.Add(new Items ( 2203, "Parchemin de Modération", "Ce parchemin vous permet d'améliorer votre Sagesse, ce qui peut servir pour arriver plus rapidement à la cheville de Dark Vlad." ));
            allItems.Add(new Items ( 2204, "Parchemin de Sénilité", "Ce parchemin vous permet de réduire la Sagesse d'une cible unique, ce qui peut servir quand votre ennemi essaye d'arriver plus rapidement à la cheville de Dark Vlad." ));
            allItems.Add(new Items ( 2205, "Changement d\'ordre", "Ce papier indique que vous avez déjà effectué un changement d'ordre." ));
            allItems.Add(new Items ( 2206, "Aubaine", "Cette bénédiction permet de gagner de la Chance pour les prochains combats." ));
            allItems.Add(new Items ( 2207, "Papier de détention", "Sur ce papier est indiqué les crimes que vous avez commis, ainsi que la date à laquelle vous avez été incarcéré..." ));
            allItems.Add(new Items ( 2208, "Guigne", "Cette malédiction vous enlève de la Chance pour le prochain combat." ));
            allItems.Add(new Items ( 2209, "Parchemin d\'Aubaine", "Ce parchemin vous permet d'améliorer votre Chance, ce qui peut servir pour trouver plus facilement un tofu dans une botte de foin." ));
            allItems.Add(new Items ( 2210, "Parchemin de Guigne", "Ce parchemin vous permet de réduire la Chance d'une cible unique, ce qui peut servir quand votre ennemi tente de faire fortune." ));
            allItems.Add(new Items ( 2211, "Costume Iopesque", "Grâce à cet accoutrement, vous allez briller dans les bals mondains. Le seul souci, c'est que l'on va souvent vous dire que vous êtes malin comme un Iop." ));
            allItems.Add(new Items ( 2212, "Costume Xeloresque", "Muni de ce chapeau et de quelques ustensiles fournis, la ressemblance est flagrante. Vous êtes un Xelor, cela ne fait aucun doute." ));
            allItems.Add(new Items ( 2213, "Costume Sadidesque", "Il suffit de très peu de choses pour ressembler à un sadida: une descente de lit, avoir le regard dans le brouillard et la tête coincée dans le rectum. Effet garanti." ));
            allItems.Add(new Items ( 2214, "Iop attitude", "Vous avez la Iop attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2215, "Xélor attitude", "Vous avez la Xélor attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2216, "Sadida attitude", "Vous avez la Sadida attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2217, "Costume Fecaesque", "Cette moumoute sort tout droit d'un vieux placard, cela se voit à l'aspect un tantinet " ));
            allItems.Add(new Items ( 2218, "Féca attitude", "Vous avez la Féca attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2219, "Costume Eniripsesque", "Ces ailes d'eniripsa ne vous permettent pas de voler. Ne faites donc pas le fanfaron devant vos camarades avec cet accoutrement, vous risquez d'avoir l'air ridicule à l'atterrissage." ));
            allItems.Add(new Items ( 2220, "Eniripsa attitude", "Vous avez l'Eniripsa attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2221, "Costume Enutrofesque", "Cette fausse barbe vous fait ressembler à un Enutrof, certes, mais elle vous donne aussi une haleine digne d'un Enu de sortie le samedi soir. De quoi donner une sacré fièvre." ));
            allItems.Add(new Items ( 2222, "Enutrof attitude", "Vous avez l'Enutrof attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2223, "Costume Sacriesque", "Un peu de sang de tofu ainsi que de fausses cicatrices, un regard de pirate psychotique et le tour est joué. Vous pourrez participer aux réunions de sacrieurs en vous faisant passer pour l'un d'eux sans être suspecté." ));
            allItems.Add(new Items ( 2224, "Sacrieur attitude", "Vous avez la Sacrieur attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2225, "Costume Sramesque", "Un Kwane, une cape sur la tête, l'illusion est parfaite. Si vous portez ce costume, vous aurez l'air d'un affreux et abject personnage sortant du fin fond d'un cimetière: un Sram, tout simplement." ));
            allItems.Add(new Items ( 2226, "Sram attitude", "Vous avez la Sram attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2227, "Costume Osamodesque", "N'en déplaise à certains, vous pouvez vous munir de cette queue fourchue et de ces cornes. Une fois équipé, ce costume vous ira comme un gant et vous serez prêt à vous inviter parmis les fêtes très connues " ));
            allItems.Add(new Items ( 2228, "Osamodas attitude", "Vous avez l'Osamodas attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2229, "Costume Ecaflipesque", "On peut y trouver des oreilles en poil de wabbit très doux, une queue tigrée et une boîte de puces excitées. Pas de doute, quand vous refilerez ces sales petites bêtes à vos voisins, on ne pourra pas douter de vos talents de transformiste." ));
            allItems.Add(new Items ( 2230, "Ecaflip attitude", "Vous avez l'Ecaflip attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2231, "Costume Craesque", "Le Cra, vous l'aurez remarqué, se promène souvent avec son carquois. Pas étonnant que muni de celui-ci, les gens vous prennent pour un des leurs. Mais ce n'est qu'un leurre, quel bonheur de les berner à toute heure." ));
            allItems.Add(new Items ( 2232, "Crâ attitude", "Vous avez la Crâ attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2233, "La Croqueuse", "Cette ceinture fut créée par Xof le Lenald, il eut la bonne idée de ramasser toutes les dents qu'il pouvait trouver sur un champ de bataille Wabbit. Depuis ce jour, ce geste est devenu une habitude." ));
            allItems.Add(new Items ( 2234, "Bonta Attitude", "Vous avez La Bonta attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2235, "Brâkmar Attitude", "Vous avez La Brâkmar attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2236, "Costume Bontesque", "Avec cet accoutrement, un bon nombre de voyageurs vous salueront et vous demanderont leurs chemins." ));
            allItems.Add(new Items ( 2237, "Costume Brâkmaresque", "Muni de ces affaires, vous serez redouté de tous." ));
            allItems.Add(new Items ( 2239, "Poudre d\'Eniripsa", "Cette poudre est produite en petite quantité à partir d'ailes d'Eniripsas. Elle semble avoir des vertus curatives sur les familiers." ));
            allItems.Add(new Items ( 2240, "Petite Fée d\'Artifice Rouge", "Idéal pour les petites fêtes de mariage, parfait pour la fête du Slip du Captain Chafer." ));
            allItems.Add(new Items ( 2241, "Gelée Menthe Royale", "La Gelée Royale a détroussé plus d'un aventurier un peu trop entreprenant, la voila enfin anéantie. Attention, il est déconseillé de la  consommer comme une vulgaire Gelée Menthe." ));
            allItems.Add(new Items ( 2242, "Gelée Fraise Royale", "Du concentré de Gelée Fraise Royale peut transformer n'importe quel tabi pacifiste en machine de guerre assoiffée de sang. Attention donc à ne pas en servir en apéritif à vos amis." ));
            allItems.Add(new Items ( 2245, "Pince de Scorbute", "Autrefois utilisée pour se gratter le bas du dos, elle est aussi très pratique une fois accrochée à un mur, pour y faire tenir des capes. Ça marche également si vous vous l'accrochez dans le bas du dos quoique un poil plus douloureux." ));
            allItems.Add(new Items ( 2246, "Couronne Brisée du Tofu Royal", "Cette couronne a été brisée par de puissantes attaques magiques, ou par le dernier coup de boule que le Tofu Royal a tenté de vous assener." ));
            allItems.Add(new Items ( 2247, "Plume de Tofu Royal", "Il n'y a qu'un Iop pour ne pas se rendre compte que cette plume provient de la croupe d'un Tofu Royal. Pourtant ça se voit, la plume est plus grosse." ));
            allItems.Add(new Items ( 2248, "Poil d\'Ouginak", "Ce Poil tient chaud, la preuve, votre main est moite depuis que vous l'observez. A moins que ce ne soit la peur des représailles du prochain Ouginak qui vous verra avec ce poil dans la poche. De quoi aboyer de rage. nWa-hoooo." ));
            allItems.Add(new Items ( 2249, "Feuille de Tronknyde", "Cette Feuille doit sûrement fournir au Tronknyde la force nécessaire pour se battre. Encore faut il savoir comment bien l'utiliser. Ce qui vient tout de suite à l'idée, c'est de la hacher et de l'incorporer à des haricots. Un régal." ));
            allItems.Add(new Items ( 2250, "Bois de Tronknyde", "Ce morceau de Bois sonne creux. La sève verte qui s'en échappe vous signale que cette partie n'était rien d'autre que le nez du Tronknyde." ));
            allItems.Add(new Items ( 2251, "Méga Pierre du Craqueleur", "Je ne vous jette pas la Mégapierre Pierre !" ));
            allItems.Add(new Items ( 2252, "Dent en Or du Craqueleur", "Vous venez d'édenter sans scrupule le Craqueleur qui croisait votre chemin. Dommage qu'il n'en possédait qu'une seule, un dentier aurait facilement couvert les frais de Zaap durant un bon mois." ));
            allItems.Add(new Items ( 2253, "Fleur de Gloutovore", "Cette Fleur facilite le transit intestinal. Elle est vivement recommandé après un séjour chez votre cuisinier favori." ));
            allItems.Add(new Items ( 2254, "Feuille de Fourbasse", "La feuille de Fourbasse a un goût très particulier, entre la noisette et la salace peu fraiche. Vos plats auront en tout cas un petit parfum de fourbasserie si vous souhaitez toutefois la goûter." ));
            allItems.Add(new Items ( 2255, "Rapport de l\'espion", "Aucune activité dangereuse en ce moment. L'organisation des miliciens est perturbée par l'assassinat de l'un des leurs. Il semblerait que ce soit un bontarien qui ait fait le coup" ));
            allItems.Add(new Items ( 2256, "Carcasse de rat d\'égouts", "Ce rat n'est plus que de la viande froide. Un de moins qui traîne dans l'entrée des égouts." ));
            allItems.Add(new Items ( 2257, "Morceau de mur d\'égout", "Ce morceau de mur d'égout a été retrouvé dans l'estomac d'un bidule" ));
            allItems.Add(new Items ( 2259, "Dent de Karne", "Vous avez dans la main le jeu le plus ancien de la contrée d'Amakna. les règles sont simples: lancez une Dent et tentez de la ratrapper avec la même main, tout en récupérant une deuxième Dent de Karne au sol." ));
            allItems.Add(new Items ( 2260, "Carte d\'un brâkmarien", "Quelqu'un a glissé cette carte de visite dans votre équipement. Il semble que ce soit un brâkmarien. Cette carte annonce que les Guerriers sont intéressés par vos talents d'assassinat et vous propose de rejoindre la folle aventure brâkmarienne." ));
            allItems.Add(new Items ( 2261, "Esprit Renifleur", "Un esprit renifleur vous hante. Il se nourrit de votre odeur et de votre vitalité." ));
            allItems.Add(new Items ( 2262, "Description de l\'assassin", "Vous avez trouvé un Bontarien qui empestait l'odeur du sang. Toutes les informations que vous avez pu rassembler sur ce personnage sont inscrites sur cette feuille de papier." ));
            allItems.Add(new Items ( 2263, "Bâton renifleur de Sang", "Ce bâton est possédé par un esprit renifleur. On peut libérer cet esprit qui foncera alors tout droit jusqu'à croiser un objet possédant l'odeur qu'il aime. Il hantera alors cette personne quelques temps avant de repartir aux pays des esprits." ));
            allItems.Add(new Items ( 2264, "Peau de Gobelin", "Un Gobelin est souvent moqueur et sournois, mais si vous le dépecez, il fera moins le malin ce satané Gobelin." ));
            allItems.Add(new Items ( 2265, "Description d\'un Bontarien", "Vous avez libéré un esprit renifleur mais il ne s'est pas fixé sur la personne visée. Vous avez noté quelques informations de la personne sur ce bout de papier afin de vous en souvenir" ));
            allItems.Add(new Items ( 2266, "Lait de Cochon de Lait", "Ce Lait de Cochon provient d'un Cochon de Lait. On sait jamais, au cas ou vous pensiez l'avoir trouvé sur un Tofu, il n'en est rien." ));
            allItems.Add(new Items ( 2267, "Dragolait", "Ce Dragolait est une denrée rare, car personne dans la contrée n'a encore vu un Dragon Cochon se laisser traire. On imagine aisément qu'il faut au minimum sept personnes pour tenir la bestiole tandis que la huitième doit traire très très rapidement." ));
            allItems.Add(new Items ( 2268, "Scalp de Bwork Archer", "Voici un Scalp qui pourrait lancer une nouvelle mode chez les Enutrofs : le poustiche. Un peu de produit anti-poux et vous serez prêt à concourir pour l'Enutrof le plus classe du monde." ));
            allItems.Add(new Items ( 2269, "Oeil de Larve bleue", "Vous venez de récupèrer cet Oeil intact. Bien préparé, des yeux brouillés sont un régal pour un petit déjeuner. Cet oeil de larve prouvera à Oto que vous avez bien tué une larve bleue." ));
            allItems.Add(new Items ( 2270, "Grappe d\'Oeufs de larve", "Cette grappe d'œufs ressemble à une grosse grappe de raisin. Mais il vaut mieux éviter de la manger." ));
            allItems.Add(new Items ( 2271, "Cuir Violet de Bwork", "Beaucoup de mystères planent encore autour de cet artisanat Bwork. Certains aventuriers pensent que la teinte violacée proviendrait des Gobelins, mais le cuir a une origine inconnue. Bizarre." ));
            allItems.Add(new Items ( 2272, "Larve Dorée", "Cette larve vient de naître d'un oeuf. Son premier regard s'étant posé sur vous, elle vous considère comme sa mère." ));
            allItems.Add(new Items ( 2273, "Tissu Pourpre", "Léger et doux au toucher, voila ce que l'on peut dire de cet étrange Tissu Pourpre. Il est écrit sur une étiquette à l'intérieur " ));
            allItems.Add(new Items ( 2274, "Bougie du Mineur Sombre", "Prenez un Enutrof. Quand il fait noir dans les mines, on ne le voit plus. Le Mineur Sombre, lorsqu'il est dans le noir, on ne le voit pas non plus, puisque maintenant vous avez sa bougie." ));
            allItems.Add(new Items ( 2275, "Cuir de Porkass", "Le Porkass a failli disparaître de la chaîne alimentaire. Il a fallut accoupler des Sangliers et des Cochons de Lait pour à nouveau retrouver les Plaines de Cania remplies de Porkass. Depuis, celui-ci en veut à la terre entière. Quel caractère de porc." ));
            allItems.Add(new Items ( 2277, "Linceul", "Pour gagner en crédibilité, les fantômes s'enrobent d'un Linceul leur permettant de terroriser les aventuriers perdus." ));
            allItems.Add(new Items ( 2278, "Tissu Invisible", "Ce tissu servit à Harry Taupeur, chasseur de taupe. Il s'enroulait dedans afin de piéger les Taupes qui infestaient son jardin. C'est le charismatique Captain Chafer qui se l'est approprié avant de disparaître dans des circonstances bien étranges..." ));
            allItems.Add(new Items ( 2279, "Dent Pointue", "Cette Dent appartenait à un Vampire, cela ne fait aucun doute. On peut le constater en observant de près le bout de la Dent qui s'avère être on ne peut plus pointue." ));
            allItems.Add(new Items ( 2280, "Paille Vampiresque", "Ne sous-estimez jamais un Vampire surtout avec une paille dans la main, il pourrait vous sucer le sang jusqu' à plus soif." ));
            allItems.Add(new Items ( 2281, "Tissu Sanguin", "Ce tissu a l'air vivant, les Vampires s'en servent souvent pour défiler dans de jolies accoutrements lors des bals costumés." ));
            allItems.Add(new Items ( 2282, "Touffe de Cheveux Blancs", "Voici la preuve que certains monstres se font beaucoup de soucis." ));
            allItems.Add(new Items ( 2283, "Oeil de Kwoan", "Cet oeil de Kwoan a des effets hypnotiques surprenants, autant dire qu'il faut toujours garder un oeil sur vous quand vous le manipulez." ));
            allItems.Add(new Items ( 2284, "Note de Harry Stote", "L'entrée de la grotte légendaire aux larves dorées se trouve un peu à l'ouest d'Amakna. Les pouvoirs latents de la larve dorée te permettront d'y rentrer. Attention si ta petite larve se prend des dégâts elle se précipitera dans les bras de son père." ));
            allItems.Add(new Items ( 2285, "Tissu Sombre", "La particularité de ce Tissu n'est plus sujet à controverse. Cela dit, il est très irritant au toucher, sûrement parce qu'un Ouginak s'est dans le meilleur des cas, mouché dessus pendant des années." ));
            allItems.Add(new Items ( 2286, "Bandelettes", "Ces bandelettes sont révolutionnaires : triple épaisseur, parfumées à la violette, ultra résistantes. C'est vraiment un produit de qualité." ));
            allItems.Add(new Items ( 2287, "Cuir Noir", "Ce cuir est déjà traité, sa provenance n'est pas certaine mais la couleur provient probablement d'une teinture spéciale." ));
            allItems.Add(new Items ( 2288, "Patte de Wabbit", "Bon nombres de touristes vont sur l'île de Wabbits essentiellement pour se munir d'une Patte de Wabbit car elle porte bonheur. Malheureusement, ça n'est pas le cas, mais il ne faut pas le dire, le tourisme en serait fort pénalisé." ));
            allItems.Add(new Items ( 2290, "Carapace de Scarafeuille Blanc", "Il ne faut pas grand chose pour avoir un bel abat-jour chez soi. Soit dit en passant, cette carapace doit avoir tout de même une autre utilité. Enfin espérons-le." ));
            allItems.Add(new Items ( 2291, "Carapace de Scarafeuille Bleu", "Il ne faut pas grand chose pour avoir un bel abat-jour chez soi. Soit dit en passant, cette carapace doit avoir tout de même une autre utilité. Enfin espérons-le." ));
            allItems.Add(new Items ( 2292, "Carapace de Scarafeuille Rouge", "Il ne faut pas grand chose pour avoir un bel abat-jour chez soi. Soit dit en passant, cette carapace doit avoir tout de même une autre utilité. Enfin espérons-le." ));
            allItems.Add(new Items ( 2293, "Carapace de Scarafeuille Vert", "Il ne faut pas grand chose pour avoir un bel abat-jour chez soi. Soit dit en passant, cette carapace doit avoir tout de même une autre utilité. Enfin espérons-le." ));
            allItems.Add(new Items ( 2294, "Viscères de Scarafeuille", "Au premier abord, ces viscères ne semblent pas très appétissants. Et pourtant, hâchés avec de fines herbes séchées au soleil, un peu de menthe sauvage, et cuits à l'étouffée dans un vieux chaudron rouillé, ces viscères combleront de joie le plus gourmet des Chafers." ));
            allItems.Add(new Items ( 2295, "Fragment d\'Epée Reptilienne", "Avec ce Fragment d'Epée accroché à votre cheminée, vous pourrez vous vanter de dompter le Crocodaille tel l'excentrique Mikoule, plus connu sous le nom de Crocodaille Dandi." ));
            allItems.Add(new Items ( 2296, "Bouclier Reptilien", "A quoi pourrait bien vous servir ce magnifique bouclier orné de pierres précieuses ? En attendant que vous ne trouviez la réponse, il peut déjà vous servir de plateau pour apporter le thé à vos amis." ));
            allItems.Add(new Items ( 2297, "Carreau d\'Arbalète", "Ce Carreau peut vous servir à confectionner une arme courte mais tranchante. Elle peut aussi faire office de cure-dents, à vous de voir." ));
            allItems.Add(new Items ( 2299, "Epaulette du Dark Vlad", "Cette épaulette a dû essuyer plus d'une bataille. Même si d'apparence elle ne semble pas très résistante, mieux vaux ne pas frapper le Dark Vlad à l'épaule si vous tenez à votre arme." ));
            allItems.Add(new Items ( 2300, "Fragment d\'Os", "Ce Fragment d'Os provient soit d'un reste de Chafer datant du néotilique, soit d'un repas de Meulou. Mais il y a de grandes chances pour que ce ne soit aucun des deux." ));
            allItems.Add(new Items ( 2301, "Moustache de Kolérat", "Peu résistant, la Moustache pourra vous servir à faire le mariol devant vos amis. Ressembler par exemple à Salve et Torh Bali, Grands peintres Brâkmarien." ));
            allItems.Add(new Items ( 2302, "Langue de Crabe", "Les têtes pensantes d'Amakna se demandent encore quelle est l'utilité de cette langue pour le crabe. Ils seraient probablement offusqués s'ils connaissaient la réponse." ));
            allItems.Add(new Items ( 2303, "Carapace vide de Crabe", "Correctement vidée, cette Carapace de Crabe peut contenir divers objets. Toutefois, les objets en question garderont éternellement une odeur de Crabe plus ou moins troublante." ));
            allItems.Add(new Items ( 2304, "Fragment de Pierre Polie", "Ce Fragment provient de la fesse gauche d'un Craqueleur des Plaines. Pour éclater une Arakne, il n'y a pas mieux, à part le Craqueleur en personne peut-être." ));
            allItems.Add(new Items ( 2305, "Fragment de Pierre Pointue", "Ce Fragment provient de l'omoplate droit d'un Craqueleur des Plaines. A moins que ce ne soit une partie du bas ventre. Dans tous les cas, cette pierre n'est pas à placer n'importe où." ));
            allItems.Add(new Items ( 2306, "Coeur de Craqueleur", "Coeur de pierre, ne vois tu pas en moi l'amour que je te porte. Si tu continues, je te laisse par terre, je ne vais pas me laisser saouler de la sorte." ));
            allItems.Add(new Items ( 2307, "Bouclier de Chafer Lancier", "..." ));
            allItems.Add(new Items ( 2308, "Badge Brâkmarien", "Ce badge permet d'identifier un guerrier lorsqu'il périt sur un champ de bataille. Il paraît que certains en font collection, à Bonta." ));
            allItems.Add(new Items ( 2309, "Bougie Rouge", "Une bougie rouge pas comme les autres..." ));
            allItems.Add(new Items ( 2310, "Bougie Verte", "..." ));
            allItems.Add(new Items ( 2311, "Bougie Bleue", "..." ));
            allItems.Add(new Items ( 2312, "Poudre de Sang", "..." ));
            allItems.Add(new Items ( 2313, "Respirateur Aquatique", "..." ));
            allItems.Add(new Items ( 2314, "Fioles de Jiva", "..." ));
            allItems.Add(new Items ( 2315, "Poils de Crocs Glands", "Broujitte Fardot lança une interdiction jadis, sur l'abattage des jeunes Crocs Glands. Depuis son exil, la chasse a repris et les tailleurs se remplissent à nouveau les poches." ));
            allItems.Add(new Items ( 2316, "Croc Jaune", "Le Croc Jaune de Croc Gland apporte une peur bleue à tous ceux qui sous estimeraient son pouvoir." ));
            allItems.Add(new Items ( 2317, "Oreilles de Loup", "C'est en observant de près que l'on se demande comment un Croc Gland entend quelquechose avec toutes les bêtes et autres crasses qu'il possède dans ses Oreilles." ));
            allItems.Add(new Items ( 2318, "Patte d\'ours", "Certains gobelins portent une patte d'ours autour du cou. Une superstition dont tout le monde a oublié l'origine et la signification." ));
            allItems.Add(new Items ( 2320, "Collier Cassé d\'Ouginak", "Comme tout bon Ouginak, son Collier donne des informations. Il est écrit: " ));
            allItems.Add(new Items ( 2321, "Babines d\'Ouginak", "Vous avez l'impression que ces Babines d'Ouginak pourraient suinter de la bave indéfiniment. Peut-être parce que cet animal est réputé pour sa gourmandise éternelle." ));
            allItems.Add(new Items ( 2322, "Crocs de Rats", "Ne vous-y méprenez pas, ces petites quenottes sont en fait de vrais crocs aiguisés, malodorants et usés. Ils servent parfois à remplacer les dents en or des Enutrofs un peu trop avares." ));
            allItems.Add(new Items ( 2323, "Colonne Vertebrale", "Voici une Colonne Vertébrale de bonne facture. Ce n'est pas une raison pour vous éclater le dos non plus." ));
            allItems.Add(new Items ( 2324, "Pastèque", "..." ));
            allItems.Add(new Items ( 2325, "Banane", "Le célèbre ménestrel Geror Lendorman composa jadis une ode en l'honneur de ce fruit : " ));
            allItems.Add(new Items ( 2326, "Orange", "..." ));
            allItems.Add(new Items ( 2327, "Pomme", "..." ));
            allItems.Add(new Items ( 2328, "Petite Perle Dorée", "Cette perle dorée est un cadeau de la petite larve que vous avez accompagnée. Elle est donc pour vous d'une grande valeur sentimentale. Sa douceur et sa couleur en font un cadeau parfait pour une personne un tant soit peu féminine." ));
            allItems.Add(new Items ( 2329, "Poire", "..." ));
            allItems.Add(new Items ( 2330, "Capsule Explosive", "Cette capsule contient des extraits d'agrumes magiques. Il est conseillé de l'agiter avant de l'utiliser pour que la pulpe ne reste pas dans le fond." ));
            allItems.Add(new Items ( 2331, "Aubergine", "Pas réellement nutritive, certains accompagnent l'aubergine avec toutes sortes de condiments. Ce légume fruit a mauvaise réputation mais il est pourtant bénéfique pour la santé. Sauf pour celle des Bworks mais personne ne sait pourquoi." ));
            allItems.Add(new Items ( 2332, "Bonbon de Consolation", "Ce bonbon vous a permis de ne pas pleurer." ));
            allItems.Add(new Items ( 2333, "Bonbon Rouge Surpuissant", "Ce bonbon, augmentera votre attention lors des combats, pas de chance, il a vraiment un goût immonde. (DOUBLE CLIC POUR UTILISER, bonus de 20 en sagesse et de 10 en Prospection pour les 30 prochains combats)." ));
            allItems.Add(new Items ( 2336, "Crâne de chafer", "Ce crâne-là ne cliquettera plus jamais... quoique." ));
            allItems.Add(new Items ( 2337, "Livre Magique", "Ce livre semble magique. Très étrange." ));
            allItems.Add(new Items ( 2338, "Vieux Bouquin", "..." ));
            allItems.Add(new Items ( 2339, "Liste d\'equipements standards", "Un guerrier de valeur moyenne se doit de porter autre chose que des haillons ou, au contraire, des choses clinquantes, il préfèrera donc: n-Un camaieu au majeur n-Un bandeau et une ceinture de vitalité n-Des bottes de Kluh n-Une cape brâkmarienne" ));
            allItems.Add(new Items ( 2340, "Caleçon de milicien", "Ceci est bien un caleçon de milicien. Vous devez être un sacré fétichiste pour posséder ceci. Ou bien quelqu'un veut rire de vous." ));
            allItems.Add(new Items ( 2341, "Arc de Mech Riorraw", "Cet arc a été conçu par Mech Riorraw, il est calibré pour pouvoir traverser la rivière de lave et atteindre le haut des murailles de Brâkmar, ni plus, ni moins." ));
            allItems.Add(new Items ( 2342, "Bague d\'allégeance", "Cette bague vous a été donnée par Domen. Lui rendre signifie que vous êtes pour la destruction de toute chose. Ce qui vous place du coté des adorateurs de Djaul." ));
            allItems.Add(new Items ( 2343, "Bracelet de Djaul", "Ce bracelet vous a été donné par Djaul. Le porter signifie que vous agréez ses propos." ));
            allItems.Add(new Items ( 2344, "Lettre pour Oto", "Cette lettre vous a été remise par Domen pour Oto." ));
            allItems.Add(new Items ( 2345, "Oeil de tortue", "Cette tortue ne fera plus la maligne maintenant." ));
            allItems.Add(new Items ( 2346, "Costume Roublardesque", "Non, ce n'est pas le fameux costume du dragovamp, la célèbre chauve-souris volante. Ceci est l'accoutrement d'un habitant du cimetière, un Roublard en quelques sortes." ));
            allItems.Add(new Items ( 2347, "Compte-rendu de mission", "Ce compte-rendu explique comment s'est passé l'assaut de Brâkmar par la muraille Est." ));
            allItems.Add(new Items ( 2348, "Roublard Attitude", "Vous avez La Roublard attitude, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2349, "Lettre pour Oto", "Cette lettre vous a été remise par Domen pour Oto. Mais vous avez oublié de la lui donner." ));
            allItems.Add(new Items ( 2350, "Touffe de cheveux", "Il semble que cette touffe de cheveux ait appartenu à l'un de vos ennemis." ));
            allItems.Add(new Items ( 2351, "Pain du Pinchaut", "Dans ce pain, il y a à boire et à manger, en l'essorant bien, il est possible de remplir une chopine de bière." ));
            allItems.Add(new Items ( 2352, "Lettre de Danathor", "" ));
            allItems.Add(new Items ( 2353, "Lettre de Larry Poller", "" ));
            allItems.Add(new Items ( 2354, "Cervelle de tortue", "Cette cervelle de tortue, si elle est bien préparé, devient un plat succulent." ));
            allItems.Add(new Items ( 2355, "Poussière d\'Oni", "Cette poussière bleutée est un catalyseur magique très puissant." ));
            allItems.Add(new Items ( 2356, "Miracle", "Vous avez un pouvoir miraculeux, jusqu'à ce que vous rentriez en combat." ));
            allItems.Add(new Items ( 2357, "Bois d\'Oliviolet", "Ce Bois a l'air de résister à toutes les températures. En effet, n'essayez pas de faire un feu pour vous réchauffer, vous risqueriez de mourir de froid avant que le Bois ne prenne." ));
            allItems.Add(new Items ( 2358, "Bois de Bombu", "Ce Bois est le plus tendre de tous. Il est maléable à souhait et procure de réels plaisirs à travailler." ));
            allItems.Add(new Items ( 2359, "Broche de Heticili Ufot", "Cette petite broche vous a été donné par Heticili Ufot." ));
            allItems.Add(new Items ( 2360, "Acte de propriété", "Cet acte de propriété est celui de la maison de Heticili Ufot." ));
            allItems.Add(new Items ( 2361, "Arc de Mulic Bere", "Cet arc est une invention du génial Mulic Bere. Sa portée maximum est tout bonnement incroyable. Le problème c'est que la portée minimum est presque aussi grande." ));
            allItems.Add(new Items ( 2362, "Prédiction de Paro Cabanne", "La prédiction que Paro Cabanne a donné sur les prochaines éléctions." ));
            allItems.Add(new Items ( 2364, "Pierre du Savoir", "Cette pierre précieuse sait tout et partage de temps en temps." ));
            allItems.Add(new Items ( 2365, "Feuille de ronce d\'Hestia", "Cette feuille de ronce, d'apparence anodine, est une plante indispensable pour certaines recettes étranges." ));
            allItems.Add(new Items ( 2366, "Canne à Kralamour", "Cette canne, de métal et de diamant, est la seule canne capable de soutenir le poids du kralamour. Elle est aussi imprégné de l'odeur de kralamourette afin d'avoir une petite chance de pêcher ce monstre." ));
            allItems.Add(new Items ( 2367, "L\'Ami Léhunui", "Léhunui savait se faire des amis, il conviait souvent ses camarades à de grandes soirées costumées. Il avait aussi pour réputation de mettre toujours les petits plats dans les grands et d'offrir de somptueux bijoux pour vous remercier de venir chez lui." ));
            allItems.Add(new Items ( 2368, "Ramoume l\'Eternelle", "Ramoume était un ancien ami d'enfance de Nova, ils se sont perdus de vue quand Ramoume décida de faire une sieste éternelle." ));
            allItems.Add(new Items ( 2369, "Puissante Ceinture Fulgurante", "Cette magnifique ceinture a été confectionnée suite à un pari fou. Le défi était d'améliorer une des plus belles ceintures conçue sur Amakna : la ceinture Fulgurante. Le pari fut réussi par Pakape Prométhé." ));
            allItems.Add(new Items ( 2370, "La Végète Hâtif", "Equipement d'une légèreté incompararable, la Végéte Hâtif vous apporte quelques grammes de finesses dans ce monde de Bwork en rût." ));
            allItems.Add(new Items ( 2371, "La Mouthmou", "Cette ceinture vous est conseillée par Jiva en personne, pour les nuits froides et rigoureuses. En cas de tâches, elle se lave avec du Rimennelle." ));
            allItems.Add(new Items ( 2372, "Bottes de Lymphe a Tik", "Ces bottes donnent l'impression de marcher constamment dans l'herbe fraîche. Elles sont donc idéales pour ceux dont l'odeur des pieds se rapproche plus du Clakoss, que de la Gelée Menthe." ));
            allItems.Add(new Items ( 2373, "Moon Boots", "Le poil de Moon, nécessaire pour cette paire de bottes, est sans aucun doute le poil le plus chaud pour les pieds, même si porter ses bottes en souvenir de Moon vous fera chaud au coeur." ));
            allItems.Add(new Items ( 2374, "Bottes Tarsy", "Tarsy était un ermite qui n'appréciait pas trop les visites. Pour preuve, il recevait ses invités en leur montrant de très près la pointure de ses Bottes." ));
            allItems.Add(new Items ( 2375, "Bottes Orino", "Grâce à ces bottes, vous serez à même d'entendre le vent dans les voiles d'un bateau situé en pleine Mer d'Asse, ou d'entendre les gémissements d'un Chafer Lancier, perdu dans les tréfonds du labyrinthe." ));
            allItems.Add(new Items ( 2376, "Hache Hémal", "Cette hache est la seule capable de résister aux pouvoirs du Kaerlier et, donc, la seule à pouvoir le couper." ));
            allItems.Add(new Items ( 2378, "Sang de Roche", "Cette pierre, à la couleur cramoisie, permet de fabriquer de très puissantes potions." ));
            allItems.Add(new Items ( 2379, "Bois de Kaerlier", "Ce bois provient du très rare Kaerlier. Ses propriétés sont plutôt...étonnantes." ));
            allItems.Add(new Items ( 2380, "Cape de Bou", "Cette Cape est bien étrange, elle est faite de bouts de boue du Boo, mais elle tient tout de même debout. Une fois sur vos épaules, elle pourra même faire de vous un boute-en-train." ));
            allItems.Add(new Items ( 2381, "Cape Flotteuse", "Cette Cape fut découpée dans un fanion, anciennement situé sur la plus haute tour du chateau d'Amakna. C'est sûrement pour cela qu'elle sent le linge séché au grand air." ));
            allItems.Add(new Items ( 2382, "Cape Maimpa", "Maimpa était un Ecaflip qui se moquait du vide, prétextant qu'il retomberait toujours sur ses pattes. Mais un jour, il atterrit sur un Trool affamé. Il fût enterré le jour même à l'endroit où l'on a trouvé ses restes. Un peu partout en quelques sortes." ));
            allItems.Add(new Items ( 2383, "Cape Sanguine", "A base de tissu sanguin, la Cape Sanguine a un aspect cartonneux au toucher, comme si la teinture rougeâtre avait coagulé." ));
            allItems.Add(new Items ( 2384, "Bottes Animales", "On peut le dire, ces Bottes ont un côté comment dire... Hmmm... Bestial. Ne vous étonnez donc pas si un Mulou tombe amoureux de vos Bottes. C'est peut-être la pire chose qui puisse vous arriver." ));
            allItems.Add(new Items ( 2385, "La Brouteuse", "Idéale pour le jardin, la Brouteuse peut aussi servir de paillasson pour votre entrée de maison. Conçue à la base pour les Sadidas, elle n'abîme pas les poils, même les plus fragiles. Certains vont même jusqu'à dire qu'elle ralentit leur chute." ));
            allItems.Add(new Items ( 2386, "La Guenille", "Cette étrange Cape puise toute sa force dans son aspect vétuste. Plus elle sera sale, et plus elle vous servira." ));
            allItems.Add(new Items ( 2387, "La Poilue", "Il faut avouer que cette Cape possède une forte odeur très désagréable. En essayant de mettre des mots sur cette infection, on peut dire que c'est tout simplement un mélange de poils de monstres divers qui créent ce magnifique pot pourri." ));
            allItems.Add(new Items ( 2388, "L\'Amulette du Puzzle de la Griffe", "Autrefois, un Ecaflip nommé Dodge, éclata en mille morceaux une carte au trésor gravée sur une pierre. Croyant réussir à reformer la carte, il garda la plus grosse partie pour en faire une Amulette mystérieuse. Nul ne sait ce que le trésor est devenu." ));
            allItems.Add(new Items ( 2389, "Amulette du Badoul", "Dodge adorait exploser tout ce qui avait de la valeur à ses yeux. Il disait que c'était la preuve qu'il existait. Chacun son truc." ));
            allItems.Add(new Items ( 2390, "Amulette de la Boule", "Cette Amulette est presque parfaite, elle accroît considérablement la sagesse de celui qui la porte autour du cou. Très utile pour paraître plus sage auprès de vos amis, ou vous faire passer pour un écolier modèle." ));
            allItems.Add(new Items ( 2391, "Amulette de la Bouboule", "Un vieux dicton de Sufokia dit: de la Boule un jour, de la Bouboule peut-être un autre jour. Vous pourrez constatez que, plus le dicton est vieux, plus il s'avère ridicule." ));
            allItems.Add(new Items ( 2392, "Dragolyre Remaniée", "Grâce aux multiples talents des artisans d'Amakna, la Dragolyre Remaniée excelle dans la perfection." ));
            allItems.Add(new Items ( 2393, "Omelette Amulette", "L'osamodas Onehaieuf Bek cherchait absolument à gagner du mérite devant les membres de sa guilde, et décida alors de faire une Amulette révolutionnaire. Il y parvint en préparant un petit déjeuner à ses amis. Étrange." ));
            allItems.Add(new Items ( 2394, "La Bourgeonette", "Gorgée de soleil, la Bourgeonette réchauffe les coeurs en plus de procurer une sensation d'invulnérabilité face à la terre. Elle est aussi tellement légère qu'on oublie souvent de l'enlever à l'heure du coucher." ));
            allItems.Add(new Items ( 2395, "Le foulard du Lard Fou", "Le Lard Fou était un petit démon bien vil qui ne pouvait s'empêcher de rester planté devant les gens sans bouger. Il était aussi résistant à la magie, ce qui imposait le respect à l'époque. Depuis, le Lard Fou se terre quelque part et attend." ));
            allItems.Add(new Items ( 2396, "La Lou Ptiote", "Sylveline n'était pas très futée. Sa mère, Lou, lui offrit cette lanterne en disant " ));
            allItems.Add(new Items ( 2397, "La Bibelaw", "Law-bibe était un vieillard qui adorait assembler n'importe quoi pour en faire un objet. Un jour, il créa ce médaillon aux formes intrigantes. Une fois autour du cou, il ressentit une étrange sensation. Bien étrange sensation même..." ));
            allItems.Add(new Items ( 2398, "L\'Oeil de Kanigrou", "Offert à Melyonen par le célèbre Kanigrou  Rook Y Balboua, pour la remercier de lui avoir présenté celle qui est maintenant sa femme, Ar d'rieeeeeeeeeneuh comme il aime à l'appeler." ));
            allItems.Add(new Items ( 2399, "La Veaux Doux", "Jozébo Vez était un berger de Veaux, lointains cousins des Bouftous. Dans son troupeau, quelques bêtes possédaient une fourrure plus douce que les autres. Elle se revendait bien plus cher et pour différencier ses bêtes, il attachait ce collier à leur cou." ));
            allItems.Add(new Items ( 2400, "Bottes Animales Sombres", "On peut le dire, ces Bottes ont un côté comment dire, .....hmmm.... Sombre. Ne vous étonnez donc pas si un Vampire tombe en amour devant vous et vos Bottes. C'est peut-être la pire chose qui puisse vous arriver." ));
            allItems.Add(new Items ( 2401, "Carte de Kwelsh", "Cette carte est liée au maître Kwelsh. C'est tout ce que vous savez et que vous devez savoir." ));
            allItems.Add(new Items ( 2402, "Epée de Sériane-Kerm", "Cette épée est un cadeau honorifique que l'on donne uniquement aux mercenaires accomplis. Mais elle coupe très bien aussi." ));
            allItems.Add(new Items ( 2403, "Chtévu", "Autrefois utilisée pour les libertés conditionnelles, la Chtévu possède paraît-il un pouvoir supplémentaire par rapport aux autres ceintures. Mais personne n'a encore trouvé. Mieux vaut donc garder un oeil sur cette ceinture." ));
            allItems.Add(new Items ( 2404, "Ceinture du Rat", "Cette ceinture a appartenu à un Rat d'Egoût, elle lui allait à merveille, bien moulante au niveau des hanches, elle lui permettait de cacher ses poignées d'amour." ));
            allItems.Add(new Items ( 2409, "Kwakoiffe de Flammes", "La Kwakoiffe de Flammes vous donne réellement une allure de tombeur, ce qui est très pratique pour mettre à terre vos adversaires, lorsque ceux-ci n'arrivent plus à tenir debout tellement ils rigolent, en vous voyant affublé de cette coiffe." ));
            allItems.Add(new Items ( 2410, "Abracaska", "L'Abracaska permet d'éviter les gueules de bois." ));
            allItems.Add(new Items ( 2411, "Coiffe du Bouftou", "Ce superbe chapeau permet à son porteur de gagner force et intelligence si ce dernier parvient à s'accommoder de l'odeur." ));
            allItems.Add(new Items ( 2412, "Kwape de Flammes", "Très peu irritante, mais un peu quand même il faut l'avouer, cette cape ne pourra pas hélas vous faire voler, ni même planer. Même en y cousant un grand " ));
            allItems.Add(new Items ( 2413, "Abracapa", "Vêtu de Feuilles d'Abraknydes, vous pourrez vous pavaner au beau milieu de la forêt sans que l'on vous reconnaisse vraiment. Attention toutefois aux Abraknydes en quête de bourgeonnements printaniers." ));
            allItems.Add(new Items ( 2414, "Cape Bouffante", "Cette cape a tout pour plaire : sa résistance, son confort, sa couleur, sa rareté... En revanche son odeur est redoutable pour les parois nasales. Mais bon, on ne peut pas tout avoir non plus." ));
            allItems.Add(new Items ( 2415, "Kwaklame de Flammes", "La Kwaklame de Flammes est une arme vraiment stylée. A défaut de faire peur à votre adversaire, elle vous servira à l'impressionner, pour ensuite mieux le surprendre par une vieille ruse de fourbe." ));
            allItems.Add(new Items ( 2416, "Marteau du Bouftou", "Le manuel de l'utilisateur explique que ce surprenant marteau est capable de libérer une énergie colossale lorsqu'il est correctement manié. Encore un manuel Osamodas traduit en Sadida..." ));
            allItems.Add(new Items ( 2417, "Abraton", "Ce bâton a gardé toute l'agressivité de l'Abraknyde, mais n'a pas conservé l'odeur de bois moisi qui caractérise souvent les vieux Abraknydes." ));
            allItems.Add(new Items ( 2418, "Kwakanneau de Flammes", "Grâce à cet anneau, si vous vous perdez en allant délivrer un message, votre maître pourra peut-être vous retrouver." ));
            allItems.Add(new Items ( 2419, "Anneau de Bouze le Clerc", "Cet anneau provient du fameux Bouze le Clerc, sauveur des temps modernes. Sa phrase culte parlait d'un fin Eni et d'au-delà." ));
            allItems.Add(new Items ( 2420, "Anobra", "Travailler la Branche d'Abraknyde n'est pas chose aisée. Mais avec de la persévérance, on peut finir par obtenir un anneau à la fois sobre, élégant et raffiné. Tout le contraire d'un Sadida dans une taverne." ));
            allItems.Add(new Items ( 2421, "Kwakobottes de Flammes", "Muni de ces ergots de Kwak de Feu, il ne vous reste plus qu'à marcher sur des oeufs pour ne pas vous les faire voler par un Sram mal intentionné." ));
            allItems.Add(new Items ( 2422, "Boufbottes", "Ces Bottes sont d'un confort inégalé, l'intérieur a l'air d'être doublé en laine de Bouftou, mais on peut ressentir également comme un liquide circulant entre les orteils. Reste à savoir si c'est de la Bave ou du Sang de Bouftou. Peu importe." ));
            allItems.Add(new Items ( 2423, "Abrabottes", "Les effets de cette paire de Bottes peuvent aussi parfaitement être retrouvés en se rendant dans une taverne. Il faut ensuite consommer un peu, voire beaucoup pour un effet optimum. Le lendemain vous aurez des jambes aussi lourdes que ces bottes." ));
            allItems.Add(new Items ( 2424, "Amukwak de Flammes", "Avec toutes ces plumes autour du cou, vous ressemblerez à Aldo Rado, grand Sacrieur aventurier en quête d'une cité d'or." ));
            allItems.Add(new Items ( 2425, "Amulette du Bouftou", "Une bonne Corne de Bouftou, c'est une Corne qui procure calme et sagesse. Un Bon pendentif de Bouftou vous procurera donc sagesse et calme. Ou l'inverse. Ou autre chose. Ou alors rien." ));
            allItems.Add(new Items ( 2426, "L\'Araknamu", "Cette petite Arakne est la preuve vivante que l'on est pas obligé d'empailler un animal pour en faire une Amulette. Elle vous chatouillera le cou, et tissera peut-être une toile devant votre bouche, si vous baillez trop souvent, ou si vous avez trop mauvaise haleine." ));
            allItems.Add(new Items ( 2427, "Kwakture de Flammes", "Pour parader comme un Kwak de Flammes pendant la saison des amours, cette ceinture sera parfaite, à condition de ne porter que ça." ));
            allItems.Add(new Items ( 2428, "Ceinture du Bouftou", "Rapide à équiper, cette ceinture s'avère tout de même assez légère pour sa taille. La laine donne l'impression d'être neuve. Normal, il a fallu selon la légende, dépecer une centaine de bouftons pour avoir cet aspect lisse et soyeux." ));
            allItems.Add(new Items ( 2429, "Abrature", "Cette ceinture vous apportera assurance et abdominaux en bois. Un traitement anti-Arakne est néanmoins recommandé tous les ans. Un peu comme la coiffure d'un Sadida, avec heureusement trois fois moins d'anti-parasites." ));
            allItems.Add(new Items ( 2430, "Flaque de Gelée Bleue", "Tu as été une vraie brute envers cette Gelée Bleue. Un jour, qui sait, tu paieras pour ce crime." ));
            allItems.Add(new Items ( 2431, "Flaque de Gelée Citron", "Tu as été une vraie brute envers cette Gelée Citron. Un jour, qui sait, tu te feras aplatir comme elle." ));
            allItems.Add(new Items ( 2432, "Flaque de Gelée Fraise", "Tu as été une vraie brute envers cette Gelée Fraise. Un jour, qui sait, tu ressembleras à une grosse tâche difforme comme elle." ));
            allItems.Add(new Items ( 2433, "Flaque de Gelée Menthe", "Tu as été une vraie brute envers cette Gelée Menthe. Un jour, qui sait, il ne restera de toi que des restes, comme elle." ));
            allItems.Add(new Items ( 2435, "L\'Eni Kère", "Kère fut le seul Eniripsa à remporter devant les Ecaflips, le grand Marathon d'Amakna. C'est grâce à ses bottes artisanales qu'il traversa la Mèr d'Asse et distança tous les écaflips médusés de voir un Eni courir sur l'eau. La classe." ));
            allItems.Add(new Items ( 2436, "Gelée Citron", "Ce pot de Gelée Citron a une apparence étrange. Selon Elrik Ola, cette gelée proviendrait de chez lui. Plus précisément, de l'autre coin de la galaxie. Autant dire que ce n'est pas la porte à côté." ));
            allItems.Add(new Items ( 2437, "Gelée Citron Royale", "Selon certaines rumeurs, la Gelée Citron Royale est bien meilleure que les autres gelées. Peut-être parce que le citron cache le goût de la gelée." ));
            allItems.Add(new Items ( 2438, "Boufcoiffe Royale", "La Boufcoiffe Royale est un objet indémodable. Elégante, elle procure à son porteur force et intelligence." ));
            allItems.Add(new Items ( 2440, "Epée Royale du Bouftou", "Cette épée ne sait pas trop sur quel sabot danser... Elle inflige des dégâts de chaque élément." ));
            allItems.Add(new Items ( 2441, "Anneau Royal du Bouftou", "Cet anneau augmente la vie de son porteur, tout comme l'odeur de la femelle du Bouftou Royal augmente son charme." ));
            allItems.Add(new Items ( 2442, "Boufbottes Royales", "Elles brillent, elles respirent la puissance, ces bottes sont en tous points royales." ));
            allItems.Add(new Items ( 2443, "Amulette Royale du Bouftou", "Cette amulette augmente la force et l'intelligence de son porteur." ));
            allItems.Add(new Items ( 2444, "Ceinture Royale du Bouftou", "Cette magnifique ceinture augmente la vitalité de son porteur et lui permet de porter plus facilement ses objets. Elle si douce qu'elle permet même de ligoter sa dulcinée lorsque celle-ci refuse de rester à la maison." ));
            allItems.Add(new Items ( 2445, "Cape Bouffante Royale", "Cette cape portée par le grand Bouftou Royal confère, en plus d'un style fabuleux, une force hors du commun." ));
            allItems.Add(new Items ( 2446, "Gelocape", "Dépourvue de fils et de poils, cette Cape reste un mystère pour les têtes pensantes d'Amakna, ce qui ne doit pas représenter plus d'une dizaine de personnes." ));
            allItems.Add(new Items ( 2447, "Gelocoiffe", "Avec cette coiffe sur la tête, vous pouvez être certain que vos amis rêveront enfin de vous lécher. Voilà un chapeau qui aurait comblé le Captain Chafer à la grande époque." ));
            allItems.Add(new Items ( 2448, "Cervelle de Bouftou", "Aucun doute, cette Cervelle provient bien du Bouftou. Bien cuisiné, ce plat est le préféré de tous les petits Iops qui veulent devenir grands et forts. Ça rend aussi intelligent, mais si on le disait aux enfants Iops, ils en mangeraient beaucoup moins." ));
            allItems.Add(new Items ( 2449, "Armée de Tiques", "Selon les chasseurs, une Armée de Tiques affamées pourrait dévorer toute la Laine d'un Bouftou en une nuit. Quelques piliers de bar certifient même qu'elle peut venir à bout de la magnifique crinière d'un Sadida." ));
            allItems.Add(new Items ( 2451, "Sabot de Bouftou", "Autrefois utilisé comme sous-verre, certains alchimistes Eniripsas, assez coquets admettons le, recyclent le Sabot de Bouftou pour en faire des Bols. De quoi donner du goût à leurs céréales préférées." ));
            allItems.Add(new Items ( 2453, "Oeil de Bouftou", "Autrefois, on faisait de cet Oeil une soupe extra. Succulente à un point que l'on a dû bannir la recette des livres, car bon nombre de chasseurs n'allaient récupérer que les yeux et laissaient fumante la carcasse du pauvre Bouftou." ));
            allItems.Add(new Items ( 2454, "Estomac de Bouftou", "Un Estomac de ce genre possède des qualités élastiques efficaces. Par contre, ne vous étonnez pas s'il reste à l'intérieur une bouillie de Trèfles, au contraire, réjouissez-vous Mesdemoiselles, cette bouillie est très bonne pour la peau." ));
            allItems.Add(new Items ( 2455, "Sang de Bouftou", "Impossible d'aller chasser sans se passer de ce Sang de Bouftou tellement son odeur attire les Bouftous les plus gros. Il attire aussi très efficacement les Moskitos sans que l'on sache encore pourquoi." ));
            allItems.Add(new Items ( 2460, "Oeil de Boufton", "Ce n'est pas que ce Boufton garde un Oeil sur vous, mais vous avez quand même l'impression qu'il vous en veut." ));
            allItems.Add(new Items ( 2462, "Cervelle de Chef de Guerre Bouftou", "Forcément, le Chef de Guerre Bouftou dont vous venez d'extraire la Cervelle, va marcher beaucoup moins bien." ));
            allItems.Add(new Items ( 2463, "Dent de Chef de Guerre Bouftou", "Extraire une Dent de Chef de Guerre n'est pas donnée à tout le monde, il faut l'avouer. Notez que cette Dent a beaucoup plus de valeur si elle est abîmée. N'allez pas me demander pourquoi, il paraît que c'est une histoire d'équilibre économique." ));
            allItems.Add(new Items ( 2464, "Queue de Chef de Guerre Bouftou", "Restez terré dans les Bois, observez patiemment, sans faire de bruit, et là, peut-être, vous verrez un Chef de Guerre Bouftou frétiller de la queue. Mais ce petit geste anodin n'a lieu que pendant la période d'accouplement." ));
            allItems.Add(new Items ( 2465, "Corne de Chef de Guerre Bouftou", "Moins doux que son congénère, le Chef de Guerre Bouftou a plus de facilité pour embrocher les Srams et autres races téméraires. On peut souvent trouver des bouts de tissus attachés à leurs cornes, un peu comme une sorte de trophée." ));
            allItems.Add(new Items ( 2466, "Langue de Chef de Guerre Bouftou", "L'expression " ));
            allItems.Add(new Items ( 2467, "Sabot de Chef de Guerre Bouftou", "Selon les cordonniers, ce Sabot est l'un des plus résistants et le plus malléable de la Province. Reste à savoir ce qu'ils entendent par " ));
            allItems.Add(new Items ( 2468, "Estomac de Chef de Guerre Bouftou", "À l'intérieur de cet Estomac se cache une surprise. Petit doigt d'Enutrof, bout de nez d'Osamodas... On pourrait quasiment se reconstruire un Héros avec tous ces morceaux." ));
            allItems.Add(new Items ( 2469, "Gelano", "Inutile de vous ronger les ongles avec cet anneau, léchez-vous les doigts !" ));
            allItems.Add(new Items ( 2470, "Gelobottes", "Ces bottes sont très confortables en été, car elles libèrent une odeur de fraise des bois, couvrant facilement l'odeur de transpiration qui pourrait se dégager des orteils d'un vieux Sadida aux pieds poilus." ));
            allItems.Add(new Items ( 2471, "Geloture", "Le plus dur avec cette Geloture, c'est qu'en cas de fringale, on est vite tenté de croquer dedans." ));
            allItems.Add(new Items ( 2472, "Gelamu", "Voilà une amulette, qui trahira votre goût prononcé pour les orgies de Gelée. On peut trouver mieux pour se mettre en valeur ou briller en société, mais il faut assumer jusqu'au bout votre faible pour la gelée." ));
            allItems.Add(new Items ( 2473, "Cape de l\'Aventurier", "Classe mais peu performante, cette cape comblera tout de même les jeunes aventuriers." ));
            allItems.Add(new Items ( 2474, "Chapeau de l\'Aventurier", "A part faire fuir les nanas, ce chapeau n'a pas vraiment grande utilité." ));
            allItems.Add(new Items ( 2475, "Anneau de l\'Aventurier", "Un petit anneau sympathique pour les débutants, et qui amusera les jeunes enfants, ou les Ecaflips." ));
            allItems.Add(new Items ( 2476, "Bottes de l\'Aventurier", "Bottes de simple facture mais idéales pour les jeunes se lançant sur le dur chemin de l'aventure." ));
            allItems.Add(new Items ( 2477, "Ceinture de l\'aventurier", "Une petite ceinture pour les aventuriers en herbe." ));
            allItems.Add(new Items ( 2478, "Amulette de l\'Aventurier", "Pas très classe, pas très efficace, mais sympathique lorsque l'on possède la panoplie." ));
            allItems.Add(new Items ( 2479, "Crête de Porkass", "Cette coiffure est signe de maturité chez le Porkass. C'est pour cela que l'on voit de temps en temps de jeunes Eniripsas en quête de puberté, se promener avec une crête en ville. Par contre, ils n'osent pas vraiment aller dans les Plaines Rocheuses avec." ));
            allItems.Add(new Items ( 2480, "Slip en cuir de Porkass", "" ));
            allItems.Add(new Items ( 2481, "Groin de Porkass", "Ce Groin, rose comme des fesses des nourrissons Osamodas, doit sûrement être utile pour une recette culinaire. Ou alors un joli collier de Groin, mais il faut quand même être courageux pour le porter et assumer les regards amusés des voisins." ));
            allItems.Add(new Items ( 2482, "Soulier de Porkass", "Autrefois, un Soulier similaire avait été trouvé sur les marches d'un grand palais royal. Des rumeurs raconteraient que ce Soulier fut perdu suite au départ pour le moins hâtif d'une porkassette, qui eut peur de rentrer après les douze coups de minuit." ));
            allItems.Add(new Items ( 2483, "Dentier de Porkass", "Certains Porkass naissent sans dents. D'autres, après s'être tapés une bonne tranche, se réveillent édentés, sans se souvenir de quoi que ce soit." ));
            allItems.Add(new Items ( 2484, "Trident Cassé", "Voici un petit souvenir de votre ami Porkass. En espérant qu'il ne vous en tienne pas rigueur au point de vous traquer à travers la Province. Caractériel comme un cochon, il en est bien capable, méfiez-vous." ));
            allItems.Add(new Items ( 2485, "Végacoiffe", "Le top du top de la mode, mais uniquement au nord-est d'Amakna." ));
            allItems.Add(new Items ( 2486, "Oreille de Porkass", "Cette Oreille est un bon moyen de se faire respecter, elle fait office de trophée et prouve que vous domptez les Porkass à la baguette. Attention toutefois à ne pas vous la raconter auprès d'autres Porkass, ils ne vous acclameront sûrement pas, eux." ));
            allItems.Add(new Items ( 2487, "Carapace de Scorbute", "Idéal pour confectionner de lourdes armures, le Scorbute était chassé jadis par la Milice de Brâkmar, car leur taux de reproduction faisait exploser tous les records. Même les Gelées n'arrivaient pas à se reproduire aussi vite." ));
            allItems.Add(new Items ( 2488, "Viscères de Scorbute", "Au premier abord, il est certain que ces viscères ne sont pas très appétissants. Et vous avez bien raison, les viscères de Scorbute sont infâmes, même cuisinés avec amour." ));
            allItems.Add(new Items ( 2489, "Vegacape", "Une cape élégante qui vous rendra irrésistible auprès des amateurs de belles plantes." ));
            allItems.Add(new Items ( 2490, "Patte de Scorbute", "Il est peu commun de pouvoir récupérer une Patte de Scorbute dans un état aussi correct. En espérant que vous vous en servirez à bon escient, et non, pour crocheter des portes. Quoique..." ));
            allItems.Add(new Items ( 2491, "Oeil d\'Ouginak", "Inquiétant, mais palpitant, cet Oeil d'Ouginak ne reflète pas vraiment ce que l'on attend. Par exemple, au lieu de vous voir dedans, vous voyez votre homologue Dopeul. Allez comprendre." ));
            allItems.Add(new Items ( 2492, "Chaîne de l\'Ouginak", "Cette chienne de Chaîne provient évidemment d'un Ouginak. Avant qu'elle ne soit brisée, elle servait probablement à attacher l'Ouginak pour éviter qu'il s'échappe." ));
            allItems.Add(new Items ( 2493, "Coeur d\'Ouginak", "Ce coeur vous restera fidèle. Tant que vous ne le vendrez, ni ne l'échangerez, il restera avec vous dans le fond de votre sac. Quelle délicate attention." ));
            allItems.Add(new Items ( 2494, "Pommeau d\'Epée", "Il a sûrement fallu un coup surpuissant pour venir à bout de cette Épée mythique. En effet, l'Épée d'un Ouginak procure à celui qui l'utilise, une envie de hurler de joie. Wa-hooooo !" ));
            allItems.Add(new Items ( 2495, "Oeil de Kolérat", "Cet Oeil brille la nuit, même une fois arraché à son propriétaire. De quoi s'éclairer à moindre frais." ));
            allItems.Add(new Items ( 2496, "Queue de Kolérat", "Tranchée convenablement, la Queue de Kolérat a le goût de saucisson. Ce qui est étrange, c'est que si vous coupez de trop grosses tranches, le goût de saucisson disparaît totalement. Encore une énigme culinaire de plus dans ce monde." ));
            allItems.Add(new Items ( 2497, "Oreille de Kolérat", "Portez-la à votre oreille, et vous entendrez la mer, parait-il." ));
            allItems.Add(new Items ( 2498, "Vegamu", "La Vegamu a parfois tendance à attirer les Moskitos, qui de loin la prennent pour une copine. Ils sont souvent déçus une fois qu'ils tombent sur le porteur de l'amulette." ));
            allItems.Add(new Items ( 2499, "Museau de Kolérat", "Le Kolérat possède un flair hors du commun. Il n'arrive pas à la cheville de l'Ouginak, mais arrive à sentir celui-ci à trois cents mètres à la ronde. Et ça, c'est vraiment très pratique pour éviter ses prédateurs." ));
            allItems.Add(new Items ( 2500, "Kolérat Mort", "Vous avez bien fait de le ramasser, il est intact. Mort, mais intact." ));
            allItems.Add(new Items ( 2501, "Queue de Croc Gland", "Le Dieu Osamodas ne supporte pas que l'on touche aux animaux sauvages : " ));
            allItems.Add(new Items ( 2502, "Papatte de Croc Gland", "Les Papattes de Croc Gland sont assez prisées par les gastronomes du coin. Elle possède des vertus magiques, comme celle de porter chance à son possesseur. C'est déjà pas mal." ));
            allItems.Add(new Items ( 2503, "Etoffe de Croc Gland", "Avec ceci, vous pourrez être aussi espiègle que le Croc Gland. Si vos rêves étaient d'arborer une Étoffe sur votre tête et de vous déplacer de manière féline, eh bien, vous faites de biens drôles de rêves, soit dit entre nous." ));
            allItems.Add(new Items ( 2504, "Langue de Croc Gland", "Cette Langue a dû traîner dans pas mal d'endroits avant de se retrouver dans votre sac." ));
            allItems.Add(new Items ( 2505, "Croquette", "Pour attirer un Croc Gland dans les mailles de la domestication, lancez-lui une Croquette, ils en raffolent. Il ne vous l'avouera pas, mais vous vous serez fait un ami pour la vie." ));
            allItems.Add(new Items ( 2506, "Grelot", "Muni d'un Grelot, le Serpentin s'entend à des Kilomètres à la ronde. Heureusement d'ailleurs, car à deux mètres, on pourrait le manquer à cause de la végétation luxuriante des Plaines de Cania." ));
            allItems.Add(new Items ( 2507, "Langue Fourchue", "Armé de cette Langue, vous pourrez fouetter vos amis. Ce sont vos collègues Osamodas et Sacrieurs qui vont être contents." ));
            allItems.Add(new Items ( 2508, "Venin de Serpentin", "Redoutable, ce Venin peut venir à bout d'une armada de Bouftous enragés. Ils servaient d'ailleurs autrefois à maîtriser les troupeaux, on en mettait un peu sur un bâton, et le tour était joué." ));
            allItems.Add(new Items ( 2509, "Dent de Serpentin", "Cette Dent appartenait à un Serpentin, cela ne fait aucun doute. On peut le constater en observant de près le bout de la Dent qui s'avère être on ne peut plus pointu." ));
            allItems.Add(new Items ( 2510, "Estomac de Sanglier des Plaines", "Cet Estomac peut contenir des choses affolantes, une Iopette serait heureuse de voir toutes les Épées qu'elle pourrait ranger dedans." ));
            allItems.Add(new Items ( 2511, "Cuir de Sanglier des Plaines", "Avec les propriétés de ce Cuir, les artisans cordonniers ont beaucoup hésité avant de confectionner des ceintures et des bottes. Le problème crucial était que ce Cuir ne sentait rien. Bon nombre de clients pensaient avoir affaire à du faux Cuir." ));
            allItems.Add(new Items ( 2512, "Queue de Sanglier des Plaines", "Pas mal de braconniers Brâkmariens collectionnent ce trophé poilu. Encore aujourd'hui, les raisons de ce pillage restent inconnues." ));
            allItems.Add(new Items ( 2513, "Etoffe de Sanglier des Plaines", "Contrairement à son Cuir, l'Etoffe du Sanglier des Plaines dispose d'un fumet radical contre le nez bouché. Pour preuve, cette odeur permettrait même, selon la légende, de réveiller un Sadida en hibernation." ));
            allItems.Add(new Items ( 2514, "Sabot de Sanglier des Plaines", "Généralement, les Sabots de Sanglier sont couverts de boue ou de terre, mais par moment, il ne faut pas s'étonner d'y dénicher des Biblops de toutes les couleurs. Oui, comme vous, le Sanglier des Plaines n'aime pas les Biblops." ));
            allItems.Add(new Items ( 2515, "Groin de Sanglier des Plaines", "À première vue, ce Groin n'est pas de toute fraîcheur. Et bien détrompez-vous, celui-ci est de loin le plus frais et tendre de tous les groins." ));
            allItems.Add(new Items ( 2516, "Défense de Sanglier des Plaines", "Cette défense vous rappellera que le Sanglier des Plaines est le plus antipathique des Sangliers. En effet, elle est aussi usée qu'une Dent de Wabbit abîmée par les Cawottes." ));
            allItems.Add(new Items ( 2517, "Mégacape", "Cette cape appartenait à un célèbre héros, connu pour avoir combattu le terrible docteur Wily qui voulait envahir et dominer le monde des douze avec ses propres invocations. Le docteur Wily mourut avant que toutes ses invocations n'eurent le temps d'attaquer." ));
            allItems.Add(new Items ( 2518, "Cape Vioutifoule", "Silvia de Jodaw a cousu elle-même cette surprenante cape à partir de vieux rideaux. Heureusement, ça ne se voit pas trop, puisqu'elle ressemble à s'y méprendre à une serpillière bouillie dans du sang de Porkass." ));
            allItems.Add(new Items ( 2519, "Mégacoiffe", "Pour assurer dans les situations les plus extrêmes, et briller en société, cette coiffe devrait faire l'affaire." ));
            allItems.Add(new Items ( 2520, "Casque Vioutifoule", "Ce casque a appartenu au bon vieux Jojo, célèbre héros de l'ancien temps. Connu pour ses divers exploits, on a tout de même du mal à comprendre la passion qui anime ses fans." ));
            allItems.Add(new Items ( 2521, "Mégabottes", "Roll, la gentille sœur du propriétaire de ces bottes, était spécialisée en taxidermie astrale. Jadis, personne n'était capable de comprendre la différence entre la taxidermie, et la taxidermie astrale. Mais on peut sans peine, imaginer de nos jours, que la taxidermie astrale permet de confectionner des créatures empaillées astrales. Si ça vous paraît évident maintenant, c'est peut-être parce que vos ancêtres étaient stupides." ));
            allItems.Add(new Items ( 2522, "Bottes Vioutifoule", "Ces bottes permettaient au célèbre Jojo d'augmenter la puissance de son célèbre coup de pied rotatif, également connu sous le nom de " ));
            allItems.Add(new Items ( 2523, "Mégature", "Cette ceinture a été spécialement conçue pour protéger les hanches des héros. Elle ne vous sera donc d'aucune utilité." ));
            allItems.Add(new Items ( 2524, "Ceinture Vioutifoule", "On raconte que cette ceinture aurait été fabriquée par le capitaine Bloue en personne. A peine croyable, et pourtant vous y croyez tout de même." ));
            allItems.Add(new Items ( 2525, "Coeur du Maitre Corbac", "Sans son Coeur, son ramage va en prendre un coup. Mais surtout ne lui dites pas, car le Maître Corbac est très susceptible." ));
            allItems.Add(new Items ( 2526, "Faux du Maitre Corbac", "Cette faux était destinée à la base au fauchage du Iop, mais le Maître Corbac s'est vite aperçu qu'elle était si aiguisée, qu'elle pouvait être utilisée pour faucher tout et n'importe qui." ));
            allItems.Add(new Items ( 2527, "Clakoss", "Le Maître est plein de surprises, dans sa poche se tenait un Clakoss, célèbre fromage royal. Le fait qu'il soit entamé prouve que le Maître Corbac l'adore. Au niveau de l'odeur, on ne peut que vous conseiller de ne pas le garder sur vous." ));
            allItems.Add(new Items ( 2528, "Ongle de Kanigrou", "Il faut être puissant et rapide pour récupérer un Ongle sur un Kanigrou. Puissant pour l'extraire et rapide pour ne pas se prendre une baffe géante par l'animal." ));
            allItems.Add(new Items ( 2529, "Potion de métal liquide", "Cette potion permet de redonner un peu de réalité à une lame éthérée." ));
            allItems.Add(new Items ( 2530, "Carabottes", "Elles sont un peu lourdes, mais ces charmantes Carabottes feront de vous la reine de la nuit." ));
            allItems.Add(new Items ( 2531, "Caracoiffe", "Comme vous, la tortue a vraiment une toute petite tête. Ca tombe bien, ce chapeau est spécialement prévu pour ça." ));
            allItems.Add(new Items ( 2532, "Caracape", "Une cape pratique et résistante, qui ne fera pas de vous une célébrité, mais comme vous n'en étiez pas une jusqu'à maintenant, où est le problème ?" ));
            allItems.Add(new Items ( 2534, "Kwape de Glace", "Idéale pour se rafraîchir sur l'île de Moon pendant la saison chaude, et parfaite pour refroidir les ardeurs de vos amis les plus entreprenants, cette cape saura répondre aux attentes de l'aventurier des temps modernes." ));
            allItems.Add(new Items ( 2535, "Kwakoiffe de Glace", "Cette Kwakoiffe de Glace vous permettra de toujours avoir des idées fraîches, même lorsque vous serez à court d'idées. Elle n'est cependant pas assez puissante pour un Iop." ));
            allItems.Add(new Items ( 2537, "Kwakture de Glace", "Idéale pour vous raffermir les hanches, cette ceinture vous permettra de garder bien au frais ce que vous avez en dessous de la ceinture." ));
            allItems.Add(new Items ( 2538, "Potion de métal lourd liquide", "Cette potion permet de redonner de la réalité à une lame éthérée." ));
            allItems.Add(new Items ( 2539, "Potion de Copeaux de Bois", "Cette potion permet de redonner un peu de réalité à une oeuvre sculptée étherée." ));
            allItems.Add(new Items ( 2540, "Potion de Copeaux de Bois Solides", "Cette potion permet de redonner de la réalité à une oeuvre sculptée étherée." ));
            allItems.Add(new Items ( 2541, "Potion de métal précieux liquide", "Cette potion permet de redonner une grande part de réalité à une lame éthérée." ));
            allItems.Add(new Items ( 2542, "Potion de Foyer", "Cette Potion permet de retourner à n'importe quel moment dans votre foyer." ));
            allItems.Add(new Items ( 2543, "Potion de Copeaux de Bois Rares", "Cette potion permet de redonner une grande part de réalité à une oeuvre sculptée étherée." ));
            allItems.Add(new Items ( 2544, "Corbalame", "Terriblement tranchante et incroyablement classe, cette lame risque de faire des jaloux parmi votre entourage de Corbacs." ));
            allItems.Add(new Items ( 2545, "Corbottes", "Ces bottes donnent à leur porteur une impression d'incroyable légèreté, mais aussi un air stupide." ));
            allItems.Add(new Items ( 2546, "Corbacoiffe", "Grâce à cette magnifique coiffe, vous aurez au moins autant de classe que le tristement célèbre Maître Corbac." ));
            allItems.Add(new Items ( 2547, "Corbacape Mastralis", "Les meilleurs tailleurs d'Amakna se sont associés jadis pour utiliser les restes de la dépouille du Maître Corbac afin d'en faire une cape somptueuse. Avec une telle cape sur les épaules, le maître c'est vous." ));
            allItems.Add(new Items ( 2549, "Boîte de Kanigrou", "Cette conserve contient du Kanigrou en morceaux, préalablement découpé puis mis en boîte. Très pratique à transporter, le Kanigrou en morceaux est vraiment le meilleur ami de l'homme." ));
            allItems.Add(new Items ( 2550, "Etoffe de Kanigrou", "Avec cette Étoffe, vous pourrez faire un substitut capillaire de toute beauté. On peut même dire que l'on vous remarquera de loin, très loin." ));
            allItems.Add(new Items ( 2551, "Oreille de Kanigrou", "Voici le plat préféré de l'Ouginak: Oreille de Kanigrou sauce nature. Mais traditionnellement, on la cuisine avec beaucoup de menthe sauvage et quelques Ongles pour le goût." ));
            allItems.Add(new Items ( 2552, "Oeil de Kanigrou", "L'oeil de Kanigrou est devenu un phénomène de mode, il est très courant de voir se promener des aventuriers avec l'Oeil autour du cou." ));
            allItems.Add(new Items ( 2553, "Gros Boulet", "Voici un véritable Boulet. Lourd à souhait, il ne cessera jamais de vous perforer le crâne quand bon lui semble. Rien de plus ennuyeux qu'un gros Boulet en quelque sorte." ));
            allItems.Add(new Items ( 2554, "Côtes de Porkass", "Les Côtes de Porkass ont une saveur particulière. Certaines personnes en raffolent avec un jus épicé, d'autres les préfèrent crues. Le Porkass quant à lui, préfèrera les vôtres. A chacun sa recette." ));
            allItems.Add(new Items ( 2555, "Selle en Cuir", "À croire que le Porkass est douillet et qu'il ne l'assume pas, car sous ses fesses, était cachée une selle munie d'un rembourrage de qualité. Une fois retravaillée, on pourrait faire de cette selle, d'excellentes pantoufles." ));
            allItems.Add(new Items ( 2556, "Pollen de Blop", "Voici la source de toutes les allergies bénignes de ce monde. Le seul souci, c'est que toutes les allergies mortelles commencent par une allergie bénigne. En gros, ne traînez pas avec ce Pollen dans vos poches si vous n'en avez pas besoin." ));
            allItems.Add(new Items ( 2557, "Pétale de Blop", "Aussi étrange que cela puisse paraître, les Pétales de Blops ne sont pas comestibles, aussi bien cuits que crus. Qui l'eut cru ?" ));
            allItems.Add(new Items ( 2558, "Blop Mort", "Vous avez bien fait de le ramasser, il est intact. Mort, mais intact." ));
            allItems.Add(new Items ( 2559, "Crinière de Trool", "Une Crinière comme celle-ci, ça ne court pas les rues. Seuls les Enutrofs les plus riches peuvent se permettre de s'en faire un substitut capillaire." ));
            allItems.Add(new Items ( 2560, "Epaulette de Trool", "Il vaut mieux observer cette Épaulette dans ces conditions que de l'admirer encore accroché sur l'épaule du Trool. Manquerait plus qu'il vous fasse un défilé !" ));
            allItems.Add(new Items ( 2561, "Bracelet de Force de Trool", "Comme si le Trool avait besoin d'un Bracelet de Force... Cette incroyable machine à broyer les os en possède un à chaque poignet." ));
            allItems.Add(new Items ( 2562, "Slip de Trool", "À voir l'état de son Slip, le Trool s'assoit un peu n'importe où. C'est aussi grâce à ce Slip que l'on peut déterminer l'âge de son propriétaire. A vu de nez, bien sûr..." ));
            allItems.Add(new Items ( 2563, "Cale en bois du Tronknyde", "Pour le Tronknyde, cette Cale sert de pied, car le pied sert de Cale, malheureusement, le Tronknyde est toujours à la recherche d'un cale-pied." ));
            allItems.Add(new Items ( 2564, "Sourcils de Tronknyde", "Vous vouliez connaître la technique du Tronknyde pour effrayer les visiteurs imprudents ? Et bien,vous l'avez dans les mains : une bonne grosse paire de Sourcils bien épais." ));
            allItems.Add(new Items ( 2565, "Tranche de Tronknyde", "Après vous être taillé une bonne Tranche de ce Tronknyde, il doit bien tirer la tronche à présent." ));
            allItems.Add(new Items ( 2566, "Tranche Magique de Tronknyde", "Cette Tranche à l'air différente des autres Tranches de Tronknyde. Au moment ou elle fut coupée, une lumière aveuglante a jailli du Tronknyde et a ensuite disparu, ne laissant au sol que la Tranche." ));
            allItems.Add(new Items ( 2567, "Anticipation", "Cette bénédiction permet d'être plus vif pour les prochains combats." ));
            allItems.Add(new Items ( 2568, "Reflexes Soushumains", "Cette malédiction vous plonge dans un état de semi-conscience et vos réflexes en sont amoindris pour le prochain combat" ));
            allItems.Add(new Items ( 2569, "Parchemin de Reflexes Surhumain", "Ce Parchemin permet d'être plus vif pour le prochain combat." ));
            allItems.Add(new Items ( 2570, "Parchemin de Reflexes Soushumains", "Ce Parchemin permet d'être un brin inerte pour le prochain combat." ));
            allItems.Add(new Items ( 2571, "Patte de Prespic", "Comment un Prespic, et vous devez vous poser sûrement la même question, fait-il pour être aussi agile avec ces deux malheureux doigts de patte? Le mystère reste entier." ));
            allItems.Add(new Items ( 2572, "Estomac de Prespic", "" ));
            allItems.Add(new Items ( 2573, "Queue de Prespic", "Le Prespic fait usage de sa queue très rarement, et fort heureusement d'ailleurs, car la puissance qui réside dans celle-ci se révèle surprenante. Certains bûcherons ont même vu des Prespic en pleine nuit trimbaler des rondins de Bois avec leurs queues." ));
            allItems.Add(new Items ( 2574, "Crinière de Prespic", "Pour les soirées dites " ));
            allItems.Add(new Items ( 2575, "Scalp de Mulou", "À votre arrivée, le Mulou avait certainement la même intention que vous, c'est-à-dire de vous extraire avec plus ou moins de diplomatie votre Scalp. Le voilà pris de vitesse." ));
            allItems.Add(new Items ( 2576, "Scalp de Milimulou", "À votre arrivée, le Milimulou avait certainement la même intention que vous, c'est-à-dire de vous extraire avec plus ou moins de délicatesse votre Scalp. Le voilà bien malin sans le haut de son crâne." ));
            allItems.Add(new Items ( 2577, "Scalp de Meulou", "À votre arrivée, le Meulou avait certainement la même intention que vous, c'est-à-dire de vous extraire avec plus ou moins de tact votre Scalp. Le voilà prêt à attraper un rhume du cerveau." ));
            allItems.Add(new Items ( 2578, "Oeil de Mulou", "Même arraché, le regard du Mulou reste porté sur vous. Et si vous regardez attentivement, vous aurez tendance à penser qu'il vous suit des yeux, ou plutôt de l'oeil." ));
            allItems.Add(new Items ( 2579, "Oeil du Milimulou", "Même arraché, le regard du Milimulou reste porté sur vous. Et si vous regardez attentivement, l'oeil cligne encore des paupières. Quelle vitalité ce Milimulou." ));
            allItems.Add(new Items ( 2580, "Oeil de Meulou", "Même arraché, le regard du Meulou reste porté sur vous. Et si vous regardez attentivement, il fronce les sourcils quand vous le pointez du doigt. Décidément, le Meulou n'est pas la pour la boutade." ));
            allItems.Add(new Items ( 2581, "Dents de Meulou", "Dans la famille des Mulous, si vous cherchez celui qui a les plus grandes dents, vous venez de faire bonne pioche. C'est pour mieux vous baver dessus mes enfants." ));
            allItems.Add(new Items ( 2582, "Testicules de Meulou", "Si le prix des Testicules de Mulou est scandaleux, vous comprendrez que ce que vous avez en main vaut au bas mot une sacrée paire... de lingots." ));
            allItems.Add(new Items ( 2583, "Oeil de Crabe", "Les marins d'eau douce sont assez friands de cet Oeil de Crabe, car on peut en capturer sur la terre ferme." ));
            allItems.Add(new Items ( 2584, "Main de Boo", "Cette Main de Boo est recouverte de boue. À moins qu'il n'y ait rien sous cette boue, même pas la Main du Boo." ));
            allItems.Add(new Items ( 2585, "Coeur de Boue du Boo", "Muni de son Coeur de Boue, le Boo est dur à classer dans la chaîne alimentaire. En réfléchissant un peu, le Boo viendrait sûrement au bout de la chaîne." ));
            allItems.Add(new Items ( 2586, "Aile de Dragodinde Rousse Sauvage", "Cette Aile sent le soufre. Tout bon éleveur de Dragodindes vous dira que c'est la partie la plus tendre pour un barbeulcu. Assaisonnez tout de même la viande, car l'Aile est la partie la plus fade." ));
            allItems.Add(new Items ( 2587, "Hachette de Bûcheron", "Cette hachette est l'outil indispensable à tout bûcheron. Non pas qu'elle soit réellement utile, impossible de couper un arbre avec, mais sa présence à la ceinture les calme et les rassure." ));
            allItems.Add(new Items ( 2588, "Aile de Dragodinde", "On peut dire que la Dragodinde domestique est bien meilleure que sa cousine sauvage. Déjà, elle n'a pas un arrière-goût de soufre, mais la chair est goûteuse à souhait, ce qui en fait le plat favori de tous les petits." ));
            allItems.Add(new Items ( 2589, "Hachette Hévlalav", "Cette petite hache ne sait pas couper les arbres, mais peut servir à se défendre contre certains petits monstres. Par contre il vous faudra toujours courir si c'est un Minotoror." ));
            allItems.Add(new Items ( 2590, "Hache Vôr\'Om", "Cette série de haches fut conçue par un bwork apprenti forgeur. Elle est bien équilibrée et relativement puissante. Ce bwork venait de créer LA hache de guerre pour jeune aventurier." ));
            allItems.Add(new Items ( 2591, "Queue de Dragodinde Rousse Sauvage", "L'intérêt de cette queue est simple: posez-la sur un rocher rond et faites tourner la Queue. Quand la Queue s'arrête, suivez la direction indiquée par la pointe. Telle était la façon de se déplacer des Sadida pendant des millénaires." ));
            allItems.Add(new Items ( 2592, "Coupeuse de Bombu", "Très légère et maniable, cette hache facilite les gestes amples nécessaires afin de couper le Bombu en toute sécurité." ));
            allItems.Add(new Items ( 2593, "Elagueuse d\'Oliviolet", "Cette hache, faite d'Oliviolet, est la hache la plus à même à résister aux ondes violettes de l'Oliviolet. Combattre le mal par le mal en quelque sorte." ));
            allItems.Add(new Items ( 2594, "Queue de Dragodinde", "Étrangement, la Queue de Dragodinde n'a aucun pouvoir magique. A l'instar d'un Crocodaille, la Queue repousse rapidement. Une légende raconte aussi qu'une Dragodinde avait poussé au bout de la Queue. Vous voilà prévenu." ));
            allItems.Add(new Items ( 2595, "Hache de Pierre", "Cette hache, très primitive, n'en reste pas moins efficace. Un peu comme certains vieillards." ));
            allItems.Add(new Items ( 2596, "Lait de Dragodinde", "Les éleveurs apprécient le fait de pouvoir traire leurs Dragodindes. Le seul problème, c'est que ce Lait ne se vend pas, car il est infect. Il sert donc juste à nourrir les Dragodindonettes." ));
            allItems.Add(new Items ( 2597, "Hache de Brèche", "Cette longue hache se faufile dans les moindres interstices des arbres pour mieux les fendre en deux. Son utilité en combat est la même, l'adversaire ayant toujours un interstice ou deux à exploiter." ));
            allItems.Add(new Items ( 2598, "Pic de Dragodinde Rousse Sauvage", "Ce Pic n'est autre qu'une protubérance osseuse. La Dragodinde s'en sert tellement peu, que l'on se demandait comment faisait-elle pour les abîmer autant. Depuis que les Biologistes l'ont vu se frotter aux Trools, on comprend mieux." ));
            allItems.Add(new Items ( 2599, "Pic de Dragodinde", "Situé sur le dos de la Dragodinde, ce Pic pourra peut-être vous servir à vous gratter l'entrejambe, si vous parvenez à monter une Dragodinde." ));
            allItems.Add(new Items ( 2600, "Coupeuse de Sifflet", "Cette grosse hache fait un bruit monstrueux lorsqu'elle s'abat sur quelque chose, ce qui a souvent pour effet de provoquer le silence autour de soi. C'est toujours ça de pris." ));
            allItems.Add(new Items ( 2601, "Hache Darsson", "Darsson était un vaillant Enutrof adroit comme ses pieds. Faisant souvent tomber sa pelle en combat, il eût l'ingénieuse idée d'attacher une hache de rechange à sa ceinture. Cela lui sauva la vie plusieurs fois, jusqu'à ce qu'il fasse tomber sa hache..." ));
            allItems.Add(new Items ( 2602, "Pétale de Trukikol", "Pas plus gros qu'un Oeil de Crabe, le Pétale de Trukikol est constitué de plusieurs bulles collantes et spongieuses au toucher. Il sert le plus souvent à colmater les trous dans les toits après les rigoureuses tempêtes de fin d'année." ));
            allItems.Add(new Items ( 2603, "Trancheuse de Partie", "Cette hache est très utile pour séparer certaines parties récalcitrantes d'un arbre, notamment les petites boursoufflures du tronc, ceci dans le but d'avoir un arbre parfaitement lisse et rond." ));
            allItems.Add(new Items ( 2604, "Bardiche du Milicien", "Cette bardiche est principalement employée par les miliciens d'Amakna. Sa longueur et son efficacité doit probablement compenser d'autres choses." ));
            allItems.Add(new Items ( 2605, "Peau de Trukikol", "Ne soyez pas étonné si l'on vous dit que la Peau de Trukikol colle. Grâce à elle, on arrive à tout faire tenir au plafond. Il faut avouer que c'est aussi grâce au paysan qui a breveté l'idée : un dénommé Roulio de Scautche." ));
            allItems.Add(new Items ( 2606, "Hachette Savante", "Sur cette grosse hachette, de multiples inscriptions gravées rappellent des notions Amakniennes de base. Son inventeur Claus Ansipedi désirait donner un semblant de culture aux brûtasses finies...peine perdue, il est mort le crâne fendu." ));
            allItems.Add(new Items ( 2607, "Trukikol Mort", "Vous avez bien fait de le ramasser, il est intact. Mort, mais intact." ));
            allItems.Add(new Items ( 2608, "Francique Basquaise", "Cette hache fait souvent le tour du monde en commençant par le bas. Et pour cause c'est dans le sud d'Amakna que ce type de hache est forgée, par une petite guilde de chasseurs de trésors réputée pour leur efficacité." ));
            allItems.Add(new Items ( 2609, "Carapace Verte Vide", "Mais où se cache le propriétaire ? Il a dû sortir s'acheter du pain avec de la tomate dessus." ));
            allItems.Add(new Items ( 2610, "Carapace Rouge Vide", "Mais ou se cache le propriétaire? il a dû déménager depuis peu." ));
            allItems.Add(new Items ( 2611, "Carapace Jaune Vide", "Mais ou se cache le propriétaire ? il fait peut-être une sieste à l'intérieur." ));
            allItems.Add(new Items ( 2612, "Vouge à Poisson", "Cette longue hache est utilisée par les marins experts pour affronter les poissons géants qui attaquent leurs navires. On imagine assez bien les dégâts qu'elle peut faire contre des monstres plus petits." ));
            allItems.Add(new Items ( 2613, "Carapace Bleue Vide", "Mais où se cache le propriétaire ? il doit être sous la douche." ));
            allItems.Add(new Items ( 2614, "Hache de Dame Oise", "La Dame Oise était une ravissante princesse qui respirait la candeur et la fraîcheur. Tout le monde fut bien surpris lorsque, lors d'un raid Bwork sur sa cité, elle sortit cette hache et fracassa à elle seule la moitié de l'armée adverse." ));
            allItems.Add(new Items ( 2615, "Hache de Lookabeer", "Cette hache est réputée pour son efficacité à terminer définitivement et très rapidement une querelle entre Enutrofs ivres." ));
            allItems.Add(new Items ( 2616, "Halle de Barde", "Cette hache tire son nom du test qui fut utilisé pour prouver sa terrible efficacité : une hache géante, un mythophobiaque et une halle remplie de bardes." ));
            allItems.Add(new Items ( 2617, "Tranche de Nodkoko", "Haaa... Enfin un élément qui vous fait penser aux vacances. Cette Agence de Voyages ne paie rien pour attendre." ));
            allItems.Add(new Items ( 2618, "Kokopaille", "Pour siroter quelque chose de frais, rien de mieux qu'une paille colorée. Il n'y a plus qu'à trouver un trou dans la Nodkoko." ));
            allItems.Add(new Items ( 2619, "Kokolait", "En voilà une bonne idée: avoir du Kokolait en bouteille, ça permet d'en boire quand on veut et où que l'on soit." ));
            allItems.Add(new Items ( 2620, "Rhum de Qualité", "Ce Rhum doit sortir d'une cave très prisée. À moins qu'il soit resté dans une poche pendant de bonnes générations." ));
            allItems.Add(new Items ( 2621, "Barbe du Chouque", "Vous venez d'extraire du menton du Chouque sa Barbe. Bizarrement, les Dents sont venues avec. C'est ça d'être mort depuis longtemps, on perd des bouts." ));
            allItems.Add(new Items ( 2622, "Boucle du Chouque", "Du bout de cette Boucle, sept générations de pirates vous contemplent." ));
            allItems.Add(new Items ( 2623, "Bwak Squelettique", "Les éleveurs ont toujours cru que les Bwaks s'envolaient très loin à la fin de leur vie. Toutefois, ce Bwak est une exception à la règle." ));
            allItems.Add(new Items ( 2624, "Feuille de Kokoko", "De hardis chasseurs veulent coûte que coûte et quoiqu'il en coûte, récupérer en guise de souvenirs une Feuille de Kokoko, pour ensuite ne jamais revenir sur cette île maudite. Pourquoi pas ?" ));
            allItems.Add(new Items ( 2625, "Poupée Vaudou Thierry", "Une force se dégage de cette Poupée, mais vous avez l'étrange sensation qu'elle appelle quelqu'un." ));
            allItems.Add(new Items ( 2626, "Poupée Vaudou Jav", "Une force se dégage de cette Poupée, mais vous avez l'étrange sensation qu'elle cherche quelqu'un." ));
            allItems.Add(new Items ( 2627, "Poupée Vaudou Sarbak", "Une force se dégage de cette Poupée, mais vous avez l'étrange sensation qu'elle écoute quelqu'un." ));
            allItems.Add(new Items ( 2628, "Poupée Vaudou Archer", "Une force se dégage de cette Poupée, mais vous avez l'étrange sensation qu'elle attend quelqu'un." ));
            allItems.Add(new Items ( 2629, "Caracape Minotoris", "Cette cape, d'allure royale, constitue pourtant une incroyable protection. C'est ce modèle qui a fait la réputation d'une célèbre tailleuse, juste avant de mourir de son succès, écrasée par la foule." ));
            allItems.Add(new Items ( 2630, "Pot de Gloutovore", "À la base, le Gloutovore n'est pas très généreux. Avec ce Pot rempli à raz bord, vous voila bien chanceux. Reste plus qu'à lui trouver une utilité." ));
            allItems.Add(new Items ( 2631, "Estomac de Gloutovore", "Voici l'estomac le plus résistant que vous puissiez voir. Extensible à souhait, vous pourriez même vous cacher dedans si vous le souhaitiez." ));
            allItems.Add(new Items ( 2632, "Glouto Rhum", "Cette Bouteille a été laissée à l'abandon à l'intérieur d'un Gloutovore. Quel gâchis ! Par contre, il a l'air d'avoir bien vieilli, il a gagné en arôme. Si les Paysans savaient ça, il y aurait des entrepôts remplis de Gloutovore." ));
            allItems.Add(new Items ( 2633, "Rhum Fourbe", "Alors là, c'est le piège. La Fourbasse est tellement fourbe qu'elle a rempli une bouteille de Rhum avec du... bah oui du Rhum, mais il a une odeur repérable à dix mille lieues : le Trukikol séché." ));
            allItems.Add(new Items ( 2634, "Lance Cassée", "Sans sa Lance, la Fourbasse fera moins la maligne. Reste à savoir ce que vous allez en faire." ));
            allItems.Add(new Items ( 2635, "Pain Gre", "Cette recette a été élaborée par un rival de Xavier le Boulanger, à savoir Danioul Garnioul le Boulanger. Son nom était si stupide que ses clients ne cessaient de se moquer de lui, il se vengeait en pétrissant avec ses pieds." ));
            allItems.Add(new Items ( 2636, "Pain Tahde", "Ce pain renferme de délicieuses lichettes de viande, qui ont la saveur et l'apparence de volaille, mais méfiance il n'en n'est rien. En y regardant de plus près, on peut sentir une légère odeur de Tofu." ));
            allItems.Add(new Items ( 2637, "La Griffe", "Cette lame recourbée, faite d'os, de cartilage et de corne, est aussi tranchante qu'une griffe d'Ecaflip. En un peu plus gros et plus propre." ));
            allItems.Add(new Items ( 2638, "La Griffe Aiguisée", "Cent pour cent naturelle, cette épée est comme une sixième griffe pour une main Ecaflip. A ce propos, faites attention à ne pas vous grattouiller le museau avec cette griffe là !" ));
            allItems.Add(new Items ( 2639, "Fausse Griffe de Ceangal", "Une forgeuse écaflipette eut l'idée de forger cette épée après son échec à maitriser la technique ancestrale du même nom. Elle se disait qu'au moins cette griffe ne lui ferait pas faux bond." ));
            allItems.Add(new Items ( 2640, "Bâton Carnivore", "Etrangement sculpté, ce Bâton ne provient pas du coin. Il sent les vieux endroits humides et sombres de Brâkmar." ));
            allItems.Add(new Items ( 2641, "Toady", "Rouge, blanc et surtout somptueusement classe, ce chapeau vous permettra d'assurer un max lors des soirées de l'ambassadeur qui sont toujours une réussite." ));
            allItems.Add(new Items ( 2642, "Mâchoire de Rib", "Déjà que le Rib est un tantinet maigrelet, alors s'il lui manque sa Mâchoire, il va se retrouver aussi offensif qu'une armée de tiques en hibernation." ));
            allItems.Add(new Items ( 2643, "Défense de Dragon Cochon", "Cette défense appartient à l'un des plus célèbres prédateurs des montagnes d'Amakna: le Dragon Cochon. Des chasseurs seraient prêts à revendre leur famille entière pour posséder ce que vous avez en main. C'est pour vous l'occasion rêvée d'acheter une famille à moindre frais." ));
            allItems.Add(new Items ( 2644, "Queue de Dragon Cochon", "Un régal que ce gros morceau de Queue de Dragon Cochon. Faites le sauter à la poële avec un peu de Graine de Sésame, le tout avec une Sauce hisson, et vos invités ne partiront plus de chez vous. N'est-ce pas super chouette ? Non ? Bon..." ));
            allItems.Add(new Items ( 2645, "Groin de Dragon Cochon", "À vue de Groin, le Dragon Cochon n'a pas senti le coup venir. C'est dommage pour lui." ));
            allItems.Add(new Items ( 2646, "Slip en cuir de Cochon de Lait", "Ce slip bien large va à ravir au Cochon de Lait, les coutures sont élastiques et renforcées, pour une plus grande efficacité lors des combats rapprochés. Comme l'exige la tradition, ce slip ne doit jamais être lavé, sous peine de perdre ses propriétés intrinsèques encore inconnues et redoutées de nos jours." ));
            allItems.Add(new Items ( 2647, "Oreille de Cochon de Lait", "Le Cochon de Lait n'est pas chassé généralement pour ses Oreilles, mais plutôt pour son ivoire. Mais bon, à défaut d'Ivoire...." ));
            allItems.Add(new Items ( 2648, "Griffes de Kwak", "Le Kwak n'a rien d'un charognard, mais il possède tout de même d'étranges dépôts sous ses griffes qu'il ne vaut mieux pas décrire." ));
            allItems.Add(new Items ( 2649, "Ver de Terre", "Ce Ver est le plat préféré des Kwaks sauvages. Ce qui est plus étrange, c'est qu'il est resté vivant alors qu'il a été extrait de l'Estomac du Kwak." ));
            allItems.Add(new Items ( 2650, "Ver d\'Eau", "Ce Ver est le plat préféré des Kwaks sauvages. Ce qui est plus étrange, c'est qu'il est resté vivant alors qu'il a été extrait de l'estomac du Kwak." ));
            allItems.Add(new Items ( 2651, "Ver de Feu", "Ce Ver est le plat préféré des Kwaks sauvages. Ce qui est plus étrange, c'est qu'il est resté vivant alors qu'il a été extrait de l'Estomac du Kwak." ));
            allItems.Add(new Items ( 2652, "Ver d\'Air", "Ce Ver est le plat préféré des Kwaks sauvages. Ce qui est plus étrange, c'est qu'il est resté vivant alors qu'il a été extrait de l'Estomac du Kwak." ));
            allItems.Add(new Items ( 2653, "Arakne Majeure Morte", "Vous avez bien fait de la ramasser, elle est intacte. Morte, mais intacte." ));
            allItems.Add(new Items ( 2654, "Oeuf d\'Arakne Majeure", "Le problème avec les Araknes, c'est que l'on peut se retrouver avec plusieurs générations en quelques jours avec la chaleur. Donc si le temps est caniculaire, ne vous promenez pas trop dans la Fôret des Abraknydes." ));
            allItems.Add(new Items ( 2655, "Ceinture Corbaquiale", "Cette ceinture a été confectionnée à partir des restes du Maître Corbac, soigneusement cousus entre eux. Vous devriez vous sentir respecté une fois équipé de celle-ci. Le Chouque a même accepté de prêter sa boucle pour l'occasion." ));
            allItems.Add(new Items ( 2656, "Fil Résistant", "Si les Cordonniers ainsi que les Tailleurs connaissaient l'existence de ce Fil Résistant, il y aurait la Semaine du Fil Résistant, la Foire du Fil Résistant ainsi que le jour saint du Fil Résistant." ));
            allItems.Add(new Items ( 2659, "Pétale de Tournesol Sauvage", "Ce Pétale bouge. Oui, il bouge en fonction du soleil. Mettez le dans votre poche droite le matin, il sera dans votre poche gauche le soir venu." ));
            allItems.Add(new Items ( 2661, "Feuille de Tournesol Sauvage", "Cette partie du Tournesol n'est pas que la Feuille de la plante, c'est aussi sa voûte plantaire. Ne vous étonnez donc pas si elle sent des pieds." ));
            allItems.Add(new Items ( 2662, "Bave de Rose Démoniaque", "Une Rose enragée a la fâcheuse habitude de baver sur quiconque ose s'approcher. Mais il paraît que c'est un signe d'affection. Une Rose Démoniaque enragée et affective qui plus est, on aura tout vu." ));
            allItems.Add(new Items ( 2663, "Engrais", "Toute belle plante a besoin d'Engrais. Les Enutrofettes sur le déclin peuvent donc s'en passer." ));
            allItems.Add(new Items ( 2664, "Os de Crocodaille", "Cet os verdâtre et pour le moins étrange, provient d'une patte de Crocodaille. Ce dernier a sûrement dû verser des larmes de Crocodaille quand il a compris qu'il avait une patte en moins." ));
            allItems.Add(new Items ( 2665, "Herbe Folle", "Voici le genre d'Herbe que l'on met dans les enclos pour Bouftous. Cette Herbe est folle car de temps à autres, elle se lève et s'échappe de l'enclos, ce qui n'est pas commun pour une touffe d'herbe." ));
            allItems.Add(new Items ( 2666, "Sabot du Minotoror", "Il a dû écraser pas mal d'aventuriers avec ce Sabot. Maintenant, il doit moins faire le malin à danser sur une seule patte. Quoique cela va lui donner une démarche encore plus inquiétante qu'avant." ));
            allItems.Add(new Items ( 2667, "Queue de Minotoror", "Cela relève tout de même de l'exploit de voir une Queue de Minotoror, sans le Minotoror qui va avec. Le pauvre doit chercher partout après. A moins qu'il ne s'en soit toujours pas rendu compte, ce qui n'est pas impossible." ));
            allItems.Add(new Items ( 2669, "Chauve souris", "Parfois, les Vampires se transforment en Chauve Souris, pour mieux fuir. Mais ils se sentent obligés de lâcher un petit rire avant de s'échapper. Ca donne à peu près ceci : " ));
            allItems.Add(new Items ( 2673, "Oeuf pourri", "C'est tout simplement honteux. Comment peut-on pondre un Oeuf de Tofu en décomposition? Il faut vraiment être Maléfique pour avoir des idées pareils." ));
            allItems.Add(new Items ( 2674, "Citrouillette", "La citrouillette est délicieuse en cette saison. Pas trop petite pour avoir du goût, mais pas trop grosse non plus pour ne pas se transformer en Citwouille, succulente et remboursée par le sec Huh." ));
            allItems.Add(new Items ( 2675, "Plume de Tofu Maléfique", "Le Tofu Maléfique n'a vraiment plus beaucoup de Plumes, il va finir par attraper un rhume carabiné, il ne faudra donc pas s'étonner si vous l'entendez éternuer la nuit." ));
            allItems.Add(new Items ( 2676, "Noeul Mort", "Vous avez bien fait de le ramasser, il est intact. Mort, mais intact." ));
            allItems.Add(new Items ( 2677, "Ceinture Chouqueuse", "Cette ceinture a été confectionnée à partir des éléments les plus puissants du Chouque, notamment son célèbre caleçon. De quoi mettre en émoi toutes les flibustières du bar des pirates." ));
            allItems.Add(new Items ( 2678, "Ceinture Kokokette", "Cette ceinture ressemble presque à un pagne, elle conviendra parfaitement si vous voulez vous camoufler parmi les Kokokos de l'île de Moon, ou tout simplement si vous désirez courtiser un Kokoko." ));
            allItems.Add(new Items ( 2681, "Ceinture Tortue Jaune", "Cette petite ceinture est idéale pour protéger les parties sensibles, des coups critiques. nVérifiez tout de même que le cordonnier a bien retiré le propriétaire de la carapace avant que vous ne l'essayez." ));
            allItems.Add(new Items ( 2683, "Ceinture Tortue Bleue", "Cette petite ceinture est idéale pour protéger les parties sensibles, des coups critiques. nVérifiez tout de même que le cordonnier a bien retiré le propriétaire de la carapace avant que vous ne l'essayez." ));
            allItems.Add(new Items ( 2685, "Ceinture Tortue Verte", "Cette petite ceinture est idéale pour protéger les parties sensibles, des coups critiques. nVérifiez tout de même que le cordonnier a bien retiré le propriétaire de la carapace avant que vous ne l'essayez." ));
            allItems.Add(new Items ( 2687, "Ceinture Tortue Rouge", "Cette petite ceinture est idéale pour protéger les parties sensibles, des coups critiques. nVérifiez tout de même que le cordonnier a bien retiré le propriétaire de la carapace avant que vous ne l'essayez." ));
            allItems.Add(new Items ( 2688, "Ceinture Kolante", "Le moins que l'on puisse dire, c'est qu'une fois enfilée, vous ne risquez plus de perdre cette ceinture. Elle fera le bonheur des étourdis et des victimes récurrentes de vols." ));
            allItems.Add(new Items ( 2689, "Ceinture Banisation", "Le cordonnier qui a créé cette ceinture avec les restes d'un Dragodinde devait vraiment manquer d'imagination, pour lui donner un nom aussi stupide." ));
            allItems.Add(new Items ( 2710, "Ceinture Banisation Sauvage", "Comportant des renforts en fer, c'est une version améliorée de la classique Ceinture Banisation, elle a été spécialement conçue pour chasser les Dragodindes Sauvages dans les contrées sauvages." ));
