            allItems.Add(new Items ( 2803, "Ceinture du Kwabe", "Cette étrange ceinture est entièrement constituée de fragments de pince, et de carapace de crabe. Même en employant une magie très puissante, elle sentira toujours aussi mauvais, il faut vous rendre à l'évidence." ));
            allItems.Add(new Items ( 2804, "Ceinture Mulante", "Le célèbre Mulou a fait don de ses plus précieux organes pour aider à la confection de cette ceinture incroyable qui respire la puissance malgrès son apparence assez étrange qui comblerait le plus pervers des Chafers." ));
            allItems.Add(new Items ( 2805, "Testicules Magiques du Mulou", "Personne ne sait exactement quand et comment cela s'est produit, mais un jour, ces testicules se sont vues dotées de pouvoirs magiques assez obscurs, il faut bien le reconnaître." ));
            allItems.Add(new Items ( 2806, "Testicules Magiques du Meulou", "Aussi étrange que cela puisse paraître, ces testicules sont dotés de pouvoirs magiques. On ne sait pas trop à quoi cela pouvait servir au Meulou, mais mieux vaut ne pas savoir." ));
            allItems.Add(new Items ( 2807, "Ceinture Meulante", "C'est Bowisse le Cordonnier en personne, qui a conçu cette ceinture. Il l'a offerte à Brouce Boulgoure lorsque celui-ci a réussi à déraciner à mains nues le Chêne Mou, il y a de ça très longtemps." ));
            allItems.Add(new Items ( 2808, "Ceinture Dracochoune", "Cette ceinture fera de vous une personne irrésistible. Bowisse recommande cependant de ne pas s'approcher du Dragon Cochon avec cette ceinture, cela pourrait réveiller certaines de ses pulsions les plus tourmentées." ));
            allItems.Add(new Items ( 2809, "Ceinture Chafeuse", "Derrière ce nom assez grotesque, se cache une belle création de Bowisse le Cordonnier qui lors d'une promenade dans le Donjon des Squelettes a eu l'idée de récupérer les restes de ses ennemis." ));
            allItems.Add(new Items ( 2810, "Ceinture Piquante", "Cette magnifique ceinture de couleur rousse, vous permettra grâce à ses pouvoirs magiques de mieux vous concentrer et d'apprendre plus rapidement. Le rêve de tous les cancres d'Amakna." ));
            allItems.Add(new Items ( 2811, "Ceinture Ouginakale", "Voilà une ceinture qui saura plaire aux amateurs de sensations fortes. En effet cette dernière a été conçue spécialement pour pouvoir s'approcher sans risques des Ouginaks. Son efficacité n'a cependant pas encore été prouvée." ));
