            allItems.Add(new Items ( 6438, "Baguette Molle", "Cette baguette est constituée d'éléments plutôt mous, ce qui en fait une baguette difficile à manier. Mais au coeur du combat, cette baguette révèle son véritable pouvoir." ));
            allItems.Add(new Items ( 6439, "Baguette Irréelle", "Des mélanges magiques d'éléments surnaturels ont permis la création de cette baguette qui vogue aux frontières du réel. Tantôt terrifiante, tantôt délirante, cette baguette est l'exemple typique de l'instabilité." ));
            allItems.Add(new Items ( 6440, "Baguette Velue", "Cette baguette n'est pas qu'un vulgaire amas de poils. Le coeur même de la baguette absorbe l'essence magique des diverses touffes et les fusionne entre elles. Aussi aberrant que cela puisse paraître, ça marche !" ));
            allItems.Add(new Items ( 6441, "Poils de Moon", "Ces poils sont incroyablement doux et chauds au toucher. On a l'impression qu'ils sont encore vivants." ));
            allItems.Add(new Items ( 6442, "Bâton d\'Aga Dou", "Toujours en quête d'un moyen de surprendre ses ennemis, le Sadida Aga Dou, a pris soin d'accrocher une petite poupée au bout de son bâton. Les jeunes Iopettes se jettent dessus pour attraper la poupée et meurent empalées." ));
            allItems.Add(new Items ( 6443, "Amulette du Renouveau", "C'est le célèbre et non moins burlesque artisan Yamato, injustement méprisé pour son arc ridicule, qui le premier, eu l'audace de tenter d'enchanter une amulette avec autant d'effets, chose réputée infaisable depuis des lustres." ));
            allItems.Add(new Items ( 6444, "Amulette Turquoise", "De la couleur du célèbre Dofus Turquoise, cette amulette n'a en fait aucun rapport avec ce dernier, ce qui ne l'empêche pas d'être très puissante pour autant. Comme quoi, les apparences ne sont pas toujours trompeuses." ));
            allItems.Add(new Items ( 6445, "Arc Boutant", "Cet arc a une forme très étudiée, sa courbure est vraiment parfaite. On raconte à ce sujet que le sculpteur d'arc s'est servi d'un fessier de femelle Bouftou comme patron." ));
            allItems.Add(new Items ( 6446, "Arc Chiduc", "Chiduc était un sculpteur d'arcs, réputé pour avoir des problèmes de prononciation. Il était en effet incapable de prononcé le son " ));
            allItems.Add(new Items ( 6447, "Potion d\'oubli de Chaferfu", "Cette potion permet d'oublier un niveau du sort d'invocation Chaferfu" ));
            allItems.Add(new Items ( 6448, "Potion d\'oubli d\'Invocation d\'Arakne", "Cette potion permet d'oublier un niveau du sort d'invocation d'Arakne." ));
            allItems.Add(new Items ( 6449, "Corbacape", "De quoi ressembler à une ombre, ou se déguiser en Maître Corbac pour impressionner les filles à la sortie de la Boulangerie avant la tombée de la nuit." ));
            allItems.Add(new Items ( 6450, "Ceinture de l\'Amant d\'Ha", "La célèbre Cordonnière Ha, avait un amant connu pour lui faire faux bond au dernier moment. Mais l'amour l'aveuglant, elle lui offrit tout de même cette magnifique ceinture comme témoin de leur union secrète." ));
            allItems.Add(new Items ( 6451, "Arc Ange", "A propos de cet arc, on est en droit de se demander pourquoi son nom est aussi stupide. Il en est de même pour vous." ));
            allItems.Add(new Items ( 6452, "Arc Chitecte", "Cet arc à l'apparence complexe est un véritable objet d'art. Le sculpteur d'arc qui l'a créé est également célèbre pour avoir exposé ses modèles les plus controversés dans la fameuse grotte de Loopine, interdite au public non averti depuis des lustres." ));
            allItems.Add(new Items ( 6453, "Arc du Pêcheur", "Issu d'un constat frappant (l'arc est très utile pour pêcher les poissons volants), cet arc est muni d'une canne à pêche intégrée qui permet de pêcher d'autres choses que des poissons volants." ));
            allItems.Add(new Items ( 6454, "Abraton Sombre", "Cette branche d'Abraknyde Sombre est en fait l'un de ses bras. Excellente pour s'entraîner au bras de fer, c'est surtout une excellente arme pour terroriser vos ennemis d'enfance ou torturer les Tronknydes." ));
            allItems.Add(new Items ( 6457, "Kriptonite", "Voici un alliage remarquable, dont l'origine semble vraiment lointaine. A défaut d'être utile, il pourra toujours servir à décorer sa cheminée ou à taper sur le crâne des Iops, ne serait-ce que pour s'assurer qu'ils sont bel et bien vides." ));
            allItems.Add(new Items ( 6458, "Kobalite", "De tous les alliages découverts à ce jour, la Kobalite est un des plus surprenants. Les anciens mineurs pensaient avoir trouvé l'alliage idéal, jusqu'au jour où l'on s'aperçut qu'il se transformait en poussière au contact de la Bave de Bouftou." ));
            allItems.Add(new Items ( 6459, "Amulette du Mineur Sombre", "La seule chance de survivre dans une mine infestée de Mineurs Sombres, c'est d'y voir clair. A défaut de leur voler leur bougie, portez cette amulette, elle vous portera chance." ));
            allItems.Add(new Items ( 6460, "La Feuille d\'Hiver", "Cette magnifique broche est un hommage à la célèbre résistance des mineurs de la mine d'argent de Grimoth, qui passèrent un hiver entier, à combattre un Rockailleux de la pire espèce, pour finalement mourir achevés par un vieux Tofu égaré." ));
            allItems.Add(new Items ( 6461, "La Feuille de Printemps", "Cette Amulette spécialement conçue pour les vieux Enutrofs, a permis il y a bien longtemps à Setag, de nettoyer la mine de Grimoth, de tous ses rivaux mineurs. Une méthode certes peu raisonnable, mais terriblement efficace." ));
            allItems.Add(new Items ( 6462, "La Feuille d\'Eté", "Cette petite broche a vraiment du charme, destinée à la base pour les malchanceux fermement décidés à participer aux jeux de la fête foraine, on a vite remarqué, qu'elle était également bien adaptée pour les chasses épiques." ));
            allItems.Add(new Items ( 6463, "Anneau Forain", "Cet anneau a été créé par un célèbre forain, dont personne ne se rappelle le nom. Il est mort lors du concours de tir à l'arc, en mettant comme cible sur sa tête, une Graine de Tournesol Sauvage à la place de la traditionnelle Citrouille." ));
            allItems.Add(new Items ( 6464, "Anneau de l\'Invocateur Satisfait", "Cet anneau est une version améliorée du célèbre Anneau de Satisfaction. Idéal pour laisser à vos petites bestioles le soin de faire le combat à votre place, ou tout simplement pour faire le malin en invoquant une armée de Tofus." ));
            allItems.Add(new Items ( 6465, "Anneau de Loopine", "Loopine a jadis demandé comment créer un tel anneau au monstre qui hante sa grotte en échange de quelques services à rendre, qui l'ont changé à jamais. Les gens avertis vous diront qu'il a perdu au change..." ));
            allItems.Add(new Items ( 6466, "Amulette de Shika", "Cette amulette a permis à de nombreuses reprises à Shika Ingalsse, de survivre aux rebellions des troupeaux de Bouftous bien décidés à lui mener la vie dure." ));
            allItems.Add(new Items ( 6467, "Anneau Nime", "Personne ne connaît le propriétaire de cet anneau, qui a tenu a garder l'anonymat suite à un combat humiliant face à une Arackne invoquée. Cependant, il faut avouer que cette dernière venait d'être compulsée par un Iop." ));
            allItems.Add(new Items ( 6468, "Anneau du Jeune Vald", "A l'époque où tous les Iops de son âge recevaient pour leur communion, une monture Tabi, ou une Goultard en bois, Vald lui a reçu cet anneau. C'est un peu moins marrant pour jouer avec ses petits camarades, mais c'est plus utile en plein combat." ));
            allItems.Add(new Items ( 6469, "Alliance de Farle", "Cette alliance a été usée par le frottement des épis d'escourgeon pendant des centaines de moissons. Elle est également le témoin, de l'union entre Farle et Shika, lorsqu'ils se sont mariés au milieu d'un champs d'orge de printemps." ));
            allItems.Add(new Items ( 6470, "Bottes de l\'Eleveur de Bouftous", "Ces magnifiques bottes, sont parfaites pour botter le derrière des Bouftous lorsqu'il faut les forcer à changer de pâtures et à traverser le village. Elles fonctionnent également pour forcer les enfants Iops à faire leurs devoirs et à aller à l'école." ));
            allItems.Add(new Items ( 6471, "Souliers Vernis de Porkass", "Ces petits souliers ont été renforcés, remis en état, mais surtout désodorisés. Ils sont surtout utilisés pour attirer les Champ-Champ, tant le substrat laissé par le Porkass leur semble appétissant." ));
            allItems.Add(new Items ( 6472, "Dragocoiffe Dorée", "Cette coquille de Dragoeuf Doré rembourrée, vous rend presque insensible aux coups de foudre magiques du Bwork Mage. En revanche, il ne vous protège en rien contre les vrais coups de foudre..." ));
            allItems.Add(new Items ( 6473, "Dragocoiffe de Saphire", "Cette coquille de Dragoeuf de Saphire vous ira à merveille si vous savez la porter. Arborer une telle coiffe n'est pas à la portée de tout le monde, la preuve, les Iops ont tendance à la porter à l'envers." ));
            allItems.Add(new Items ( 6474, "Dragocoiffe noire", "Cette coquille de Dragoeuf Noir est idéale pour rester discret la nuit, ou se protéger des chutes de pierres, courantes en montagne, ou lorsque l'on défie un Craqueleur au lancer de rochers." ));
            allItems.Add(new Items ( 6475, "Casque Cassé du Chafer", "Ce casque est inutilisable dans cet état, il vous ferait bien trop mal à la tête. Un bon Tailleur devrait pouvoir le réparer, mettre un bon rembourrage à l'intérieur et au passage se faire beaucoup de kamas sur votre dos." ));
            allItems.Add(new Items ( 6476, "Bougie Magique du Mineur Sombre", "Cette bougie brille même lorsqu'elle est éteinte, et ne dégage pas de chaleur. On peut juste si l'on tend l'oreille, entendre un léger crépitement, un peu comme le bruit de certaines céréales dans un bol de lait." ));
            allItems.Add(new Items ( 6477, "Chapeau du Mineur Sombre", "Ce somptueux chapeau vous permet d'y voir plus clair même lorsqu'il fait déjà assez clair. Il est également très utile pour éblouir les bêtes qui hantent les mines mal famées, ou tout simplement, pour faire croire aux Iops que vous êtes une lumière." ));
            allItems.Add(new Items ( 6478, "Makroute Déchiqueté", "Ce Makroute magique est vraiment dans un très mauvais état, il n'a pas supporté la violence de vos attaques et son poil est devenu sombre. Une nouvelle vie pourrait peut-être s'offrir à lui si vous étiez capable de le soigner." ));
            allItems.Add(new Items ( 6479, "Ours Déchiqueté", "Cette petite tête d'Ours est vraiment dans un très mauvais état. Un peu de fil, de tissu et d'inventivité auront raison de cette horreur et pourront la transformer en un remarquable couvre-chef." ));
            allItems.Add(new Items ( 6480, "Osier Sombre", "Ce morceau d'osier est sombre comme le plus sombre des fantômes, magique, comme le plus magique des fantômes, résistant comme le plus résistant des fantômes. Rappelez-vous que les fantômes n'existent pas." ));
            allItems.Add(new Items ( 6481, "Dora Bora", "Ce splendide chapeau a été imaginé sur l'île de Dora Bora un peu au sud de l'île de Moon. Parfaitement adapté au combat, il est également très apprécié comme corbeille à fruits, pour entreposer les noix de Kokokos Traumatisées par exemple." ));
            allItems.Add(new Items ( 6482, "Korko Kousto", "Version retravaillée du Jouik Krampe, ce chapeau utilise les meilleurs ressources de l'île de Moon, ce qui lui donne un cachet très terroir reconnaissons-le. A défaut de ressembler à un Kanniboul, vous pourrez sortir de très bons coups de boule." ));
            allItems.Add(new Items ( 6483, "Casque du Bwork de Bronze", "Ce surprenant casque transforme son propriétaire en Bwork de Bronze selon la légende. A essayer d'urgence sur les gros lourds, vous pourrez les refondre ensuite et en tirer un bon prix." ));
            allItems.Add(new Items ( 6484, "Arc du Sram Archer", "Quand les dagues ne suffisent plus, que les dieux se moquent de vous, à vous de rechercher votre propre style de combat. Certains Srams l'ont bien compris, d'autres espèrent que les dieux leur viennent en aide." ));
            allItems.Add(new Items ( 6485, "Arc du Chafer Archer", "Le Chafer Archer, n'aura jamais été assez astucieux et habile à cause de ses os tremblants, pour manier un tel arc. Excepté pour le problème des os tremblants, il est probable que vous soyez dans le même cas que ce ridicule Chafer." ));
            allItems.Add(new Items ( 6486, "Ecorce du Chêne Mou", "Cette écorce est plus résistante que n'importe quelle autre écorce, elle respire la puissance. En revanche, rien n'indique que ce n'est pas une fausse écorce d'Abraknyde repeinte. Il serait peut-être temps d'arnaquer à votre tour un ami." ));
            allItems.Add(new Items ( 6487, "Bourgeon du Chêne Mou", "Cet énorme bourgeon gorgé de sève chaude et sirupeuse, devrait vous rappeler vos biberons d'enfance si vous vous risquez à aspirer son contenu. Dans un deuxième temps, vous vous transformerez peut-être en feuille morte." ));
            allItems.Add(new Items ( 6488, "Racine du Chêne Mou", "Certains tenteront de faire de la bière avec cette racine. C'est fortement déconseillé, le Chêne Mou ne tenant pas du tout l'alcool, ces racines risquent de devenir très instables au contact de la moindre goutte d'alcool. Un peu comme un Sadida." ));
            allItems.Add(new Items ( 6489, "Bois de Chêne Mou", "Ce bois-là a vu la Hache de Brouce Boulgoure, et de très près. Il a également connu d'autres choses mystérieuses et intrigantes, que même le livre du Bwork Mage se refuse de révéler. On est en droit de s'imaginer le pire." ));
            allItems.Add(new Items ( 6490, "Ambre du Chêne Mou", "Cette Ambre contient une misérable bestiole prise dans la résine. Ce bijou du Chêne Mou est une pièce de collection, au même titre que les chaussettes rouges et jaunes à petits pois du Chouque." ));
            allItems.Add(new Items ( 6491, "Le Tronknydum", "Cet arc est issu du croisement raté d'un Tronknyde et d'un Abraknyde. Pour un Bûcheron, il s'agit de toute évidence d'un échec mais pour un Sculpteur d'Arcs, c'est une vraie réussite. Vos parents auraient aimé pouvoir en dire autant de vous." ));
            allItems.Add(new Items ( 6492, "Bâton du Wobot", "L'écart entre les deux pointes de ce bâton correspond à celui des yeux de Lenalds... Mais jusqu'où iront ces howibles Wabbits ?" ));
            allItems.Add(new Items ( 6493, "Tongues Wabbits", "Adoptez la " ));
            allItems.Add(new Items ( 6494, "Baguette d\'Elya Wood", "Utilisée par les plus célèbres Eniripsas d'Amakna, cette baguette constitue l'arme idéale pour envoyer un sortilège sur ses adversaires ou tenter de rendre plus intelligents ses propres amis. Ainsi, vous pouvez transformer un Iop en citrouille." ));
            allItems.Add(new Items ( 6495, "Baguette du Chêne Mou", "Fabriquée à partir de restes du Chêne Mou ayant repris vie, cette baguette est tout simplement surpuissante. Il suffit de l'agiter à droite puis à gauche avec le bon rythme, pour déclencher des pluies de crapauds ou passer pour un idiot notoire." ));
            allItems.Add(new Items ( 6496, "Baguette en Racine d\'Abraknyde Sombre", "Cette baguette a parfois tendance à reprendre vie et se révolter contre son maître. Dans ce cas-là, la meilleure chose à faire reste de la donner à votre ennemi, ou à un Iop qui pensera que vous lui faîtes un cadeau." ));
            allItems.Add(new Items ( 6497, "Balgourde des Clairettes", "La femme de Brouce Boulgoure, une Eniripsa, ne cessait de lui voler sa célèbre hache. Pour mettre un terme à ces bêtises, il lui coupa une aile et un bras et pour s'excuser, il lui offrît cette Baguette. Voilà un homme qui savait y faire avec les femmes." ));
            allItems.Add(new Items ( 6498, "Slip Kangouwou du Wabbit GM", "De taille plus que respectable, ce magnifique slip comporte encore les traces du passage de son ancien propriétaire et quelques poils bien étranges. Sur les fesses ou sur la tête, ce slip fera de vous une personnalité respectée de tout Amakna." ));
            allItems.Add(new Items ( 6499, "Amulette ", "Les dents de cette amulette ont rongé plus de Cawottes que Brouce Boulgoure n'a déraciné d'arbres, ne vous étonnez donc pas si vous sentez la Cawotte, et si les Tiwabbits cherchent à vous faire des bisous dans le cou." ));
            allItems.Add(new Items ( 6500, "Oreilles de Wabbits", "Ces oreilles ne vous permettront pas de mieux entendre, mais permettront à vos amis de mieux vous retrouver dans la foule, et à vos ennemis, de mourir de rire. Une paire d'oreilles qui s'avère avec le temps vraiment indispensable." ));
            allItems.Add(new Items ( 6501, "Sac-Cawotte", "Avec ce sac sur les épaules, vous pouvez partir à l'aventure sans vous soucier des problèmes alimentaires. En effet, même si la Cawotte est vide comme un crâne de Iop, les feuilles du dessus repoussent toutes seules, de quoi se faire de bonnes tisanes." ));
            allItems.Add(new Items ( 6503, "Marteau Polpoulette", "La Polpoulette est le résultat d'un croisement entre un Kwak de Feu agressif et un Tofu maladif. Depuis la nuit des temps, la chasse à la Polpoulette est considérée comme un rituel immuable, chasser cette horrible créature est un vrai plaisir." ));
            allItems.Add(new Items ( 6504, "Marteau Fantal", "On ne connaît que le diminutif de son créateur, un certain Fantal, mais on sait qu'il avait très mauvais goût. En effet, il appréciait s'enduire de Gelée Royale pour ensuite se recouvrir de plumes de Tofu Royal. Et oui en plus, il avait des goûts de luxe." ));
            allItems.Add(new Items ( 6505, "Marteau Réhadaure", "Pour lutter contre les bêtes à cornes, ce marteau est idéal. En revanche, pour planter des clous, ou des choux, même à la mode de chez nous, c'est une autre histoire." ));
            allItems.Add(new Items ( 6506, "Anneau du Mineur Gogorifiant", "Chez les Enutrofs, on dit qu'un mineur est gogorifié lorsqu'il ne supporte plus la lumière du jour, à force de travailler dans sa mine. Ne tentez jamais d'approcher une bougie d'un tel mineur, il vous transformerait en bronze." ));
            allItems.Add(new Items ( 6507, "Marteau de la Terps", "La Terps était une très vilaine créature aussi dangereuse et mal aimée que sa grande rivale, la vieille Liche. Déterminée à la surpasser, elle forgea ce marteau pour lui écraser les orteils, mais elle lui cassa surtout les pieds. Une grande histoire d'amour naquit." ));
            allItems.Add(new Items ( 6508, "Marteau Pinambour", "Un marteau exceptionnel, destiné à soigner son petit Prespic adoré, ou ses ennemis si vous êtes un disciple du Chafer Fu." ));
            allItems.Add(new Items ( 6509, "Marteau R\'Ticolis", "Ce somptueux marteau a fait tourner la tête de plus d'un Osamodas. En effet, il est si puissant, que tout le monde se retourne quand on croise son propriétaire. Ce marteau est depuis ce temps-là, à l'origine d'une bien étrange pathologie." ));
            allItems.Add(new Items ( 6510, "Marteau Kahr", "Kahr était vraiment à la masse lorsqu'il s'agissait de combattre avec un marteau. Heureusement pour lui, il reçut en héritage cette véritable merveille, qui fit de lui une personne respectée et jalousée. Le succès ne tient parfois à rien." ));
            allItems.Add(new Items ( 6511, "Marteau Ridhe", "C'est le marteau parfait des moments chauds, vous aurez un succès énorme parmi votre entourage si vous savez vous y prendre correctement. De toute évidence, à ne pas mettre entre toutes les mains..." ));
            allItems.Add(new Items ( 6512, "Marteau Nitruhant", "C'est l'arme rêvée des Xélors un tantinet rebelles et en manque de sensations fortes. Ca leur permet surtout de compenser une vieille histoire de complexe à propos de leur taille franchement ridicule." ));
            allItems.Add(new Items ( 6513, "Marteau Réfacteur", "Ce marteau tire sa puissance d'un subtile enchantement. Ca ne se voit pas au premier coup d'oeil, ni au second. C'est pour cela que l'on prend la peine de le préciser, histoire d'augmenter la valeur de cet objet aux yeux des acheteurs un peu naïfs." ));
            allItems.Add(new Items ( 6516, "Dagues R\'Hoh", "Aucun Sram ne peut résister à l'envie d'agresser un ami avec ces sublimes dagues. Leur principal intérêt est donc de faire baisser de manière considérable l'insécurité en Amakna, en supprimant un à un les derniers Srams restants." ));
            allItems.Add(new Items ( 6517, "Dagues Hischantes", "Ces dagues feraient craquer n'importe quelle petite Sramette en quête d'expériences nouvelles. En effet, ses pouvoirs étranges lui permettent d'accomplir les pires fourberies, aux amis comme aux ennemis. Le Captain Chafer aurait adoré." ));
            allItems.Add(new Items ( 6518, "Amrothiline", "Conçue par un célèbre Sculpteur, cette baguette a gagné 13 fois le grand concours de la Baguette N'hi, en réussissant à surpasser les anciens modèles de Boulgourdes aujourd'hui retirés du marché car trop dangereuses pour les petites Eniripsas." ));
            allItems.Add(new Items ( 6519, "Baguette de Kouartz", "C'est un sculpteur réputé, connu également pour la ressemblance de son nom avec un des alliages les plus précieux d'Amakna, qui eut le premier, l'idée d'introduire des métaux dans ses créations. La folie mène parfois au succès." ));
            allItems.Add(new Items ( 6520, "La Migraine", "Le sculpteur de cette baguette, a maintes fois demandé à changer de nom. Trop habitués à passer leurs journées à la Taverne, les responsables du registre d'Amakna, ont fini par avoir mal à la tête avec toutes ces histoires. Cette baguette leur est dédiée." ));
            allItems.Add(new Items ( 6521, "Kelinobranche", "On raconte que ce bâton permet de faire rougir une femelle Abraknyde, ou de chatouiller les parties intimes du Chêne Mou pour mieux le séduire. Dans tous les cas, on vous déconseille d'essayer, le Chêne Mou est vraiment très possessif..." ));
            allItems.Add(new Items ( 6522, "La Racine Cinati", "La légende raconte que cette racine est herbivore : elle aurait tendance à se faufiler très vite sous les feuilles mortes et avaler les jeunes pousses. Très impressionnant. Bon, à part si vous combattez des Pissenlits, ça ne sert à rien." ));
            allItems.Add(new Items ( 6523, "La Racine Hagogue", "Création élégante et raffinée, cette racine sculptée fera de vous un invocateur confirmé. Pas de quoi frimer en soirée, mais pour rendre folles de vous les Sadidettes d'Amakna, il n'y a pas mieux." ));
            allItems.Add(new Items ( 6524, "Racine Sauvageonne", "Un Sadida, éperdument amoureux d'une Sadidette Sauvage a tenté de l'attirer sous un charme avec cette racine, il a attendu une centaine d'années. Elle lui a posé une sorte de Wabbit. Depuis ce jour, les Sadidas parlent de poser une Racine." ));
            allItems.Add(new Items ( 6525, "Bâton du Féca", "Les habitants d'Amakna en avaient assez d'entendre les disciples Fécas se plaindre à longueur de journée de leurs armures. Ils ont donc conçu ce bâton pour espérer les voir parler d'autre chose à la taverne." ));
            allItems.Add(new Items ( 6526, "Pelle Hébuse", "Le forgeur de cette pelle a voulu rester anonyme pour faire le modeste, mais il a pris soin de laisser un indice dans le nom de cette pelle. Certains artisans sont vraiment irrécupérables..." ));
            allItems.Add(new Items ( 6527, "Pelle Musicale", "" ));
            allItems.Add(new Items ( 6528, "Pelle Rinage", "Cette pelle a traversé les continents, elle a vu le soleil se coucher sur la crête du Mont Crakelour, le soleil se lever là où la Mer d'Asse et l'horizon ne font plus qu'un. Et dire que vous vous n'êtes jamais allé plus loin que l'île de Moon, pitoyable." ));
            allItems.Add(new Items ( 6535, "Pelle Hikule", "Cette étrange pelle multi-usages, servait à tuer les poux des jeunes Iops, à leur enlever les pellicules, et à leur faire rentrer dans le crâne les bases de l'arithmétique." ));
            allItems.Add(new Items ( 6536, "Pelle du Mineur Sombre", "Le Mineur Sombre se sert de cette pelle de la taille d'une main de Craqueleur,  essentiellement pour écraser plusieurs Eniripsas d'un seul coup. On vous encourage à faire de même, histoire de ne pas mourir idiot." ));
            allItems.Add(new Items ( 6537, "Pelle Mairhe", "Cette pelle est vraiment antipathique. Personne ne sait réellement pourquoi, mais les Pelles Mairhes sont toujours insupportables. Heureusement, qu'elles ont tout de même des bons côtés, même si on les cherche encore à l'heure actuelle." ));
            allItems.Add(new Items ( 6538, "Pelle Vaidaire", "Cette pelle permet aux Enutrofs de voir plus loin. Elle est faîte pour qu'ils puissent la planter et monter dessus pour scruter l'horizon et se donner des airs intéressants. Idéal pour retrouver son hospice lorsque l'on est perdu entre deux fougères." ));
            allItems.Add(new Items ( 6539, "Pelle Gicque", "Cette pelle est vraiment différente des autres. Si vous l'utilisez pour combattre, on risque de beaucoup rire et de faire des blagues sur vous. En effet, les blagues sur la Pelle Gicque sont très populaires du côté de la Taverne." ));
            allItems.Add(new Items ( 6540, "La Pelle Hélabète", "On raconte que celui qui a forgé cette pelle ressemblait à une véritable bête. Une sorte de croisement entre un Sadida et un Mulou. Sa pelle serait tombée amoureuse de lui. Vraiment une histoire débile." ));
            allItems.Add(new Items ( 6541, "Fée d\'Artifice Rouge", "Idéal pour les petites fêtes de mariage, parfait pour la fête du Slip du Captain Chafer." ));
