            allItems.Add(new Items ( 6601, "Poudre d\'Artifice", "Lorsque vos invités commencent à s'ennuyer, la meilleure solution reste l'utilisation de la célèbre Fée d'Artifice, à construire vous-même à partir de cette poudre et de divers ingrédients. Cette Fée saura rendre votre soirée franchement explosive." ));
            allItems.Add(new Items ( 6602, "Sang de rat d\'Hyoactif", "Les normes de sécurité Bontarienne prévoient de se tenir à une distance de 100 pouces de toute substance Hyoactive sous peine de multiplier son temps de jeu par trois." ));
            allItems.Add(new Items ( 6603, "Peluche du Bouftou", "Cette peluche est la réplique exacte d'un Bouftou, ses créateurs ont même poussé le vice jusqu'à mettre la propre odeur du Bouftou sur la laine de cette peluche. Ne vous étonnez donc pas, si vous sentez mauvais en vous réveillant le matin." ));
            allItems.Add(new Items ( 6604, "Kouin-Kouin", "Au delà du Tofu, du Bouftou où encore du Iop, le Kouin-kouin excelle dans la crétinerie." ));
            allItems.Add(new Items ( 6605, "Peluche du Bouftou Royal", "Cette peluche est en tout point royale, des coutures, jusqu'au rembourrage, rien n'a été laissé au hasard. Un excellent cadeau pour les enfants gâtés ou les gosses de riches." ));
            allItems.Add(new Items ( 6606, "Peluche du Dopeul Sadida", "Cette peluche contient dans son rembourrage un gros réveil, très utile pour les Sadidas qui ont le réveil difficile. En revanche, il leur est déconseillé de la prendre avec eux lorsqu'il vont sous un charme, le temps ne compte plus dans ces moments-là !" ));
            allItems.Add(new Items ( 6607, "Peluche du Dopeul Ecaflip", "Cette petite peluche amuse les Ecaflips de tout âge." ));
            allItems.Add(new Items ( 6608, "Peluche du Dopeul Iop", "Cette peluche ressemble dans un certain sens à un vrai Iop, stupide et bête comme ses pieds, son crâne, ne contient même pas de rembourrage." ));
            allItems.Add(new Items ( 6610, "Peluche du Dopeul Sram", "Cette peluche a été volée à un jeune Sram, qui lui-même l'avait dérobée à un de ses camarades." ));
            allItems.Add(new Items ( 6611, "Peluche du Dopeul Eniripsa", "Cette peluche pourrait presque voler toute seule si elle si ces ailes n'étaient pas usées. En effet, cette peluche est loin d'être de première main. Mais qu'importe, ça reste un joli un cadeau." ));
            allItems.Add(new Items ( 6612, "Peluche du Dopeul Enutrof", "Attention, cette peluche est vraiment très lourde. En effet, elle est rembourrée avec de la Pierre de Bauxite, et des Pépites d'Or." ));
            allItems.Add(new Items ( 6613, "Peluche du Dopeul Féca", "Cette peluche est presque incassable, elle conviendra parfaitement aux jeunes Fécas turbulents et peu soigneux." ));
            allItems.Add(new Items ( 6614, "Peluche du Dopeul Osamodas", "On raconte que cette peluche peut invoquer d'autres petites peluches si elle est correctement entretenue. Une vieille légende pour forcer les jeunes Osamodas à ne pas la manger." ));
            allItems.Add(new Items ( 6615, "Peluche du Dopeul Crâ", "Cette peluche à l'allure toute fière, est faîte pour être envoyée de très loin sur vos camarades de jeu, une sorte de boomerang, mais qui ne revient pas à l'envoyeur." ));
            allItems.Add(new Items ( 6616, "Peluche du Dopeul Xélor", "Cette peluche fait un bruit étrange. Elle semble contenir dans le rembourrage de sa tête, une horloge. De quoi bercer le sommeil des jeunes Xélors." ));
            allItems.Add(new Items ( 6617, "Peluche du Sanglier", "Cette peluche ressemble à s'y méprendre à une peluche de Sanglier. Impressionnant. Vous noterez que ses poils sont de véritables poils de Sangliers. Un gage de qualité, assurément." ));
            allItems.Add(new Items ( 6618, "Peluche du Tofu", "Qui n'a jamais rêvé de serrer dans ses bras, une réplique de Tofu ? Peut-être vous. Dans ce cas, vous achetez n'importe quoi ou vos amis ne savent pas vous offrir des cadeaux." ));
            allItems.Add(new Items ( 6619, "Peluche du Champ", "Ce champ-là n'a pas d'épines douloureuses ou de poison paralysant. Il est tout de même redoutable pour faire peur aux Iops les plus naïfs." ));
            allItems.Add(new Items ( 6620, "Peluche du Prespic", "Ce Prespic en peluche n'enverra pas d'aiguilles et ne se rendra pas invisible. Mais il remplira son rôle de peluche à merveille et rendra vos amis terriblement jaloux." ));
            allItems.Add(new Items ( 6621, "Peluche du Wabbit", "Le Wabbit en peluche reste un incontournable, peut-être parce qu'il est indémodable. Vous n'avez pas cette chance." ));
            allItems.Add(new Items ( 6622, "Hochet du Dark Vlad", "Ce hochet a appartenu au Dark Vlad en personne, ce qui suffit à le rendre inestimable, même s'il s'agit d'un hochet  classique." ));
            allItems.Add(new Items ( 6624, "Peluche du Dragon Cochon", "Cette peluche est vraiment unique en son genre. Mais attention, elle est destinée à un public averti." ));
            allItems.Add(new Items ( 6625, "Bilboquet Du Maître Corbac", "Ce magnifique jouet, a appartenu au Maître Corbac en personne. Trop stupide pour comprendre comment il fonctionnait, il s'en servait pour taper sur la tête de ses chers petits Corbacs. Chacun ses distractions." ));
            allItems.Add(new Items ( 6626, "Corde à Sauter du Captain Chafer", "Pour entretenir ses articulations, le Captain Chafer utilisait cette fameuse corde à sauter. Depuis qu'il a disparu d'Amakna, elle passe de main en main." ));
