            allItems.Add(new Items ( 6654, "Bottes du Gardien des Egoûts", "Comble de l'ironie, le gardien des égoûts s'est fait dévorer par la Gardienne des Egoûts. Il a été digéré pendant plus de cent ans avant de finir par mourir. Heureusement, ses bottes étaient plus résistantes que lui et sont encore en excellent état." ));
            allItems.Add(new Items ( 6655, "Sac Surprise du Chanceux", "Les personnes qui ont déposé ça dans votre banque sont vraiment généreuses, et ont voulu vous témoigner leur amitié. (DOUBLE CLIC POUR OUVRIR LE SAC)" ));
            allItems.Add(new Items ( 6656, "Sac Surprise du Chanceux", "Les personnes qui ont déposé ça dans votre banque sont vraiment généreuses, et ont voulu vous témoigner leur amitié. (DOUBLE CLIC POUR OUVRIR LE SAC)" ));
