            allItems.Add(new Items ( 6658, "Peluche du Mulou", "Cette magnifique peluche en véritables poils de Mulou, vous tiendra chaud la nuit, tout en diffusant dans votre chambre, une agréable odeur de transpiration de Mulou mort." ));
            allItems.Add(new Items ( 6659, "Faux Koinkoin", "Ne vous y fiez pas, ce koinkoin est un faux, donnez le à Krip Soue à côté du stand de canards pour recevoir une récompense." ));
            allItems.Add(new Items ( 6660, "Chapeau du Marié", "Avec un tel chapeau qui saurait vous dire non ? nA vous la gloire et les conquêtes !" ));
            allItems.Add(new Items ( 6661, "Canne à Koinkoin", "Cette canne vous permet de pêcher un koinkoin dans le stand de la foire du Trool." ));
            allItems.Add(new Items ( 6662, "Chapeau de la Mariée", "Faites-le fondre comme une gelée menthe au soleil avec cette merveilleuse coiffe en soie de larve du sud, lavable à l'eau douce." ));
            allItems.Add(new Items ( 6663, "Bouquet de Roses démoniaques", "Avec ce superbe bouquet de pétales de roses démoniaques, vous montrerez à votre bien aimé(e) que vous êtes prêt(e) à braver 1000 et 1 dangers pour sa jolie frimousse. Et cela sans le moindre Bobo, pour une somme modique adaptée aux petits budgets." ));
            allItems.Add(new Items ( 6664, "Parchemin de Sort Spécial Félintion", "(Double clic pour apprendre le sort) nCe parchemin vous permet d'apprendre le sort Félintion... Réservé aux Ecaflips uniquement." ));
            allItems.Add(new Items ( 6666, "Sucette Tofu", "Cette délicieuse sucette est aromatisée aux extraits de Tofus. Un régal pour vos papilles, un cataclysme pour votre haleine." ));
            allItems.Add(new Items ( 6667, "Sucette Trool", "Une sucette qui a vraiment du goût et qui est surtout interminable. A consommer entre amis de préférence." ));
            allItems.Add(new Items ( 6668, "Hot Dog de Tofu", "Cette saucisse bien grasse vous permettra, de reprendre des forces, pour pouvoir digérer les autres saucisses que vous venez de consommer." ));
            allItems.Add(new Items ( 6669, "Burger de Trool", "Cet impressionnant burger, pourrait remettre sur pieds une armée de Chafers affamés. A consommer avec modération, l'abus de Trool est dangereux pour la santé." ));
            allItems.Add(new Items ( 6670, "Frites de Tournesol Sauvage", "Ces frites sont découpées dans des tiges de Tournesol Sauvage, puis elles sont cuîtes dans l'huile de Tournesol Sauvage bouillante, histoire qu'elles se sentent un peu comme chez elles, lorsqu'elles sont en train de frire." ));
