            allItems.Add(new Items ( 6706, "Bandelette de la Momie Nova", "Ces bandelettes sont aussi vieilles que son propriétaire, lui-même aussi vieux que ses bandelettes. Au fil du temps, elles se sont imprégnées des pouvoirs magiques de la Momie Nova, mais également de son odeur de chaire en décomposition." ));
            allItems.Add(new Items ( 6707, "Cape de la Momie Nova", "Cette cape malodorante est faîte à partir des bandelettes malodorantes de la Momie Nova. De quoi exciter tous les habitants des cimetières alentours." ));
            allItems.Add(new Items ( 6708, "Certificat Dopeul", "Ce certificat indique la date de votre dernière victoire sur un dopeul." ));
            allItems.Add(new Items ( 6712, "Cape Hucine", "Cette magnifique cape sent la fleur des bois. Pas de quoi rendre folle de vous une Craêtte, mais suffisant pour couvrir les odeurs d'un Sadida qui se néglige." ));
            allItems.Add(new Items ( 6713, "Lorsotheuses", "Imprégnées du sang du terrible Lordsoth, ces dagues lui ont permis de terrasser le roi des Chafers lors du célèbre concours de lancer de dagues de la foire du Trool. Le roi des Chafers n'a plus jamais osé depuis ce jour, montrer le bout de son slip en cuir, et s'est exilé avec l'illustre Captain Chafer dans les environs du cimetière de Pandala." ));
            allItems.Add(new Items ( 6716, "Nomoon", "Ce petit singe chapardeur semble s'être attaché à vous maintenant. Très docile, il est cependant très exigeant sur le plan alimentaire et n'accepte que les végétaux qui poussent sur son île." ));
            allItems.Add(new Items ( 6717, "Chienchien Noir", "Avec ce petit toutou, vous goûterez aux joies des petites promenades pipi. Fort de ses racines sauvages, il est déconseillé de lui donner à manger, sous peine de perdre une main.  n(CE FAMILIER LEGENDAIRE N'A PAS BESOIN DE MANGER)" ));
            allItems.Add(new Items ( 6718, "Chacha Blanc", "Cette petite boule de poil friponne et câline fait craquer la gent féminine toutes classes confondues. Un animal redoutable donc. n(CE FAMILIER LÉGENDAIRE N'A PAS BESOIN DE MANGER)" ));
            allItems.Add(new Items ( 6719, "Coiffe de l\'Homme Ours", "Cette étrange coiffe a appartenu au tristement célèbre Homme Ours. Cousue à partir des restes de la tête d'un ours en décomposition, cette coiffe vous permettra de ne pas passer inaperçu, et surtout, de laisser grâce à son odeur originale, une trace de votre passage." ));
            allItems.Add(new Items ( 6720, "Cape de l\'Homme Ours", "Cette jolie cape a été portée pendant des années par le célèbre Homme Ours. Elle ne fera pas de vous un ours, mais vous pourrez rivaliser avec les plus grands." ));
            allItems.Add(new Items ( 6721, "Bottes de l\'Homme Ours", "Ces bottes sont rembourrées avec de la fourrure d'ours. De quoi garder les pieds au chaud même pour ceux qui ont le sang froid." ));
            allItems.Add(new Items ( 6722, "Alliance de l\'Homme Ours", "L'Homme Ours n'a jamais quitté cette alliance, bien que sa femme fût dévorée sous ses yeux, par un des ours qu'il venait d'invoquer, et cela pour impressionner sa dulcinée. Elle a effectivement été très impressionnée." ));
            allItems.Add(new Items ( 6723, "Amulette de l\'Homme Ours", "L'Homme Ours portait cette amulette autour du cou, lorsqu'il a affronté les brigandins de la tanière de Bimdhoul. La légende raconte qu'elle lui a sauvé la vie, mais quand on la regarde de près, on s'aperçoit qu'il s'agit bien d'une légende." ));
            allItems.Add(new Items ( 6724, "Ceinture de l\'Homme Ours", "Cette petite ceinture recouverte de poils d'ours, est idéale pour cacher vos poignées d'amour. Il est donc conseillé de ne jamais l'enlever, pour ne pas décevoir l'élu de votre coeur." ));
            allItems.Add(new Items ( 6725, "Bâton de l\'Homme Ours", "L'Homme Ours utilisait ce bâton pour dresser ses invocations d'ours, et parfois pour frapper les doigts de ses enfants, lorsque ces derniers ne faisaient pas leurs devoirs en revenant de l'école. Voilà un homme qui avait tout compris à l'éducation des enfants." ));
            allItems.Add(new Items ( 6731, "Amukwak de Glace", "Avec cette amulette autour du cou, vous pourriez tenter de faire croire à votre entourage que vous avez une haleine fraîche. Laissez tomber, c'est peine perdue." ));
            allItems.Add(new Items ( 6732, "Kwakanneau de Glace", "Ce petit anneau était jadis très convoité par les Enutrofettes qui préféraient s'en servir pour décorer leurs orteils. Une excellente façon de cacher leurs verrues." ));
            allItems.Add(new Items ( 6735, "Racine de Fourbasse", "Cette minuscule racine incarne parfaitement la fourberie. Personne ne sait pourquoi, mais tout le monde le sait." ));
            allItems.Add(new Items ( 6736, "Bourgeon de Fourbasse", "Ce petit bourgeon de Fourbasse contient une sève sirupeuse et collante. Si vous avez le malheur de marcher sur une vieille Fourbasse tapie dans les herbes hautes, vous risquez de restez accrochée à celle-ci jusqu'à ce qu'elle vous dévore entièrement vos bottes. En revanche, si vous arrivez à prélever un bourgeon de Fourbasse intact, vous pourrez faire taire le plus râleur des Fécas, en le lui offrant en guise de pâte à mâcher." ));
            allItems.Add(new Items ( 6737, "Ecorce de Fourbasse", "La Fourbasse n'a pas réellement de vraies branches comme tout arbre qui se respecte, elle a cependant une fine écorce assez résistante et recouverte d'une multitude d'aspérités. Très pratique pour créer des manches d'outils qui ne glissent pas entre les mains, ou pour donner des coups de bâtons qui arrachent la peau du derrière des enfants les plus effrontés. Dans les deux cas, la satisfaction est maximale. Pour celui qui tient le manche." ));
            allItems.Add(new Items ( 6738, "Crâne de Chef Crocodaille", "Cet énorme crâne témoigne de l'hygiène dentaire déplorable du Chef Crocodaille. Chose amusante, il semble encore animé d'une certaine magie, l'âme du Chef Crocodaille l'habitant encore. Il lui arrive même parfois de verser de grosses larmes de Crocodaille si vous êtes méchant avec lui." ));
            allItems.Add(new Items ( 6739, "Peau de Crocodaille", "Réussir à avoir un morceau complet de peau de Crocodaille est presque un miracle, bien souvent, les braconniers ne récupèrent que quelques écailles. Idéale pour faire un sac, cette vieille peau saura également contenter les tailleurs de sous-vêtements de combat." ));
            allItems.Add(new Items ( 6740, "Peau de Chef Crocodaille", "Cette peau constituée d'une multitude d'écailles, est en tout point supérieure à une classique peau de Crocodaille. En effet, la face extérieure de la peau du Chef Crocodaille est plus résistante et recouverte d'écailles acérées très coupantes, alors que la face intérieure est particulièrement douce. On raconte que certains vieux tailleurs confondent les deux faces lors de la confection de sous-vêtements. L'histoire ne raconte pas si cela leur apporte plus ou moins de clientèle..." ));
            allItems.Add(new Items ( 6742, "Fourbamulette", "Cette amulette est constituée d'un mélange de feuilles de Fourbasse et de détritus de l'île de Moon. Elle ressemble plus à un collier de fleurs qu'à une véritable amulette, mais elle vous permettra de séduire sans trop d'efforts la faune locale de l'île de Moon, composée presque exclusivement d'abominables créatures. Ainsi, plus aucune Fourbasse ne saura vous résister, elles vous sauteront toutes au cou pour vous faire des suçons. Les plus malchanceux en amour sauront s'en contenter." ));
            allItems.Add(new Items ( 6743, "Fourballiance", "Prenez des feuilles de Fourbasse, empilez-les, laissez-les macérer dans de l'alcool éventé, perforez-les, buvez le reste d'alcool, collez les feuilles autour d'un morceau d'écorce, fixez des morceaux de feuille de Kokoko avec une matière gluante, et voilà, vous avez une magnifique Fourballiance. Elle sent très mauvais, et vous sera particulièrement utile pour justifier et cacher l'odeur de vos doigts surtout si vous avez tendance à les mettre n'importe où. Surtout là où il ne faut pas." ));
            allItems.Add(new Items ( 6744, "Fourbabottes", "Une fois que vous aurez essayé ces bottes, vous ne serez plus capable de vous en défaire. Non pas parce qu'elles sont exceptionnelles, mais car elles sont incroyablement collantes. Une caractéristique trop souvent partagée avec les femelles Fourbasses." ));
            allItems.Add(new Items ( 6745, "Ceinture Fourbissante", "Remarquable création artisanale à base de restes de Fourbasses et de détritus ramassés le long des plages de Moon, au milieu de l'écume nauséabonde, cette ceinture si elle est portée toute seule, vous donnera une allure vraiment surprenante. De quoi décontenancer vos adversaires ou surprendre l'être aimé, pour mieux tirer partie de cet instant où pour une fois c'est vous qui aurez le dessus." ));
            allItems.Add(new Items ( 6746, "Amukwak de Terre", "Jadis portée par Oto Mustam, chef des guerriers de Brâkmar, cette petite amulette ne lui servait qu'à se faire beau lors des orgies organisées à Brâkmar. Tout un programme." ));
            allItems.Add(new Items ( 6747, "Amukwak du Vent", "Lorsque le vent souffle dans ses plumes, le Kwak du Vent se sent libre comme l'air, et se laisse planer tel une vieille feuille morte emportée par une bourrasque. Contrairement aux idées reçues, c'est assez impressionnant." ));
            allItems.Add(new Items ( 6748, "Kwakanneau de Terre", "Judicieusement porté, cet anneau vous permettra d'embellir vos doigts boudinés. Malheureusement pour certains, il n'est efficace que sur les doigts." ));
            allItems.Add(new Items ( 6749, "Kwakanneau de Vent", "Cet anneau est si léger, qu'il pourrait s'envoler au premier coup de vent. Mais ne rêvez pas, si cela se produit, vous ne vous envolerez pas avec lui. On se moquera de vous dans le meilleur des cas." ));
            allItems.Add(new Items ( 6750, "Kwaklame de Glace", "Lame froide et tranchante, elle réveillera les instincts les plus primitifs de ceux qui l'utilisent." ));
            allItems.Add(new Items ( 6751, "Kwaklame de Terre", "" ));
            allItems.Add(new Items ( 6752, "Kwaklame du Vent", "Idéale pour couper les nuages, cette lame pourra également vous servir à faire du vent si vous l'agitez comme un éventail. Une excellente technique pour décimer les Moskitos ou défigurer vos amis." ));
            allItems.Add(new Items ( 6753, "Kwakobottes de Glace", "Idéales comme patins à glace sur les lacs gelés, ces bottes sont également très utiles pour garder les pieds au frais, ou pour y stocker votre goûter durant les journées de forte chaleur. Il est cependant déconseillé de les faire sécher au coin du feu, sous peine de ne plus jamais les revoir." ));
            allItems.Add(new Items ( 6754, "Kwakobottes de Terre", "" ));
            allItems.Add(new Items ( 6755, "Kwakobottes du Vent", "Rudimentaires mais efficaces, ces bottes d'une légéreté surprenante sont parfois utilisées après avoir été retravaillées, comme manche à air pour indiquer la direction et la force du vent. Il est alors conseillé de se placer du bon côté de la manche à air, pour ne pas mourir asphyxié." ));
            allItems.Add(new Items ( 6756, "Kwape de Terre", "Idéale pour se camoufler en motte de terre géante, cette cape vous permettra d'attaquer par surprise les laboureurs et leur voler leurs richesses. Elle est donc avant tout réservée aux collectionneurs de silex." ));
            allItems.Add(new Items ( 6757, "Kwape du Vent", "Légère et raffinée, cette création peut accessoirement servir de rideau pour les plus dépourvus. Elle était jadis très convoitée par la Momie Nova en personne, qui désirait être correctement habillée pour sortir le soir. Un exemple à suivre pour certains." ));
            allItems.Add(new Items ( 6758, "Kwakture de Terre", "Le Kwak de Terre n'est pas très social et surtout très chassé pour ses plumes, c'est bien connu. En portant cette ceinture autour des hanches, vous vous exposez donc à de multiples fourberies de la part des chasseurs de Kwaks de Terre, qui vous prendront de loin pour un vieux Kwak grabataire sur le déclin. Les apparences ne sont finalement pas toujours trompeuses." ));
            allItems.Add(new Items ( 6759, "Kwakture de Vent", "" ));
            allItems.Add(new Items ( 6760, "Kwakoiffe de Terre", "Cette Kwakoiffe de Terre est tellement lourde qu'elle vous permettra de garder en permanence les pieds sur terre, mais vous fera également parfois tomber par terre. On ne peut pas tout avoir." ));
            allItems.Add(new Items ( 6761, "Kwakoiffe du Vent", "Pour garder les cheveux au vent même une fois équipé d'une coiffe, rien ne vaut cette sympathique Kwakoiffe du Vent, qui vous permettra sans aucun doute de laisser libre court à votre expression capillaire dans la plupart des circonstances. C'est suffisant pour réussir à interloquer le plus blasé des Chafers." ));
            allItems.Add(new Items ( 6762, "Fourbasse-Ton", "" ));
            allItems.Add(new Items ( 6763, "Fourbacapa", "On raconte que les Fourbasses mortes sont très confortables une fois qu'elles ont pris le temps de se décomposer au soleil. En effet, avec le temps elles deviennent beaucoup plus souples et douces. Beaucoup d'aventuriers aimeraient pouvoir en dire autant de leur dulcinée." ));
            allItems.Add(new Items ( 6764, "Fourbacoiffe", "Avec cette coiffe sur la tête, vous n'avez vraiment pas l'air très futé. Mais plus que sans la coiffe, donc autant la garder." ));
            allItems.Add(new Items ( 6765, "Lailait", "Attention, il est bien précisé sur l'emballage de ne pas donner ce lait à un jeune Chacha, il risquerait de faire quelques tâches dans votre demeure." ));
            allItems.Add(new Items ( 6766, "Epis de Farle", "Cette amulette d'apparence rudimentaire a été confectionnée à partir de trois épis de Blé magiques, recouverts de poussière de pierre précieuse, et fauchés dans les plaines de Snossios pendant la treizième moisson d'Istrabraq. Elle confère un courage hors du commun à son porteur, désormais à même de faucher plaines et vallées sans jamais faillir." ));
            allItems.Add(new Items ( 6767, "Bracelet Magique de Farle", "Ce bracelet en pierres précieuses a été recouvert d'une toile de Chanvre par Shika, pour éviter d'attirer l'attention des Corbacs, lorsque Farle semait son orge." ));
            allItems.Add(new Items ( 6768, "Soki", "Vous emmenez ce chacha malheureux afin de le distraire." ));
            allItems.Add(new Items ( 6769, "Bâton de Farle", "Ce splendide bâton est muni de très belles dents à son extrémité, ce qui en fait un excellent fourcher. Farle s'en servait pour créer d'immenses meules de foin, plus hautes que le plus grand des Ormes qui poussait jadis en forêt de Litneg. On raconte que c'est Brouce Boulgoure en personne qui aurait coupé le bois nécessaire à la fabrication de ce bâton, en échange des  monstrueuses rations quotidiennes de flocons d'avoine offertes par Shika Ingalsse, qu'il engouffrait avant de partir raser des forêts complètes." ));
            allItems.Add(new Items ( 6770, "Sousouris", "Cette Sousouris a été dénichée à coup de balai, de la maison de Grand-mère." ));
            allItems.Add(new Items ( 6771, "Anneau Thé", "Cet anneau n'a pas encore fait ses preuves. Il s'agit d'une confection unique faite par un apprenti bijoutier à l'occasion d'un test important. Il est fabriqué à partir d'un anneau de fer classique plongé dans une solution de Thé magique." ));
            allItems.Add(new Items ( 6773, "Amulette Tude", "Cette amulette est construite de façon Tude, d'où son nom. C'est l'une des premières méthodes de création, car l'une des plus simples, que l'on enseigne aux apprentis bijoutiers. Certes le produit n'est pas puissant mais il ne faut pas en demander trop non plus." ));
            allItems.Add(new Items ( 6774, "Sabots de Farle", "Ces sabots ont été taillés dans des bois rares, renforcés avec des pièces de cuirs d'une valeur inestimable, et rembourrés avec des céréales magiques. A la fois légère, élégante, et confortable, cette paire de sabots saura résister aux affres du temps. Beaucoup d'aventuriers un peu trop misogynes aimeraient pouvoir en dire autant de leur dulcinée." ));
            allItems.Add(new Items ( 6775, "Cape de Farle", "Cette cape en tissu et en peaux permettait à Farle Ingalsse d'ensemencer des champs entiers pendant une saison complète sans jamais devoir s'arrêter. En effet, une fois repliée et utilisée comme une besace, sa capacité semble presque illimitée. Personne ne connaît réellement le tailleur à l'origine de cette merveille, la légende raconte seulement, qu'il s'est retiré de la profession lorsque ses clients ont commencé à confondre ses capes avec des serpillères. Quel triste départ en retraite !" ));
            allItems.Add(new Items ( 6776, "Farlature", "Cette ceinture servait également de tablier de boucher à Farle Ingalsse lorsqu'il devait égorger ses troupeaux de Bouftous. Une tâche ingrate et cruelle, dont il s'affranchissait pourtant avec un plaisir non dissimulé." ));
            allItems.Add(new Items ( 6778, "Farlacoiffe", "Ce chapeau de paille a été confectionné à l'aide d'une multitude d'épis magiques tressés entre eux, puis l'intérieur a été rembourré avec divers poils et étoffes cousues à la main par Shika Ingalsse, femme de Farle. Plus classe que le Ploukosse, et plus stylé que le Kraignosse, ce chapeau vous protègera du cagnard comme il l'a fait pour Farle pendant des centaines de moissons, tout en vous donnant une allure guillerette et vraiment " ));
            allItems.Add(new Items ( 6779, "Rapport de l\'espion Sram.", "Rapport de l'espion Ztiv Oztin, récupéré avec une certaine difficulté des mains de Joe Pierlivet." ));
            allItems.Add(new Items ( 6780, "Épée de l\'initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6781, "Baguette de l\'Initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6782, "Bâton de l\'initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6783, "Arc de l\'initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6784, "Dagues de l\'initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6785, "Marteau de l\'initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6786, "Pelle de l\'Initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 6787, "Fiche remplie valide", "Cette petite fiche possède le nom d'un potentiel candidat pour être livreur du boulanger" ));
            allItems.Add(new Items ( 6788, "Gâteaux", "Ces gâteaux viennent juste d'être sortis du four. Il faut les livrer tant qu'ils sont chauds. Pensez-vous vraiment que vous avez du temps à perdre pour les examiner ainsi ?" ));
            allItems.Add(new Items ( 6789, "Amulette des Champs", "Cette petite amulette fleurit au printemps, mais se dessèche en été, se fane en automne, et pourrit en hiver. nL'occasion rêvée pour changer de look au fil des saisons." ));
            allItems.Add(new Items ( 6790, "Fiche à remplir", "Cette fiche ressemble à une feuille toute simple, cependant au contact de la peau d'un individu celle ci affiche tout de suite si la personne convient aux critères fournis dans l'entête." ));
            allItems.Add(new Items ( 6791, "Anneau Champêtre", "Avec ce petit anneau autour du doigt, vous pourrez presque essayer de passer inaperçu au milieu des champs." ));
            allItems.Add(new Items ( 6792, "La Plantouze des Champs", "Ce bâton en forme de fleur a été confectionné à partir des restes de fleurs champêtres ayant succombé durant d'horribles et cruels combats. Toute la rage et la haine de ces pauvres plantes est contenue dans ce ridicule petit bâton. Cette arme est utilisée par les Srams les plus fourbes, qui tentent de persuader les ignorantes qu'ils comptent leur offrir un bouquet de fleurs. Une fois à portée, l'imprudente se retrouve souvent assenée de coups sur la tête bien mérités." ));
            allItems.Add(new Items ( 6793, "Anneau Thé", "Cet anneau a fait ses preuves, une petite marque à l'intérieur le prouve. Il s'agit d'une confection unique faite par un apprenti bijoutier à l'occasion d'un test important. Il est fabriqué à partir d'un anneau de fer classique plongé dans une solution de Thé magique." ));
            allItems.Add(new Items ( 6794, "Bottes Champêtres", "Rien de mieux que d'utiliser des bottes confectionnées à partir de restes de fleurs champêtres, pour écraser ces misérables fleurs qui poussent dans vos champs. De plus, ces bottes permettront à vos pieds de dégager une délicieuse odeur de fleur. Dommage qu'il n'existe pas un équivalent à ces bottes pour les caleçons." ));
            allItems.Add(new Items ( 6795, "Capouze des Champs", "Si vous voulez être la plus belle ce soir pour aller danser, c'est la cape qu'il vous faut." ));
            allItems.Add(new Items ( 6796, "Ceinture Fleurie", "Avec cette ceinture décorée de petits pétales autour de la taille, vous pourrez sans problème vous faire passer pour une danseuse étoile au milieu d'un combat perdu d'avance. De quoi décontenancer une armée de Chafers, pour retourner la situation à votre avantage." ));
            allItems.Add(new Items ( 6797, "Coiffe Champêtre", "Si vous avez toujours rêvé d'être fleuriste et d'avoir une haleine parfumée à l'eau de rose, c'est le moment d'adopter cette étrange coiffe." ));
            allItems.Add(new Items ( 6798, "Fleur de Dzibi", "Son parfum est léger, subtil, enivrant ... Mais seuls les Sadidas et les Eniripsas connaissent le secret de ces fleurs." ));
            allItems.Add(new Items ( 6799, "Lame du Chef Bwork", "Tranchante comme une lame de Bwork, cette lame du Chef Bwork a l'avantage de peser beaucoup plus lourd. Elle est certes moins maniable, mais il suffit de la laisser tomber sur son adversaire pour le trancher en deux, telle une vulgaire tranche de Tronknyde. Si votre ennemi ne meurt pas de douleur, il mourra de honte, et vous de rire." ));
            allItems.Add(new Items ( 6800, "Cape Banale", "Cette cape est faîte de laine de Boufton. Elle ne vous sera guère utile si ce n'est pour avoir l'air classe... Et encore..." ));
            allItems.Add(new Items ( 6801, "Cape Hivernale", "Une jolie doublure très chaude et très odorante a été admirablement cousue sur une cape banale. nSur votre dos, ou comme peau de bête devant la cheminée, cette cape aura enfin une vraie utilité." ));
            allItems.Add(new Items ( 6802, "Cache Oreilles", "Ce cache oreille très doux tiendra vos feuilles de choux au chaud." ));
            allItems.Add(new Items ( 6803, "Extrait de fleur de Dzibi", "Eviter d'en boire... simplement !" ));
            allItems.Add(new Items ( 6804, "Amulette du Chef Bwork", "D'apparence rudimentaire, cette amulette en forme de silex taillé est pourtant dotée d'une magie très puissante capable de transformer un Bwork en princesse, ou l'inverse, personne ne sait." ));
            allItems.Add(new Items ( 6805, "Bracelet du Chef Bwork", "Muni de ce petit anneau, vous pourrez faire croire à votre entourage que vous avez épousé un grand Chef Bwork et qu'il vous a offert ce bracelet comme cadeau de mariage. De quoi vous rendre intéressant lors des soirées animées à la Taverne si vous n'avez pas beaucoup de conversation." ));
            allItems.Add(new Items ( 6806, "Fragment de bois", "Ce fragment de bois provient d'une construction récente, vraisemblablement rouge." ));
            allItems.Add(new Items ( 6807, "Bottes du Chef Bwork", "De loin, ces bottes ressemblent à de classiques bottes de Bwork. De près aussi. Et pourtant, ce sont des bottes de Chef Bwork. Plus résistantes, plus puissantes, et plus malodorantes, elles sauront faire la différence dans un combat perdu d'avance." ));
            allItems.Add(new Items ( 6808, "Morceau de tissu arraché", "Ce morceau de tissu blanc est à la fois très doux et très taché." ));
            allItems.Add(new Items ( 6809, "Description approximative", "Cette description décrit une petite fille habillée d'une couleur foncée et les cheveux blonds." ));
            allItems.Add(new Items ( 6810, "Rapport d\'activité du quartier", "Ce rapport stipule qu'aucun incident n'a été constaté dans le quartier le jour du vol de la maison. nHormis la vision d'un roublard dans la nuit de l'incident." ));
            allItems.Add(new Items ( 6811, "Cape du Chef Bwork", "Cette cape déchirée et pleine de trous vous fera passer pour un très grand guerrier Bwork. Elle est parfaite si vous voulez terroriser les vieux du village. En revanche, pour séduire l'âme sœur, cela risque d'être un peu plus difficile, à moins de la porter entièrement nu, ce qui vous permettra en plus, de continuer à faire peur aux vieux du village." ));
            allItems.Add(new Items ( 6812, "Slip du Chef Bwork", "Ce slip en cuir violet se suffit à lui-même, vous pouvez vous contenter de ne porter que ça, vous aurez tout de même fière allure, à condition qu'il soit à votre taille et qu'il ne vous tombe pas sur les genoux lors d'une partie de corde à sauter un peu trop mouvementée." ));
            allItems.Add(new Items ( 6813, "Casque du Chef Bwork", "Si vous voulez réellement avoir une tête de gros débile, ce casque est fait pour vous. Avec un peu de chance, on ne verra même pas la différence lorsque vous l'enlèverez." ));
            allItems.Add(new Items ( 6814, "Ensemble de bouts de bois", "Cet ensemble de bouts de bois forme un petit lit douillet, idéal pour un Piou." ));
            allItems.Add(new Items ( 6815, "Seringue de Nibé Lulle", "Nibé Lulle vous a donné cette seringue pour prélever un échantillon de sang de Radlu Minite le Mineur Sombre." ));
            allItems.Add(new Items ( 6816, "Echantillon de sang", "Vous avez enfin réussi à prélever un échantillon de sang sur Radlu Minite. Très appétissant." ));
            allItems.Add(new Items ( 6817, "Amulette du Chef Crocodaille", "Cette surprenante amulette a en fait été confectionnée à partir d'un Bouclier Reptilien dont on a détaché les multiples pierres précieuses. C'est donc un bijou particulièrement lourd à porter mais vraiment très résistant. Votre cou sera parfaitement protégé des vampires, mais aussi des suçons des villageoises un peu trop entreprenantes." ));
            allItems.Add(new Items ( 6818, "Rose démoniaque coupée", "Cette fleur, autrefois démoniaque, est à présent un cadeau angélique pour une personne. Cela dit, si vous n'obtenez en retour qu'un regard neutre, vous pourrez toujours en faire de la confiture." ));
            allItems.Add(new Items ( 6819, "Bracelet du Chef Crocodaille", "Ce bracelet ressemble étrangement à un gant et pourtant ce n'est pas un gant mais bien un bracelet. Les apparences sont parfois trompeuses." ));
            allItems.Add(new Items ( 6820, "Herbe curative", "Cette herbe est l'ingrédient principal de vieux remèdes d'alchimiste. Elle n'est plus du tout utilisée de nos jours. On lui préfère le trèfle car ce dernier ne provoque pas de démangeaison faciale, ce qui, il faut l'avouer, est un plus." ));
            allItems.Add(new Items ( 6821, "Lame du Chef Crocodaille", "Fabriquée à partir de débris trouvés sur un Chef Crocodaille, cette arme saura se révéler très efficace si vous êtes assez beau, habile, intelligent et fort pour la manier. Avec un peu de chance, vous finirez par trouver quelqu'un qui acceptera de vous l'échanger contre une épée de boisaille." ));
            allItems.Add(new Items ( 6822, "Piqûre de réveil", "Cette piqûre contient un remède si puissant qu'il pourrait réveiller un mort. D'ailleurs il parait que l'illustre mort-vivant Hyrkul s'en injectait régulièrement. On ne peut trouver meilleure preuve de son efficacité." ));
            allItems.Add(new Items ( 6823, "Potion à base de plantes", "Cette potion, à l'odeur nauséabonde pourrait réveiller un sadida pendant sa sieste...Ou presque...Si le sadida est jeune et sur le point de se réveiller." ));
            allItems.Add(new Items ( 6824, "Pierre de Soin Fuyard", "Cette pierre de soin perd de son pouvoir très rapidement, il faut l'entourer d'un métal qui absorbe certaines radiations, afin de confiner l'effet de la pierre et ainsi de préserver son efficacité. On lui préfère des amulettes standards presque aussi efficaces et surtout bien moins chères." ));
            allItems.Add(new Items ( 6825, "Pantoufles Crochues du Chef Crocodaille", "Ces magnifiques pantoufles en peau et en écailles de Chef Crocodaille sont munies de dents acérées à leurs extrémités. Méfiez-vous lorsque vous ferez du pied à votre dulcinée, vous pourriez lui déchirer ses petits bas en laine de Tabi." ));
            allItems.Add(new Items ( 6826, "Pierre de soin sertie", "Cette amulette est une pierre de soin fuyard joliement sertie par un morceau de métal rare. La tenir dans sa main procure une sensation de bien-être, la fatigue s'en va et le cerveau est libéré. Et en plus on peut vous voir dans la nuit avec." ));
            allItems.Add(new Items ( 6827, "Lettre de Joe Pierlivet", "Joe t'a confié cettre lettre afin que tu l'amènes à un bon ami à lui, Howard le Simaisrien, un Iop euh ... comment dire ... un Iop quoi !" ));
            allItems.Add(new Items ( 6828, "Poignée d\'epée", "Cette poignée ne vous transformera pas en as du maniement d'epée mais elle peut vous y aider." ));
            allItems.Add(new Items ( 6829, "Epée tout juste forgée", "Cette épée vient tout juste d'être forgée." ));
            allItems.Add(new Items ( 6830, "Petit sac d\'écolier du Chef Crocodaille", "A Amakna aussi, la rentrée scolaire est une épreuve douloureuse pour les jeunes enfants. Surtout pour les jeunes Iops, pour qui la rentrée des classes est synonyme de véritable calvaire, parsemé d'épreuves insurmontables. Réconfortez-les avec ce magnifique sac d'écolier en peau et en écailles de Chef Crocodaille, et rembourré d'une multitude de poils." ));
            allItems.Add(new Items ( 6831, "Ceinture du Chef Crocodaille", "Trop rigide pour servir de fouet, cette ceinture recouverte de minuscules pierres précisieuses peut néanmoins être utilisée de manière plus conventionnelle, pour éviter que vos guêtres ne tombent sur vos genoux par exemple." ));
            allItems.Add(new Items ( 6832, "Livreur de Bière", "Le livreur de bière est aussi important qu'un convoyeur de banque. A ceci près qu'il est bien plus hargneux." ));
            allItems.Add(new Items ( 6833, "Bourse de Kamas douteuse", "Ces kamas semblent venir d'un autre temps, il faudrait les fondre rapidement avant qu'il ne décident de repartir d'où ils sont venus." ));
            allItems.Add(new Items ( 6834, "Coiffe du Chef Crocodaille", "Si vous avez toujours rêvé de ressembler à un Crocodaille sans pour autant vous transformer en Crocodaille, cette coiffe est faîte pour vous. N'espérez pas pour autant utiliser cette coiffe pour faire croire à vos amis que vous avez un jour réussi à tuer vous-même un Crocodaille. Personne n'y croira, à part vous peut-être." ));
            allItems.Add(new Items ( 6835, "Métal transmuté", "La bourse de kamas douteuse a été habilement transmutée par Nibé Lulle. Une épée est désormais prête à être créée avec ce métal ..." ));
            allItems.Add(new Items ( 6836, "Epée grossière", "Voici une épée forgée des mains de Dedoiné, mais elle semble encore trop fragile pour vous. Nibé Lulle l'Alchimiste devrait pouvoir y faire quelque chose" ));
            allItems.Add(new Items ( 6837, "Epée bondissante", "Une épée faîte pour vous, légère et redoutable. Mais il y manque une petite touche finale, quelque chose d'Ecaflip ..." ));
            allItems.Add(new Items ( 6838, "Epée de feu grossière", "L'eau de lune semble avoir fait son effet. Cette épée est d'une solidité à toute épreuve, mais il y manque quelque chose d'Ecaflip ..." ));
            allItems.Add(new Items ( 6839, "Epée de Yingnitié", "Cette arme estampillée par Meow Meow pourrait faire de vous un Ecaflip de légende, éventuellement ..." ));
            allItems.Add(new Items ( 6840, "Epée de Yanguru", "Cet arme estampillée par Meow Meow pourrait faire de vous une Ecaflip de légende, éventuellement ..." ));
            allItems.Add(new Items ( 6841, "Etoffe d\'Ours", "Cette étrange étoffe sent l'Ours. De quoi faire craquer une partie non négligeable de la gente féminine." ));
            allItems.Add(new Items ( 6842, "Dent d\'Ours", "Cette étrange dent a appartenu à un vieil Ours. Ou plutôt, à une invocation d'Ours, ce qui il faut bien le reconnaître, est totalement différent, puisque qu'en réalité, vous avez entre vos mains, une dent invoquée. Très intéressant." ));
            allItems.Add(new Items ( 6843, "Os d\'Ours", "Cet os pointu s'est cassé pendant un terrible combat contre un des Ours invoqués de l'Homme Ours. Il était trop tard pour mettre un plâtre sur la patte de ce pauvre Ours agonisant et gémissant de douleur à cause de vos attaques répétées. Vous avez préféré l'achever lentement en l'étouffant avec de la bave de Bouftou, le dépecer et ensuite lui arracher cet os alors qu'il était encore conscient. Pas de pitié pour les invocations." ));
            allItems.Add(new Items ( 6844, "Poils d\'Ours", "Parfois, l'Homme Ours se décide à épiler une de ses invocations d'Ours pour s'amuser ou pour passer le temps. Il récupère ensuite les poils pour en faire des sous-vêtements de grande qualité, très prisés des chasseurs solitaires attirés par les sensations et les odeurs fortes." ));
            allItems.Add(new Items ( 6845, "Peau d\'Ours", "Cette peau rugueuse et dépourvue de poils est le fruit d'un long travail d'épilation intensive à l'huile bouillante et au fer rouge. Ne vous étonnez donc pas si elle sent légèrement le roussi." ));
            allItems.Add(new Items ( 6846, "Fiole cristal de pâle Anthir", "Fiole banale au premier regard. Elle possède cependant un pouvoir que seul les Xelors savent exploiter." ));
            allItems.Add(new Items ( 6847, "Sac de grains d\'Epyss", "Grain assez rare, qui ne sert pas à grand chose. Qui à dit comme les Xélors ?" ));
            allItems.Add(new Items ( 6848, "Bois fiorituré", "Bois de moyenne facture." ));
            allItems.Add(new Items ( 6849, "Boîte de Suborhube", "Les seuls à connaître le mode d'emploi sont les Enutrofs. Peut-être, simplement, parce qu'on ne préfère pas savoir trop de choses sur leur secrets... Et quels secrets !!!" ));
            allItems.Add(new Items ( 6850, "Pizaille", "Une des spécialités culinaires d'Astrub, la pizaille à l'avantage de pouvoir être mangé n'importe où et n'importe quand, à l'exception peut-être du quartier des bouchers car l'odeur y est insupportable ..." ));
            allItems.Add(new Items ( 6852, "Peluche de la foire", "La peluche que Pleur Nycheuz a gagnée à la foire du trool ..." ));
            allItems.Add(new Items ( 6853, "Bois d\'Arash", "Ce bois a la particularité de pousser tout droit, comme un bâton. Pratique pour le stockage..." ));
            allItems.Add(new Items ( 6854, "Peau de larve bleue", "Morceau de chair gluant arraché à une pauvre larve sans défense." ));
            allItems.Add(new Items ( 6855, "Petit arc d\'Enileda", "Fruit du travail de la célèbre tailleuse d'arc Enileda, ce petit arc n'est pas un modèle en soit." ));
            allItems.Add(new Items ( 6856, "Ciseaux à rose démoniaque", "La forme de ces ciseaux est spécialement étudiée pour couper la tige de n'importe quelle rose démoniaque sans abîmer les pétales. Et ceci alors même qu'elle vous mord le mollet." ));
            allItems.Add(new Items ( 6857, "Bière d\'Astrub", "Une bière classique favorisant le développement de votre muscle abdominal, plus connu sous le nom de l'Abdo kro." ));
            allItems.Add(new Items ( 6858, "Fiche remplie mais non valide", "Cette petite fiche possède, hélas, le nom d'un candidat non valide pour être livreur du boulanger. La petite tâche rouge en bas indiquant qu'il ne possédait soit pas assez de force, soit pas assez d'agilité pour ce métier." ));
            allItems.Add(new Items ( 6859, "Paire de ciseaux", "Une paire de ciseaux qui ne peigne pas de mine... elle aurait même plutôt tendance à couper qu'à peigner, racontent certains coiffeurs..." ));
            allItems.Add(new Items ( 6862, "Manuscrit de l\'Ours", "Cet étrange parchemin explique comment l'Homme Ours a appris a invoquer des Ours. Incroyable." ));
            allItems.Add(new Items ( 6863, "Casque des Mercenaires d\'Astrub", "Casque des Mercenaires d'Astrub." ));
            allItems.Add(new Items ( 6864, "Fleur de rose Obscure", "Une fleur de rose si sombre que son ombre parait étincelante en comparaison." ));
            allItems.Add(new Items ( 6865, "Note", "" ));
            allItems.Add(new Items ( 6866, "Morceau de cape", "Un morceau de cape bleue de ceux qui vous ont agressé. Mine de rien vous les avez dérouillés." ));
            allItems.Add(new Items ( 6867, "Note de récolte", "Pour ne pas oublier l'heure à laquelle vous devez récolter le Vhan, vous l'avez noté sur ce papier." ));
            allItems.Add(new Items ( 6868, "Planche en Châtaignier", "Cette planche en Châtaignier est parfois utilisée pour construire des habitations, mais le plus souvent on l'utilise pour faire des pièges à mouche..." ));
            allItems.Add(new Items ( 6869, "Pleur Nycheuz", "Pleur Nycheuz ne sait plus où se trouve sa maison, il faut donc la raccompagner ..." ));
            allItems.Add(new Items ( 6870, "Padgref Demoël", "Padgref Demoël est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6871, "Frakacia Leukocytine", "Frakacia est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6872, "Nervoes Brakdoun", "Nervoes Brakdoun est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6873, "Qil Bil", "Qil Bil est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6874, "Brumen Tinctorias", "Brumen Tinctorias est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6875, "Fouduglen", "Fouduglen est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6876, "Ogivol Scarlarcin", "Ogivol est maintenant sous votre contrôle. A vous de l'amener en sécurité à la prison" ));
            allItems.Add(new Items ( 6884, "Clef du Donjon du Bworker", "Cette clef permet de rentrer dans le donjon du Bworker. Attention, elle sera détruite après utilisation." ));
            allItems.Add(new Items ( 6885, "Jeton du Bworker", "Idéal pour jouer à pile ou face ou pour tuer sur place votre ennemi lors d'un lancer de pièce, ce jeton en or massif fera de vous un joueur heureux, mais peut-être malchanceux." ));
            allItems.Add(new Items ( 6886, "Cape des Mercenaires d\'Astrub", "Cape des Mercenaires d'Astrub." ));
            allItems.Add(new Items ( 6887, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6888, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6889, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6890, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6891, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6892, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6893, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérinité, qui influencera directement la qualité de son contenu." ));
            allItems.Add(new Items ( 6894, "Surpuissant Chacha de Combat (MJ)", "Ce surpuissant Chacha de combat incarne à merveille la surpuissance. Très impressionnant. Mais attention, il est réservé aux Maîtres de jeu qui le valent bien." ));
            allItems.Add(new Items ( 6895, "Petit Chacha de Combat (MJ)", "Moins surpuissant que le surpuissant Chacha de Combat, mais néanmoins très puissant, ce familier reste une valeur sûre pour tout Maître de jeu qui se respecte. En revanche, il faut avouer qu'il est nettement plus salissant. N'oubliez pas de le laver vigoureusement à l'eau bouillante de temps à autre pour qu'il garde toute sa splendeur." ));
            allItems.Add(new Items ( 6897, "Plume de Piou Bleu", "Cette petite plume de Piou a été directement arrachée du derrière d'un pauvre Piou qui n'avait rien demandé." ));
            allItems.Add(new Items ( 6898, "Plume de Piou Violet", "Cette petite plume de Piou a été directement arrachée du derrière d'un pauvre Piou qui n'avait rien demandé." ));
            allItems.Add(new Items ( 6899, "Plume de Piou Vert", "Cette petite plume de Piou a été directement arrachée du derrière d'un pauvre Piou qui n'avait rien demandé." ));
            allItems.Add(new Items ( 6900, "Plume de Piou Rouge", "Cette petite plume de Piou a été directement arrachée du derrière d'un pauvre Piou qui n'avait rien demandé." ));
            allItems.Add(new Items ( 6902, "Plume de Piou Jaune", "Cette petite plume de Piou a été directement arrachée du derrière d'un pauvre Piou qui n'avait rien demandé." ));
            allItems.Add(new Items ( 6903, "Plume de Piou Rose", "Cette petite plume de Piou a été directement arrachée du derrière d'un pauvre Piou qui n'avait rien demandé." ));
            allItems.Add(new Items ( 6904, "Protection usagée du Bworker", "L'amour fait parfois plus de ravages et de dégâts qu'une guerre. Ce n'est pas au Bworker que vous apprendrez cela." ));
            allItems.Add(new Items ( 6905, "Pack Surprise Bwork", "L'ouverture de ce pack requiert de la part de son possesseur, une grande sérénité, qui influencera directement sur la qualité de son contenu." ));
            allItems.Add(new Items ( 6906, "Mercenaire Neophyte", "Ce parchemin est le formulaire d'entrée parmi les Mercenaires. Celui à qui il est proposé sera inscrit dans les registres du clan, en tant que néophyte." ));
            allItems.Add(new Items ( 6907, "Neutraliseur", "Permet de donner l'alignement neutre au personnage ciblé." ));
            allItems.Add(new Items ( 6908, "Sanglature", "Poilue mais élégante, cette ceinture devrait parfois vous faire penser à votre dulcinée." ));
            allItems.Add(new Items ( 6909, "Pieds du Sanglier", "Ces bottes sont parfaites pour marcher dans la boue et rayer les parquets. Elles sont généralement interdites sur toutes les pistes de danse." ));
            allItems.Add(new Items ( 6910, "Anneau du Sanglier", "Cet anneau était placé sur les pattes des sangliers en rut, pour que les éleveurs puissent facilement les reconnaître. Aujourd'hui, c'est vous qui portez cet anneau. Toutes les interprétations sont possibles." ));
            allItems.Add(new Items ( 6911, "Araknoton", "Ce ridicule petit anneau vous permettra de combattre plus facilement votre phobie des Araknes." ));
            allItems.Add(new Items ( 6912, "Araknoture", "Confectionnée à partir de vieux restes d'Araknes en décomposition, cette ceinture a tout pour plaire. Elle reprendra parfois vie et prendra un malin plaisir à vous mordre les hanches. Sales bêtes !" ));
            allItems.Add(new Items ( 6913, "L\'Araknacoiffe", "Affublé de cette coiffe, vous ressemblerez à une Arakne sauvage. Dans le meilleur des cas, vous effraierez les Moskitos un peu naïfs." ));
            allItems.Add(new Items ( 6914, "La Trancheuse d\'Arakne", "Fait très étrange, l'Arakne, n'a que quatre pattes velues, ce qui signifie qu'avec seulement quatre coups de haches, vous pourrez la transformer en Arakne Tronc." ));
            allItems.Add(new Items ( 6915, "Mos Kitano", "Cet anneau est si léger qu'il vous permettra de réaliser n'importe quelle acrobatie avec vos doigts. Très pratique pour humilier un adversaire dix fois plus petit que vous, en lui écrasant la tête avec le pouce." ));
            allItems.Add(new Items ( 6916, "Sac du Petit Moskito", "Ce sac en peau et en ailes de Moskitos faisait jadis fureur à la sortie des écoles. Aujourd'hui, il est totalement démodé, et a été remplacé dans le coeur des enfants, par le Petit sac d'écolier du Chef Crocodaille. Du grand n'importe quoi, quand on voit que ce dernier est largement plus cher, et surtout beaucoup plus laid. Une sombre histoire de mode." ));
            allItems.Add(new Items ( 6917, "Moskitogalurette", "Cette coiffe doit son nom surprenant à l'excentricité du premier Enutrof qui l'a découverte en dépouillant un vieux Moskito malade. Très utile pour les aventuriers qui débutent, elle permet avant tout, de rendre folles de vous les Araknes affamées." ));
            allItems.Add(new Items ( 6918, "Amulette du Moskito", "Moins efficace et moins prisée que la célèbre Araknamu, cette petite amulette a pourtant beaucoup de charme lorsqu'elle est portée toute seule, sur un corps très poilu." ));
            allItems.Add(new Items ( 6919, "Anneau du Champ Champ", "Le Champ Champ n'a jamais été connu pour être une créature très sympathique. Il vous empoisonne la vie dès qu'il peut, il mord assez fort, et a très mauvais caractère. C'est étonnant comme ces vilaines petites créatures peuvent parfois vous faire penser à un conjoint." ));
            allItems.Add(new Items ( 6920, "Alliance Du Champ Champ", "Lorsque le Champ Champ se marie, il offre cette alliance à sa dulcinée. Cette dernière étant dépourvue de bras, l'alliance est déposée soigneusement sur une de ses épines. En vous équipant de cette alliance, vous briserez les liens du mariage qui unissaient ces deux créatures, et dans le meilleur des cas, le Champ Champ vous prendra pour sa dulcinée." ));
            allItems.Add(new Items ( 6921, "Champcoiffe", "Autrefois utilisée en infusion, et parfois même fumée, cette coiffe de Champ Champ a tout pour plaire." ));
            allItems.Add(new Items ( 6922, "Cape du Champ Champ", "La seule chose qui soit plus impressionnante qu'un Champ Champ enragé, c'est un Champ Champ enragé avec une cape. En effet, cette cape incarne une certaine forme de sur-puissance vestimentaire, bien qu'elle soit avouons-le, assez laide." ));
            allItems.Add(new Items ( 6923, "Amulette du Bandit", "Cette amulette a jadis appartenu au grand bandit " ));
            allItems.Add(new Items ( 6924, "Dagues du Bandit", "Ces dagues aiguisées sont populaires auprès des bandits et impopulaires auprès des jeunes aventuriers pour les mêmes raisons, elles disposent d'un enchantement leur permettant de voler les kamas de ses adversaires. Une forme d'impôt assez originale." ));
            allItems.Add(new Items ( 6925, "Ceinture du Bandit", "Cette ceinture saillante vous ira à ravir si vous rejoignez un jour la cause des bandits de grand chemin. En attendant, elle ne vous rendra que plus ridicule." ));
            allItems.Add(new Items ( 6926, "Coiffe du Prespic", "Avec cette coiffe de Prespic sur la tête, vous ressemblez presque à Dévie Cloquette, célèbre chasseuse des temps modernes, prête à tout pour cacher sa calvitie naissante. Pas de chance pour vous si vous vouliez faire de même, vous devrez vous contenter d'une coiffe de couleur rousse, qui de toute évidence, dégagera une horrible odeur dès qu'il se mettra à pleuvoir." ));
            allItems.Add(new Items ( 6927, "Cape du Prespic", "Cette petite cape rousse peut également faire office de descente de lit, ou de paillasson, de quoi remettre au goût du jour l'intérieur de votre habitation, de manière économique." ));
            allItems.Add(new Items ( 6928, "Anneau du Prespic", "A la fois sobre et tout de même original, cet anneau saura séduire les aventuriers avides d'apparat de luxes mais complètement fauchés, au point d'aller dépouiller de ridicules petites créatures sauvages pour s'orner de bijoux. La mesquinerie ne connaît aucune limite." ));
            allItems.Add(new Items ( 6929, "Ceinture du Prespic", "Idéale pour garder ses hanches au chaud, cette ceinture a tout de même l'inconvénient d'irriter et de démanger les peaux douces. A réserver aux durs à cuir." ));
            allItems.Add(new Items ( 6930, "Casque du Craqueleur", "Ce casque directement taillé dans la pierre vaut son pesant d'or. Il saura vous protéger des attaques d'un Craqueboule tout en vous permettant d'enchaîner des coups de boules dignes du Désiroboule. De quoi faire perdre la boule à tous vos adversaires." ));
            allItems.Add(new Items ( 6931, "Craquelocape", "Cette cape en pierre est un peu trop rigide, mais elle n'en reste pas moins une excellente protection contre les jets de pierres, ou plus dangereux encore, contre les crachats de Craqueleurs." ));
            allItems.Add(new Items ( 6933, "Bottes du Craqueleur", "Incroyablement lourdes, mais néanmoins assez efficaces, ces bottes en pierre vous permettront enfin d'avoir une excuse, lorsque vous traînez des pieds pour défendre votre percepteur dans un combat perdu d'avance." ));
            allItems.Add(new Items ( 6934, "Craquamulette", "Cette amulette est aussi lourde qu'un Blop boulimique, voilà qui devrait décourager les aventuriers un peu trop fragiles de la porter. En revanche, une fois son poids supporté, il faut reconnaître qu'elle vous donne fière allure." ));
            allItems.Add(new Items ( 6935, "Ceinture du Craqueleur", "Porter cette ceinture autour des hanches fera de vous un aventurier respecté. La porter sur la tête en guise de bandeau fera de vous la risée du village d'Astrub." ));
            allItems.Add(new Items ( 6936, "Scaracoiffe Verte", "Affublé de cette coiffe originale, vous pourrez enfin faire croire à vos amis que vous êtes un chasseur de Scarafeuilles." ));
            allItems.Add(new Items ( 6937, "Scaracoiffe Rouge", "Affublé de cette coiffe originale, vous pourrez enfin faire croire à vos amis que vous êtes un chasseur de Scarafeuilles." ));
            allItems.Add(new Items ( 6938, "Scaracoiffe Blanche", "Affublé de cette coiffe originale, vous pourrez enfin faire croire à vos amis que vous êtes un chasseur de Scarafeuilles." ));
            allItems.Add(new Items ( 6939, "Scaracoiffe Bleue", "Affublé de cette coiffe originale, vous pourrez enfin faire croire à vos amis que vous êtes un chasseur de Scarafeuilles." ));
            allItems.Add(new Items ( 6940, "Scaracape Verte", "Qui n'a jamais rêvé de revêtir une cape en coquille de Scarafeuilles ? Une fois équipée, vous devriez réussir à vous prendre pour un véritable Scarafeuille, et peut-être arriverez-vous à vous déplacer en glissant et en dérapant. De quoi décontenancer une horde de Trools." ));
            allItems.Add(new Items ( 6941, "Scaracape Bleue", "Qui n'a jamais rêvé de revêtir une cape en coquille de Scarafeuilles ? Une fois équipée, vous devriez réussir à vous prendre pour un véritable Scarafeuille, et peut-être arriverez-vous à vous déplacer en glissant et en dérapant. De quoi décontenancer une horde de Trools." ));
            allItems.Add(new Items ( 6942, "Scaracape Rouge", "Qui n'a jamais rêvé de revêtir une cape en coquille de Scarafeuilles ? Une fois équipée, vous devriez réussir à vous prendre pour un véritable Scarafeuille, et peut-être arriverez-vous à vous déplacer en glissant et en dérapant. De quoi décontenancer une horde de Trools." ));
            allItems.Add(new Items ( 6943, "Scaracape Blanche", "Qui n'a jamais rêvé de revêtir une cape en coquille de Scarafeuilles ? Une fois équipée, vous devriez réussir à vous prendre pour un véritable Scarafeuille, et peut-être arriverez-vous à vous déplacer en glissant et en dérapant. De quoi décontenancer une horde de Trools." ));
            allItems.Add(new Items ( 6944, "Scaranneau Vert", "Porter cet anneau autour du doigt vous permettra d'avoir une pensée émue pour tous les Scarafeuilles que vous avez exterminés. Alors peut-être, vous vous demanderez si tout cela en valait réellement la peine." ));
            allItems.Add(new Items ( 6945, "Scaranneau Rouge", "Porter cet anneau autour du doigt vous permettra d'avoir une pensée émue pour tous les Scarafeuilles que vous avez exterminés. Alors peut-être, vous vous demanderez si tout cela en valait réellement la peine." ));
            allItems.Add(new Items ( 6946, "Scaranneau Blanc", "Porter cet anneau autour du doigt vous permettra d'avoir une pensée émue pour tous les Scarafeuilles que vous avez exterminés. Alors peut-être, vous vous demanderez si tout cela en valait réellement la peine." ));
            allItems.Add(new Items ( 6947, "Scaranneau Bleu", "Porter cet anneau autour du doigt vous permettra d'avoir une pensée émue pour tous les Scarafeuilles que vous avez exterminés. Alors peut-être, vous vous demanderez si tout cela en valait réellement la peine." ));
            allItems.Add(new Items ( 6948, "Scarature Verte", "La Scarature servait autrefois à donner une indication sur votre niveau en combat à mains nues. Posséder une Scarature verte signifiait que vous étiez un débutant, une rouge, signifiait que vous léchiez le sang de vos ennemis une fois ceux-ci exterminés, une blanche, signifiait que vous étiez une véritable machine à exterminer les ennemis, et une bleue signifiait que vous n'aviez pas assez de kamas pour vous acheter une des trois autres Scaratures." ));
            allItems.Add(new Items ( 6949, "Scarature Blanche", "La Scarature servait autrefois à donner une indication sur votre niveau en combat à mains nues. Posséder une Scarature verte signifiait que vous étiez un débutant, une rouge, signifiait que vous léchiez le sang de vos ennemis une fois ceux-ci exterminés, une blanche, signifiait que vous étiez une véritable machine à exterminer les ennemis, et une bleue signifiait que vous n'aviez pas assez de kamas pour vous acheter une des trois autres Scaratures." ));
            allItems.Add(new Items ( 6950, "Scarature Rouge", "La Scarature servait autrefois à donner une indication sur votre niveau en combat à mains nues. Posséder une Scarature verte signifiait que vous étiez un débutant, une rouge, signifiait que vous léchiez le sang de vos ennemis une fois ceux-ci exterminés, une blanche, signifiait que vous étiez une véritable machine à exterminer les ennemis, et une bleue signifiait que vous n'aviez pas assez de kamas pour vous acheter une des trois autres Scaratures." ));
            allItems.Add(new Items ( 6951, "Scarature Bleue", "La Scarature servait autrefois à donner une indication sur votre niveau en combat à mains nues. Posséder une Scarature verte signifiait que vous étiez un débutant, une rouge, signifiait que vous léchiez le sang de vos ennemis une fois ceux-ci exterminés, une blanche, signifiait que vous étiez une véritable machine à exterminer les ennemis, et une bleue signifiait que vous n'aviez pas assez de kamas pour vous acheter une des trois autres Scaratures." ));
            allItems.Add(new Items ( 6952, "Coiffe du Mulou", "Avec cette tête de Mulou sur le crâne, vous ressemblez à Bernaize Mainarht, illustre ménestrel en avance sur son temps, dont la tête fut retrouvée coincée dans la gueule d'un Mulou agonisant, n'ayant pas supporté les chansons que le ménestrel avait tenté de lui infliger." ));
            allItems.Add(new Items ( 6953, "Bottines du Mulou", "Ces bottines recouvertes de poils de Mulou, sont très pratiques pour nettoyer les parquets poussiéreux, ou tout simplement pour nettoyer la tête de vos adversaires lorsqu'ils sont au sol en train d'agoniser." ));
            allItems.Add(new Items ( 6954, "Cape du Mulou", "Cette peau de Mulou constitue une excellente protection contre le froid, mais également contre les aventuriers aux sens olfactifs un peu trop développés." ));
            allItems.Add(new Items ( 6955, "String du Mulou", "Utilisé comme fouet, ce string peut se révéler plus efficace qu'il n'y paraît, pour remotiver vos compagnons de route, lorsque l'amour du risque commence à leur faire défaut." ));
            allItems.Add(new Items ( 6956, "Anneau du Mulou", "Attention, cet anneau rendra votre doigt particulièrement malodorant. Une odeur qui risque d'en couvrir une autre. C'est peut-être pour votre doigt et votre entourage une très bonne chose..." ));
            allItems.Add(new Items ( 6957, "Lame du Craqueleur", "Cette épée servait autrefois à découper des blocs de granit pour confectionner les murailles des cités prestigieuses telles que Bonta ou Brâkmar. Aujourd'hui, cette arme est avant tout recherché pour sa capacité à écraser toutes sortes de créatures vivantes, consentantes ou pas." ));
            allItems.Add(new Items ( 6958, "Géniallissime Coiffe de Torte Hou", "A peine démodée, cette coiffe vous donnera un style unique et vous permettra de regarder sous les jupes des Crâettes en toute discrétion. Idéale pour les soirées d'été plutôt arrosées." ));
            allItems.Add(new Items ( 6961, "Anneau du Bandit", "Cet anneau est aussi populaire auprès des dentistes que des bandits, qui s'en servent pour casser une à une les dents des imprudents. Une forme de prévention comme une autre contre l'apparition des caries." ));
            allItems.Add(new Items ( 6962, "Cadeau Dofusien", "Pas moyen de vous révéler le contenu de ce cadeau, mais sachez qu'il vous permettra à la fois de vous éclater et de faire la foire. Lorsque c'est jour de fête, il faut bien ça." ));
            allItems.Add(new Items ( 6963, "Boules du Minotoboule", "Ces énormes boules sont vraiment délicieuses. Vous n'en verrez pas le bout tout de suite, mais qu'importe." ));
            allItems.Add(new Items ( 6964, "Potion de cité :  Brâkmar", "Cette potion à base de riz permet à toutes les personnes d'alignement Brâkmarien, de rejoindre la cité de Brâkmar de manière instantanée." ));
            allItems.Add(new Items ( 6965, "Potion de cité :  Bonta", "Cette potion à base de riz permet à toutes les personnes d'alignement Bontarien, de rejoindre la cité de Bonta de manière instantanée." ));
            allItems.Add(new Items ( 6966, "Parchemin de Sort Spécial Poisse", "(Double clic pour apprendre le sort) nCe parchemin vous permet d'apprendre le sort Poisse... Réservé aux Srams uniquement." ));
            allItems.Add(new Items ( 6967, "Parchemin d\'intronisation du Mercenaire Apprenti", "Ce parchemin est la clé qui permet à un Mercenaire d'être promu apprenti." ));
            allItems.Add(new Items ( 6968, "Parchemin d\'intronisation chez les Mercenaires", "Ce parchemin est la clé qui permet à un Mercenaire d'être promu Mercenaire à part entière." ));
            allItems.Add(new Items ( 6969, "Parchemin d\'intronisation du Grand Mercenaire", "Ce parchemin est la clé qui permet à un Mercenaire d'être promu Grand Mercenaire." ));
            allItems.Add(new Items ( 6970, "Parchemin d\'intronisation du Maître Mercenaire", "Ce parchemin est la clé qui permet à un Mercenaire d'être promu Maître du Clan des Mercenaires." ));
            allItems.Add(new Items ( 6971, "Clint du disciple mercenaire", "Ce chapeau est tout de même une assez bonne réplique du célèbre Clint, il conviendra parfaitement aux disciples mercenaires qui voudront se faire passer pour le maître." ));
            allItems.Add(new Items ( 6975, "Clint du mercenaire néophyte", "Ce chapeau surpuissant est l'insigne des mercenaires. Il a le pouvoir de vous protéger de la pluie et du soleil, et c'est déjà pas mal..." ));
            allItems.Add(new Items ( 6978, "Péki", "Cette créature ressemble de loin à un Chienchien. Sa tête montre clairement qu'elle a subi d'énormes traumatismes qui l'ont poussée à devenir végétarienne. Elle ne se nourrit donc que de végétaux et plantes qui poussent sur l'île de Pandala." ));
            allItems.Add(new Items ( 6980, "Dofus Vulbis", "Ce Dofus a été pondu par un dragon qui a décidé de rester anonyme. Les aventuriers les plus avisés se demandent toujours pourquoi." ));
            allItems.Add(new Items ( 6981, "Dagues Lassay", "La lame de ces dagues est en glace, ce qui est très pratique pour rafraîchir l'haleine de vos ennemis, ouvrez-leur la bouche, plantez-y votre grosse dague glacée, et le tour est joué." ));
            allItems.Add(new Items ( 6982, "Dagues Honies", "Ces dagues ont baigné dans le sang de nombreuses créatures démoniaques agonisantes, comme des enfants par exemple, ce qui leur donne un cachet franchement original." ));
            allItems.Add(new Items ( 6983, "Dagues Réceuses", "Affûtées une fois par décennie seulement, ces dagues n'en restent pas moins mortelles. Animées d'une magie des plus obscures, elles se déplacent parfois toutes seules la nuit, pour aller se planter dans le meilleur des cas, dans le derrière des imprudents... Encore une histoire que l'on raconte aux jeunes Iops pour leur faire peur." ));
            allItems.Add(new Items ( 6984, "Dagues de l\'Apprenti Tueur de Bouftons", "Ces dagues ont appartenu à un pauvre Sadida, dont la richesse fulgurante fera date dans l'histoire d'Amakna. En effet, ce Sadida avait dédié son existence à la chasse aux Bouftons, ce qui ne manquait pas de faire rire tous les habitants du village. Certains badauds venaient même de loin, pour le regarder chasser ces ridicules créatures et pour se moquer de lui. Sa vengeance fut terrible, car entre temps, il était devenu assez riche pour mettre à prix toutes les sales têtes qui s'étaient moquées de lui." ));
            allItems.Add(new Items ( 6986, "Bouclier du Mont Kipou brisé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 6987, "Petite Pierre d\'âme hasardeuse", "Cette rune offre une petite chance de capturer des âmes de petite taille." ));
            allItems.Add(new Items ( 6988, "Sulik", "Autrefois porté par un Xélor sauvage dont la soeur avait été enlevée par des esclavagistes sans scrupules, ce somptueux chapeau fera de vous un sauvage vraiment original." ));
            allItems.Add(new Items ( 6989, "Krokop", "Ce chapeau a été confectionné il y a bien longtemps par des apprentis tailleurs qui voulaient se protéger des crises de colères de Mirko le tailleur. Ce dernier avait la fâcheuse habitude de décimer ses apprentis avec des coups de pieds en pleine tête, lorsqu'ils ne travaillaient pas assez vite. Une façon comme une autre de les remotiver pour qu'ils se mettent au travail." ));
            allItems.Add(new Items ( 6990, "Phalène", "Cette coiffe est un hommage aux cousins lointains des Moskitos, les Phalènes. Bien plus gros, mais plus gentils que les Moskitos, les Phalènes avaient une fâcheuse tendance à chercher à se reproduire avec tout et n'importe quoi, mais surtout avec n'importe quoi, ce qui a conduit à une extinction progressive de leur espèce. Cette terrible histoire n'avait laissé personne indifférent, et de nos jours, il est courant d'insulter un aventurier de Phalène, lorsque l'on veut insinuer qu'il choisit très mal ses conjoints..." ));
            allItems.Add(new Items ( 6991, "Goyave", "Cette étrange coiffe ressemble à un gros fruit déshydraté en décomposition, un peu comme vous après une soirée passée à la taverne." ));
            allItems.Add(new Items ( 6992, "Kannipiou", "Version embellie du célèbre masque Kanniboule, le Kannipiou vous permettra de passer pour un véritable chasseur de Pious, auprès de vos amis." ));
            allItems.Add(new Items ( 6993, "Cape Ricéfini", "Hirvé Vélard, célèbre ménestrel de son état, aimait chanter à en perdre la voix. Il fut brûlé vif près de la banque de Bonta, après avoir fait scandale avec sa chanson " ));
            allItems.Add(new Items ( 6994, "Cape Hôte", "Cette création hybride entre une hôte et une cape, est franchement étonnante. Si elle avait été confectionnée en cuir, le Captain Chafer l'aurait adorée." ));
            allItems.Add(new Items ( 6995, "Cape Houte", "Cette cape très puissante a renversé plus d'un imprudent ayant tenté de la porter, une caractéristique partagée avec la Momie Nova." ));
            allItems.Add(new Items ( 6996, "Anneau Stradamus", "Stradamus, était un viel Enutrof, complètement ivre, qui lisait l'avenir dans les choppes de bière. La légende raconte qu'à sa demande, il fût enseveli à la verticale dans un des murs de la taverne de Brâkmar, pour " ));
            allItems.Add(new Items ( 6997, "Anneau Fioutioure", "Celle qui a créé cet anneau pensait que la fin du monde était proche, elle passait ses journées à graver sur les tables en bois à l'aide de son sertisseur, qu'il n'y avait plus d'avenir pour les habitants d'Amakna. La suite des événements, lui montra qu'elle avait tort." ));
            allItems.Add(new Items ( 6998, "Alliance d\'Elya Wood", "Cette petite alliance est parfaitement adaptée aux petits doigts des Eniripsas. Elle est en revanche presque inutilisable par les Iops à cause de leurs gros doigts boudinés, et leur incapacité à comprendre où se met une alliance." ));
            allItems.Add(new Items ( 6999, "Médaille Lahilama", "Lahilama était un grand chef spirituel qui officiait au fin fond des montagnes. Il s'ennuyait tellement, qu'il passait ses journées à coudre des bonnets jaunes pour ses disciples. La folie n'a pas de limites." ));
            allItems.Add(new Items ( 7000, "Bambamulette", "Ce ridicule petit morceau de bambou est doté de pouvoirs magiques bien étranges. Il était autrefois porté par les jeunes Pandawas, une fois la puberté passée. Vous devriez peut-être attendre encore quelques années avant de le porter autour du coup..." ));
            allItems.Add(new Items ( 7001, "Talisman du Faucheur d\'Avoine", "" ));
            allItems.Add(new Items ( 7002, "La Résinette", "Cette amulette en résine de Bambou diffusera autour de vous une odeur agréable. Très efficace pour dissimuler vos essais de Vents de Panique." ));
            allItems.Add(new Items ( 7003, "Talisman d\'Elya Wood", "Ce Talisman a été confectionné avec de la poussière d'étoiles et de pierres précieuses, prises dans de l'ambre d'Abraknyde Ancestral. Le mélange étant parfois explosif, il est déconseillé d'en fabriquer une vous-même, laissez cela à un Bijoutier que vous n'aimez pas." ));
            allItems.Add(new Items ( 7004, "Moyenne Pierre d\'âme hasardeuse", "Cette rune offre une petite chance de capturer des âmes relativement importantes." ));
            allItems.Add(new Items ( 7005, "Grande Pierre d\'âme hasardeuse", "Cette rune offre une petite chance de capturer des âmes particulièrement farouches..." ));
            allItems.Add(new Items ( 7006, "Enorme Pierre d\'âme hasardeuse", "Cette rune offre une petite chance de capturer des âmes extrêmement hargneuses..." ));
            allItems.Add(new Items ( 7007, "Petite Pierre d\'âme", "Cette rune offre une chance correcte de capturer des âmes de petite taille." ));
            allItems.Add(new Items ( 7008, "Moyenne Pierre d\'âme", "Cette rune offre une chance correcte de capturer des âmes relativement élevées..." ));
            allItems.Add(new Items ( 7009, "Grande Pierre d\'âme", "Cette rune offre une chance correcte de capturer des âmes particulièrement farouches..." ));
            allItems.Add(new Items ( 7010, "Pierre d\'âme pleine", "Cette pierre d'âme contient les âmes d'un groupe de monstres qui ne demandent qu'à revenir à la vie pour se venger de vous. Elle n'est cependant utilisable que dans les arènes prévues à cet effet, situées dans les grandes cités." ));
            allItems.Add(new Items ( 7011, "Cape du Désir O\'Boul", "Cette cape presque vivante en poils de barbe roux est unique en son genre. Animée d'une magie extraordinaire, elle a tendance à jouer de nombreux tours à son porteur, en se cachant toujours au moment de partir à l'aventure, lorsqu'on a besoin d'elle. Elle compense heureusement sa fourberie, par une puissance en combat à toute épreuve, qui fait d'elle une " ));
            allItems.Add(new Items ( 7012, "La Doubitch O\'Boul", "Cette amulette est en fait une confiserie. Elle pourrait être mangée si elle n'avait pas auparavant été roulée sous les aisselles pendant 36 générations, par une horde de femelles Bwork en chaleur." ));
            allItems.Add(new Items ( 7013, "Bois de Bambou", "Contrairement aux roseaux, le bambou plie et casse. Heureusement, ce sont souvent les dents des aventuriers imprudents qui cassent sous les coups de bambous. Parfois, même la nature se met au service des dentistes sans scrupules." ));
            allItems.Add(new Items ( 7014, "Bois de Bambou Sacré", "Contrairement aux apparences, ce bois est sacré. Seuls les meilleurs Pandawas, ont normalement accès à la coupe de ce bois, mais il arrive parfois que des touristes bûcherons, ne se gênent pas pour repartir avec un souvenir local, de leur voyage sur l'île de Pandala. On parle alors de " ));
            allItems.Add(new Items ( 7015, "Pousses de Bambou", "Ces pousses de bambou sont impropres à la consommation, car elles n'ont vraiment aucun goût. Il est donc conseillé de les faucher avec une faux rouillée, ou de les laisser macérer quelques années dans du jus de Gloutovore avant de les servir à vos amis. Encore une vieille pratique empruntée aux restaurateurs peu scrupuleux, prêts à tous les subterfuges pour vous faire avaler n'importe quoi." ));
            allItems.Add(new Items ( 7016, "Bois de Bambou Sombre", "Galthouze le jeune Pandawa avait un jour demandé à son père " ));
            allItems.Add(new Items ( 7017, "Bois de Bambou Doré", "Galthouze le jeune Pandawa avait un jour demandé à son père " ));
            allItems.Add(new Items ( 7018, "Riz", "Collant, ou même gluant, le riz a toujours du succès auprès des jeunes Pandawas affamés." ));
            allItems.Add(new Items ( 7020, "Fleur de Lotus", "On raconte que c'est la plus vieille fleur connue à ce jour, elle symbolise la fécondité, mais aussi le détachement, parce qu'elle émerge parfois de l'eau sans se mouiller. Certains chefs Pandawas se servent de ce symbole pour faire comprendre à leurs fidèles qu'ils doivent vivre dans le monde sans en être affecté." ));
            allItems.Add(new Items ( 7022, "Bâton de Sangroku", "Ce bâton appartenait au grand-père de Sangroku, Sangrokrane, réputé pour avoir la grosse tête. La légende raconte que ce bâton magique est capable de s'allonger pour atteindre n'importe quel ennemi, ou tout simplement pour compenser certains complexes d'infériorité." ));
            allItems.Add(new Items ( 7023, "Pierre d\'Aigue-Marine", "On trouve généralement cette pierre dans les environs de l'île de Pandala. Les Pandawas racontent que cette pierre est dotée de pouvoirs particuliers. Tout le monde en parle, mais personne ne les a encore découverts. Un peu comme les Iops intelligents." ));
            allItems.Add(new Items ( 7024, "Pierre de Topaze", "Cette pierre étrange servait il y a très longtemps, de nourriture aux jeunes Craqueboules de certains éleveurs de Pandala. Ces pierres sont devenues si rares, qu'il est devenu interdit de s'en servir pour nourrir les Craqueboules." ));
            allItems.Add(new Items ( 7025, "Pierre d\'Agathe", "Cette pierre tire son nom d'une rivière de Pandala dans laquelle on la trouvait jadis en abondance. Mais les temps ont bien changé, et l'exploitation intensive de ses gisements, a fini par la rendre presque introuvable." ));
            allItems.Add(new Items ( 7026, "Aigue-Marine", "Cette pierre précieuse transparente et parfois légérement bleutée, est si éclatante, que portée autour du cou, elle permettra de cacher les rides les plus marquées. Les Pandawas n'hésitent d'ailleurs pas à lancer aux touristes : " ));
            allItems.Add(new Items ( 7027, "Topaze", "Très appréciée des bijoutiers pour des raisons un peu douteuses, cette pierre précieuse n'en reste pas moins une valeur sûre pour attirer l'attention des Corbacs au milieu des Plaines de Cania." ));
            allItems.Add(new Items ( 7028, "Agathe", "Cette pierre tient son nom d'une très puissante femelle Pandawa, connue sous le nom d'Agathe Pahtes, célèbre dans les lieux de débauches peu recommandables, comme le bar des Pirates de Sufokia par exemple, où elle passa ses dernières années à danser enchaînée à un gros mât de bateau planté au milieu du bar." ));
            allItems.Add(new Items ( 7029, "Ceinture de Sangroku", "Lorsque Sangroku avait trop abusé des haricots magiques, il serrait bien fort cette ceinture autour de sa taille, pour contenir au maximum, les vents de panique qui l'ont rendu célèbre à travers toute la province d'Amakna." ));
            allItems.Add(new Items ( 7030, "Peau de Kanigrou", "Une fois épilée, la peau de Kanigrou est très douce, un peu comme pour les femelles Sadidas." ));
            allItems.Add(new Items ( 7031, "Pendule du Gudule", "Le pauvre Gudule a perdu la tête suite à un terrible combat avec Many. Heureusement, son amulette elle n'a pas été abimée pendant l'affrontement. Elle a gardé ses pouvoirs hypnotisants, mais aussi laxatifs. A utiliser avec modération." ));
            allItems.Add(new Items ( 7032, "Silicate", "Ce minerai provient sûrement des profondes mines sombres de l'île de Pandala dans lesquelles Gardan Framine est tombé éperdument amoureux d'un spectre de femelle Pandawa. Comme quoi, l'amour ne connaît aucune limite." ));
            allItems.Add(new Items ( 7033, "Dolomite", "" ));
            allItems.Add(new Items ( 7034, "Casque de Maître Nabur", "Transmis de générations en générations, ce casque difforme a même été porté par Crail en personne. Beaucoup d'aventuriers lui prêtent des pouvoirs qu'il n'a pas, caractéristique partagée avec beaucoup de divinités et de héros des temps modernes." ));
            allItems.Add(new Items ( 7035, "Pyrute", "Cet alliage était utilisé pour forger des lames qui ne rouillaient pas au contact du sang et de la bave des ennemis." ));
            allItems.Add(new Items ( 7036, "Rutile", "Cet alliage brillant, est très apprécié des bijoutiers ruinés, qui ne peuvent s'offrir des pierres précieuses et qui en profitent généralement pour confectionner des alliances à un prix défiant toute concurrence. Très pratique lorsque vous tenez à vous marier par intérêt." ));
            allItems.Add(new Items ( 7037, "Canne de Mamie Bonbon", "Cette canne appartenait à Mamie Bonbon en personne, une des trois terribles prêtresses de l'amour. Affûtée à une extrémité et lustrée à une autre, cette canne était surtout célèbre pour sa polyvalence et sa capacité à pouvoir se fourrer n'importe où. Personne ne pouvait y rester insensible." ));
            allItems.Add(new Items ( 7038, "Couronne du Roi Gelax", "Cette couronne a appartenu à l'illustre Roi Gelax, réputé pour son romantisme débordant et son pouvoir de séduction qui a marqué à vie plus d'une Gelée femelle... On raconte même, qu'il est plus ou moins le père de toutes les Gelées de la région d'Amakna, preuve s'il en est, de son endurance et de sa fertilité légendaire." ));
            allItems.Add(new Items ( 7039, "Ceinture Noire de Frank Lee Bruce", "Célèbre combattant du dimanche, Frank s'est reconverti dans les arts martiaux après avoir lamentablement raté son diplôme de tailleur de pierres." ));
            allItems.Add(new Items ( 7040, "Arc de Kuri", "Cet arc a appartenu à Kuri en personne, célèbre prêtresse de l'amour, et bras gauche de Mamie Bonbon. Ne tournez jamais le dos à une prêtresse de l'amour, une seule flèche de cet arc peut suffire à vous déchirer le coeur, voire même les fesses, si vous ne faites pas face à votre adversaire." ));
            allItems.Add(new Items ( 7041, "Pandrista", "Aussi appelée Galop du Pandawa, la Pandrista est une spécialité de Pandala bien connue des voyageurs. Les symptômes : envie pressante de monter sur le trône suivie d'une incapacité à mouler un étron digne de ce nom." ));
            allItems.Add(new Items ( 7042, "Couvre-chef Pandawa", "Contrairement au Pandawa d'Amakna, le Pandawa de Pandala ne vous donnera pas la grosse tête, juste une grosse envie d'aller déposer un engrais naturel sur le végétal le plus proche... Ou à défaut, sur un chacha : c'est tout aussi efficace (pour vous) et surtout plus rigolo." ));
            allItems.Add(new Items ( 7043, "Dofus des Glaces", "La légende dit que ce splendide Dofus est sorti du trou givré d'Aguabrial le dragon de l'Eau. La légende ne dit pas qu'il était enrhumé ce jour-là, mais c'est pourtant facile à deviner." ));
            allItems.Add(new Items ( 7044, "Faux Dofus", "Ce Dofus ne ressemble pas aux autres Dofus et pourtant..." ));
            allItems.Add(new Items ( 7045, "Une fiole de Pandaburg", "La Pandaburg est une boisson goûtue, qui a du retour, et qui paraîtrait-il rendrait aveugle, muet et sourd, mais lentement. Ça tombe bien, les Pandawas ne sont pas pressés." ));
            allItems.Add(new Items ( 7046, "Une fiole de Pandapiler", "Selon l'érudit Hel Munster, cet authentique nectar ravirait la déesse Pandawa elle-même, la rendant toute euphorique. Tout Pandawa qui se respecte devrait offrir ses Pandapilers à sa déesse vénérée." ));
            allItems.Add(new Items ( 7047, "Une fiole de Pandapils", "Etrange cette vieille coutume pandawaïenne qui veut que l'on trinque, une plume de Tofu sur la tête, en chantant : " ));
            allItems.Add(new Items ( 7051, "Une fiole de Pandarium Tremens", "La Pandarium Tremens est la preuve que Pandawa nous aime et veut que nous soyons heureux. Cette prière de remerciement adressée à la déesse, est aussi un gage de paix entre tous les Pandawas." ));
            allItems.Add(new Items ( 7055, "Une fiole de Pandneken", "" ));
            allItems.Add(new Items ( 7056, "Krépite", "Ce chapeau unique a appartenu à un Xélor, connu pour ses idées explosives et sa maîtrise du temps. Il est mort d'une façon assez peu commune en lançant un Vent de Panique décoiffant, et en oubliant que son chapeau lançait de petites étincelles autour de lui. Sa mort fut paraît-il assez douloureuse, mais si lumineuse, qu'on raconte qu'il aurait éclairé la cité de Brâkmar toute entière pendant quelques secondes." ));
            allItems.Add(new Items ( 7057, "Assistant du shaman", "Amenez l'assistant auprès de la déesse, il vous en sera reconnaissant." ));
            allItems.Add(new Items ( 7058, "Octovius", "Cette coiffe a été confectionnée à partir de restes de Kralamoures morts, séchés. Attention, elle risque de reprendre vie si vous vous en servez comme bonnet de bain." ));
            allItems.Add(new Items ( 7059, "Graine de Pandouille", "Cette énorme graine est gorgée d'une huile indigeste, mais magique. Sa coquille est réputée incassable, de nombreux artisans s'en servent pour enchanter des armes, et d'autres l'utilisent comme cochonnet magique pour jouer aux boules." ));
            allItems.Add(new Items ( 7060, "Pandapiler spéciale", "Le sage Lenald vous a donné cette Pandapiler pour l'apporter au Maître Pandawushu de la voie de l'eau." ));
            allItems.Add(new Items ( 7061, "Transformation Chacha", "L'eau de la source jus-sang-ki-eau du Chacha vous a transformé temporairement en Chacha, le moindre choc vous fera redevenir normal." ));
            allItems.Add(new Items ( 7062, "Poochan", "Vous devez amener Poochan où se trouve son futur mari." ));
            allItems.Add(new Items ( 7068, "Farine de Riz", "Cette farine est parfaite pour faire du ciment ou de la litière d'Ouginak." ));
            allItems.Add(new Items ( 7069, "Bouclier de Sidimote enfoncé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
