            allItems.Add(new Items ( 7092, "Cadran d\'Air déréglé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7093, "Cadran de Terre déréglé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7094, "Cadran d\'Eau déréglé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7097, "Bouclier d\'entraînement", "Ce bouclier est donné aux disciples qui ne savent pas encore correctement utiliser un bouclier." ));
            allItems.Add(new Items ( 7098, "Marteau du Forgeur de Boucliers", "Ce marteau est indispensable à la confection de boucliers." ));
            allItems.Add(new Items ( 7102, "Epée du Chevalier de Glace", "Le Chevalier de Glace était connu pour être un des plus grands forgeurs d'épées de tout Amakna. Seule l'horrible et impitoyable Liche refusait de reconnaître son immense talent. Vexé qu'une telle créature puisse ne pas s'émerveiller devant ses créations, il lui posa un ultimatum : il refusa d'exercer sa profession tant qu'elle ne fît pas l'effort de reconnaître publiquement le talent du forgeur d'épées. Mais c'était mal connaître la terrible Liche, qui ne céda point. Le forgeur dû se résoudre à forger à nouveau plusieurs centaines d'années plus tard. Entre temps, il avait complètement oublié son métier, et cette épée ridicule fut sa dernière création utilisable." ));
            allItems.Add(new Items ( 7103, "La Xyothine", "Cette baguette servait à décapiter tous les ploucs d'Amakna. Autant dire qu'elle a beaucoup servi. Il est intéressant de constater qu'elle ressemble à s'y méprendre à un bâton, et même peut-être à une canne... Peut-être était-elle utilisée pour assouvir les vieux fantasmes de son propriétaire, qui a fini par lui donner son nom. Personne ne saura jamais le fin mot de cette histoire sordide." ));
            allItems.Add(new Items ( 7104, "Tongues Baques", "Ces tongues servaient à pratiquer différents arts martiaux. D'un aspect peu innovant et artistiquement ratées, elles sont très vite tombées dans l'oubli, un peu comme vous, mais moins vite tout de même." ));
            allItems.Add(new Items ( 7105, "Amulette de Pandala", "Cette amulette d'apparence trés simple renferme un mystère que seul un Pandawa sur le point de mourir est capable de comprendre ..." ));
            allItems.Add(new Items ( 7106, "Larvamulette", "Avec cette tête de larve autour du cou, vous ressemblez à Ulrik Holgan célèbre dompteur de larves des temps modernes." ));
            allItems.Add(new Items ( 7107, "Larvabottes", "Qui n'a jamais rêvé d'enfoncer son pied directement dans les entrailles d'une larve ? En tout cas sûrement pas vous, alors profitez-en et lâchez-vous." ));
            allItems.Add(new Items ( 7108, "Larvasac", "Ce sac  original mais sans fioritures était très en vogue il y a bien longtemps. Un exemple typique du mauvais goût de vos ancêtres." ));
            allItems.Add(new Items ( 7109, "Larvacoiffe", "Enfin, l'opportunité de ressembler à une vraie larve s'offre à vous. Vous pourrez enfin afficher ouvertement votre mode de vie larvesque." ));
            allItems.Add(new Items ( 7110, "Baguette Larvesque", "Cette terrifiante baguette est en fait un boyau de larve desséché puis enchanté, et enfin réhydraté. Il ne ressemble plus à rien désormais, un peu comme vous en fait, sauf que vous n'êtes même pas enchanté." ));
            allItems.Add(new Items ( 7112, "Dofus Tâcheté", "Seul l'accouplement d'un dragon noir et d'un dragon blanc a pu permettre la création d'un tel œuf. Ou alors il s'agit d'une supercherie." ));
            allItems.Add(new Items ( 7113, "Dofawa", "Ce Dofus ressemble étrangement à un œuf de Dragodinde sauvage... Pour une fois, les apparences ne sont peut-être pas trompeuses." ));
            allItems.Add(new Items ( 7114, "Dofus Ebène", "Sombre comme les ténèbres, dur comme le roc, Grougalorasalar le Dragon Noir a créé le Dofus Ebène pour lui tenir compagnie. Il avait simplement oublié qu'un Œuf, aussi magique fut-il, ne parle pas." ));
            allItems.Add(new Items ( 7115, "Dofus Ivoire", "Si sa blancheur n'a pas d'égal, c'est que Dardondakal le Dragon blanc, blanchit le Dofus Ivoire à grands coups d'haleine chaque matin (un vieux truc que lui a refilé sa grand-mère Dragon)." ));
            allItems.Add(new Items ( 7116, "Anneau Merta", "Cet anneau dispose d'un bien étrange enchantement. En effet, une fois porté autour du doigt, vous ne pourrez plus parler qu'au Bijoutier qui vous l'a confectionné." ));
            allItems.Add(new Items ( 7117, "Alliance Ethnique", "Un groupe de Ménestrels cosmopolites, mais particulièrement mauvais, avaient décidé d'utiliser cette alliance pour se reconnaître dans la foule qui venait assister à leurs représentations. Finalement, cette alliance ne leur fut d'aucune utilité car ils n'eurent aucun succès. Selon certaines mauvaises langues ou bonnes oreilles, c'était bien mérité." ));
            allItems.Add(new Items ( 7118, "Anneau K\'Tuelle", "Cet anneau en forme de gros papillon de nuit ne mettra pas vos mains en valeur, mais ce n'est peut-être pas plus mal..." ));
            allItems.Add(new Items ( 7119, "Anneau Ha", "Le bijoutier à l'origine de cette oeuvre n'a contrairement aux apparences aucun lien de parenté avec un célèbre forgeur d'épées. D'après son créateur, cet anneau est censé vous rappeler l'ambiance de la brousse, sans pour autant sentir les secousses. Personne n'a encore compris de quoi il parlait." ));
            allItems.Add(new Items ( 7120, "Anneau Hé", "Cet anneau appartenait à un vieillard, qui selon certaines rumeurs, aurait sauvé toutes les créatures de l'île de Moon d'un terrible déluge, en les amenant sur son radeau. Une autre version des faits raconte qu'il aurait troqué son radeau contre un petit singe, au lieu de sauver les créatures de l'île." ));
            allItems.Add(new Items ( 7121, "Anneau Zocomial", "Cet anneau vivant dispose d'un terrible envoûtement, capable de rendre malades les aventuriers les plus résistants. On raconte qu'il était à l'origine censé soigner celui qui le porte autour de l'index et qu'il est devenu dangereux et agressif à force de se retrouver contre sa volonté dans des endroits peu recommandables." ));
            allItems.Add(new Items ( 7122, "Anneau Vice", "Anneau des débutants par excellence, c'est également l'anneau symbolisant le mieux un bon nombre de moeurs inavouables. Il semblerait qu'il vous corresponde dans tous les cas, très bien." ));
            allItems.Add(new Items ( 7123, "Anneau Bsène", "Réservé aux aventuriers avertis, cet anneau risque de choquer votre entourage s'il est composé de jeunes créatures innocentes." ));
            allItems.Add(new Items ( 7124, "Dofus au lait", "Cet œuf de Dofus possède un pouvoir hors-pair, celui de vous faire saliver de bonheur." ));
            allItems.Add(new Items ( 7125, "Dofus au chocolat blanc", "Cet oeuf de Dofus possède un pouvoir hors-pair, celui de vous faire saliver de bonheur." ));
            allItems.Add(new Items ( 7128, "Anneau Karina", "Contrairement à ce que certains pensent, cet anneau n'a aucun rapport avec la sulfureuse Karina, qui s'est à maintes reprises, mise en scène d'une façon assez exotique dans le bar des pirates de Sufokia. En réalité, c'est un musicien éleveur de Tofus, qui a donné ce nom à cet anneau, capable une fois porté autour du pouce, d'émettre un son proche de celui que l'on peut entendre lorsque l'on tord le cou d'un Tofu." ));
            allItems.Add(new Items ( 7129, "Panda au chocolat", "Ne vous méprenez pas, il n'y a pas que le chocolat qui donne du goût, la forme de la friandise apporte aussi son lot de saveur. Goûtez une Ouassingue au chocolat et vous comprendrez peut-être mieux." ));
            allItems.Add(new Items ( 7130, "Recette du Dofus en chocolat", "La recette du Dofus en chocolat, un must pour épater vos amis." ));
            allItems.Add(new Items ( 7131, "Anneau Piaume", "Ce bijou a fait rêver plus d'un aventurier avant vous. En effet, on lui prête énormément de vertus, comme celle de transformer les plus belliqueux en doux rêveurs. La notice indique clairement qu'il est déconseillé de le mettre au feu, mais elle ne précise pas pourquoi." ));
            allItems.Add(new Items ( 7132, "Anneau Rigami", "Cet anneau doit son nom aux dernières paroles de son créateur : " ));
            allItems.Add(new Items ( 7133, "Mixomawose", "N'importe quel Wabbit vous le dira, si vous ne voulez pas voir la vie en rouge, évitez comme la peste la Mixomawose." ));
            allItems.Add(new Items ( 7134, "Rage", "On raconte que la rage transforme complètement les êtres atteints. Les personnes belliqueuses deviennent dociles, et les personnes calmes deviennent agressives. En revanche, celles qui bavaient déjà avant continueront de baver." ));
            allItems.Add(new Items ( 7135, "Maloria", "Maladie transmise par les Moskitos qui habitent les régions au climat tropical, la Maloria, est aussi appelée Molaria par certains indigènes, car la maladie semble se transmettre également par les crachats de Moskitos." ));
            allItems.Add(new Items ( 7136, "Médaille Kido", "Cette médaille appartenait à un célèbre Pandawa, maître d'une vieille école d'arts martiaux qui enseignait à ses disciples la défense en utilisant la force de ses adversaires. Son école a fini par disparaître car ses adversaires n'étaient plus assez forts." ));
            allItems.Add(new Items ( 7137, "Cape Huccino", "Cette cape a été cousue dans un seul et unique but, créer une cape capable de maintenir éveillé son porteur pendant plusieurs jours d'affilée. Le résultat est bel et bien présent, elle est si rigide, qu'elle vous gardera debout dans toutes les circonstances. Attention cependant, vous ne serez plus capables de nouer vos lacets." ));
            allItems.Add(new Items ( 7138, "Ortiz", "Portée par les plus grands de ce monde, cette cape est surtout réputée pour sa douceur légendaire, qui lui permet d'être portée sans aucun sous-vêtement lors des saisons chaudes. Une forme raffinée d'exhibitionnisme qui en a séduit plus d'un." ));
            allItems.Add(new Items ( 7139, "Ceinture Sula", "Sula la cordonnière aimait perdre son temps à flâner dans les champs, en quête d'un amour improbable. A défaut de trouver l'être tant convoité, elle trouva la mort en tombant dans une grande fosse remplie d'immondices, près de la zone Cochonne. Son corps ne fût retrouvé que 7 décennies plus tard, au moment de vider la fameuse fosse. Son frère se serait alors écrié : " ));
            allItems.Add(new Items ( 7140, "Xénature", "Contrairement aux apparences, Xéna, n'était pas une guerrière, mais une cordonnière. Ses formes généreuses et sa passion sans limite pour les tâches ménagères la prédestinaient à une brillante carrière dans les brasseries d'Amakna. Le destin en a décidé autrement." ));
            allItems.Add(new Items ( 7141, "Holoune", "Ce chapeau étrange vous protègera des rayons du soleil, même la nuit. Attention, il reprend vie une fois toutes les trois décennies, et se retourne contre son maître en tentant de l'étouffer pendant son sommeil." ));
            allItems.Add(new Items ( 7142, "Piruk", "Ce chapeau était utilisé par certains Xélors, pour cacher leur calvitie naissante. Certains Enutrofs s'en servaient même pour cacher leur femme. Une façon habile de cacher deux problèmes majeurs de la vie de tous les jours." ));
            allItems.Add(new Items ( 7143, "Solomonk", "Le Solomonk était le chapeau d'un vieux chef d'une Tribu de Pandawas, experts en potions laxatives. Les Pandawas en question étant souvent obligés de tester eux-mêmes les effets de leurs potions, ils utilisaient ce type de chapeau, qui à défaut de les mettre en valeur, faisait un excellent pot de chambre." ));
            allItems.Add(new Items ( 7144, "Blémiche", "Cette coiffe de mariée est passée de mode depuis bien longtemps, peut-être à cause de sa dentelle qui date d'une autre ère. Recyclée en culotte, et portée sur une tête bien garnie en cheveux, elle pourrait cependant mettre en transe, le plus blasé des Chafers." ));
            allItems.Add(new Items ( 7145, "Coiffe du GAddie", "Créature étrange sortie d'un monde imaginaire, le GAddie se démarquait avant tout par son extraordinaire ruse, son aversion totale pour le règne végétal, et sa faculté à jeûner pendant plusieurs jours en temps de guerre. Une créature aussi complexe à comprendre était de toute évidence impossible à maîtriser, on la laissa donc vivre en paix dans les mines sans fond de Kruup, où elle fit connaissance avec une vieille Liche dévoreuse de Bouftons." ));
            allItems.Add(new Items ( 7146, "La Bertheuze", "Cette coiffe surpuissante tient plus de la perruque qu'autre chose, mais qu'importe, elle est surpuissante. En effet, son propriétaire aimait se travestir et se faire passer pour les autres, afin de mieux profiter des personnes naïves. Ses jeux lugubres et glauques finirent par être découverts, tout comme son crâne, une fois sa coiffe retirée. Son immense talent d'artiste lui sauva la vie, puisqu'il se proposa quelques jours avant sa mise à mort, pour sculpter une statue géante à l'effigie des parties intimes du Meulou. Il fût tout de même brûlé vif une fois la statue finie." ));
            allItems.Add(new Items ( 7149, "Carré de chocolat noir", "Extrait du cacao brut, ce carré de chocolat en sublime la saveur. Attention à ne pas en abuser sous peine de problème de digestion." ));
            allItems.Add(new Items ( 7150, "La Bekille", "Ce chapeau au nom surprenant, appartenait à un pauvre Ecaflip, qui avait perdu l'usage de la parole suite à un terrible traumatisme. Artiste dans l'âme, et dans le sang, il fût contraint de suivre une formation de Brasseur. Il se noya dans un fût de bière et de larmes, vers son quatorzième anniversaire. Heureusement, la bière ne fut pas perdue pour autant, puisque le prêtre responsable des obsèques, tomba dans un coma éthylique pendant plusieurs années avant de reprendre son office là où il s'était arrêté." ));
            allItems.Add(new Items ( 7151, "Koloss", "Le slogan publicitaire de ce casque était : " ));
            allItems.Add(new Items ( 7152, "Kanistère", "Ce chapeau en forme de cuve servait aux pompiers d'Amakna lors des incendies de champs de céréales. De nos jours, il sert avant tout aux Brasseurs et aux serveuses de taverne, qui servent de fûts sur pattes." ));
            allItems.Add(new Items ( 7153, "Marteau Hubohu", "Cet énorme marteau fait un bruit incroyable lorsqu'il tape sur un crâne de Iop, car il a tendance à amplifier les résonances. Surprenant, mais terriblement efficace." ));
            allItems.Add(new Items ( 7154, "Marteau M\'Pouce", "Ce léger marteau, était destiné à la base aux petits êtres : les Xélors utilisaient le manche, et les Araknes recevaient la masse sur la tête. Il a vite été détourné de son usage et constitue maintenant un petit jouet pour les jeunes Iops bricoleurs." ));
            allItems.Add(new Items ( 7155, "Marteau Nairedeubrest", "Le célèbre Capitaine Kradoc a utilisé pendant des dizaines d'années ce terrible marteau afin d'occire tous ceux qui se mettaient en travers de son chemin, y compris femmes et enfants. Son mauvais caractère légendaire et son goût prononcé pour l'alcool, en ont tout de même fait une personne très respectée des différents corps d'armées, ennemis, comme alliés." ));
            allItems.Add(new Items ( 7156, "Marteau Ronto", "Ce marteau rouillé est parfait pour éclater les érables, ou les Moskitos." ));
            allItems.Add(new Items ( 7157, "Marteau Ksain", "Ce marteau était autrefois utilisé pour faire sonner la cloche de l'église d'Amakna. De nos jours, il sert toujours à faire de la musique, en broyant les os des imprudents par exemple." ));
            allItems.Add(new Items ( 7158, "Marteau Lahre", "Conçu par un alchimiste de renom pendant un séjour dans les prisons de Brâkmar, ce marteau devait à l'origine servir à casser le matériel d'alchimie de ses concurrents." ));
            allItems.Add(new Items ( 7159, "Marteau Pinière", "Ce marteau de mineur est utilisable comme une pelle ou pioche selon vos besoins. Son utilisation en jardinage est cependant proscrite, car il a tendance à détruire de manière définitive les pelouses." ));
            allItems.Add(new Items ( 7160, "Arc Hanne", "Cet arc a été enchanté il y a bien longtemps par une magie des plus obscures. De quoi inquiéter autant son propriétaire que ses ennemis." ));
            allItems.Add(new Items ( 7161, "Arc Assin", "Cet arc était avant tout utilisé pour chasser les Sangliers des Plaines. En effet, il est si puissant, qu'il permet à l'aide d'une seule flèche, de tuer plusieurs petits marcassins, à condition qu'ils soient correctement alignés." ));
            allItems.Add(new Items ( 7162, "Arc Lavoine", "Cet arc appartenait à un ménestrel indépendant, surtout célèbre, pour détruire les oreilles de ses ennemis avec ses chants de guerre. Il fut exilé pendant plusieurs années sur Pandala, à cause de ses chants qui étaient capables de rendre sourds les Corbacs de Brâkmar. Après avoir rendu fous plusieurs générations de Pandawas, il fut tout simplement livré vivant au grand Sorlok, pour être digéré lentement pendant plus de 752 années. Il y découvrit une nouvelle forme de plaisir intense." ));
            allItems.Add(new Items ( 7163, "Arc Huledela", "Cet arc était avant tout utilisé comme une arme de dissuasion. Son propriétaire expliquait à qui voulait l'entendre que ses flèches étaient empoisonnées, ce qui ne manquait pas d'effrayer la population locale. Ainsi, il arrivait à faire reculer tous les pleutres qui se mettaient en travers de son chemin." ));
            allItems.Add(new Items ( 7164, "Arc Hisedaisange", "Cet arc léger et fragile a séduit bon nombre d'aventuriers, plus attachés aux apparences qu'à l'efficacité." ));
            allItems.Add(new Items ( 7165, "Baguette Nah", "Cette baguette explosive a été trempée dans la lave d'un volcan en éruption avant d'être sculptée, puis enchantée. Il va sans dire qu'elle semble très puissante, malgré son apparence ridicule." ));
            allItems.Add(new Items ( 7167, "Baguette Cetera", "L'artisan à l'origine de cette baguette avait de nombreux apprentis qu'il faisait travailler à longueur de journées. Il était censé leur apprendre les ficelles du métier, mais était en fait très mauvais pédagogue, il commençait toujours ses explications, mais ne les finissait jamais." ));
            allItems.Add(new Items ( 7168, "Baguette Hylique", "Une fois sculptée, cette baguette a été trempée dans la bière pendant plusieurs années. Elle a déjà commencé à pourrir, mais elle garde cependant encore quelques propriétés magiques, suffisantes pour divertir ou exterminer vos alliés." ));
            allItems.Add(new Items ( 7169, "Baguette Hincelle", "Idéale pour allumer le feu, ou animer les mariages ennuyeux, cette baguette vous permettra peut-être une fois dans votre vie, de faire des étincelles. Une occasion à saisir." ));
            allItems.Add(new Items ( 7170, "Baguette Houffe-Craitien", "Houffe le pâtissier était fou au point de se marier avec la bûcheronne Craitien, et de l'étouffer le lendemain avec ses pâtisseries trop grasses. On parle donc depuis cet événement, " ));
            allItems.Add(new Items ( 7171, "Baguette Ourderie", "Le sculpteur de cette baguette avait la fâcheuse habitude d'oublier ses enfants à l'école. Sa femme, une mère indigne comme tant d'autres, s'en réjouissait. Conscient d'être trop étourdi, il sculpta cette baguette qui tous les jours, prenait vie et lui rappelait d'aller chercher ses enfants. Il a par la suite tenté de faire des baguettes similaires pour rappeler à sa femme d'aller faire le ménage, mais la baguette ne fut jamais assez puissante pour relever ce défi." ));
            allItems.Add(new Items ( 7172, "Baguette Rhon", "Cette baguette magique est encore de nos jours utilisée pour vider les fosses sceptiques, curer les fossés, et accessoirement pour se curer le nez. Les sculpteurs les plus avertis, vous expliqueront qu'elle tire toute sa magie, des immondices qu'elle a pu côtoyer, surtout celles présentes dans votre nez." ));
            allItems.Add(new Items ( 7173, "Baguette Rangleuse", "Cette terrifiante baguette est capable de s'enrouler autour du cou de vos adversaires pour les étrangler lentement mais sûrement. Une façon originale et distinguée de donner la mort aux adversaires que vous respectez un peu." ));
            allItems.Add(new Items ( 7174, "Cape Pandawushu", "Cette cape est indispensable à l'entraînement au Pandawushu..." ));
            allItems.Add(new Items ( 7175, "Certificat shamanique", "Certificat médical indispensable pour la pratique du Pandawushuka." ));
            allItems.Add(new Items ( 7176, "Racine Cithi", "C'est un certain Frank, sculpteur de bâtons de son état, qui serait à l'origine de cette petite merveille, capable d'assombrir le ciel et de faire couler le sang des ennemis qui n'en n'ont pas. On raconte qu'il aurait mis plus de mille heures pour la sculpter. Même vos ennemis ne pourront y rester indifférents." ));
            allItems.Add(new Items ( 7177, "Bandeau Pandawushu", "Avec ce bandeau Pandawushu noué autour du crâne, vous pourrez sans problème effrayer les jeunes Lenalds, en vous faisant passer pour un Maître en arts martiaux. N'oubliez pas de pousser quelques cris de guerre pour paraître encore plus crédible." ));
            allItems.Add(new Items ( 7178, "Racine Horodon", "Depuis des générations, les Sadidas utilisent des racines magiques comme bâtons. Cependant, il semblerait que certains d'entre eux, se refusent encore à utiliser celle-ci, qui serait paraît-il, plus proche du fruit que de la racine. Fruit ou racine, qu'importe, elle fait très mal là où elle se glisse et c'est l'essentiel." ));
            allItems.Add(new Items ( 7179, "Route et Chemin : Forêt Maudite", "Ce livre regroupe les meilleurs itinéraires touristiques à faire dans le labyrinthe de la forêt maudite...un Must ! Du moins c'est ce que la jaquette annonce." ));
            allItems.Add(new Items ( 7181, "Racine Sémilla", "Sémilla était une jeune Sadidette, très intéressée par le jardinage et accessoirement, une piètre chanteuse. Elle prenait d'ailleurs régulièrement un malin plaisir à recevoir des râteaux, mais bizarrement, beaucoup moins de pelles." ));
            allItems.Add(new Items ( 7182, "Racine Hécouanone", "Hécouanone n'avait pas de chance, son mari était un gros fainéant, incapable d'assumer son statut (pourtant mérité par sa condition) de chef de famille. Elle était en permanence obligée de lui fixer des conditions : " ));
            allItems.Add(new Items ( 7183, "Racine Huzohide", "Cette racine a des formes pures, que certains artisans n'hésiteront pas à qualifier de parfaites. C'est peut-être pour cela que certains aventuriers y attachent plus d'importance qu'à leur dulcinée." ));
            allItems.Add(new Items ( 7184, "Racine Histre", "Cette terrible racine, est avant tout connue pour déclencher des catastrophes climatiques aux endroits précis où elle est utilisée. Evitez donc de vous en servir pour botter les fesses de vos adversaires, on ne sait pas ce qu'il pourrait arriver..." ));
            allItems.Add(new Items ( 7185, "Racine Hécure", "Utiliser cette racine en combat est censé être assez simple. Parfois, elle reprend vie et décide de n'en faire qu'à sa tête. C'est dans ces moments-là qu'il est possible d'entendre son propriétaire crier : " ));
            allItems.Add(new Items ( 7186, "Passage de grade", "Sur cette feuille est inscrit la dernière date de votre passage de grade Pandawashuka." ));
            allItems.Add(new Items ( 7187, "Jakchir Arc", "Cet arc est réputé pour voler l'argent du peuple à distance, et en toute discrétion. Le plus surprenant c'est qu'il reste tout de même très populaire au fil des années. Heureusement pour le peuple, il commence déjà à s'user." ));
            allItems.Add(new Items ( 7188, "Dagues Rhumes", "Ces dagues sont parfaites pour couper les fruits, ou pour couper l'herbe sous les pieds de vos adversaires. Une  façon originale de les surprendre." ));
            allItems.Add(new Items ( 7189, "Rod Gerse", "Ce bâton étrange, est une version améliorée du célèbre God Rod. En effet, ce bâton a été imaginé et confectionné après les nombreux tests du God Rod effectués par les Prêtresses de l'Amour. Ce modèle a même été utilisé et usé par Kuri en personne, ce qui en fait en quelque sorte une pièce unique." ));
            allItems.Add(new Items ( 7190, "Dagues Maydhyn China", "Ces dagues sublimes ont été confectionnées sur mesure, pour une Sramette en manque de sensations fortes. En effet, déçue de ne pas trouver de dagues à sa convenance, elle lança un défi à tous les forgeurs de dagues d'Amakna : celui qui réussirait à lui forger des dagues capables de tuer sur le coup, n'importe quel Tofu, pourrait passer 3 nuits en sa compagnie. Contrairement à ce qu'elle pensait, cette annonce n'eut pas l'effet escompté, puisque personne ne participa au défi. Elle dut attendre une centaine d'années, pour qu'un forgeur de dagues Enutrof à la retraite, soit intéressé par la récompense qui avait bien changé avec le temps..." ));
            allItems.Add(new Items ( 7191, "Sertisseur de Ramougre", "Ramougre était un vieux Sram, devenu maître dans la confection de bijoux. Il se servait à la fois de ce sertisseur pour ses créations, mais aussi pour assassiner ses propres clients, lorsqu'il s'ennuyait trop dans sa bijouterie. Il était connu pour son très mauvais caractère, qui le poussait parfois à détruire avec son sertisseur, ses plus belles créations. Aujourd'hui, les utilisateurs de ces dagues savent lui rendre hommage, puisqu'il les utilisent essentiellement pour trancher les bijoux de famille." ));
            allItems.Add(new Items ( 7192, "Griffe de Grizmine", "Grizmine n'a jamais réussi à utiliser correctement cette griffe. A force de jouer aux cartes et aux jeux d'argent tout en buvant de la bière, ses muscles ont vite fini par s'atrophier. Chez certaines personnes, il semblerait que ce type de loisirs puisse même atrophier d'autres organes." ));
            allItems.Add(new Items ( 7193, "Epée du Dark Vlad", "Seul le Dark Vlad en personne est à même de faire ressurgir toute la magie de cette épée, pour la rendre réellement étincelante. En la portant, vous pourrez fièrement montrer que vous n'avez pas fini comme ces ridicules aventuriers qui ont péri sous sa lame." ));
            allItems.Add(new Items ( 7194, "Arc Anum", "Cet arc est la preuve que l'association de la technologie et de la magie, peut donner d'excellents résultats. Pourtant, cet arc est tout de même tombé rapidement dans l'oubli pour des raisons encore méconnues. L'artisan à l'origine de cette arc a d'ailleurs été contraint de fermer sa boutique, ce qui n'a pas manqué d'attrister bon nombre de ses anciens clients." ));
            allItems.Add(new Items ( 7195, "Karnak", "Cette épée légendaire a été forgée au petit matin, par une armée d'Enutrofs saouls, dans le fond des mines de Grimoth. Elle dégage une aura de puissance surprenante, mais aussi une odeur de bière éventée." ));
            allItems.Add(new Items ( 7197, "Marteau Bleuronne", "Ce marteau servait à l'origine à casser les tablettes de chocolat, pour en faire de la poudre. Il a donc acquis au fil des années, une odeur et un goût délicieux. Une raison de plus, pour aller le faire goûter à vos ennemis." ));
            allItems.Add(new Items ( 7198, "Razoir du Soir", "Tous les vieux Enutrofs vous le diront, une belle barbe est une barbe entretenue. Sa lame est si finement aiguisée, qu'elle conviendra même aux dames un peu trop poilues." ));
            allItems.Add(new Items ( 7199, "Glaive Icération", "Ce glaive appartenait à Metzger le boucher, qui s'en servait pour récupérer rapidement les entrailles de ses ennemis pour en faire de la chair à saucisse qu'il revendait à bon prix sur les marchés de Bonta." ));
            allItems.Add(new Items ( 7200, "Sabre Shodanwa", "Ce magnifique sabre est utilisé par les disciples Pandawushuka lorsqu'ils réussissent à atteindre le grade Shodanwa sans être morts entre temps." ));
            allItems.Add(new Items ( 7201, "Sabre Nidanwa", "Ce magnifique sabre est utilisé par les disciples Pandawushuka lorsqu'ils réussissent à atteindre le grade Nidanwa sans être morts entre temps." ));
            allItems.Add(new Items ( 7202, "Sabre Sandanwa", "Ce magnifique sabre est utilisé par les disciples Pandawushuka lorsqu'ils réussissent à atteindre le grade Sandanwa sans être morts entre temps." ));
            allItems.Add(new Items ( 7203, "Sabre Yondanwa", "Ce magnifique sabre est utilisé par les disciples Pandawushuka lorsqu'ils réussissent à atteindre le grade Yondanwa sans être morts entre temps." ));
            allItems.Add(new Items ( 7204, "Bâton du Shodanwa", "Ce bâton est en règle générale utilisé par les disciples Pandawushuka qui ont réussi à atteindre le grade de Shodanwa sans perdre l'usage de leurs bras entre temps." ));
            allItems.Add(new Items ( 7205, "Bâton du Nidanwa", "Ce bâton est en règle générale utilisé par les disciples Pandawushuka qui ont réussi à atteindre le grade de Nidanwa sans perdre l'usage de leurs bras entre temps." ));
            allItems.Add(new Items ( 7206, "Bâton du Sandanwa", "Ce bâton est en règle générale utilisé par les disciples Pandawushuka qui ont réussi à atteindre le grade de Sandanwa sans perdre l'usage de leurs bras entre temps." ));
            allItems.Add(new Items ( 7207, "Bâton du Yondanwa", "Ce bâton est en règle générale utilisé par les disciples Pandawushuka qui ont réussi à atteindre le grade de Yondanwa sans perdre l'usage de leurs bras entre-temps." ));
            allItems.Add(new Items ( 7208, "Hache du Shodanwa", "Cette hache est utilisée par les disciples Pandawushuka, lorsqu'ils obtiennent le grade de Shodanwa. Elle est également très pratique pour ravager les forêts de Bambous et tous ceux qui s'y cachent." ));
            allItems.Add(new Items ( 7209, "Hache du Nidanwa", "Cette hache est utilisée par les disciples Pandawushuka, lorsqu'ils obtiennent le grade de Nidanwa. Elle est également très pratique pour ravager les forêts de Bambous et tous ceux qui s'y cachent." ));
            allItems.Add(new Items ( 7210, "Hache du Sandanwa", "Cette hache est utilisée par les disciples Pandawushuka, lorsqu'ils obtiennent le grade de Sandanwa. Elle est également très pratique pour ravager les forêts de Bambous et tous ceux qui s'y cachent." ));
            allItems.Add(new Items ( 7211, "Hache du Yondanwa", "Cette hache est utilisée par les disciples Pandawushuka, lorsqu'ils obtiennent le grade de yondanwa. Elle est également très pratique pour ravager les forêts de Bambous et tous ceux qui s'y cachent." ));
            allItems.Add(new Items ( 7212, "La Trancheuse de Bambou", "Légère et élégante, cette hache vous permettra de progresser facilement dans les forêts de Bambou de Pandala,  et de vous défendre efficacement contre les Pandis les plus fourbes, toujours prêts à attaquer les bûcherons isolés pour de sombres histoires de bourses..." ));
            allItems.Add(new Items ( 7213, "Pelle Rhon", "Cette pelle est censée fendre l'air à une vitesse assez incroyable. Ceux qui savent correctement s'en servir, fendent surtout les crânes de leurs ennemis avec." ));
            allItems.Add(new Items ( 7214, "Sandales Macien", "Ces sandales appartenaient aux Maciens, une tribu d'Ouginaks, disparue aujourd'hui. Ces derniers étaient très complices avec les Pandawas, ce qui avait fini au fil des générations, par changer la couleur de leur pelage, qui était devenu blanc, tâcheté de noir. Ils furent tous transformés en manteau par une cruelle tailleuse." ));
            allItems.Add(new Items ( 7215, "Sandales Humeuses", "Ces sandales étaient utilisées par certaines femelles Pandits, pour séduire les aventuriers égarés, et les attirer dans d'horribles pièges." ));
            allItems.Add(new Items ( 7216, "Sandales Koliques", "Ces sandales étaient données aux Pandawas malades, qui avaient besoin de courir très vite lorsqu'ils avaient contracté le virus de la Pandrista, ou lorsqu'ils avaient passé une nuit trop arrosée à la taverne." ));
            allItems.Add(new Items ( 7217, "Sandales Hambic", "Les Pandawas ont toujours été attirés par les boissons alcoolisées, ils ont ainsi au fil du temps, appris à mieux les conserver et les aromatiser. Quant à ces sandales, elles ont été faites spécialement pour les Pandawas alcooliques, elles sont lestées au niveau de la semelle, pour que vous puissiez garder les pieds sur terre plus souvent." ));
            allItems.Add(new Items ( 7218, "Sandales Adin", "Adin était un jeune Pandawa, grand collectionneur de lampes, mais très loin d'être un génie." ));
            allItems.Add(new Items ( 7219, "Sandales Ailuya", "Ces sandales étaient confiées aux Pandits à la fin de rituels religieux. Elles sont de nos jours surtout utilisées dans les championnats de claquettes." ));
            allItems.Add(new Items ( 7220, "Ticket de loterie", "Vous avez entre les mains, un superbe ticket de loterie, gardez-le précieusement, il se pourrait que vous soyez l'heureux élu du très grand tirage au sort !" ));
            allItems.Add(new Items ( 7221, "Collier du Tanukouï San", "Ce collier est porté par le Tanukouï San depuis sa tendre enfance, ce qui lui confère une valeur ajoutée non négligeable lorsque l'on sait de quoi il était capable, même durant son enfance." ));
            allItems.Add(new Items ( 7222, "Artefact Pandawushu Eau", "Cet artefact magique est utilisé par les disciples Pandawushu pour enchanter leur équipement." ));
            allItems.Add(new Items ( 7223, "Artefact Pandawushu Air", "Cet artefact magique est utilisé par les disciples Pandawushu pour enchanter leur équipement." ));
            allItems.Add(new Items ( 7224, "Artefact Pandawushu Terre", "Cet artefact magique est utilisé par les disciples Pandawushu pour enchanter leur équipement." ));
            allItems.Add(new Items ( 7225, "Artefact Pandawushu Feu", "Cet artefact magique est utilisé par les disciples Pandawushu pour enchanter leur équipement." ));
            allItems.Add(new Items ( 7226, "Chapeau Akwadala", "Avec ce chapeau sur la tête, vous ressemblez de loin à un Pandawushuka du village d'Akwadala. De près, vous ne ressemblez à rien." ));
            allItems.Add(new Items ( 7227, "Chapeau Terrdala", "Avec ce chapeau sur la tête, vous ressemblez de loin à un Pandawushuka du village de Terrdala. De près, vous ne ressemblez à rien." ));
            allItems.Add(new Items ( 7228, "Chapeau Feudala", "Avec ce chapeau sur la tête, vous ressemblez de loin à un Pandawushuka du village de Feudala. De près, vous ne ressemblez à rien." ));
            allItems.Add(new Items ( 7229, "Chapeau Aerdala", "Avec ce chapeau sur la tête, vous ressemblez de loin à un Pandawushuka du village d'Aerdala. De près, vous ne ressemblez à rien." ));
            allItems.Add(new Items ( 7230, "Cape Akwadala", "Cette cape qui vient du village d'Akwadala, peut servir de paillasson, ou même de serpillière si vous venez de sortir de l'eau." ));
            allItems.Add(new Items ( 7231, "Cape Terrdala", "Cette cape qui vient du village de Terrdala, peut servir de paillasson, ou même de serpillière si vous venez de sortir de l'eau." ));
            allItems.Add(new Items ( 7232, "Cape Feudala", "Cette cape qui vient du village de Feudala, peut servir de paillasson, ou même de serpillière si vous venez de sortir de l'eau." ));
            allItems.Add(new Items ( 7233, "Cape Aerdala", "Cette cape qui vient du village d'Aerdala, peut servir de paillasson, ou même de serpillière si vous venez de sortir de l'eau." ));
            allItems.Add(new Items ( 7234, "Bouclier Akwadala englouti", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7235, "Bouclier Terrdala enterré", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7236, "Bouclier Feudala brulé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7237, "Bouclier Aerdala envolé", "Ce brave bouclier a vu trop de batailles, il n'est plus bon à rien dans son état. Peut-être que Bouwada, au village Pandala, pourra vous le remplacer." ));
            allItems.Add(new Items ( 7238, "Ceinture Akwadala", "Les Pandawas du village d'Akwadala ont toujours voulu garder la taille fine. En serrant au maximum cette ceinture, vous devriez arriver au même résultat." ));
