            allItems.Add(new Items ( 7379, "Etoffe de Fantôme Pandikaze", "Cet amas de poils est presque intact malgré la punition que vous avez dû infliger au pauvre Fantôme Pandikaze, pour la lui arracher." ));
            allItems.Add(new Items ( 7380, "Etoffe de Fantôme Maho Firefoux", "Cette étoffe magique de couleur rousse est imprégnée d'une magie surpuissante. On ne peut pas en dire autant de vous." ));
            allItems.Add(new Items ( 7381, "Etoffe de Fantôme Soryo Firefoux", "Cette étoffe de taille modeste est parfaite pour boucher les trous sous les vieilles portes, lorsque l'hiver approche." ));
            allItems.Add(new Items ( 7382, "Etoffe de Fantôme Léopardo", "Cette étoffe n'est pas aussi douce que la musique du Léopardo, et pourtant, elle devrait pouvoir vous servir. Mais à quoi ? Là est la question." ));
            allItems.Add(new Items ( 7383, "Etoffe de Fantôme Yokai Firefoux", "Cette étoffe provient des parties les plus sensibles du Fantôme Yokai Firefoux, qui sont également les plus malodorantes. Beaucoup d'aventuriers pensent qu'il s'agit de ses moustaches, mais ils se trompent." ));
            allItems.Add(new Items ( 7384, "Etoffe de Fantôme Pandule", "Contrairement aux idées reçues, cette étoffe n'est pas mangeable. Les derniers qui ont essayé de la cuisiner se sont transformés en Tanukouï San. Une aubaine pour certains d'entre eux." ));
            allItems.Add(new Items ( 7385, "Etoffe de Fantôme Tanukouï San", "Cette étoffe provient directement des aisselles du Fantôme Tanukouï San, réputées pour leur douceur, mais aussi pour leur odeur, proche dans le meilleur des cas, de celle que dégage la viande de Corbac en putréfaction." ));
            allItems.Add(new Items ( 7386, "Testicules du Tanukouï San", "Ces énormes testicules sont recouverts d'un très doux duvet qui prévient à merveille les attaques magiques, mais aussi des coups d'échasses que le Tanukouï San se donne lorsqu'il ne fait pas attention, ou lorsqu'il entre en transe lors de l'invocation d'une femelle Tanuki Chan. Dans tous les cas, vous n'avez pas n'importe quoi entre les mains." ));
            allItems.Add(new Items ( 7387, "Le clan des Mercenaires", "Tout, tout, tout vous saurez tout sur les Mercenaires ! Un ouvrage écrit par le fondateur et Grand-Maître du Clan." ));
