            allItems.Add(new Items ( 7403, "Os de Fantôme Léopardo", "Ce petit os pointu était autrefois utilisé par les Brigandins pour décorer leurs narines. Une façon élégante de mettre en valeur un nez crochu par exemple." ));
            allItems.Add(new Items ( 7404, "Os de Fantôme Maho Firefoux", "Cet os est encore imprégné d'une magie que l'on peut qualifier d'artisanale, ne vous étonnez donc pas, si cet os reprend vie et cherche à retrouver les restes de son propriétaire." ));
            allItems.Add(new Items ( 7405, "Os de Fantôme Pandikaze", "Cet os est tout ce qu'il reste du fantôme que vous venez d'exterminer. Pas de chance pour vous, aucune magie ne semble l'habiter." ));
            allItems.Add(new Items ( 7406, "Os de Fantôme Pandore", "Cet os ne sort pas de la boîte du Pandore et pourtant, tout porte à croire que si." ));
            allItems.Add(new Items ( 7407, "Os de Fantôme Pandule", "Finement taillé, cet os peut sans problème, servir d'aiguille pour une horloge géante. Tout simplement indispensable." ));
            allItems.Add(new Items ( 7408, "Os de Fantôme Soryo Firefoux", "La première fois que le Soryo Firefoux s'est cassé un os, il a tenté de se venger sur le premier venu. Au même moment, Francisque Kabroule, ménestrel de son état, se promenait en chantant et croisait le Soryo Firefoux. La suite de l'histoire, personne ne la connaît, tout ce que l'on sait, c'est qu'après ça, sa voix était devenue encore plus aiguë." ));
            allItems.Add(new Items ( 7410, "Os de Fantôme Tanukouï San", "A force de supporter le poids incommensurable de ses énormes testicules, les os du Tanukouï San ont fini par devenir incroyablement résistants." ));
            allItems.Add(new Items ( 7411, "Os de Fantôme Yokai Firefoux", "Cet os aurait pu servir de massue pour fendre les crânes des aventuriers insolents, ou même de marteau pour enfoncer les Enutrofs dans le sol comme de vulgaire asperges. Mais depuis qu'il est entre vos mains, il semblerait que son destin soit différent..." ));
