            allItems.Add(new Items ( 7413, "Parchemin ", "(Double clic pour apprendre cette emote) nCe parchemin vous permet d'apprendre l'attitude " ));
            allItems.Add(new Items ( 7414, "Petit Chacha Blanc", "Cette petite boule de poils griffe, mord et pince même la main qui la nourrit. C'est donc une créature terrifiante, mais tout de même irrésistiblement attachante et fascinante. Toute ressemblance avec des personnes de la gent féminine ne serait pas fortuite. Attention, cette créature en plus de son caractère irascible est très difficile sur le plan alimentaire, puisqu'elle ne consomme que de la viande issue de créatures chassées." ));
            allItems.Add(new Items ( 7415, "Petit Chienchien Noir", "Cette étrange petite créature ne se nourrit que de poissons fraîchement pêchés. Mais ne vous inquiétez pas, sa compagnie agréable compense largement son haleine de poisson déshydraté." ));
