            allItems.Add(new Items ( 7423, "Oeuf de Larve Dorée", "Aussi étrange que cela puisse paraître, les oeufs de Larves Dorées ne sont pas pondus par des Larves Dorées, mais par des larves plus communes, de couleur verte, orange ou encore bleue. La nature est décidément pleine de mystères." ));
