            allItems.Add(new Items ( 7499, "Potion de Village des Kwaks de Glace", "Cette potion vous permet de vous téléporter dans le village des Kwaks de Glace, si ce dernier vous appartient." ));
            allItems.Add(new Items ( 7500, "Potion du Village des Kwaks de Feu", "Cette potion vous permet de vous téléporter dans le village des Kwaks de Feu, si ce dernier vous appartient." ));
            allItems.Add(new Items ( 7501, "Potion du Village des Kwaks de Vent", "Cette potion vous permet de vous téléporter dans le village des Kwaks de Vent, si ce dernier vous appartient." ));
            allItems.Add(new Items ( 7502, "Potion de Ralliement", "Cette potion permet de créer un point de ralliement, pour permettre aux aventuriers alignés de vous rejoindre, pour organiser une attaque ou une défense de village par exemple. Cette potion magique permet également de sauvegarder la position des personnes qui vous rejoignent, pour leur permettre de repartir rapidement jusqu'à leur point de départ initial." ));
            allItems.Add(new Items ( 7505, "Potion d\'oubli de métier : Costumage", "Cette potion vous fera oublier le métier de Costumage." ));
            allItems.Add(new Items ( 7506, "Potion d\'oubli de métier : Joaillomage", "Cette potion vous fera oublier le métier de Joaillomage." ));
            allItems.Add(new Items ( 7507, "Potion d\'oubli de métier : Cordomage", "Cette potion vous fera oublier le métier de Cordomage." ));
            allItems.Add(new Items ( 7508, "Rune de Signature", "Cette rune permet aux artisans les plus doués, de signer leurs créations. n(à utiliser lors de la fabrication de l'objet en plus des ingrédients)" ));
            allItems.Add(new Items ( 7509, "Première partie de la clef du labyrinthe du Dragon Cochon", "Si ce groin ne vous dit rien, alors ne cherchez pas à en faire quelque chose, sous peine de recevoir une claque mortelle, surtout si vous êtes un peu étourdi." ));
            allItems.Add(new Items ( 7510, "Deuxième partie de la clef du labyrinthe du Dragon Cochon", "Ce morceau est peut-être une partie de la clef qui ouvre le coffre au trésor dont vous avez toujours rêvé. A moins que ce ne soit la clef de votre tombeau." ));
            allItems.Add(new Items ( 7511, "Clef du labyrinthe du Dragon Cochon", "Si vous espérez rencontrer beaucoup de grosses cochonnes avec cette clef vous vous fourrez le doigt dans l'oeil. Elle ne vous permettra que d'ouvrir le labyrinthe du Dragon Cochon. Attention, elle sera détruite après utilisation." ));
            allItems.Add(new Items ( 7513, "Sandales Titude", "Titude était un modeste Enutrof cordonnier, plus petit encore qu'un Xélor. On raconte qu'il aurait confectionné ces sandales pour enfin réussir à embrasser sa dulcinée, une Eniripsa cul-de-jatte, mais que cela ne suffit point, et qu'il dut se résoudre à ne pas l'embrasser debout." ));
            allItems.Add(new Items ( 7514, "Bottes Harry", "Harry était un vieux sage, qui passait ses journées à pêcher sur les quais de Sufokia. Sa grande moustache lui donnait un air de Danous Kouaide, célèbre acteur d'une troupe de théatre qui se produisait régulièrement à la taverne d'Amakna en guise de prélude à d'incroyables orgies à base de bière et de Bougnette." ));
            allItems.Add(new Items ( 7515, "Cape Itaineflam", "Cette cape ne vient pas de votre galaxie et pourtant,  elle semble assortie à la couleur de vos bas résille en laine de Boufton." ));
            allItems.Add(new Items ( 7516, "Coiffe du Bimd\'Oule", "C'est un vieil Enutrof, connu sous le nom de Dhy-Ah-Mhanhd, aigri et rongé par des siècles de malchance qui fit le premier, la découverte de cette terrible coiffe, cachée au fond d'une alcôve dans les grottes de Bimd'Oule. Plus tard, l'Enutrof s'exila loin des terres d'Amakna pour tenter d'échapper à l'ennui et découvrir de nouveaux horizons. La légende raconte qu'il revient sur ses terres natales une fois par siècle, pour se moquer de ses anciens amis, mais surtout par nostalgie." ));
            allItems.Add(new Items ( 7517, "Bière du Ripate", "Cette bière est si acide, que la chope dans laquelle elle est servie a une espérance de vie digne d'un Moskito." ));
            allItems.Add(new Items ( 7518, "Fotome", "Cette surprenante créature vient de l'au-delà et n'est pas prête d'y retourner, sauf si malencontreusement, vous deviez l'y accompagner..." ));
            allItems.Add(new Items ( 7519, "Dragoune Rose", "Ce petit dragon a la peau aussi rose que les fesses d'une femelle Bwork. Un bon moyen de vous faire fantasmer à moindre frais et sans risques, ou tout simplement de vous rappeler de bons souvenirs. Heureusement pour lui, ce dragon n'est pas uniquement l'objet de vos fantasmes, mais avant tout une créature très puissante et sage. Attention tout de même, sa voracité n'a d'égal que la perversité d'un Chafer les soirs de pleine lune, et il ne se nourrit que d'autres familiers." ));
            allItems.Add(new Items ( 7520, "Croum", "Ancien soldat de plomb retraité, cette créature en métal est devenue vivante. Elle vous suivra partout et fera tout pour vous défendre au péril de sa vie, si vous prenez la peine de lui donner à manger régulièrement. Mais n'espérez pas qu'elle prenne pour autant les coups à votre place, elle ne remplacera donc pas les aventuriers débutants d'Astrub." ));
            allItems.Add(new Items ( 7521, "Cadeau Fotome", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 7522, "Minimino", "Ce bébé Minotoror apprivoisé et nettoyé peut être approché et caressé, sans risquer de perdre une main. Cependant, restez prudents, la nature reprend parfois ses droits et il se pourrait que l'instinct destructeur de cette créature refasse surface à un moment où vous comptiez sur lui. Pour éviter les mauvaises surprises, élevez-le comme vos propres enfants, avec des coups de bâtons." ));
            allItems.Add(new Items ( 7523, "Chachate Rousse", "Cette adorable créature rousse est capable de faire craquer tous les matous, même les plus blasés. n(CE FAMILIER LÉGENDAIRE N'A PAS BESOIN DE MANGER)" ));
            allItems.Add(new Items ( 7524, "Chacha Tigré", "Cette surprenante créature résulte du croisement entre un Chacha blanc et un Chacha noir. Elle ne mange que du poisson." ));
