            allItems.Add(new Items ( 7552, "Capodindo", "Parfois, lorsqu'un guerrier et une Dragodinde s'unissent, il peut se produire de bien étranges événements. nCette cape en est la preuve vivante, puisqu'il s'agit des ailes d'une Dragodinde cousues sur une cape. La Dragodinde qui s'est sacrifiée pour cette cape, l'a offerte au célèbre ménestrel Lonne Jenone, le jour de leur union. Francisque Kabroule en aurait même fait une chanson." ));
            allItems.Add(new Items ( 7553, "Chapodindo", "Vous qui avez toujours rêvé de ressembler à une Dragodinde, c'est le moment ou jamais. Cependant, si vous voulez conserver votre bonne santé, nous vous conseillons d'éviter de vous promener dans les rues d'Amakna durant la période de Noël." ));
            allItems.Add(new Items ( 7554, "Botodindo", "Vos copines vont vous envier ces bottes, car nul doute qu'elles vous permettront d'aller croquer les chocolats de l'ambassadeur durant l'une des ses merveilleuses soirées." ));
            allItems.Add(new Items ( 7555, "Anodindo", "Grâce à cet anneau, vos amies diront de vous " ));
            allItems.Add(new Items ( 7556, "Cadeau Dindo", "Ce paquet cadeau contient une partie d'un célèbre déguisement. De quoi rendre folle de jalousie vos amies les Dragodindes." ));
            allItems.Add(new Items ( 7557, "Clef du Donjon Abraknyde", "Cette clef en écorce d'Abraknyde pourrait prendre racine, si vous la déposiez dans un endroit chaud et humide, comme un slip de Bwork par exemple." ));
