            allItems.Add(new Items ( 7879, "L\'avènement D\'Hyrkul", "Les Annales Colossales, Immorales et pas Banales de Domen font froid dans le dos. A lire en respectant les doses prescrites. Effets secondaires : sueurs froides et incontinence nocturne." ));
            allItems.Add(new Items ( 7880, "Amulette du Boufcoul", "Cette amulette était jadis portée par tous les aventuriers qui préféraient répandre l'amour plutôt que de faire la guerre. Chacun ses goûts." ));
            allItems.Add(new Items ( 7881, "Anneau du Boufcoul", "Cet anneau vous permettra de faire encore plus mal, lorsque vous donnerez des fessées bien méritées à vos enfants." ));
            allItems.Add(new Items ( 7882, "Marteau du Boufcoul", "Ce marteau est idéal pour imprimer votre pacifisme sur le crâne de vos adversaires." ));
            allItems.Add(new Items ( 7883, "Bottes du Boufcoul", "Rembourrées avec de la laine de Boufcoul, ces bottes ont tout pour plaire. Légères, chaudes pendant les saisons froides, et aérées pendant les saisons chaudes, elles sauront satisfaire le plus blasé des aventuriers." ));
            allItems.Add(new Items ( 7884, "Cape du Boufcoul", "Si vous aimez prendre la vie du bon côté, cette cape est faite pour vous. En effet, elle est si épaisse et confortable qu'elle peut servir de lit improvisé si la fatigue commence à vous gagner. Son odeur de Kaliptus fumé lui permet même de repousser les insectes, les créatures indésirables ou encore les belles-mères." ));
            allItems.Add(new Items ( 7885, "Ceinture du Boufcoul", "Portée autour des hanches, cette ceinture vous permettra de passer pour un aventurier pacifiste et ainsi de mieux surprendre vos adversaires en leur assenant un coup d'épée dans le dos." ));
            allItems.Add(new Items ( 7886, "Coiffe du Boufcoul", "Avec cette coiffe sur le crâne, vous n'aurez pas l'air plus astucieux qu'avant, c'est une certitude. En revanche, vous aurez l'air beaucoup plus " ));
            allItems.Add(new Items ( 7887, "Coiffe du Koulosse", "Cette coiffe a appartenu au célèbre Koulosse, réputé pour ses pieds d'argile et sa bonne humeur légendaire." ));
            allItems.Add(new Items ( 7888, "Sac du Koulosse", "Ce sac permet au Koulosse de garder au frais ses feuilles de Kaliptus ou d'étouffer les Koalaks un peu trop entreprenants." ));
            allItems.Add(new Items ( 7889, "Bottes du Koulosse", "Ces bottes ont appartenu au Koulosse, et à défaut de se voir, cela peut se sentir. Ces bottes sont si confortables, qu'elles vous feront presque oublier votre misérable vie." ));
            allItems.Add(new Items ( 7890, "Bâton du Koulosse", "Ce bâton est en réalité un os vidé, d'un des ancêtres du Koulosse. Percé aux deux extrémités, il peut servir de pipe à Kaliptus, mais aussi de bâton de berger. Rien n'arrête le progrès." ));
            allItems.Add(new Items ( 7891, "Mini Wa", "Ce petit Wabbit, est en réalité un fils caché du tristement célèbre Wa Wabbit. Peut-être qu'il montera un jour à son tour sur le trône." ));
            allItems.Add(new Items ( 7892, "Willy le Relou", "Willy le Relou est vraiment une méchante créature. Son enfance a semble-t-il été particulièrement difficile. Il vous accompagnera partout et fera fuir vos amis." ));
            allItems.Add(new Items ( 7893, "Fantôme de Mini Wa", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 7894, "Fantôme de Willy le Relou", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 7895, "Certificat de Mise en Chanil : Mini Wa", "Ce certificat décrit les caractéristiques du Mini Wa que vous avez placé au chanil." ));
            allItems.Add(new Items ( 7896, "Certificat de Mise en Chanil : Willy le Relou", "Ce certificat décrit les caractéristiques du Willy le Relou que vous avez placé au chanil." ));
            allItems.Add(new Items ( 7897, "Cadeau Mini Wa", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner. n(ATTENTION, ce cadeau contient des objets très lourds, vous risquez d'être en surcharge lorsque vous l'ouvrirez, pensez à vous délester de vos objets lourds ou a vous faire aider d'un ami.)" ));
            allItems.Add(new Items ( 7898, "Cadeau Willy le Relou", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner. n(ATTENTION, ce cadeau contient des objets très lourds, vous risquez d'être en surcharge lorsque vous l'ouvrirez, pensez à vous délester de vos objets lourds ou a vous faire aider d'un ami.)" ));
            allItems.Add(new Items ( 7899, "Le Mini Wa : Bien l\'élever", "Ce livre écrit par Oshimo Li'kross Babine explique comment élever un Mini Wa." ));
            allItems.Add(new Items ( 7900, "Willy le Relou : Bien l\'élever", "Ce livre écrit par Oshimo Li'kross Babine explique comment élever un Willy le Relou." ));
            allItems.Add(new Items ( 7902, "Ceinture d\'une Femme en Or", "Une vieille légende extraite du fameux grimoire " ));
            allItems.Add(new Items ( 7903, "Feuille de Kaliptus", "Délicatement insérées au fond des narines, ces feuilles déboucheront n'importe quel nez. Certaines légendes tribales racontent que cette technique fonctionnerait avec d'autres orifices." ));
            allItems.Add(new Items ( 7904, "Fleur de Kaliptus", "Une fois brûlée, cette fleur dégage une fumée épaisse aux vertus hallucinogènes. Mâchée, elle a tendance à rendre impuissant. Consommée en infusion, elle peut engendrer d'importants effets laxatifs. La meilleure solution reste de ne jamais y toucher." ));
            allItems.Add(new Items ( 7905, "Laine du Boufcoul", "Cette laine ressemble à s'y méprendre à de la laine de Bouftou et pourtant il n'en n'est rien, puisqu'elle ne sent pas mauvais." ));
            allItems.Add(new Items ( 7906, "Corne du Boufcoul", "Une vieille légende explique que la poudre extraite de ces cornes aurait des vertus aphrodisiaques. Cette même légende précise qu'elle peut également être source d'effets secondaires assez surprenants. Une raison de plus pour essayer." ));
            allItems.Add(new Items ( 7907, "Cuir du Boufcoul", "Ce cuir multicolore permet de confectionner des sous-vêtement en cuir multicolores. De quoi rendre jaloux le plus blasé les Chafers." ));
            allItems.Add(new Items ( 7908, "Clef du Donjon du Koulosse", "Cette clef en os vous permettra d'accéder au donjon du Koulosse sans y avoir été invité au préalable." ));
            allItems.Add(new Items ( 7911, "Koalak Sanguin", "Ce petit Koalak a l'air inoffensif, mais il cache bien son jeu. Martyrisé pendant sa tendre enfance, il a reçu de nombreux boomerangs dans les dents ou même dans le derrière. Il cultive depuis ce temps-là une aversion totale pour les boomerangs, et les dévore sans même les mâcher." ));
            allItems.Add(new Items ( 7913, "Le Diviseur (MJ)", "Cet objet mysterieux, oublié de tous, appartenait à un grand mage... Il permettait parait-il de parler avec certains animaux..." ));
            allItems.Add(new Items ( 7917, "Cadeau de Compensation", "Afin de s'excuser des récents problèmes que vous avez rencontrés, l'équipe d'Ankama vous offre ce paquet cadeau qui comprend : n- 5 Bonbons de Compensation du Sage n- 5 Bonbons de Compensation du Chanceux n- 10 Bonbons de Compensation Energétiques n- 10 Poudres d'Eniripsas" ));
            allItems.Add(new Items ( 7918, "Clef du Donjon des Tofus", "Cette clef confectionnée à partir d'une plume de Tofu, est capable de chatouiller l'intérieur de certaines serrures... Accessoirement, elle vous permettra d'accéder au Tofulailler de Farle Ingalsse." ));
            allItems.Add(new Items ( 7919, "Jeton du Maître de Jeu", "Ce splendide jeton doit sûrement pouvoir être échangé à la Foire du Trool contre de précieux cadeaux." ));
            allItems.Add(new Items ( 7920, "Baguette du Tournoi (MJ)", "Cette modeste baguette permet aux Maîtres de Jeu de téléporter des aventuriers dans une zone réservée aux tournois." ));
            allItems.Add(new Items ( 7921, "Couronne du Tofu Royal", "Cette couronne est assez difficile à attraper, elle file entre les doigts. Aucun doute, c'est bien la Couronne du Tofu Royal. En revanche, elle ne semble pas du tout pouvoir s'assortir avec votre petite tête, ne parlons même pas de celle d'un Iop !" ));
            allItems.Add(new Items ( 7922, "Peluche du Dopeul Sacrieur", "Cette peluche est faite pour les jeunes Sacrieurs, elle est parfaite pour éponger leur sang et se nettoie facilement à n'importe quelle température." ));
            allItems.Add(new Items ( 7923, "Peluche du Dopeul Pandawa", "Cette peluche sent l'alcool et le poil de Pandawa mouillé, comme maman." ));
            allItems.Add(new Items ( 7924, "Clef du Labyrinthe du Minotoror", "Cette magnifique clef vous permettra de rentrer dans le Labyrinthe du Minotoror, mais pas d'en sortir..." ));
            allItems.Add(new Items ( 7925, "Bois de Kaliptus", "Ce bois parfumé permet de construire des maisons qui sentent bon, même si leurs propriétaires sentent mauvais. Malheureusement, ce bois n'est pas assez souple pour la confection des sous-vêtements." ));
            allItems.Add(new Items ( 7926, "Clef du Donjon du Maître Corbac", "Cette clef vous permettra de rencontrer le Maître Corbac en personne. Ce dernier vous invitera sûrement à partager un morceau de fromage en sa compagnie." ));
            allItems.Add(new Items ( 7927, "Clef du Donjon des Craqueleurs", "Cette clef est presque incassable, mais très usée, elle a ouvert de nombreuses portes. Elle semble avoir été confectionnée à partir des parties intimes d'un Craqueleur. Tout simplement effrayant." ));
            allItems.Add(new Items ( 7929, "Manuel du Mercenaire d\'Astrub", "Le Manuel du Mercenaire d'Astrub, écrit par le fameux Badufron, chef mercenaire d'Astrub est le livre de chevet de tous ceux qui aiment respecter la loi ou la contourner." ));
            allItems.Add(new Items ( 7930, "Se faire des Amis et les garder", "Conseils et astuces pour se faire de bons et loyaux amis par Acidrik Fenlapanse" ));
            allItems.Add(new Items ( 7931, "Le temps des dragons - fragments retrouvés III", "Tome III des Fragments de l'Histoire du Monde, retrouvés par l'érudit Acidrik Fenlapanse, et réécrits par lui." ));
            allItems.Add(new Items ( 7932, "Parchemin Miss Astrub", "La plantureuse Miss Astrub de cette année nous dévoile les charmes avec lesquels elle a séduit le jury." ));
            allItems.Add(new Items ( 7934, "Voyager sans jamais perdre le Nord", "Arpagnagrobis a écrit cet ouvrage pour tous les aventuriers ne sachant point se servir de leur boussole et des zaaps." ));
            allItems.Add(new Items ( 7935, "Broche Saphir", "Ce bijou est resté accroché pendant des mois dans une chevelure épaisse. Une inscription explique qu'il peut servir de clef si d'autres broches sont réunies..." ));
