            allItems.Add(new Items ( 7996, "Sac de Bois de Kaliptus", "Cet énorme sac contient une quantité impressionnante de bois de Kaliptus." ));
            allItems.Add(new Items ( 7999, "Capananas", "Certains aventuriers racontent que cette cape est aussi rugueuse que la peau d'un Crocodaille, et d'autres racontent même qu'elle est encore plus rugueuse que la peau des fesses d'une femelle Bwork. Quoi qu'il en soit, faites attention à la porter du bon côté, si vous aussi, vous ne voulez pas finir par avoir les fesses rugueuses." ));
            allItems.Add(new Items ( 8000, "Bworky", "Cette petite créature est un Bwork, resté enfermé vivant dans un bocal pendant plusieurs décennies. Il a donc pris la forme du bocal, et restera nain toute sa vie. Sa bonne humeur et son humour légendaire en font le compagnon idéal pour les donjons perdus d'avance, ou les soirées mondaines endiablées. Attention, c'est un adepte des apéritifs, et il ne supporte que les noix et noisettes, même s'il ne rechigne pas avaler un gland de temps en temps." ));
            allItems.Add(new Items ( 8001, "Bandelette du Guerrier Koalak", "Cette bandelette normalement enroulée le long des bras du Guerrier Koalak, lui permet de se moucher le nez sans utiliser de mouchoirs. Lorsqu'elle a beaucoup servi, cette bandelette peut-être accrochée à un plafond pour coller les Moskitos de passage dans les habitations." ));
            allItems.Add(new Items ( 8002, "Etoffe de Dok Alako", "Cette étoffe semble imprégnée d'une magie ancestrale difficilement prévisible et maîtrisable. Encore un point commun avec votre dulcinée." ));
            allItems.Add(new Items ( 8003, "Amulette du Koalak", "Cette amulette ne ressemble à aucune autre amulette, tout simplement car aucune amulette ne lui ressemble." ));
            allItems.Add(new Items ( 8004, "Anneau du Koalak", "Ce magnifique anneau est porté par les Koalaks les plus téméraires, ceux qui ont été assez courageux pour le voler aux aventuriers d'Astrub à la sortie de la banque." ));
            allItems.Add(new Items ( 8005, "Arc du Koalak", "Cet arc surprenant permet de voler la vie de ses adversaires, et parfois même, de voler vos adversaires à la vie, ce qui ne signifie rien en soi, mais reste tout de même très impressionnant." ));
            allItems.Add(new Items ( 8006, "Bottes du Koalak", "Ces bottes sont en réalité des pieds de Koalaks, sauvagement arrachés à leur propriétaires, puis vidés de leur contenu avant d'être rembourrés avec des poils de Koalaks. A la fois terrifiantes et séduisantes, ces bottes feront de vous la terreur d'Astrub." ));
            allItems.Add(new Items ( 8007, "Cape du Koalak", "Cette cape vous protégera de la majorité des attaques, mais pas du ridicule." ));
            allItems.Add(new Items ( 8008, "Ceinture du Koalak", "Cette ceinture en feuilles de Kaliptus diffuse une délicieuse odeur, capable d'en masquer d'autres. Elle est en outre parfaite pour masquer vos rondeurs." ));
            allItems.Add(new Items ( 8009, "Coiffe du Koalak", "Cette coiffe est ornée d'énormes oreilles de Koalak, qui permettent d'amplifier les sons émis par les créatures qui vous entourent. Attention, elle peut détruire vos tympans si vos amis sont adeptes des vents de panique." ));
            allItems.Add(new Items ( 8010, "Démonteur", "Permet de récupérer un équipement installé dans un enclos." ));
            allItems.Add(new Items ( 8011, "Les Prophètes, les cultes, la Foi", "Quelques bonnes histoires de cultes qui raviront le mystique de base." ));
            allItems.Add(new Items ( 8012, "Poudre de résurrection", "Cette poudre permet de ramener à la vie un familier fantôme, à condition de savoir comment nourrir un fantôme de familier." ));
            allItems.Add(new Items ( 8013, "Transformation Fantôme Chacha", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8016, "Fantôme de Koalak Sanguin", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8017, "Fantôme de Bworky", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
