            allItems.Add(new Items ( 8049, "Transformation Fantôme Mini Wa", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8050, "Peau de Koalak Immature", "Le petit Koalak Immature à qui vous avez arraché cette peau, n'était pas d'accord pour vous la donner, mais qu'importe, vous étiez plus fort et plus cruel que lui." ));
            allItems.Add(new Items ( 8052, "Peau de Maître Koalak", "Cette peau vielle et usée recouvrait les fesses du Maître Koalak. Elle comporte encore quelques belles traces rouges, témoins d'une vie agitée et un peu folle." ));
            allItems.Add(new Items ( 8053, "Peau de Chevaucheur Koalak", "Cette peau déchirée a été usée contre celle de la monture du Chevaucheur Koalak.  De toute évidence, c'est un  gage de qualité." ));
            allItems.Add(new Items ( 8054, "Peau de Drakoalak", "Cette peau devrait vous rappeler l'odeur des étables du château d'Amakna. Très pratique pour effrayer les âmes sensibles, et les tuer avant qu'elles ne décident de vous accompagner dans un donjon." ));
            allItems.Add(new Items ( 8055, "Os de Mama Koalak", "La Mama Koalak possède des os particulièrement résistants qui ne cèdent pas sous son poids. On ne peut pas en dire autant de Francisque Kabroule, le célèbre Ménestrel, qui perdit l'usage de ses deux bras en se faisant écraser par les énormes cuisses de la Mama Koalak. La curiosité est un vilain défaut." ));
            allItems.Add(new Items ( 8056, "Fémur de Koalak Farouche", "Une fois taillé en forme de dague, cet os peut faire office de dague, ou de perce-furoncle." ));
            allItems.Add(new Items ( 8057, "Tibia de Koalak Fossoyeur", "Cet os peut servir de pelle pour déterrer les cadavres et les dépouiller, si vous aimez passer vos vacances près des cimetières. Accessoirement, il sert de tibia au Koalak Fossoyeur." ));
            allItems.Add(new Items ( 8058, "Cubitus de Momie Koalak", "Cet os a gardé toute sa splendeur grâce aux nombreuses bandelettes de la Momie Koalak, qui protègent des attaques magiques les plus puissantes." ));
            allItems.Add(new Items ( 8059, "Poils de Koalak Griotte", "Ces poils sont recouverts de pollen de Blop, ce qui leur donne un goût délectable, mais une odeur déplorable." ));
            allItems.Add(new Items ( 8060, "Poils de Koalak Koko", "Ces poils sont recouverts de pollen de Blop, ce qui leur donne un goût délectable, mais une odeur déplorable." ));
            allItems.Add(new Items ( 8061, "Poils de Koalak Reinette", "Ces poils sont recouverts de pollen de Blop, ce qui leur donne un goût délectable, mais une odeur déplorable." ));
            allItems.Add(new Items ( 8062, "Poils de Koalak Indigo", "Ces poils sont recouverts de pollen de Blop, ce qui leur donne un goût délectable, mais une odeur déplorable." ));
            allItems.Add(new Items ( 8063, "Queue de Koalak Sanguin", "Cette ridicule petite queue aurait pû vous appartenir, tant vous êtes insignifiant. Et pourtant il n'en n'est rien, estimez-vous heureux." ));
            allItems.Add(new Items ( 8064, "Boomerang du Koalak Sanguin", "Ce morceau de bois d'une forme étrange a voyagé longtemps dans les airs et entre les rangs ennemis avant de se briser. On raconte qu'il revient toujours vers son propriétaire une fois lancé, mais rarement dans ses mains..." ));
            allItems.Add(new Items ( 8065, "Poils de Barbe du Warko Marron", "Ces poils sont urticants, assez pour transformer les bises du Warko Marron en armes de destruction massives." ));
            allItems.Add(new Items ( 8066, "Poils de Barbe du Warko Violet", "Ces poils de barbe sont urticants, ce qui fait de cette barbe à titre de comparaison, une arme de guerre bien plus destructrice que votre haleine de Bwork. Tout simplement effrayant." ));
            allItems.Add(new Items ( 8067, "Bottes du Champion", "Certains aventuriers racontent que ces bottes permettent de marcher sur les pièges sans les déclencher. Attention, c'est peut-être un piège..." ));
            allItems.Add(new Items ( 8068, "Cape du Champion", "Cette splendide cape dispose d'épaulettes intégrées afin de vous donner plus de prestance pour enfin ressembler à un véritable champion." ));
            allItems.Add(new Items ( 8069, "Coiffe du Champion", "Cette imposante coiffe vous donnera de toute évidence la grosse tête, si vous ne l'avez pas déjà." ));
            allItems.Add(new Items ( 8070, "Ceinture du Champion", "Avec cette ceinture autour des hanches, vous cacherez facilement ce qui ne vous met pas en valeur." ));
            allItems.Add(new Items ( 8071, "Mini Champion", "Cette petite créature n'accepte de suivre que les champions, elle risque donc de vous suivre quelques jours, pour ensuite s'apercevoir que vous êtes un imposteur. Dès lors, c'est vous qui la suivrez comme un petit familier." ));
            allItems.Add(new Items ( 8072, "Dofus Kaliptus", "Personne ne connaît le dragon à l'origine de ce Dofus. On raconte juste qu'il aurait ingéré une quantité effroyable de feuilles de Kaliptus, avant de pondre et de mourir d'une terrible crise d'aérophagie." ));
            allItems.Add(new Items ( 8073, "Clef du Repaire de Skeunk", "Cette clef vous a été donnée par Rigroulak en échange d'une panoplie du Koulosse, elle vous permettra de rentrer dans le repaire de Skeunk." ));
            allItems.Add(new Items ( 8075, "Boomerang du Dok Alako", "Ce morceau de bois d'une forme étrange a voyagé longtemps dans les airs et entre les rangs ennemis avant de se briser. On raconte qu'il revient toujours vers son propriétaire une fois lancé, mais rarement dans ses mains..." ));
            allItems.Add(new Items ( 8076, "Boomerang du Maître Koalak", "Ce morceau de bois d'une forme étrange a voyagé longtemps dans les airs et entre les rangs ennemis avant de se briser. On raconte qu'il revient toujours vers son propriétaire une fois lancé, mais rarement dans ses mains..." ));
            allItems.Add(new Items ( 8077, "Boomerang du Warko Marron", "Ce morceau de bois d'une forme étrange a voyagé longtemps dans les airs et entre les rangs ennemis avant de se briser. On raconte qu'il revient toujours vers son propriétaire une fois lancé, mais rarement dans ses mains..." ));
            allItems.Add(new Items ( 8078, "Planche en Bois de Kaliptus", "Cette planche permet aux bricoleurs de construire des trucs en bois, comme un cercueil en planches par exemple." ));
            allItems.Add(new Items ( 8079, "Transformation Fantôme Petit Chacha Blanc", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8080, "Transformation Fantôme Petit ChienChien Noir", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8081, "Sac de Bois de Charme", "Cet énorme sac contient une quantité impressionnante de Bois de Charme." ));
            allItems.Add(new Items ( 8082, "Etoffe du Fauchalak", "Cette étoffe sent la mort, alors que vous sentez le mort. La différence est notable." ));
            allItems.Add(new Items ( 8083, "Os de Pékeualak", "Cet os est spongieux, ce qui lui permet de flotter si le Pékeualak tombe à l'eau en essayant de remonter une grosse prise." ));
            allItems.Add(new Items ( 8084, "Peau de Piralak", "Cette peau recouverte de balafres est devenue plus résistante que la roche, tout en gardant une souplesse remarquable. C'est la peau idéale pour les martinets et les sandales de mauvais goût." ));
            allItems.Add(new Items ( 8085, "Poils de Koalak Forestier", "Ces poils sont recouverts de sève de Kaliptus, ce qui leur confère un aspect luisant et brillant. Malheureusement, ils sont inutilisables en haute couture à cause de leur odeur de Dragodinde morte." ));
            allItems.Add(new Items ( 8086, "Coiffe déchiquetée de Drakoalak", "Cette étrange coiffe cachait jadis la calvitie naissante du Drakoalak. Avec un peu de travail, elle pourra peut-être bientôt cacher la vôtre." ));
            allItems.Add(new Items ( 8087, "Sort Maîtrise de la Hache", "(Double clic pour apprendre le sort) nCe parchemin vous permettra de faire plus de dégâts en utilisant une hache." ));
            allItems.Add(new Items ( 8088, "Baguette Gyver", "Cette baguette faite de bric et de broc renferme une énergie surprenante. On raconte qu'elle est capable de se déplacer elle-même, pour choisir sa cible et défendre son maître même lorsque ce dernier est en train d'agoniser. Le Chouque aurait même dit à propos de cette baguette " ));
            allItems.Add(new Items ( 8089, "Baguette Iots", "Iots était un perroquet qui répétait toujours tout, on raconte que cette baguette fut sculptée dans son bec." ));
            allItems.Add(new Items ( 8090, "Bâton des Caraïbes", "Ce bâton a déjà mordu des gens, ou été mordu par des gens, ou des gens sont mordus de lui, on ne sait pas trop." ));
            allItems.Add(new Items ( 8091, "La Canne Bière", "Ce bâton vient du sud, et par tous les chemins, il y revient." ));
            allItems.Add(new Items ( 8092, "Les Dagues du Bouwin", "Le bouwin n'est pas fin, le bouwin n'est pas malin, mais quand il a ses dagues en main, personne ne cherche à provoquer le bouwin." ));
            allItems.Add(new Items ( 8093, "Dagues Ristocrates", "Porter ces dagues ne fera pas de vous un héros, mais si vous les rangez dans vos sous-vêtements, vous finirez peut-être par ressembler à une héroïne." ));
            allItems.Add(new Items ( 8094, "L\'Epée Rilleuse", "Cette épée illustre bien un vieux proverbe Iop, quand l'ennemi périt, l'épée rit." ));
            allItems.Add(new Items ( 8095, "Le Sabre Ayassalama", "Cette épée fut forgée en visant uniquement l'esthétisme afin de décorer une cheminée de grande valeur, mais son efficacité lors des tueries lui vaut d'être maniée régulièrement. Les gens n'ont décidément aucun respect pour l'art." ));
            allItems.Add(new Items ( 8096, "Marteau Mawoque", "Son précédent propriétaire dansait avec les Mulous. Il est mort dévoré." ));
            allItems.Add(new Items ( 8097, "Marteau Xiko", "Ce marteau fait voir la vie en rose avant de vous réduire le crâne en bouillie." ));
            allItems.Add(new Items ( 8098, "Lame Usicke", "Cette hache à la forme exceptionnelle, dispose d'une lame vibrante qui permet d'achever ses ennemis en musique." ));
            allItems.Add(new Items ( 8099, "La Hache Menbien", "Cette arme a été trempée dans une fontaine de foudre pure puis saupoudrée de poussière d'étoile, mais il paraît que c'est entièrement faux." ));
            allItems.Add(new Items ( 8100, "La Hache Ticot", "Cette hache est percée de trous en divers endroits, ce qui accompagne ses coups d'élégants sifflements guillerets, cachant les cris de douleur de vos ennemis. C'est regrettable." ));
            allItems.Add(new Items ( 8101, "Le Fendoir Tichaud", "Cette hache fut forgée par Brouce Boulgoure lorsqu'il s'essayait à la boulangerie pour faire plaisir à son ami Xavier le boulanger. Il voulait faire une pelle à tarte." ));
            allItems.Add(new Items ( 8102, "Pierre du Craqueleur Légendaire", "On dit de cette pierre qu'elle pourrait être le coeur du Craqueleur Légendaire. Ce qui est certain c'est que le Craqueleur Légendaire n'avait jamais le coeur lourd, cette pierre est donc d'une légèreté incroyable, ce qui permet d'en faire des objets adaptés aux personnages qui recherchent avant tout la mobilité, l'agilité et protection. Tout simplement génial." ));
            allItems.Add(new Items ( 8103, "Arc Rainier", "Cet arc est en réalité la chevelure fossilisée d'un Sadida endormi trop profondément, sur laquelle des Araknes ont tissé leurs toiles." ));
            allItems.Add(new Items ( 8104, "Arc Chtelion", "Cet arc d'une couleur discutable est parfait pour parader au Zaap de Bonta et à défaut de vous faire passer pour un coq, il attirera toutes les poulettes. Tantôt renversant, tantôt révulsant, il viendra peut-être à bout de vos ennemis sans que vous ne décochiez la moindre flèche." ));
            allItems.Add(new Items ( 8106, "Potion d\'oubli de métier : Bricoleur", "Cette potion vous fera oublier le métier de Bricoleur." ));
            allItems.Add(new Items ( 8107, "Livre Honorifique des Bardes", "Ce livre a été remis solennellement à Nolliuos, pour avoir remporté le concours de Bardise de l'an 636. Qu'il soit témoigné ici de ses mérites et de son talent ! Hujikan, Doux-Leurre, et Madahine sont également présents dans ce recueil. Longue vie à eux et à leur plume !" ));
            allItems.Add(new Items ( 8108, "Amulette du Tofu", "Légère comme une plume de Tofu, cette amulette se porte autour du cou ou du front, pour les aventuriers en quête d'originalité." ));
            allItems.Add(new Items ( 8109, "Anneau du Tofu", "Cet anneau vraiment insignifiant tant il est ridicule, peut tout de même se révéler indispensable. En effet, vos ennemis risquent d'avoir tellement pitié de vous, qu'ils vous achèveront, plutôt que de vous laisser souffrir atrocement. Il faut toujours voir le bon côté des choses." ));
            allItems.Add(new Items ( 8110, "Baguette du Tofu", "Contrairement à ce que certains aventuriers peuvent croire, cette baguette n'est pas utilisée par les Tofus (bien trop stupides pour savoir se servir d'une telle arme), mais contre les Tofus. En effet, les plumes de Tofu qui la composent permettent aux chasseurs de simuler la parade nuptiale des Tofus pour ensuite mieux les attraper. Mais jusqu'où iront ces terribles chasseurs pour satisfaire leurs pulsions meurtrières ?" ));
            allItems.Add(new Items ( 8111, "Pantoufles du Tofu", "A la fois souples et légères, ces pantoufles restent chaudes l'hiver et fraîches l'été. Malheureusement, elles ne supportent pas les lavages en machine." ));
            allItems.Add(new Items ( 8112, "Cape du Tofu", "Cette étrange cape semble avoir été coupée en deux. Mais pourquoi aurait-elle était coupée en deux ? Personne ne le sait, même si tout le monde a son avis sur la question." ));
            allItems.Add(new Items ( 8113, "Ceinture du Tofu", "Idéale pour étrangler les Tofus, cette ceinture permet également aux chasseurs de fouetter les aventuriers qui s'aventurent sur leur domaine en période de chasse. Il est donc déconseillé de plaisanter pas avec nos amis les chasseurs, ce ne sont pas forcément les Tofus qui ont un coup dans l'aile." ));
            allItems.Add(new Items ( 8114, "Kaskofu", "Ce casque de chantier est devenu un objet de mode incontournable pour les aventurières coquettes. Mais où va le monde ?" ));
            allItems.Add(new Items ( 8116, "Scaracoiffe Dorée", "Cette coiffe est parfaite pour cacher votre acné, ou votre visage de chérubin." ));
            allItems.Add(new Items ( 8117, "Scaracape Dorée", "Plus dorée qu'une Scaracape classique et moins noire qu'une Scaracape Noire, cette cape est parfaite pour cacher vos excroissances. Elle servait autrefois au Scarabosse Doré qui l'utilisait pour cacher son horrible bosse. De nos jours, cette cape est avant tout utilisée pour cacher les horribles postérieurs. Autant dire que cette cape est populaire." ));
            allItems.Add(new Items ( 8118, "Baguette du Scarabosse Doré", "Cette baguette magique est parfaite pour dépoussiérer les vieux meubles ou les vieilles personnes." ));
            allItems.Add(new Items ( 8119, "Scarature Dorée", "Si vous aimez parader entièrement nu sur les plages de l'île de Moon, vous devriez rapidement tomber amoureux de cette ceinture, réputée pour être un excellent cache-misère." ));
            allItems.Add(new Items ( 8120, "Amulette Scarabosse Doré", "Portée sur un torse velu, cette amulette a tout pour plaire tant elle se marie bien avec les touffes de poils." ));
            allItems.Add(new Items ( 8121, "Anneau du Scarabosse Doré", "Le Sacrabosse est une étrange créature. Bossue et laide depuis sa naissance, elle s'est retrouvée être la risée des autres Scarafeuilles. C'est peut-être pour cela que vous vous sentez si proche de cette créature." ));
            allItems.Add(new Items ( 8122, "Scarabottes Dorées", "Ces bottes crochues permettent d'escalader facilement les falaises les plus abruptes, ou de labourer les champs sans charrue." ));
            allItems.Add(new Items ( 8123, "Amulette Paysanne", "Cette amulette est parfumée à l'huile de Tournesol Sauvage et aux fines herbes. Elle peut donc servir d'appât pour les ruminants dangereux que vous voudrez piéger, comme les Bouftous, ou les belles-mères par exemple." ));
            allItems.Add(new Items ( 8124, "Ceinturemuda du Paysan", "Cette ceinture est franchement exceptionnelle, puisqu'un bermuda en fibre de lin y est attaché. C'est la ceinture idéale pour ceux qui aiment se délester de leurs vêtements rapidement, et passer à l'action sur le champ. Attention cependant, pour des raisons d'hygiène, cette ceinture est interdite dans les piscines municipales." ));
            allItems.Add(new Items ( 8125, "Bob du Paysan", "Pour se protéger du soleil lorsque vous travaillez dans les champs, rien de mieux qu'un Ploukosse. Pour se protéger du soleil lorsque vous travaillez dans les champs et que vous êtes pauvre, rien de mieux que le célèbre Bob du Paysan." ));
            allItems.Add(new Items ( 8126, "Mitaines Mitées du Paysan", "Ces mitaines mitées sont de véritables bijoux. On raconte qu'elles sont recouvertes de poussière de diamant, ce qui leur confère une résistance extraordinaire. Elles disposent d'une face abrasive, idéale pour frotter le derrière des Bouftous, ou celui de votre épouse." ));
            allItems.Add(new Items ( 8127, "Faux usée du Paysan", "Cette faux robuste mais abîmée a déjà beaucoup servi et coupe assez mal. En revanche, elle est très efficace pour arracher les chaires tendres et superficielles. Très utile pour lutter contre les varices de vos adversaires." ));
            allItems.Add(new Items ( 8128, "Bottes Paysannes", "Ces bottes sont rembourrées avec de la paille de blé, ce qui permet d'absorber la transpiration et de cultiver des champignons toxiques à moindre frais. Grâce à leur semelle molle, ces bottes sont recommandées par les arbitres de la fédération de Boufball." ));
            allItems.Add(new Items ( 8129, "Amuloumulette", "Derrière ce surnom stupide, se cache en réalité une amulette relativement puissante, confectionnée à partir de crocs, de dents et d'ossements de Mulous, ce qui explique la terrible odeur qui s'en dégage. A défaut d'être assortie à votre cou, elle sera assortie à votre haleine." ));
            allItems.Add(new Items ( 8130, "Hache du Mulou", "Cette terrible hache a permis de décapiter plus d'un aventurier imprudent. Et pourtant, elle coupe assez mal, il faut parfois une dizaine de tentatives pour parvenir à couper un cou un peu résistant." ));
            allItems.Add(new Items ( 8131, "Sac du Paysan", "Ce sac très en vogue chez les jeunes écoliers, est idéal pour entreposer des farines animales et végétales. Une fois ouvert, puis déplié et déchiré le long des coutures, il pourra vous servir de jupe si vous n'avez pas peur de montrer vos cuisses velues." ));
            allItems.Add(new Items ( 8132, "Scarature Noire", "Cette ceinture manque cruellement d'originalité. Elle semble cependant assortie à votre coiffure." ));
            allItems.Add(new Items ( 8133, "Scaracoiffe Noire", "C'est la coiffe parfaite pour ceux qui veulent avoir des idées noires." ));
            allItems.Add(new Items ( 8134, "Scaracape Noire", "Si vous n'avez pas les moyens de vous acheter une Corbacape Mastralis, mais que vous aimez faire peur aux Araknes en vous habillant en noir, c'est la cape qu'il vous faut." ));
            allItems.Add(new Items ( 8135, "Clef du Donjon des Bworks", "Cette clef surprenante vous permettra de rentrer dans le donjon des Bworks, mais pas d'en sortir vivant." ));
            allItems.Add(new Items ( 8136, "Scaranneau Noir", "Cet anneau magnifique peut être porté autour du gros orteil. Idéal pour cacher vos verrues et vos ongles incarnés." ));
            allItems.Add(new Items ( 8137, "Viscères du Scarabosse Doré", "Ces viscères sont en partie responsables des digestions difficiles du Scarabosse Doré. Vous êtes donc autorisé à les faire frire sans remord." ));
            allItems.Add(new Items ( 8138, "Carapace de Scarafeuille Noir", "Une fois retournée, cette carapace fera un excellent cendrier." ));
            allItems.Add(new Items ( 8139, "Clef du Donjon des Scarafeuilles", "Cette magnifique clef dorée, vous permettra de rentrer dans le donjon des Scarafeuilles. Vous pensiez avoir tout vu ?" ));
            allItems.Add(new Items ( 8140, "Antennes du Scarafeuille Noir", "Le Scarafeuille noir n'a jamais trouvé la moindre utilité à ses propres antennes. La nature est vraiment mal faite." ));
            allItems.Add(new Items ( 8141, "Ailes du Scarafeuille Noir", "Contrairement à ce que certains aventuriers peuvent penser, les ailes du Scarafeuille Noir ne sont pas noires, mais blanches. Encore un mystère de la nature qui alimente les débats mouvementés de la taverne." ));
            allItems.Add(new Items ( 8142, "Clef Secrète du Tofulailler Royal", "Cette clef vous donne accès au Tofulailler Royal. Une bonne dérouillée vous y attend." ));
            allItems.Add(new Items ( 8143, "Clef des Champs", "Cette clef ne vous permettra pas de vous évader du Donjon des Champs, mais plutôt de vous y enfermer." ));
            allItems.Add(new Items ( 8144, "Mojo du Meulou", "Cette petite fiole contient toute la puissance virile du Meulou. Attention, son contenu n'est pas comestible." ));
            allItems.Add(new Items ( 8145, "Bière de Bwork", "La Bière de Bwork enivre un Féca à 12 pas et un Sadida de quinze pieds. On ne sait pas vraiment si elle est faite par les Bworks, à partir de Bworks, ou les deux." ));
            allItems.Add(new Items ( 8146, "Bottes Du Craqueleur Légendaire", "Si vous aimez danser et écraser les pieds de vos partenaires, ces bottes sont faites pour vous." ));
            allItems.Add(new Items ( 8147, "Casque du Craqueleur Légendaire", "Version améliorée du casque du Craqueleur, ce roc vous protègera efficacement des chutes de pierres, mais pas des chutes de cheveux." ));
            allItems.Add(new Items ( 8148, "Marteau du Craqueleur Légendaire", "Cet énorme marteau est en fait une épée de Craqueleur, munie d'une pierre du Craqueleur Légendaire. Cette création surprenante saura surprendre vos adversaires, juste avant de les écraser." ));
            allItems.Add(new Items ( 8149, "Craquanneau Légendaire", "Ce rocher serti de pierres précieuses est idéal pour marquer les tempes ou le front de vos adversaires de façon définitive." ));
            allItems.Add(new Items ( 8150, "Amulette du Craqueleur Légendaire", "D'apparence modeste, cette amulette renferme bien des secrets notamment celui précisant quels secrets elle renferme." ));
            allItems.Add(new Items ( 8151, "Bouloute", "Ce petit monstre vous suivra partout, et se nourrira des restes de ses congénères. Tantôt terrifiant, tantôt amusant, il égayera votre vie, avant de peut-être vous la prendre définitivement." ));
            allItems.Add(new Items ( 8152, "Ceinture du Craqueleur Légendaire", "Le Craqueleur légendaire porte cette ceinture autour de ses orteils, vous autour de la taille, ou des chevilles peut-être..." ));
            allItems.Add(new Items ( 8153, "Abra Kadabra", "Comme vous, ce petit Abraknyde ne supportait plus sa famille, et a décidé de se rebeller. Aujourd'hui, il vous accompagne et comptabilise tous les membres de sa famille que vous réussirez à exterminer." ));
            allItems.Add(new Items ( 8154, "El Scarador", "Ce charmant petit coléoptère venu du sud est en fait redoutable. Humilié par ses amis lorsqu'il était jeune, il a décidé de se venger, et vous aidera à botter le derrière de tous les Scarafeuilles que vous rencontrerez." ));
            allItems.Add(new Items ( 8155, "Tifoux", "Cette splendide créature est un lointain cousin des célèbres Lenalds de l'île des Wabbits. Plus téméraire, mais également beaucoup plus indisciplinée, elle saura vous apporter son aide durant vos combats, mais également faire preuve d'une fourberie sans nom, en vous aidant à vaincre les membres de sa propre famille." ));
            allItems.Add(new Items ( 8156, "Clef du Donjon des Canidés", "Cette clef vous permettra de rentrer dans le terrible donjon des Canidés, et de vous faire par la même occasion, étriper dans la joie et la bonne humeur." ));
            allItems.Add(new Items ( 8157, "Pétale Magique du Tournesol Affamé", "Comme tous les objets magiques, ce pétale impressionne, et dégage une aura de puissance proportionnelle à la crédulité des aventuriers qui l'observent." ));
            allItems.Add(new Items ( 8158, "Plume Graisseuse du Tofu Ventripotent", "Le Tofu Ventripotent se badigeonne régulièrement de graisse pour que ses plumes ne se détachent jamais de son plumage. Une technique vieille comme le monde, qui permet à certains Enutrofs de garder leurs cheveux, même après la puberté." ));
            allItems.Add(new Items ( 8159, "Carapace du Scarabosse Doré", "Portée sur la tête, cette carapace vous protègera des intempéries et des chutes de guano." ));
            allItems.Add(new Items ( 8160, "Antennes du Scarabosse Doré", "Si vous voulez ressembler à Korgamone le Chapon des Karpates, c'est très simple, enfoncez-vous ces antennes dans les narines, et chantez à travers la campagne en remuant le postérieur de droite à gauche." ));
            allItems.Add(new Items ( 8161, "Ailes du Scarabosse Doré", "Si vous avez toujours rêvé de voler comme un Piou adipeux, accrochez ces ailes à votre ceinture, et jetez-vous depuis la tour des ordres de Brâkmar. Avec de la chance, vous ressemblerez à un Corbac écrasé, vous offrirez aux Brâkmariens, un très bon moment de rigolade, et aux Corbacs, un excellent repas." ));
            allItems.Add(new Items ( 8162, "Tête de Citwouille", "Vous aurez l'air encore plus stupide que d'habitude avec cette Citwouille magique sur la tête, mais rappelez-vous de la malédiction d'Halouine, qui transforme certaines créatures en d'étranges bêtes difformes... nIl semblerait que seule cette coiffe puisse vous prémunir de cette terrible malédiction." ));
            allItems.Add(new Items ( 8163, "Kabuto", "Porté autrefois par les forces de l'ordre des plus grandes cités, ce casque est aujourd'hui utilisé pour effrayer ceux qui contestent l'ordre établi et cherchent à en apprendre trop sur ce qui régit leur univers." ));
            allItems.Add(new Items ( 8164, "Cadeau Bouloute", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8165, "Cadeau Abra Kadabra", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8166, "Cadeau El Scarador", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8167, "Cadeau Tifoux", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, un livre vous expliquant comment l'élever, et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8168, "Cadeau d\'Halouine", "Halouine était de loin le plus grand cultivateur de Citwouilles de tout le continent. Malheureusement, il était également l'apprenti d'un Bwork Mage un peu trop versé dans les arcanes. Afin de gagner le concours de la plus grosse Citwouille cultivée au purin de Porkass, Halouine tricha en faisant appel au Bwork Mage, qui tenta une incantation inédite sur les Citwouilles. Ceux qui goûtèrent ces Citwouilles magiques, finirent transformés en d'étranges créatures difformes pendant plusieurs jours, et ce chaque année à la même période. Cette malédiction semble même parfois se transmettre aux aventuriers qui s'approchent trop près des créatures maudites. Pour conjurer le sort, les habitants d'Amakna et de ses alentours, se mettent une Citwouille sur la tête pendant plusieurs jours, en espérant que cela les sauve. Le plus surprenant c'est qu'il semblerait que cela fonctionne." ));
            allItems.Add(new Items ( 8169, "Transformation Bouftou d\'Halouine", "Vous avez été transformé en Bouftou d'Halouine. Vous devez essayer de vaincre un autre aventurier pour vous débarrasser de cette terrible malédiction." ));
            allItems.Add(new Items ( 8170, "Transformation Tofu d\'Halouine", "Vous avez été transformé en Tofu d'Halouine. Vous devez essayer de vaincre un autre aventurier pour vous débarrasser de cette terrible malédiction." ));
            allItems.Add(new Items ( 8171, "Fantôme d\'Abra Kadabra", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8172, "Fantôme de Bouloute", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
