            allItems.Add(new Items ( 8175, "Certificat de Mise en Chanil : Abra Kadabra", "Ce certificat décrit les caractéristiques de l'Abra Kadabra que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8176, "Certificat de Mise en Chanil : Bouloute", "Ce certificat décrit les caractéristiques du Bouloute que vous avez placé au chanil." ));
