            allItems.Add(new Items ( 8181, "Craquelocape Légendaire", "Cette cape épaisse en pierre brute fera de vous une brute épaisse en pierre." ));
            allItems.Add(new Items ( 8184, "Transformation Fantôme Tifoux", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
