            allItems.Add(new Items ( 8237, "Ceinture du Piou Rouge", "Cette petite ceinture mettra vos hanches en valeur l'espace d'un combat. Elle servira ensuite à contenir vos tripes lorsqu'un sanglier des plaines un peu énervé vous aura éventré au fin fond des bois." ));
