            allItems.Add(new Items ( 8243, "Chapeau du Piou Rouge", "Muni de ce petit chapeau sur la tête, vous aurez l'air bien ridicule, mais aussi terriblement irrésistible. La vie est une question de choix." ));
            allItems.Add(new Items ( 8244, "Chapeau du Piou Bleu", "Muni de ce petit chapeau sur la tête, vous aurez l'air bien ridicule, mais aussi terriblement irrésistible. La vie est une question de choix." ));
            allItems.Add(new Items ( 8245, "Chapeau du Piou Violet", "Muni de ce petit chapeau sur la tête, vous aurez l'air bien ridicule, mais aussi terriblement irrésistible. La vie est une question de choix." ));
            allItems.Add(new Items ( 8246, "Chapeau du Piou Vert", "Muni de ce petit chapeau sur la tête, vous aurez l'air bien ridicule, mais aussi terriblement irrésistible. La vie est une question de choix." ));
            allItems.Add(new Items ( 8247, "Chapeau du Piou Jaune", "Muni de ce petit chapeau sur la tête, vous aurez l'air bien ridicule, mais aussi terriblement irrésistible. La vie est une question de choix." ));
            allItems.Add(new Items ( 8248, "Chapeau du Piou Rose", "Muni de ce petit chapeau sur la tête, vous aurez l'air bien ridicule, mais aussi terriblement irrésistible. La vie est une question de choix." ));
            allItems.Add(new Items ( 8249, "Plume de Buveur", "Cette plume usée et imbibée d'alcool fort, est très inflammable. Très pratique pour cuisiner un Buveur rôti." ));
            allItems.Add(new Items ( 8250, "Poil de Renarbo", "Le Renarbo adore se travestir en Corbac, personne ne sait réellement pourquoi, même si les esprits les plus mal tournés ont leur avis sur la question..." ));
            allItems.Add(new Items ( 8251, "Poil de Péki", "Qui aurait pu croire que le Péki accepterait de se délester d'une touffe de poils pour vos beaux yeux ? Personne, à part Joche Banette, célèbre idiot du village d'Amakna, réputé pour son affection maladive pour les Pékis empaillés." ));
            allItems.Add(new Items ( 8252, "Plume de Corbac Apprivoisé", "Cette plume bien grasse est celle d'un Corbac Apprivoisé, qui a passé la majeure partie de son existence, enfermé, et gavé jusqu'à ce qu'il occupe totalement l'espace de sa cage." ));
            allItems.Add(new Items ( 8253, "Amulette en Mousse", "Cette amulette est souvent confondue avec un jouet à trouver dans des pochettes surprises, alors qu'en fait elle se gagne au stand de tir." ));
            allItems.Add(new Items ( 8254, "Anneau en Mousse", "Offert en alliance, cet anneau permet de faire passer deux messages à l'être aimé : " ));
            allItems.Add(new Items ( 8255, "Pelle en Mousse", "Cette arme terriblement puissante contre les mottes de terre, a marqué plus d'un aventurier débutant. Une fois mouillée, elle devient molle et peut être roulée, mais il faut généralement être à deux pour y arriver." ));
            allItems.Add(new Items ( 8256, "Bottes en Mousse", "Avec ces bottes, un Eniripsa peut marcher sur l'eau, ce qui ne sert à rien puisqu'il peut voler, mais ça impressionne toujours les foules. Les autres pourront toujours se servir de ces bottes pour se gratter les pieds en marchant, ou tout simplement pour les nettoyer." ));
            allItems.Add(new Items ( 8257, "Ceinture en Mousse", "Cette ceinture permettait à Vald de prendre son bain sans danger car aucun Iop n'a encore réussi à apprendre à nager." ));
            allItems.Add(new Items ( 8258, "Cape en Mousse", "Cette cape est idéale pour le camouflage en milieu naturel, mais uniquement s'il n'y a personne pour vous voir." ));
            allItems.Add(new Items ( 8259, "Coiffe en Mousse", "Sur la notice de cette coiffe, on peut lire :" ));
            allItems.Add(new Items ( 8260, "Bandeau Kaliptus", "Avec ce bandeau, les Iops n'auront jamais été aussi séduisants !" ));
            allItems.Add(new Items ( 8262, "La Meulette", "La Meulette renferme une étrange substance trouvée dans les entrailles du Meulou, d'aucuns prétendent qu'il s'agit de l'essence du monstre, d'autres pensent qu'il avait juste dévoré un Kirboule pas très frais." ));
            allItems.Add(new Items ( 8263, "Anneau du Meulou", "Attention, cet anneau rendra votre doigt particulièrement malodorant. Une odeur qui risque d'en couvrir une autre. C'est peut-être pour votre doigt et votre entourage une très bonne chose..." ));
            allItems.Add(new Items ( 8264, "Bottes du Meulou", "Ces bottes sont encore animées d'une partie de l'appétit vorace du Meulou. Si vos pieds sentent le Clakoss, elles risquent de vous les mordre." ));
            allItems.Add(new Items ( 8265, "Cape du Meulou", "Le Meulou a la queue si belle et si longue qu'elle traîne au sol. Certains aventuriers jaloux la lui ont arrachée et la portent, espérant sûrement y gagner en virilité." ));
            allItems.Add(new Items ( 8266, "Ceinture du Meulou", "Cette ceinture colorée est dotée d'une superbe touffe violacée sur le devant, vous pouvez également la porter à l'envers pour vous travestir en Wabbit." ));
            allItems.Add(new Items ( 8267, "Coiffe du Meulou", "En dépeçant le Meulou, des chasseurs se sont aperçus qu'il ne possède qu'une seule grosse corne qui traverse son crâne de part en part, ce qui ne gène nullement les Iops qui la portent avec enthousiasme non dissimulé." ));
            allItems.Add(new Items ( 8268, "Collier du Minotoror", "Ce collier permettait au Minotoror de ne pas avoir de puces, de tiques, ou même de poux. De toute évidence, vous en aviez besoin." ));
            allItems.Add(new Items ( 8269, "Anneau du Minotoror", "Contrairement à ce que certains aventuriers rapportent, cet anneau n'a jamais été porté par le Minotoror, incapable d'enfiler un tel anneau autour de ses sabots." ));
            allItems.Add(new Items ( 8270, "Bracelet du Minotot", "Ce bracelet permet de protéger entièrement le bras d'un Eniripsa, mais triple son poids." ));
            allItems.Add(new Items ( 8271, "Anneau du Dragon Cochon", "Cet anneau en cuir et en ivoire permet de stimuler plus facilement vos partenaires." ));
            allItems.Add(new Items ( 8272, "Collier du Minotot", "Ce collier permettait aux Minotot, de protéger ses cervicales et d'éviter de périr à cause d'un stupide coup du Wabbit." ));
            allItems.Add(new Items ( 8273, "Collier du Dragon Cochon", "Ce collier dispose d'une petite boucle, qui permettait d'accrocher une laisse au cou du Dragon Cochon, lorsque son propriétaire voulait l'emmener faire une petite promenade nocturne." ));
            allItems.Add(new Items ( 8274, "Hache du Minotoror", "Cette hache a permis de trancher la tête d'un des plus dangereux Minotorors que la province d'Amakna ait connu. Le plus incroyable dans cette histoire c'est qu'elle semble vous laisser indifférent." ));
            allItems.Add(new Items ( 8275, "Sceptre du Minotot", "Ce Sceptre orné à son extrémité d'une tête de Serpentin en pierres précieuses, permet d'effrayer les jeunes aventuriers, afin de les faire fuir, et de botter leurs petites fesses toutes roses." ));
            allItems.Add(new Items ( 8276, "Bottes du Minotoror", "De toute évidence, ces terribles bottes en poils et en cuir de Minotoror, sont bien trop lourdes pour vous faire gagner la légendaire course à pieds de la baie d'Amakna. Cependant, elles devraient vous permettre d'écraser facilement les pieds des autres concurrents sur la ligne de départ." ));
            allItems.Add(new Items ( 8277, "Sandales du Minotot", "Ces incroyables sandales vous permettront de vous baigner dans les eaux les plus dangereuses, sans risquer de vous abîmer les pieds. Attention cependant, elles ont la réputation de rayer le fond des baignoires." ));
            allItems.Add(new Items ( 8278, "Mules du Dragon Cochon", "Une bonne paire de mules et vous vous sentez de suite plus fort, plus puissant, plus performant. Alors que ce ne sont que des mules." ));
            allItems.Add(new Items ( 8279, "Cape du Minotoror", "Prenez un Minotoror, attachez-le fermement, prenez un rabot géant, et pelez-lui (en un seul passage) le dos. Voilà, c'est fait, vous avez désormais entre les mains, une cape du Minotoror." ));
            allItems.Add(new Items ( 8280, "Cape du Minotot", "Pour sortir en soirée, ou séduire au bal masqué du Captain Chafer, c'est la cape qu'il vous faut." ));
            allItems.Add(new Items ( 8281, "Cape du Dragon Cochon", "Cette cape ne fera pas de vous un petit Cochon de Lait, mais elle vous permettra peut-être de vous travestir l'espace d'une soirée, afin de séduire une belle Cochonne." ));
            allItems.Add(new Items ( 8282, "Ceinture du Minotoror", "Cette ceinture est si souple, qu'elle peut servir à fouetter les fesses des enfants trop turbulents. Avec un peu de chance, ses ferrures permettront d'arracher quelques lambeaux de chair, et les terribles garnements qui vous servent de marmaille auront compris une bonne fois pour toute la leçon." ));
            allItems.Add(new Items ( 8283, "Ceinture du Minotot", "Cette ceinture rigide, permettait au Minotot, de contenir son énorme bedaine, afin de ne pas basculer en avant au moindre courant d'air." ));
            allItems.Add(new Items ( 8284, "Coiffe du Minotot", "Cette coiffe se transmet dans la famille du Minotot, de génération en génération. Ce qui explique en partie son odeur de pellicules en décomposition." ));
            allItems.Add(new Items ( 8285, "Coiffe du Dragon Cochon", "Affublé de cet énorme crâne, vous aurez l'air d'un Grizboule à poils durs, mais sans poils. De quoi laisser sans voix vos admiratrices." ));
            allItems.Add(new Items ( 8286, "Cape Dragoeuf", "Cette cape recouverte d'écailles vous protègera d'après la légende, de la foudre, des haricots, et surtout des femmes fatales." ));
            allItems.Add(new Items ( 8287, "Casque Dragoeuf", "Ce casque absolument terrifiant et d'une taille démesurée ferait passer un petit chérubin comme vous, pour un impitoyable guerrier venu de l'au delà. Cloué sur un mur, il fera un excellent porte manteau." ));
            allItems.Add(new Items ( 8288, "Ceinture du Dragoeuf", "Les esprits les plus pervertis d'Amakna, racontent que cette ceinture pouvait servir de prothèse. Personne ne comprend pourquoi, et heureusement il n'en n'est rien, ce n'est qu'une banale ceinture du Dragoeuf." ));
            allItems.Add(new Items ( 8289, "Anneau du Dragoeuf", "Cet anneau directement taillé dans une écaille géante de Dragoeuf protègera une petite partie d'une de vos phalanges. Sûrement la plus importante." ));
            allItems.Add(new Items ( 8290, "Amulette du Dragoeuf", "Confectionnée à partir d'écailles de Dragoeufs, cette amulette protégera efficacement votre pomme d'Adam, ou vos amygdales.  De quoi faire la différence sur un champ de bataille." ));
            allItems.Add(new Items ( 8291, "Bottes du Dragoeuf", "Ces bottes sont parfaites pour tuer le Bouftou lors d'une partie de Boufball ou pour faire souffrir un peu plus vos ennemis lors d'un botte-fesses." ));
            allItems.Add(new Items ( 8292, "Epée du Dragoeuf", "Aiguisée comme le rasoir du soir, cette lame vous permettra tout de même de vous raser le matin, mais uniquement les aisselles." ));
            allItems.Add(new Items ( 8293, "Hache du Dragoeuf", "Cette hache fend le beurre aussi facilement que les crânes, à moins que ça ne soit l'inverse." ));
            allItems.Add(new Items ( 8294, "Marteau du Dragoeuf", "Plus lourd qu'un crâne vide de Krokeur, mais moins solide qu'un fémur de Kirboule des cavernes, ce marteau reste néanmoins une arme redoutable." ));
            allItems.Add(new Items ( 8295, "Arc du Dragoeuf", "Cet arc est si puissant qu'il permet d'envoyer des oeufs pourris de Dragoeufs au dessus des murailles de Brâkmar. Quel beau gâchis !" ));
            allItems.Add(new Items ( 8296, "Baguette du Dragoeuf", "Cette baguette semble plus efficace lorsqu'elle est utilisée comme un gourdin ou comme une tapette à Moskitos." ));
            allItems.Add(new Items ( 8297, "Bâton du Dragoeuf", "Ce bâton est muni à son extrémité, d'une grosse pointe dont personne ne connaît l'origine. En revanche, les aventuriers avertis connaissent sa destination..." ));
            allItems.Add(new Items ( 8298, "Epis de Shika", "Shika assistait Farle lors des moissons, en récupérant les épis magiques qui tombaient sous sa faux. Elle prit l'habitude au fil des moissons, de les accrocher autour de son cou. Privés de leurs racines et du reste de leur tige, ces épis magiques continuent tout de même de mûrir chaque année à la même période." ));
            allItems.Add(new Items ( 8299, "Bracelet Magique de Shika", "Ce bracelet couvrait les bras de Shika, la protégeant des coups de soleil, et des coups de bec des Corbacs attirés par sa beauté rayonnante. Ils sont encore imprégnés de sa sueur suave." ));
            allItems.Add(new Items ( 8300, "Bâton de Shika", "Ce bâton taillé sur mesure, permettait à Shika de créer les meules de foin les plus hautes de tout Amakna. La légende raconte que leur taille était si importante, que les marins s'en servaient de points de repères pour savoir s'ils approchaient ou non de la côte d'Amakna." ));
            allItems.Add(new Items ( 8301, "Sabots de Shika", "Ces sabots ont été conçus pour permettre à Shika de ne jamais écraser les semis, lorsqu'elle ensemençait les plaines de Snossios. Elle y fit rajouter des talons hauts, pour qu'elle puisse courir librement le matin dans les éteules, sans remplir ses sabots de rosée, ou sans abîmer ses chevilles fragiles." ));
            allItems.Add(new Items ( 8302, "Cape de Shika", "Des fleurs des moissons ont été cousues sur cette cape confectionnée à partir de céréales magiques tissées entre elles. Elle est animée d'une magie si puissante, que Shika s'en servait de protection contre la foudre, les soirs d'orage." ));
            allItems.Add(new Items ( 8303, "Shikature", "Cette ceinture en cuir protégeait les cuisses de Shika, lorsqu'elle traversait des champs de blé en courant pour rattraper les Bouftous égarés de son troupeau. Recouverte de pollen de céréales magiques, on raconte qu'elle brillait au clair de lune, et permettait à Farle de ne pas sectionner les jambes de sa propre femme lorsqu'il fauchait des champs entiers de céréales une fois la nuit tombée." ));
            allItems.Add(new Items ( 8304, "Shikacoiffe", "Recouverte d'une multitude de fleurs des moissons, cette coiffe donnait une allure guillerette à Shika, lorsqu'elle partait au petit matin, labourer les collines Koln." ));
            allItems.Add(new Items ( 8305, "Relique du Mominotor", "Cette étrange relique est indispensable pour pouvoir rencontrer le Minotoror." ));
            allItems.Add(new Items ( 8306, "Relique du Déminoboule", "Cette étrange relique est indispensable pour pouvoir rencontrer le Minotoror." ));
            allItems.Add(new Items ( 8307, "Clef de la salle du Minotot", "Cette clef construite à partir de diverses ressources, vous permettra de rencontrer l'incroyable Minotot. Une rencontre qui vous marquera à vie, lorsque le Minotot vous aura envoyé quelques coups de sabots dans le derrière." ));
            allItems.Add(new Items ( 8308, "Carapace de Scaratos", "Cette petite carapace luisante est très pratique pour créer de beaux bijoux à moindre frais, que vous revendrez une fortune, une fois que vous aurez persuadé un Astrubien, que c'est une puissante relique qui permet de voler dans les airs en se rendant invisible." ));
            allItems.Add(new Items ( 8309, "Plume du Serpiplume", "Cette plume est recouverte de poussière de pierre magique, ce qui explique qu'une unité de plume de Serpiplume, pèse plus lourd que trois unités de plume de Tofu. Tout est une question de logique." ));
            allItems.Add(new Items ( 8310, "Gelée de Dégelée", "Ce pot de gelée contient une substance à l'odeur repoussante et au goût épouvantable, et pourtant, vous rêvez tous les jours secrètement de vous enduire de cette substance. Un autre trouble de votre comportement, qui doit en cacher d'autres..." ));
            allItems.Add(new Items ( 8311, "Poil de Gamino", "Ce poil semble recouvert d'une sorte de gomina à base de musc de Bitouf à poils ras, et de graisse de Kirboule des cavernes. On aurait presque envie de les manger." ));
            allItems.Add(new Items ( 8312, "Peau de Minoskito", "Cette peau usée par le sable fin et le sel de mer est fine comme une vieille chaussette usée par le sable fin et le sel de mer. De là à dire que le Minoskito ressemble à une vieille chaussette, il n'y a qu'un pas, que vous ne franchirez pas." ));
            allItems.Add(new Items ( 8313, "Fémur du Minotoror", "Cet os est assez solide pour servir de poutrelle, ou pour casser des briques par lots de dix. Très pratique pour détruire la maison de ses voisins, mais complètement inutile pour les personnes saines d'esprit." ));
            allItems.Add(new Items ( 8314, "Peau de Kraméléhon", "Cette peau brûlée protège efficacement les peaux sensibles, des rayons du soleil. A porter très près du corps." ));
            allItems.Add(new Items ( 8315, "Peau de Mandrine", "Cette peau tachetée a la réputation d'être recouverte d'un poison capable de transformer n'importe qui en Karaboule desséché. Inquiétant, mais tout de même tentant." ));
            allItems.Add(new Items ( 8316, "Le Tifoux : Bien l\'élever", "Ce livre écrit par Oshimo Li'kross Babine explique comment élever un Tifoux." ));
            allItems.Add(new Items ( 8317, "El Scarador : Bien l\'élever", "Ce livre écrit par Oshimo Li'kross Babine explique comment élever El Scarador." ));
            allItems.Add(new Items ( 8318, "Abra Kadabra : Bien l\'élever", "Ce livre écrit par Oshimo Li'kross Babine explique comment élever Abra Kadabra." ));
            allItems.Add(new Items ( 8319, "Bouloute : Bien l\'élever", "Ce livre écrit par Oshimo Li'kross Babine explique comment élever Bouloute." ));
            allItems.Add(new Items ( 8320, "Clef de l\'antre du Dragon Cochon", "Après être entré et vous être perdu dans le labyrinthe du Dragon Cochon, vous vous êtes aperçu que vous aviez oublié de prendre vos clefs de l'antre du Dragon Cochon. Heureusement, vos amis les Porcos avaient un double des clefs sur eux." ));
            allItems.Add(new Items ( 8321, "Poil de Déminoboule", "Inutile d'insister, ces poils ne sont pas comestibles." ));
            allItems.Add(new Items ( 8322, "Poil de Mominotor", "Sous ses bandelettes, le Mominotor cache un duvet très doux qui rendrait jalouse la plus coquette des princesses." ));
            allItems.Add(new Items ( 8323, "Cuir de Déminoboule", "Craquelé et rugueux, ce cuir vous rappellera les joues de votre dulcinée." ));
            allItems.Add(new Items ( 8324, "Cuir de Mominotor", "Le Mominotor a toujours eu la peau très fragile, c'est pour cela qu'il doit se recouvrir de nombreuses bandelettes. Les mauvaises langues racontent que c'est pour cacher d'autres choses assez surprenantes..." ));
            allItems.Add(new Items ( 8325, "Coiffe du Mominotor", "Si vous voulez préserver votre visage des affres du temps, nous vous conseillons cette coiffe. Attention, elle est totalement inefficace pour vous protéger des pertes de mémoire." ));
            allItems.Add(new Items ( 8326, "Bandelette du Mominotor", "Enroulée autour des cornes, mais surtout des parties intimes du Mominotor, ces bandelettes se sont imprégnées pendant de longues années de toute la magie du Mominotor. A la fois malodorantes et terrifiantes, ces bandelettes ont leur place dans les collections de tous les archéologues de renom." ));
            allItems.Add(new Items ( 8327, "Corne de Déminoboule", "Cette corne impressionnante n'est à première vue d'aucune utilité, et même en y regardant de plus près, elle se révèle bien peu utile.  Un peu comme une armée de tiques par exemple." ));
            allItems.Add(new Items ( 8328, "Corne de Mominotor", "Enroulée dans des bandelettes magiques, cette corne doit de toute évidence valoir très cher, être très précieuse, sentir très mauvais, et être maudite. Un peu comme une momie." ));
            allItems.Add(new Items ( 8330, "Coiffe du Dragoeuf volant", "Cette petite coiffe permet au Dragoeuf volant de se jeter sur ses ennemis sans risquer de se faire de jolies bosses sur le crâne. Utilisée depuis longtemps sur les grands chantiers d'Amakna, cette coiffe a connu un regain de popularité après l'invasion d'Astrub par l'illustre troupe des Meulous débridés de la montagne de Kruup. Il n'y a aucune logique dans cette histoire, mais quelle importance ?" ));
            allItems.Add(new Items ( 8331, "Coiffe de la Dragueuse", "Equipé de cette coiffe, vous pourrez sans peine vous travestir et séduire le premier Astrubien un peu trop crédule que vous voudrez arnaquer, en lui proposant une folle nuit d'amour derrière un grand orme, pour 100 kamas seulement." ));
            allItems.Add(new Items ( 8333, "Cape du Papa Nowel", "Cette mystérieuse cape fait son apparition lors des saisons froides, et permet aux imposteurs de se faire passer pour un vieillard généreux." ));
            allItems.Add(new Items ( 8334, "Chapeau du Papa Nowel", "Ce chapeau vous permettra de cacher les cicatrices que vous avez sur le crâne. Vous pourrez enfin plaire aux enfants." ));
            allItems.Add(new Items ( 8335, "Tout petit cadeau de Nowel", "L'équipe d'Ankama vous souhaite de joyeuses et agréables fêtes. Ce cadeau vous rendra heureux comme vous ne l'avez jamais été." ));
            allItems.Add(new Items ( 8336, "Petit cadeau de Nowel", "L'équipe d'Ankama vous souhaite de joyeuses et agréables fêtes. Ce cadeau vous rendra heureux comme vous ne l'avez jamais été." ));
            allItems.Add(new Items ( 8337, "Cadeau de Nowel", "L'équipe d'Ankama vous souhaite de joyeuses et agréables fêtes. Ce cadeau vous rendra heureux comme vous ne l'avez jamais été." ));
            allItems.Add(new Items ( 8338, "Epée de Nowel", "Cette étrange épée ne vous rendra pas invincible, mais vous pourrez peut-être vous en servir pour tuer des boules de neige." ));
            allItems.Add(new Items ( 8339, "Grand cadeau de Nowel", "L'équipe d'Ankama vous souhaite de joyeuses et agréables fêtes. Ce cadeau vous rendra heureux comme vous ne l'avez jamais été." ));
            allItems.Add(new Items ( 8340, "Incroyable cadeau de Nowel", "L'équipe d'Ankama vous souhaite de joyeuses et agréables fêtes. Ce cadeau vous rendra heureux comme vous ne l'avez jamais été." ));
            allItems.Add(new Items ( 8341, "Bottananas", "Ces bottes sentent le jus d'ananas macéré dans du substrat de chaussette trouée. L'avantage, c'est qu'on ne vous demandera jamais de les enlever lorsque vous irez chez un ami." ));
            allItems.Add(new Items ( 8342, "Clef du Sanctuaire des Dragoeufs", "Cette clef confectionnée à base de coquilles de Dragoeufs vous permettra d'accéder au terrible Sanctuaire des Dragoeufs, réputé pour ses habitants peu loquaces." ));
            allItems.Add(new Items ( 8343, "Clef du Donjon des Dragoeufs", "Vous pensiez être arrivé au bout de vos peines en rentrant dans le Sanctuaire des Dragoeufs, mais vous vous êtes lamentablement trompé. Cette clef vous mènera peut-être vers une mort certaine." ));
            allItems.Add(new Items ( 8344, "Corne de Dragoss Noir", "Moins pointue qu'une aiguille de Xélor, mais plus solide, cette corne permet aux Dragoss de transformer un botte-fesses, en perce-fesses." ));
            allItems.Add(new Items ( 8345, "Corne de Dragoss de Saphire", "Moins pointue qu'une aiguille de Xélor, mais plus solide, cette corne permet aux Dragoss de transformer un botte-fesses, en perce-fesses." ));
            allItems.Add(new Items ( 8346, "Corne de Dragoss Blanc", "Moins pointue qu'une aiguille de Xélor, mais plus solide, cette corne permet aux Dragoss de transformer un botte-fesses, en perce-fesses." ));
            allItems.Add(new Items ( 8347, "Corne de Dragoss Doré", "Moins pointue qu'une aiguille de Xélor, mais plus solide, cette corne permet aux Dragoss de transformer un botte-fesses, en perce-fesses." ));
            allItems.Add(new Items ( 8348, "Coquille de Dragoss Noir", "Cette coquille peut éventuellement servir à protéger ce que vous avez de plus précieux." ));
            allItems.Add(new Items ( 8349, "Coquille de Dragoss de Saphir", "Cette coquille peut éventuellement servir à protéger ce que vous avez de plus précieux." ));
            allItems.Add(new Items ( 8350, "Coquille de Dragoss Blanc", "Cette coquille peut éventuellement servir à protéger ce que vous avez de plus précieux." ));
            allItems.Add(new Items ( 8351, "Coquille de Dragoss Doré", "Cette coquille peut éventuellement servir à protéger ce que vous avez de plus précieux." ));
            allItems.Add(new Items ( 8352, "Ecaille de Dragoss Noir", "Cette écaille de Dragoss pourrait servir à confectionner de belles bottines, ou des sous-vêtements de combat." ));
            allItems.Add(new Items ( 8353, "Ecaille de Dragoss de Saphire", "Cette écaille de Dragoss pourrait servir à confectionner de belles bottines, ou des sous-vêtements de combat." ));
            allItems.Add(new Items ( 8354, "Ecaille de Dragoss Blanc", "Cette écaille de Dragoss pourrait servir à confectionner de belles bottines, ou des sous-vêtements de combat." ));
            allItems.Add(new Items ( 8355, "Ecaille de Dragoss Doré", "Cette écaille de Dragoss pourrait servir à confectionner de belles bottines, ou des sous-vêtements de combat." ));
            allItems.Add(new Items ( 8356, "Peau de Dragueuse", "Lisse et parfumée avec du sang de jeunes aventuriers, cette peau rend de toute évidence les Dragueuses irrésistibles. Utilisée pour des bottes, ou des ceintures, cette peau a l'avantage d'être lavable en machine, à n'importe quelle température." ));
            allItems.Add(new Items ( 8357, "Corne de Dragueuse", "La Dragueuse dispose de deux petites cornes à l'arrière de son crâne, qui lui permettent de capter plus facilement les ondes amoureuses émises par les autres Dragoeufs pendant la saison des amours, qui rappelons-le, ne s'arrête jamais chez les Dragoeufs." ));
            allItems.Add(new Items ( 8358, "Protection de la Dragueuse", "Cette paire de coquilles de Dragoeuf permet à la Dragueuse de se protéger de ses propres coups de foudre. Cette protection est également utilisable par les mâles, si elle est portée sous la ceinture." ));
            allItems.Add(new Items ( 8359, "Aile de Dragoeuf Volant", "Même abimée, déchirée et brûlée, cette aile permet au Dragoeuf Volant de se mouvoir librement au dessus du vide, comme un jeune Moskito insouciant." ));
            allItems.Add(new Items ( 8360, "Peau de Dragoeuf Volant", "Cette peau sombre est recouverte d'une multitude de petites écailles censées améliorer l'aérodynamisme du Dragoeuf Volant. En réalité, elles servent juste à impressionner les badauds." ));
            allItems.Add(new Items ( 8361, "Corne de Dragoeuf Volant", "Cette corne sert de viseur au Dragoeuf Volant afin de ne pas râter ses cibles, lorsqu'il vole en état d'ébriété." ));
            allItems.Add(new Items ( 8362, "Peau de Dragoeuf Guerrier", "Assez solide pour résister aux coups de sa propre lame, la peau du Dragoeuf Guerrier est surtout réputée pour être comestible, une fois bouillie pendant plusieurs années." ));
            allItems.Add(new Items ( 8363, "Corne de Dragoeuf Guerrier", "Le Dragoeuf Guerrier dispose de trois cornes différentes, deux derrières la tête, et une sur le bout du museau. Généralement, après une attaque, une corne se retrouve plantée dans le derrière de ses victimes, et les deux dernières enfoncées profondément dans les narines d'un imprudent. Mais parfois le Dragoeuf Guerrier confond le derrière et les narines de ses victimes, ce qui pour le coup, leur cause beaucoup plus de tort." ));
            allItems.Add(new Items ( 8364, "Lanière en cuir du Dragoeuf Guerrier", "Cette lanière en cuir permet au Dragoeuf Guerrier d'accrocher son épaulette, ou de remplacer vos bretelles." ));
            allItems.Add(new Items ( 8365, "Corne brisée de Crocabulia", "Crocabulia a cassé sa propre corne lors d'un terrible combat dans lequel elle tentait de protéger l'oeuf qui donna naissance 15 années plus tard, au Crocoburio." ));
            allItems.Add(new Items ( 8366, "Cape du Petit Chapon Rouge", "Cette cape appartenait à un célèbre Chapon, ancien volatile aux longues plumes qui aimait se pavoiser devant le Zaap de Bonta devant sa basse-cour composée de volailles crédules et de poulardes immatures." ));
            allItems.Add(new Items ( 8367, "Peau de Crocabulia", "Cette peau rouge vous permettra peut-être de confectionner une cape du petit Chapon Rouge, si vous ne redoutez pas les railleries de vos alliés." ));
            allItems.Add(new Items ( 8368, "Dent de Crocabulia", "Cette énorme dent a été arrachée in extremis avant que Crocabulia ne referme sa gueule pour toujours." ));
            allItems.Add(new Items ( 8369, "Fragment de Coquille de Terrakoubiak le Guerrier", "Ce fragment de coquille appartenant à Terrakoubiak le Guerrier, devrait vous aider à fabriquer une clef pour le sanctuaire des Dragoeufs." ));
            allItems.Add(new Items ( 8370, "Fragment de Coquille d\'Ignelicrobur le Guerrier", "Ce fragment de coquille appartenant à Ignelicrobur le Guerrier, devrait vous aider à fabriquer une clef pour le sanctuaire des Dragoeufs." ));
            allItems.Add(new Items ( 8371, "Fragment de Coquille d\'Aerohouctor le Guerrier", "Ce fragment de coquille appartenant à Aerohouctor le Guerrier, devrait vous aider à fabriquer une clef pour le sanctuaire des Dragoeufs." ));
            allItems.Add(new Items ( 8372, "Fragment de Coquille d\'Aquabralak le Guerrier", "Ce fragment de coquille appartenant à Aquabralak le Guerrier, devrait vous aider à fabriquer une clef pour le sanctuaire des Dragoeufs." ));
            allItems.Add(new Items ( 8373, "Pierre d\'Ignerkocropos l\'Affamé", "Cette pierre, si elle est combinée avec les trois autres manquantes, devrait libérer une onde magique assez puissante pour activer et ouvrir une porte magique." ));
            allItems.Add(new Items ( 8374, "Pierre d\'Aqualikros l\'Impitoyable", "Cette pierre, si elle est combinée avec les trois autres manquantes, devrait libérer une onde magique assez puissante pour activer et ouvrir une porte magique." ));
            allItems.Add(new Items ( 8375, "Pierre de Terraburkal le Perfide", "Cette pierre, si elle est combinée avec les trois autres manquantes, devrait libérer une onde magique assez puissante pour activer et ouvrir une porte magique." ));
            allItems.Add(new Items ( 8376, "Pierre d\'Aerotrugobur le Malveillant", "Cette pierre, si elle est combinée avec les trois autres manquantes, devrait libérer une onde magique assez puissante pour activer et ouvrir une porte magique." ));
            allItems.Add(new Items ( 8377, "Rune de Bricoleur", "Cette pierre magique permet aux Bricoleurs de donner leur position aux clients." ));
            allItems.Add(new Items ( 8378, "Fragment Magique", "Ce fragment magique, contient une partie de la magie autrefois contenue dans un objet. Avec un peu de chance, vous devriez pouvoir récupérer quelques runes magiques." ));
            allItems.Add(new Items ( 8379, "Rune Vie", "Cette petite rune a le pouvoir de rendre plus résistant." ));
            allItems.Add(new Items ( 8380, "Etoffe de Péki", "Cette étoffe recouvrait les fesses poilues du Péki avant que vous ne décidiez de le caresser à rebrousse-poils." ));
            allItems.Add(new Items ( 8381, "Queue Magique du Péki", "Cette queue est si magique, qu'elle frétille encore, même après avoir été coupée, séchée, et empaillée. En cherchant bien, vous pourrez lui trouver une utilité autre que décorative." ));
            allItems.Add(new Items ( 8383, "Peau de Moon", "Privé de sa petite peau douce, le pauvre petit Moon s'est retrouvé bien au dépourvu et à préféré fuir lâchement dans la jungle en hurlant de douleur. Une scène réjouissante que vous ne manquerez pas de raconter à vos enfants le soir, pour les aider à trouver le sommeil." ));
            allItems.Add(new Items ( 8384, "Etoffe Magique de Moon", "Cette étoffe ressemble à une étoffe magique, et vous avez du mal à l'accepter. Pourtant il faudra bien que vous reconnaissiez un jour ou l'autre, que vous êtes trop stupide pour déterminer vous-même ce qui est magique ou non. Ne suivez pas votre instinct de pourfendeur d'Astrubiens, faites confiance aux descriptions." ));
            allItems.Add(new Items ( 8385, "Peau de Shin Larve", "Cette peau de Shin Larve est encore plus gluante et collante que vous ne l'imaginiez. Elle vous rappelle la dernière princesse que vous avez délivrée." ));
            allItems.Add(new Items ( 8386, "Dent de Shin Larve", "Personne n'aurait pu croire qu'une Shin Larve avait la bouche remplie de dents. C'est peut-être parce que la Shin Larve ne dispose que d'une seule dent." ));
            allItems.Add(new Items ( 8387, "Plume Vibrante du Tofu Royal", "Plus royale que la plume du Tofu Royal, plus vibrante que la plume d'un Kirboule Vibreur des mines de Kruup, cette plume saura chatouiller qui vous voudrez, et où vous voudrez. Il est expliqué sur la notice, que son entretien est relativement simple." ));
            allItems.Add(new Items ( 8388, "Peau de Bworkette", "La Bworkette vous a laissé lui arracher un morceau de peau en échange de votre clémence. Mais elle a oublié de vous dire qu'elle a prévenu son grand frère le Bworker, qui se fera un plaisir de vous arracher la votre." ));
            allItems.Add(new Items ( 8389, "Poil de Bworkette", "La Bworkette a la peau lisse, sauf sous les aisselles. Il faut bien que les gâteaux qu'elle roule sous ses aisselles soient assaisonnés de temps en temps." ));
            allItems.Add(new Items ( 8390, "Peau de Cochon de Farle", "Farle était le plus grand éleveur de cochons et peut-être même de cochonnes, d'après les éleveurs qui jalousaient Shika. Ce qui est certain, c'est qu'il est à l'origine des Cochons de Farle, à qui il a donné son nom, car ils les avaient créés à son image." ));
            allItems.Add(new Items ( 8391, "Peau de Don Dorgan", "Le Don Dorgan a été une fois de plus très généreux avec vous, malgré son terrible caractère. Il vous a fait don de sa peau avant de partir en grognant comme un gros Porkass. Quel culot !" ));
            allItems.Add(new Items ( 8392, "Peau de Don Duss Ang", "Le Don Duss Ang que vous avez dû peler avec une râpe à fromage pour obtenir cette peau très fine, s'en souviendra toute sa vie. On peut supposer qu'il vous en voudra longtemps. Ne comptez plus sur lui pour vous indiquer le bon chemin la prochaine fois que vous vous égarerez dans son labyrinthe." ));
            allItems.Add(new Items ( 8393, "Poil de Dragon Cochon", "Vous avez beau chercher, vous n'arrivez pas à trouver la partie du corps du Dragon Cochon sur laquelle ce poil était planté. Cherchez-mieux, l'anatomie du Dragon Cochon semble révéler de nombreuses surprises..." ));
            allItems.Add(new Items ( 8394, "Méga Pierre du Craqueleur Légendaire", "Cette énorme pierre ressemble à une pierre du Craqueleur Légendaire, et pourtant il n'en n'est rien, c'est bien plus encore." ));
            allItems.Add(new Items ( 8396, "Mojo du Mulou ", "Cette petite fiole contient toute la puissance virile du Mulou. Attention, son contenu n'est pas comestible." ));
            allItems.Add(new Items ( 8397, "Poil du Wa Wabbit", "Le Wa Wabbit est en règle générale assez avare et rechigne à partager ses poils. Exceptionnellement, pour récompenser votre bravoure (ou acheter votre clémence), il a décidé de vous faire don d'une partie de son corps. Touchant." ));
            allItems.Add(new Items ( 8398, "Etoffe du Wa Wabbit", "Personne ne sait réellement d'où le Wa Wabbit a sorti cette étoffe, et il semblerait que personne ne cherche à savoir également. A la fois inquiétante et troublante, l'origine de cette étoffe reste un des plus grands mystères de tout Amakna." ));
            allItems.Add(new Items ( 8399, "Poil de Maître Pandore", "Une fois de plus le Maître Pandore a voulu faire le malin, et s'est pris une bonne leçon qu'il n'oubliera pas de si tôt. Il aurait mieux fait de rester au fond de sa boîte." ));
            allItems.Add(new Items ( 8400, "Etoffe de Maître Pandore", "Protégeant tantôt sa nuque, tantôt son arrière train, cette étoffe a sauvé la vie du Maître Pandore plus d'une fois. Mais pas cette fois-ci." ));
            allItems.Add(new Items ( 8401, "Peau de Maître Pandore", "Usée par les attaques magiques, desséchée par le vent de l'île de Pandala, cette peau est désormais d'une piètre qualité, et pourtant vous semblez vous y attacher, comme si vous cherchiez à garder un précieux souvenir de ce brave Maître Pandore, qui a tout de même failli vous ôter la vie plusieurs fois sans aucun remord. C'est dans ce genre de situations que l'on reconnaît un véritable monstre d'une petite nature." ));
            allItems.Add(new Items ( 8402, "Pierre du Koulosse", "Le Koulosse a beau être généreux, il ne vous a pas donné sa grosse pierre de gaieté de cœur, et on peut le comprendre lorsque l'on sait ce que vous allez en faire." ));
            allItems.Add(new Items ( 8403, "Tresse du Koulosse", "Cette énorme tresse a été confectionnée par le Koulosse en personne il y a plusieurs centaines d'années. Elle est devenue avec le temps, presque aussi rigide qu'un bloc de pierre. Elle est cependant encore gorgée d'un liquide gluant et nauséabond qui attire de temps en temps les Moskitos. Le plus surprenant dans tout cela c'est que cette tresse allait très bien au Koulosse. Comme un gros Ploukosse, vous avez eu l'idée saugrenue de la lui voler." ));
            allItems.Add(new Items ( 8404, "Peau de Skeunk", "Ce morceau de peau semble provenir des talons de Skeunk, ce qui explique sa couleur jaunâtre du plus bel effet. A défaut de vous confectionner des vêtements à la mode avec cette vieille peau, vous pourrez vous confectionner des vêtements d'une autre époque, et adopter une apparence " ));
            allItems.Add(new Items ( 8405, "Poil de Skeunk ", "Qui à part vous, aurait pu croire que Skeunk ne s'épilait pas entièrement le corps depuis sa tendre enfance ?" ));
            allItems.Add(new Items ( 8406, "Laine du Minotot", "Cette laine a la particularité d'être très fine, et permet au Minotot de ne pas avoir de bouclettes le long du corps. Personne ne sait à quoi cela lui sert, vous devriez peut-être essayer de vous confectionner des implants pilaires avec cette laine, vous pourrez ainsi arborer une imposante toison de poils bruns sur votre torse. Même les rêves d'enfance peuvent se réaliser." ));
            allItems.Add(new Items ( 8407, "Cuir du Minotot", "A peine plus rare que le cuir de Bouftou, ce cuir est idéal pour les créations qui nécessitent des coutures souples, comme les masques en cuir par exemple." ));
            allItems.Add(new Items ( 8408, "Sabot du Minotot", "Ce sabot a foulé énormément d'aventuriers, mais aussi de grosses bouses égarées dans le labyrinthe du Minotoror. A la décharge du Minotot, il faut reconnaître qu'il n'est parfois pas évident de faire la différence au premier coup d'oeil." ));
            allItems.Add(new Items ( 8409, "Corne du Minotot", "Cette corne ressemble de loin à une corne de Bouftou géante et extrêmement magique. Fait surprenant, de près, elle ressemble à une corne de Minotot." ));
            allItems.Add(new Items ( 8410, "Fémur du Minotot", "Cet os imposant a réussi à supporter pendant plusieurs siècles, la surcharge pondérale du Minotot. Un exploit qui méritait d'être signalé." ));
            allItems.Add(new Items ( 8411, "Amulette du Mominotor", "Cette amulette maudite recouverte de bandelettes vous permettra peut-être de survivre à la malédiction du labyrinthe du Minotoror. Mais rien n'est moins sûr, peut-être n'est-ce qu'un bibelot destiné à épater les touristes." ));
            allItems.Add(new Items ( 8412, "Anneau du Mominotor", "Cet anneau confectionné à partir de bandelettes de Mominotor enroulées sur elles-mêmes, sent plutôt mauvais,  et est assez laid, mais fera un très bon pansement lorsque vous vous coincerez un doigt dans une des portes du labyrinthe du Minotoror." ));
            allItems.Add(new Items ( 8413, "Marteau du Mominotor", "Le bloc de pierre à l'extrémité de ce marteau, est enroulé dans des bandelettes du Mominotor afin d'absorber les fluides qui s'échapperont des créatures que vous écraserez, ou tout simplement leur faire moins mal, et ne pas les tuer trop rapidement." ));
            allItems.Add(new Items ( 8414, "Dagues du Dragoeuf", "Ces dagues sont idéales pour découper le haut d'un oeuf de Dragoeuf cuit à la coque. Elles sont aussi très légères, ce qui permet de courir vite lorsque le reste de la communauté des Dragoeufs s'en aperçoit." ));
            allItems.Add(new Items ( 8415, "Bottes du Mominotor", "Rembourrées et décorées avec des bandelettes de Mominotor, ces bottes sont à la fois très confortables et très chaudes. Elles vous rappellent peut-être quelqu'un." ));
            allItems.Add(new Items ( 8416, "Cape du Mominotor", "Confectionnée à partir de bandelettes du Mominotor tressées entre elles, cette cape vous donnera un style tout droit sorti d'outre-tombe. Vos ennemis risquent de crever de jalousie." ));
            allItems.Add(new Items ( 8417, "Pelle à Gâteau", "Cette petite pelle a été offerte a un jeune Enutrof, il y est resté si attaché qu'il l'utilisait encore juste avant sa mort." ));
            allItems.Add(new Items ( 8418, "Ceinture du Mominotor", "Cette grosse bandelette de Mominotor possède une valeur ajoutée extraordinaire, dont l'origine reste cependant méconnue. Mais qu'importe, elle se vendra cher, et sa malédiction accélèrera la putréfaction des hanches des amis à qui vous offrirez cette ceinture, ce qui est il faut bien l'avouer, tout simplement inestimable." ));
            allItems.Add(new Items ( 8419, "Pelle Moh", "Avec cette pelle, Le capitaine Machab a éventré Grobill Dick, le grand Kralamoure Blanc." ));
            allItems.Add(new Items ( 8420, "Pelle Dragoeuf", "Les Dragoeufs aiment creuser des galeries, mais ne se servent pas de pelles, la communauté Enutrof a longtemps étudié l'énigme de celle-ci, et a fini par conclure qu'il était moins important de savoir d'où elle venait que de taper avec." ));
            allItems.Add(new Items ( 8421, "Certificat de Mise en Chanil : Vilain Petit Corbac", "Ce certificat décrit les caractéristiques du Vilain Petit Corbac que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8424, "Cadeau de compensation de l\'éleveur", "Ce cadeau destiné aux éleveurs, contient de quoi remplir un enclos avec d'excellentes machines d'élevage. n(Attention, les objets contenus dans ce cadeau sont très lourds, vous risquez d'être en surcharge lorsque vous l'ouvrirez)." ));
            allItems.Add(new Items ( 8425, "Bourse de 10000 Kamas", "Cette bourse est très lourde, elle contient sûrement une vraie fortune. Un marchand sera ravi de vous l'échanger contre sa valeur en Kamas." ));
            allItems.Add(new Items ( 8428, "Potion d\'invisibilité Nébuleuse", "Cette potion permet de s'évaporer pour revenir au dernier point de sauvegarde sans laisser de trace." ));
            allItems.Add(new Items ( 8430, "Ballon Jaune Magique", "Contrairement aux contrefaçons que l'on trouve à la foire du Trool, ce ballon-ci sait au moins s'envoler avec grâce et aisance. Confectionné à la main comme autrefois, en véritable plume de Tofu selon la traditionnelle recette du grand mage Vahy Nez." ));
            allItems.Add(new Items ( 8431, "Potion fumigène Cyclonique", "Cette potion fumigène crée un tourbillonnant écran de fumée qui camoufle vos actions avec style." ));
            allItems.Add(new Items ( 8435, "Ballon Rouge Magique", "Contrairement aux contrefaçons que l'on trouve à la foire du Trool, ce ballon-ci sait au moins s'envoler avec grâce et aisance. Confectionné à la main comme autrefois, en véritable paupière de Gelée Fraise selon la traditionnelle recette du grand mage Vahy Nez." ));
            allItems.Add(new Items ( 8436, "Clef du Donjon du Chêne Mou", "Cette clef vous permettra d'accéder aux parties les plus intimes de l'anatomie du Chêne Mou, sans pour autant comprendre où vous allez. A la fois perturbant et excitant, ce voyage vers l'infini et l'au-delà ne vous laissera pas indifférent." ));
            allItems.Add(new Items ( 8437, "Clef du Donjon Ensablé", "Cette clef confectionnée à partir de détritus marins et de coquillages vous permettra d'accéder à la plage qui dispose du sable le plus fin de toute la côte de la Mer d'Asse." ));
            allItems.Add(new Items ( 8438, "Clef du Donjon des Rats de Brâkmar", "Cette clef vous permettra d'accéder au donjon des Rats situé dans les égouts de Brâkmar. Inutile de vous déguiser, vous passerez inaperçu, même si vous n'êtes pas Brâkmarien." ));
            allItems.Add(new Items ( 8439, "Clef du Donjon des Rats de Bonta", "Cette clef vous permettra d'accéder au donjon des Rats situé dans les égouts de Bonta. Inutile de vous déguiser, vous passerez inaperçu, même si vous n'êtes pas Bontarien." ));
            allItems.Add(new Items ( 8441, "Billréole", "Bill voulait une auréole. Il cria si fort et fit si grand caprice qu'on la lui accorda. Un vrai petit diable." ));
            allItems.Add(new Items ( 8442, "Masque du Rat Noir", "Ce masque a servi à Brouce Boulgoure lorsqu'il infiltra subtilement un repaire de rats d'Egoûtants afin de couper un orme en commençant par les racines. Il finit tout de même par massacrer tous les rats du repaire, parce qu'il n'était en réalité pas très discret, avec ses gros sabots en bois." ));
            allItems.Add(new Items ( 8443, "Cape du Rat Noir", "Cette cape peut être confectionnée avec n'importe quels poils, à condition qu'ils soient les plus sales possibles. Ils sont tressés entre eux puis fixés à l'aide de sécrétions diverses. L'odeur n'est pas en option." ));
            allItems.Add(new Items ( 8444, "Dagues du Rat Noir", "A la fois tranchantes et abîmées, les lames de ces dagues permettent de trancher les membres de vos adversaires avec une facilité déconcertante, tout en s'assurant que vous leur retirerez une quantité impressionnante de lambeaux de chair. Dommage qu'elles ne soient pas rouillées, chauffées à blanc et empoisonnées lorsque vous les utilisez." ));
            allItems.Add(new Items ( 8445, "Collier du Rat Noir", "Le rat qui possédait ce collier pensait devenir le chef de son clan. Il s'est fait manger par un Lenald enragé." ));
            allItems.Add(new Items ( 8446, "Bottes du Rat Noir", "Attention en enfilant ces bottes, il n'est pas rare qu'elles contiennent encore un ou deux pieds du propriétaire précédent." ));
            allItems.Add(new Items ( 8447, "Ceinture du Rat Noir", "Contrairement à son cousin blanc bontarien, le Rat Noir aime faire dans la discrétion, et porte donc une simple ceinture d'étoffe. Mais il aime aussi emporter un maximum d'armes avec lui, qui font un bruit d'enfer en s'entrechoquant à chaque pas." ));
            allItems.Add(new Items ( 8448, "Anneau du Rat Noir", "Tournez cet anneau vers la droite si vous devez serrer la main d'un ennemi, et vers la gauche si vous devez serrer celle d'un ami. Dans tous les cas, on vous déconseille d'applaudir avec cet anneau." ));
            allItems.Add(new Items ( 8449, "Transformation du Donjon Abraknyde", "Un Abraknyde vous a mordu et vous a transmis une étrange malédiction." ));
            allItems.Add(new Items ( 8450, "Epée du Rat Blanc", "Cette Arme est le symbole du Rat Blanc. Elle est réputée pour être capable de trancher ennemis et alliés avec la même facilité." ));
            allItems.Add(new Items ( 8451, "Coiffe du Rat Blanc", "Avec cette coiffe, le Rat Blanc ressemble à un vrai Rat Blanc de Bonta. Sans cette coiffe, il ressemble à un vrai Rat Blanc de Bonta sans coiffe." ));
            allItems.Add(new Items ( 8452, "Cape du Rat Blanc", "Cette cape est le symbole identifiant un rat blanc du réseau d'égoûts de Bonta, elle lui est remise après un examen long et difficile, et il la porte fièrement jusqu'à sa mort, c'est à dire jamais longtemps." ));
            allItems.Add(new Items ( 8453, "Collier du Rat Blanc", "Si vous le portez comme un collier, il vous protègera des émanations toxiques provenant des égoûts. En revanche, si vous le portez comme une ceinture, ce sont vos alliés qui seront protégés de vos émanations toxiques." ));
            allItems.Add(new Items ( 8454, "Gantelet du Rat Blanc", "Ce gantelet appartenait à Marie Thé, cuisinière du palais de Bonta. Elle s'en servait pour assommer les Bouftous qui refusaient de se faire égorger, les plantes sauvages qui ne voulaient pas aller dans le saladier, et les clients qui ne voulaient pas les manger." ));
            allItems.Add(new Items ( 8455, "Ceinture du Rat Blanc", "Contrairement à ce que pense son cousin noir brâkmarien, le rat blanc aime également faire dans la discrétion. Les raccords métalliques de cette ceinture sont doublés de goudron et ne font aucun bruit, sauf quand son porteur tombe lamentablement, c'est à dire souvent puisqu'elle pèse plusieurs tonnes." ));
            allItems.Add(new Items ( 8456, "Bottes du Rat Blanc", "Avec ces bottes, votre équilibre est parfait, même par grand vent vous ne bougerez plus d'un pouce." ));
            allItems.Add(new Items ( 8457, "Coiffe Cérémoniale du Seigneur des Rats", "Cette coiffe n'a rien à voir avec un Dora... Absolument rien on vous dit ! Il est donc inutile de répandre de fausses rumeurs à propos du manque d'inspiration ou de la fainéantise de son concepteur . En revanche elle est très jolie." ));
            allItems.Add(new Items ( 8458, "Cape Cérémoniale du Seigneur des Rats", "Cette cape colorée fait de vous une cible privilégiée. Seuls les aventuriers vifs et agiles ont su la porter longtemps." ));
            allItems.Add(new Items ( 8459, "Collier Cérémonial du Seigneur des Rats", "Afin d'éviter qu'on le lui vole, ce collier est glissé autour du cou de l'héritier du Seigneur des rats à sa naissance. Ainsi, à l'âge adulte, le seul moyen de le lui enlever est de lui couper la tête. On le lui vole tout de même." ));
            allItems.Add(new Items ( 8460, "Anneau Cérémonial du Seigneur des Rats", "Cet anneau a été conçu à une époque où le seigneur des rats s'était mutilé et n'avait plus qu'un doigt. Les malheureux seigneurs suivants avaient encore tous les leurs." ));
            allItems.Add(new Items ( 8461, "Ceinture Cérémoniale du Seigneur des Rats", "Les motifs sur cette ceinture donnent l'impression que vous avez le feu au fondement. Le trompe l'oeil est si bon que la plupart des porteurs y croient eux-mêmes et courent partout en hurlant. Ce qui correspond exactement au rituel de la cérémonie." ));
            allItems.Add(new Items ( 8462, "Bottes Cérémoniales du Seigneur des Rats", "Le tout premier Seigneur des rats est mort en marchant par inadvertance dans une flaque de lave en fusion. Ces bottes sont portées en hommage par ses seigneurs. Attention, elles ne sont pas ignifugées." ));
            allItems.Add(new Items ( 8463, "Abracaska Ancestral", "Cette Coiffe fut créée pour Jicé Aouaire car il a la tête plate, aucun soucis d'équilibre pour l'arborer donc. nIl l'a faite faire après avoir vaincu l'Abraknyde Ancestral en brisant ses noisettes entre ses fesses, mais il s'en vante moins." ));
            allItems.Add(new Items ( 8464, "Abracapa Ancestrale", "En été, l'Abraknyde Ancestral aime se dorer au soleil, mais son bois habitué aux forêts sombres le supporte mal. Cette cape est confectionnée avec les pelures de son écorce." ));
            allItems.Add(new Items ( 8465, "Torque Ancestral", "Autour du cou, ce torque met en valeur votre barbe. Si vous n'avez pas de barbe, vous ne pouvez vous en prendre qu'à vous-même." ));
            allItems.Add(new Items ( 8466, "Anneau Ancestral", "Les soirs où vous aurez trop bu, vous verrez peut-être le Moskito pris dans cet anneau vous faire un clin d'oeil. Mais évitez d'en parler à vos amis, il fait seulement semblant." ));
            allItems.Add(new Items ( 8467, "Protège-Tibias Ancestraux", "Avec ces protège-tibias en bois massif, vos jambes ne craignent plus rien. Ils sont en revanche incompatibles avec le port de bottes . Vous pourrez les utiliser  pour vous protéger lors des compétitions de Boufball, mais vous ne pourrez plus faire de pointu avec vos bottes coquées." ));
            allItems.Add(new Items ( 8468, "Abrature Ancestrale", "Cette ceinture est censée vous donner la virilité ancestrale. Que ce soit vrai ou pas, l'effet est périmé." ));
            allItems.Add(new Items ( 8469, "Talisman du Chêne Mou", "Cette pièce de bois prélevée au coeur du Chêne mou renferme un grand pouvoir, comme celui de mettre le Chêne Mou en colère." ));
            allItems.Add(new Items ( 8470, "Anneau du Chêne Mou", "Cet anneau est un excrément du Chêne Mou, en bois, mais l'odeur est la même." ));
            allItems.Add(new Items ( 8471, "Tongues du dimanche du Chêne Mou", "Le dimanche, le Chêne Mou fait la sieste, et lorsqu'il se réveille, il aime qu'un Tronknyde obéissant lui apporte ses tongues sans qu'il les demande. Il n'a malheureusement pas de pieds, elles sont donc encore neuves." ));
            allItems.Add(new Items ( 8472, "Cape Usée du Chêne Mou", "Il y a plus de 200 ans, une étoffe d'un tissu anodin s'est prise dans les branches du Chêne Mou. L'arbre a poussé autour et au travers, l'imprégnant ainsi de sa magie. Elle est désormais indissociable de ce gros morceau de bois ce qui la rend peu pratique et inflammable, mais sa puissance est indéniable." ));
            allItems.Add(new Items ( 8473, "String Automnal du Chêne Mou", "En automne, le Chêne Mou porte son string pour protéger ses précieux glands. En été, il les trimballe à l'air." ));
            allItems.Add(new Items ( 8474, "Coiffe du Chêne Mou", "Vous pouvez porter cette coiffe dans n'importe quel sens, on aura toujours l'impression qu'elle n'est pas droite." ));
            allItems.Add(new Items ( 8476, "Première clef du Donjon de Sphincter", "Cette clef ne vous suffira pas pour pénétrer dans le donjon de Sphincter, il vous faut l'autre clef !" ));
            allItems.Add(new Items ( 8477, "Deuxième clef du Donjon de Sphincter", "Cette clef ne vous suffira pas pour pénétrer dans le donjon de Sphincter, il vous faut l'autre clef !" ));
            allItems.Add(new Items ( 8478, "Potion de lèche-bottes", "Cette potion hautement illégale est redoutée par les plus grands. Une simple gorgée et la personne n'a qu'une obsession, lécher les bottes les plus proches. Certains disent que des variantes qui ciblent d'autres endroits existent. Le malheureux buveur aura alors mauvaise haleine." ));
            allItems.Add(new Items ( 8480, "Potion de lèche-bottes", "Cette potion hautement illégale est redoutée même des plus grand. En effet, une petite gorgée et le buveur n'a plus qu'une obsession : celle de lécher les bottes les plus proches. Des rumeurs parlent d'une version alternative qui cible autre chose. Le seul indice quant à cet endroit est la mauvaise haleine des buveurs malchanceux." ));
            allItems.Add(new Items ( 8481, "Poil de Rat d\'Egoutant", "Ce poil est sale et malodorant, il a trainé dans les égoûts les plus insalubres de la région. Et pourtant il brille encore." ));
            allItems.Add(new Items ( 8482, "Peau de Rat d\'Egoutant", "Cette peau est recouverte de petites cicatrices qui témoignent de l'enfance difficile qu'a connue le Rat d'Egoutant. Vous pourriez au moins avoir un peu de pitié pour lui !" ));
            allItems.Add(new Items ( 8483, "Peau de Chaman d\'Egoutant", "Une fois lavée, cette peau pourra faire office de tapis de sol, ou de paillasson." ));
            allItems.Add(new Items ( 8484, "Poil de Chaman d\'Egoutant", "Utilisé autrefois pour les moustaches postiches, ce type de poil convient également parfaitement pour les poils d'aisselle postiches." ));
            allItems.Add(new Items ( 8485, "Etoffe du Rat Noir", "Enroulée autour d'une lamelle de bois tendre, cette étoffe pourra servir d'éponge, ou même de gratte-fesses. Il en faut pour tous les goûts." ));
            allItems.Add(new Items ( 8486, "Etoffe du Rat Blanc", "Cette étoffe aussi grasse qu'un poil de Wabbit trempé dans la graisse de Blop, protège efficacement du froid, surtout si vous y mettez le feu." ));
            allItems.Add(new Items ( 8487, "Etoffe de Sphincter Cell", "Cette Etoffe semble recouverte d'un produit chimique étrange aux effets secondaires inconnus. Mieux vaut ne pas garder cette Etoffe trop longtemps entre les mains, un gros poil pourrait y pousser." ));
            allItems.Add(new Items ( 8488, "Cubitus du Rat Noir", "Cet os de rat peut être vidé, percé et taillé pour ensuite servir d'instrument de musique, ou de sarbacane si vous n'avez pas l'oreille musicale." ));
            allItems.Add(new Items ( 8489, "Dent de Rat Blanc", "Cette dent est en réalité un horrible croc qui permettait au Rat Blanc de transmettre une multitude de maladies à ses victimes lorsqu'il les mordait. Une façon comme une autre de s'assurer de la mort de ses victimes, même s'il ne parvenait pas à les achever. Tous les moyens sont bons lorsque l'on veut se faire respecter." ));
            allItems.Add(new Items ( 8490, "Fémur de Sphincter Cell", "Sphincter n'était pas du genre à plaisanter avec les os, il les brisait en deux, puis en trois, puis en quatre, et parfois même en cinq, lorsqu'il fallait ragaillardir les foules endormies pas un combat lassant." ));
            allItems.Add(new Items ( 8491, "Peau du Rat Noir", "Le Rat Noir était réputé pour sa mauvaise haleine, mais aussi pour la peau rugueuse de son postérieur. Très pratique pour ne pas glisser sur la cuvette des toilettes. Malheureusement, le Rat Noir ne prenait jamais la peine d'aller jusqu'aux toilettes, puisqu'il vivait directement dans les égouts." ));
            allItems.Add(new Items ( 8492, "Peau de Rat Blanc", "Le Rat Blanc prenait un malin plaisir à se morfondre au fond de ses égouts, et à attendre qu'une personne bien attentionnée vienne le réconforter. Il profitait ensuite de la naïveté et de la crédulité de ses victimes, pour les dépecer vivantes, et les rôtir en les ayant au préalable fourrées avec des noix de cajou. Pauvres noix de cajou." ));
            allItems.Add(new Items ( 8493, "Peau de Sphincter Cell", "Qui aurait pu croire que vous seriez un jour capable de tenir entre vos mains, la peau du célèbre Sphincter ? Certainement pas Sphincter. Sa trop grande confiance en lui aura causé sa propre perte." ));
            allItems.Add(new Items ( 8494, "Ecorce d\'Abraknyde Ancestral", "Cette écorce s'est détachée de l'Abraknyde Ancestral, sans que vous n'ayez eu besoin de la lui arracher avec les dents." ));
            allItems.Add(new Items ( 8495, "Bourgeon de l\'Abraknyde Ancestral", "Ce bourgeon desséché a été jadis entièrement vidé de sa sève par une créature affamée. Si vous le plongez dans l'eau, vous pourrez le voir reprendre sa forme initiale, gonfler, puis si vous attendez très longtemps, pourrir." ));
            allItems.Add(new Items ( 8496, "Racine d\'Abraknyde Ancestral", "Même arrachée à son propriétaire, cette racine peut vous fouetter les fesses ou vous tordre les chevilles. Sale bête !" ));
            allItems.Add(new Items ( 8497, "La recette de la filtounga", "Ce livre renferme les secrets de la filtounga, le plat mythique imaginé par Mouss Bouff." ));
            allItems.Add(new Items ( 8498, "Viande de Kanigrou", "Cette viande est aussi tendre qu'un cerveau de Boue." ));
            allItems.Add(new Items ( 8499, "Viande de Koalak", "La viande de Koalak est en règle générale assez ferme. Il suffit simplement de l'attendrir en la frappant avec un tronc de Kaliptus. Comme pour les enfants indisciplinés." ));
            allItems.Add(new Items ( 8500, "Viande de Crocodaille", "Cette viande insipide peut être conservée dans la vase pendant plusieurs années, avant de pourrir et de commencer à avoir du goût." ));
            allItems.Add(new Items ( 8501, "Viande de Kanigrou Conservée", "Cette viande de Kanigrou peut maintenant être conservée plusieurs jours avant d'être utilisée pour un plat." ));
            allItems.Add(new Items ( 8502, "Viande de Koalak Conservée", "Cette viande Koalak peut maintenant être conservée plusieurs jours avant d'être utilisée pour un plat." ));
            allItems.Add(new Items ( 8503, "Viande de Crocodaille Conservée", "Cette viande de Crocodaille peut maintenant être conservée plusieurs jours avant d'être utilisée pour un plat." ));
            allItems.Add(new Items ( 8504, "Entrecôte de Kanigrou", "A manger chaude ou froide, cette entrecôte saura rassasier les appétits les plus voraces." ));
            allItems.Add(new Items ( 8505, "Filet de Koalak", "Ce morceau de viande remplira même les estomacs sans fond." ));
            allItems.Add(new Items ( 8506, "Entrecôte de Crocodaille", "Cette part de viande imposante est de toute évidence, très difficile à digérer. Mais la gourmandise l'emporte toujours sur la raison." ));
            allItems.Add(new Items ( 8507, "Plat de Filtounga", "La Filtounga est une plat unique. Grâce aux ingrédients qui le composent, il garde une saveur ... unique. Servir tiède." ));
            allItems.Add(new Items ( 8508, "Plat de Filtounga", "Ce plat est pour Paindep la sorcière." ));
            allItems.Add(new Items ( 8509, "La recette de la bière Bwork", "Le grand mage Bworknroll dévoile ici la recette sacrée de la bière Bwork. Il n'épargne aucun secret de fabrication. Il va même jusqu'à donner la meilleure manière de la déguster. Un régal pour les amateurs, un cauchemar pour ceux qui ont déjà bu de cette bière sans la connaître." ));
            allItems.Add(new Items ( 8511, "Arbalète de Surpuissance Ténébreuse de la Mort qui Tue", "...." ));
            allItems.Add(new Items ( 8513, "Scalpel de Bworknroll", "Pour récolter des « morceaux » de créature, rien ne vaut le scalpel de Bworknroll qui préserve la vitalité des membres arrachés. Il faut l'utiliser au combat pour récupérer les parties désirées." ));
            allItems.Add(new Items ( 8514, "Cervelle de vampire", "Surchargée de globules frais en permanence, la cervelle d'un vampire aspire vers lui toutes les globules rouges disponibles. C'est ce qui donne  aux vampires un teint pâle. Chez les Iop, ce sont plutôt les pieds qui attirent leur sang, d'où leur adresse à bondir au lieu de réfléchir..." ));
            allItems.Add(new Items ( 8515, "Os de Trooll", "Lorsque l'on constate la solidité et le poids d'un os de Trooll, on comprend mieux pourquoi leurs claques font si mal lors d'une partie de jeu Trooll." ));
            allItems.Add(new Items ( 8516, "Coeur de Bworkcelhaine", "Cette Bworkette sans coeur qui a jeté son fils Krazybwork dans une fosse a été maudite par Bworknroll à qui elle a refusé son amour. Enfermée dans une cave, elle se nourrit de son propre coeur chaque jour. Toutes les nuits, il repousse et bat à nouveau de mille feux." ));
            allItems.Add(new Items ( 8517, "Moumoule", "Cette belle Moumoule fraîche sert aux Bwork esseulés lors qu'ils partent en rapines loin de leurs compagnes." ));
            allItems.Add(new Items ( 8518, "Moumoules frites", "Dans les profondeurs de la Mer d'Asse, les Moumoules récoltent du varech qu'elles roulent sous leurs aisselles. Une fois la matière formée en frites, elles se hissent sur la plage pour les faire dorer au soleil avant de les ingérer." ));
            allItems.Add(new Items ( 8519, "Gâtrool", "Cette galette rituelle fourrée aux krottes de Trool est une vieille recette bwork. Seul un Bwork qui a vaincu un Trool à mains nues a le droit d'en manger. S'il a survécu à ce combat, il est prêt à avaler n'importe quoi !" ));
            allItems.Add(new Items ( 8520, "Gratrooll", "Cette graisse épaisse et nutritive permet de cuire la plupart des aliments dans de vieilles marmites sans accrocher le plat. Certains adeptes Osamodas s'en servent aussi pour glisser des Tofus dans des endroits que la morale Iop réprouve." ));
            allItems.Add(new Items ( 8521, "La cape rouge", "La cape rouge est l'artefact préféré des mégalomanes. Une fois posée sur leurs frêles épaules, ils ont l'impression d'incarner Superman ou un magicien surpuissant jusqu'à ce qu'ils croisent un Minotoror." ));
            allItems.Add(new Items ( 8522, "Lanceur d\'effet Magique", "Cet objet surprenant renferme un concentré de magie obscure plutôt instable. Personne ne peut prédire ce qu'il renferme, mais il est déconseillé de trop attendre avant de l'utiliser." ));
            allItems.Add(new Items ( 8523, "La relique des Robill", "Cet objet familial suinte d'un flux magique extrêmement puissant. Transmis de génération en génération entre les membres de cette famille, il n'a rien perdu de son pouvoir. Il n'existe que peu d'artéfacts d'où émane une telle force. Il a peut-être été en contact avec un Dofus..." ));
            allItems.Add(new Items ( 8524, "Fantôme Walk", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8525, "Oeuf de piou", "Les œufs de pious sont un vrai régal pour les amateurs de friandises. Cependant, ils éclosent très vite lorsqu'ils sont proches d'une source de chaleur. C'est pourquoi, il faut éviter de les laisser traîner dans sa poche ou dans sa bouche car au bout de quelques minutes un mini piou peut sortir de sa coquille. Avoir un piou affamé coincé entre vos dents ou se faufilant dans vos vêtements peut vous chatouiller le palais ou d'autres parties plus intimes de votre anatomie." ));
            allItems.Add(new Items ( 8526, "Certificat de Mise en Chanil : Koalak Sanguin", "Ce certificat décrit les caractéristiques du Koalak Sanguin que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8527, "Gobtroteur", "Ces bottes en peaux de gobelins ont été créées par Planbworksek afin de charger l'ennemi (si possible dans le dos) avec une grande vélocité. En chaussant ces bottes, il a sauvé plus d'un camarade Bwork dans la panade." ));
            allItems.Add(new Items ( 8528, "La recette des crêpes", "Grâce à Pol Bokuz, vous saurez enfin réussir votre pâte à crêpe pour nourrir vos petits enfants et les envoyer rapidement à la sieste. Le secret de cette recette nourrissante et soporifique : la poudre de perlinpainpain bien sûr !" ));
            allItems.Add(new Items ( 8529, "Le marteau à Zaap", "Rien ne vaut un bon coup de marteau pour réparer un Zaap déréglé !" ));
            allItems.Add(new Items ( 8530, "Ruskauffe", "Cette coiffe tient chaud aux oreilles, et absorbe les effluves d'alcool qui s'échappent de votre haleine. Que demander de plus ?" ));
            allItems.Add(new Items ( 8531, "Lapinocoiffe", "Qui oserait porter une coiffe aussi ridicule ? Personne excepté vous." ));
            allItems.Add(new Items ( 8532, "Cape du Lapino Blasé", "Avec cette cape sur les épaules, vous pourrez enfin montrer aux personnes à qui vous tournez le dos, que vous êtes blasé par leur stupidité." ));
            allItems.Add(new Items ( 8533, "Le Floude", "Le Floude n'est pas réellement un chapeau mais un parasite se développant sur le crâne des personnes trop volubiles, excessivement bruyantes, ou simplement verbalement agressives. Son aspect repoussant fait fuir les gens, ce qui pousse son porteur à crier encore plus fort pour se faire entendre, sans succès évidement, puisqu'il n'a rien à dire de toute façon." ));
            allItems.Add(new Items ( 8534, "La Cape S\'loque", "Cette cape molletonnée absorbe tous les bruits environnants, obligeant son porteur à hurler en permanence." ));
            allItems.Add(new Items ( 8535, "Les Incrustes", "Ces Getas sont si légères qu'elles poussent leur propriétaire à courir dans tous les sens et à sauter partout lorsqu'un combat commence. Les aventuriers expérimentés détestent ces getas, qui permettent aux premiers venus de prendre part à un combat auquel ils n'ont pas été invités." ));
            allItems.Add(new Items ( 8536, "La Spamette", "Cette ceinture se porte si serrée qu'elle force presque son porteur à bégayer, trembloter et faire n'importe quoi, tant que c'est en boucle." ));
            allItems.Add(new Items ( 8537, "Le Plussain", "Ce petit anneau ne sert à rien, sinon à être très voyant, histoire de montrer aux gens que vous existez." ));
            allItems.Add(new Items ( 8538, "Le S\'Mesme", "Après plusieurs nuits de dur labeur pour réaliser cette amulette, le plus grand bijoutier d'Astrub, connu pour avoir été élevé par les Bworks s'exclama devant une foule ébahie : " ));
            allItems.Add(new Items ( 8539, "Hache de l\'apprenti bûcheron", "Même si vous ne savez pas très bien manier la hache, vous ne risquez guère de vous couper avec celle-ci tant la lame est émoussée. Mais bon, en tapant suffisamment fort, vous arriverez bien à couper quelques arbres." ));
            allItems.Add(new Items ( 8540, "Faux de l\'apprenti paysan", "Cette faux est de qualité médiocre mais on vous l'a offerte, vous n'avez donc pas à vous plaindre. De plus, elle fauche correctement le blé et on ne lui en demande pas plus." ));
            allItems.Add(new Items ( 8541, "Canne de l\'apprenti pêcheur", "Cette canne à pêche a connu des jours meilleurs, le fil n'est plus très solide et l'hameçon incertain. Parfaite pour la pêche au goujon." ));
            allItems.Add(new Items ( 8542, "Gants de l\'apprenti alchimiste", "Des gants d'une qualité indiscutable, presque pas de trou, l'odeur de sueur de l'ancien propriétaire est tout juste perceptible, des doigts trop courts,  un maintien incertain... C'est indiscutable, ces gants sont pourris." ));
            allItems.Add(new Items ( 8543, "Limonade d\'Incarnam", "Cette limonade fabriquée par les dieux est un mélange d'eau, de bulles, et d'extraits d'animaux. Elle revigore les jeunes âmes venant justes de s'incarner dans Incarnam. Pour les autres, c'est de l'eau gazeuse tout juste bonne à s'entraîner pour les concours de rots." ));
            allItems.Add(new Items ( 8544, "Certificat de Mise en Chanil : Walk", "Ce certificat décrit les caractéristiques du Walk que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8545, "Clef du Donjon d\'Incarnam", "Cette clef permet d'entrer dans le donjon d'Incarnam, habité par le terrible Milimilou." ));
            allItems.Add(new Items ( 8546, "Peau de Milimilou", "De légères bouclettes font de cette peau une belle descente de lit. Il est certain que quelqu'un sur Incarnam est prêt à troquer contre cette peau. Reste à trouver qui ..." ));
            allItems.Add(new Items ( 8547, "Trinsfirmitik ik Nikiril", "Lik nisittik miditik sint mivisik pir lik sintik." ));
            allItems.Add(new Items ( 8548, "Twansfowmation en Tiwabbit", "Les cawottes maudites sont mauwaises pouw la santé." ));
            allItems.Add(new Items ( 8557, "Plume du Batofu", "Cette plume directement arrachée des aisselles du Batofu diffuse une odeur de Doubitchou ancestral mariné." ));
            allItems.Add(new Items ( 8558, "Chapeau du Lapino", "Si vous envisagez de dialoguer avec les Wabbits, c'est peut-être la coiffe qu'il vous faut. Si vous cherchez à séduire, posez cette coiffe sur la tête de vos rivaux." ));
            allItems.Add(new Items ( 8559, "La HmmMmmMmmm", "Mmmmm mm mmmmmm mmmmm, mmm mmm mm mmmmmm mmmmm mmmmmmm mmmmmm mmm mmmm'm mmmmm mmmm mmmmmmm." ));
            allItems.Add(new Items ( 8561, "Bébé Pandawa", "Cet adorable bébé Pandawa adorera vous suivre partout pour découvrir le monde de ses grands yeux ronds. Ce familier apprivoisé mange du riz, mais accepte également les noisettes." ));
            allItems.Add(new Items ( 8563, "Titi Gobelait", "Titi doit rencontrer son futur professeur de magie." ));
            allItems.Add(new Items ( 8564, "Certificat de Mise en Chanil : Bébé Pandawa", "Ce certificat décrit les caractéristiques du Bébé Pandawa que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8565, "Fantôme Bébé Pandawa", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8566, "Transformation Walk", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8567, "Transformation Fantôme Bébé Pandawa", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8568, "Cadeau Bébé Pandawa", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8569, "Doro le Blak", "Cette coiffe appartenait à une petite Xélorette, qui prenait un malin plaisir à dérégler l'écoulement du temps. La légende raconte qu'elle parvint à voler plusieurs jours aux habitants d'Amakna. Elle ne s'est soustraite à la colère de la population qu'en distribuant de nombreuses friandises." ));
            allItems.Add(new Items ( 8570, "Poil de Rat Bontarien", "Ce poil appartenait aux rats des égouts de Bonta, les seuls qui possèdent des touffes de poils assorties aux couleurs des murailles de leur cité." ));
            allItems.Add(new Items ( 8571, "Poil de Rat Brâkmarien", "Ce poil appartenait aux rats des égouts de Brâkmar, les seuls qui possèdent des touffes de poils assorties aux couleurs des murailles de leur cité." ));
            allItems.Add(new Items ( 8572, "Potion d\'oubli : \'Apprivoisement de monture\'", "Cette potion d'oubli permet d'oublier un niveau du sort " ));
            allItems.Add(new Items ( 8573, "Potion d\'oubli : \'Boomerang perfide\'", "Cette potion d'oubli permet d'oublier un niveau du sort 'Boomerang perfide'." ));
            allItems.Add(new Items ( 8574, "Dofus vinaigrette", "Avec ou sans lardons, ce Dofus est toujours aussi bon." ));
            allItems.Add(new Items ( 8575, "Le Ramboton", "Un célèbre guerrier Féca qui vivait dans la jungle de Pandala disait souvent " ));
            allItems.Add(new Items ( 8576, "Parchemin de recommandation d\'Yse Vewybad", "Ce parchemin vous a été remis par Yse Vewybad pour services rendus." ));
            allItems.Add(new Items ( 8588, "Insigne Roublard", "Cet insigne, donné par un roublard, sera peut-être la clef pour rentrer dans le repaire de ces derniers." ));
            allItems.Add(new Items ( 8590, "Contrat entre Vil Smisse et Yse Vewybad", "Ce contrat prouve la félonie d'Yse Vewybad. Il ne reste plus qu'à le porter à qui de droit." ));
            allItems.Add(new Items ( 8593, "Potion de transformation en Wa Wabbit", "Cette potion vous a été remise par Uba Tessé. Elle va vous transformer en Wabbit et devrait vous permettre de vous faire passer pour le prétendant d'Allisteria." ));
            allItems.Add(new Items ( 8594, "Potion de twansfowmation en Wa Wabbit", "Cette potion vous a été remise par Uba Tessé. Elle va vous transformer en Wabbit et devrait vous permettre de vous faire passer pour le prétendant d'Allisteria." ));
            allItems.Add(new Items ( 8595, "Oeuf de Tofu Obèse", "Cet oeuf renferme un tofu obèse, peut-être le futur vainqueur du pwince d'Wa." ));
            allItems.Add(new Items ( 8596, "Griffe Pourpre", "Cette épée acérée a pris cette teinte pourpre si élégante après avoir éviscéré des milliers de Bworks. Alors qu'avant, elle n'était que violet foncé." ));
            allItems.Add(new Items ( 8597, "Potion de Gavage", "Cette potion va permettre à Vil Smisse d'engraisser le tofu obèse que vous lui avez ramené." ));
            allItems.Add(new Items ( 8598, "Dagues Ruyère", "Ces dagues sont perforées tout au long de la lame, afin d'émettre un joli sifflement quand on les manie rapidement. Les trous sont également très efficaces pour entraîner les viscères de ses ennemis." ));
            allItems.Add(new Items ( 8599, "Griffettes", "Ces dagues ont été forgées pour une écaflipette maniaque de la manucure. Elle trouvait que les lourdes épées s'accordaient mal à ses jolies griffes vernies." ));
            allItems.Add(new Items ( 8600, "La flemme de monter les escaliers", "Vous avez attrapé un gros coup de fatigue à force de crier à tous vents, et vous ne vous sentez plus capable de grimper autant de marches avant au moins 6 minutes ..." ));
            allItems.Add(new Items ( 8603, "Livrée de valet du Prince Wabbit.", "Avec ça, le valet du prince Wabbit sera à la dernière mode." ));
            allItems.Add(new Items ( 8604, "Epée Doncule", "Cette épée brillante est idéale pour cueillir des fleurs, même si elles se défendent. Elle est même parfaite pour cueillir des arbres." ));
            allItems.Add(new Items ( 8605, "Epée Zervatif", "Cette lame est lubrifiée afin de glisser plus facilement dans son fourreau. En revanche elle est impossible à nettoyer et doit être jetée après utilisation, surtout si elle est recouverte de sang. N'oubliez pas qu'elle sert avant tout à se protéger." ));
            allItems.Add(new Items ( 8606, "Robe de la mariée", "La robe de la mariée est soigneusement emballée dans ce beau paquet. Il ne reste plus qu'à l'apporter à l'intendant San Lavalss." ));
            allItems.Add(new Items ( 8607, "Le Bâton Cloutay", "C'est un gros bâton, avec des clous plantés dedans. N'y cherchez pas autre chose, c'est la simplicité même, et pourtant, il est diablement efficace pour déchausser les dents de vos adversaires." ));
            allItems.Add(new Items ( 8608, "La Bastonneuze", "On raconte qu'un disciple de Sadida du nom de Fraye a utilisé ce bâton pour défendre sa femme et son fils face à un troupeau de Bouftous enragés. Il est regrettable qu'il ait confondu sa famille avec les monstres pendant l'échauffourée." ));
            allItems.Add(new Items ( 8609, "Geish\'Arc", "Cet Arc est léger, fin, élégant, et sent bon la fleur de cerisier. Il est également meurtrier si l'on s'en sert pour autre chose que se pavaner." ));
            allItems.Add(new Items ( 8610, "Le Kikoularc", "Si vous tenez cet arc bien haut et prononcez avec entrain le mot " ));
            allItems.Add(new Items ( 8611, "Bagdette Café", "Cette baguette appartient au célèbre Grinegau, qui fait du très bon café, mais qui ne vendra pas son échelle." ));
            allItems.Add(new Items ( 8612, "La Bidjiz", "Avec la Bidjiz, vous resterez vivant, sûrement parce que vos adversaires se mettront à danser en voyant les multiples facettes de la boule qui orne cette baguette." ));
            allItems.Add(new Items ( 8613, "Pelle Minstère", "Créée comme une œuvre d'art de la forgemagie, cette pelle creuse très mal, sauf dans la tête de vos adversaires." ));
            allItems.Add(new Items ( 8614, "Pelle Ripe", "Magnifique invention, cette pelle deux-en-un permet de creuser une tombe et de la laisser comme croix. A condition de ne perdre qu'un compagnon à la fois, sous peine de creuser les tombes suivantes avec les mains." ));
            allItems.Add(new Items ( 8615, "Marteau Mato", "Ce marteau réduit si facilement les crânes en bouillie qu'il a pris une élégante teinte rouge tomate." ));
            allItems.Add(new Items ( 8616, "Marteau Minokers", "Ce marteau a été créé à l'époque du Roi Stéphane, qui a terrorisé des millions de gens." ));
            allItems.Add(new Items ( 8617, "Hache Ebbah", "Pour dire je t'aime." ));
            allItems.Add(new Items ( 8618, "Hache Eulouh", "Cette étrange hache est profilée de façon à décapiter plus facilement les Bworks. L'aisance avec laquelle elle décapite aussi les habitants d'Astrub indique clairement qu'il existe un lien entre ces deux populations. Mais personne n'a encore trouvé lequel." ));
            allItems.Add(new Items ( 8619, "Casque Keutumedi", "Grâce à cette coiffe guerrière, les Iops ont vraiment fière allure. Le seul problème est qu'elle leur cache une partie de leur champ de vision et les empêche d'entendre correctement les paroles de leurs adversaires. Le casque Keutumedi est vraiment fait pour les gens qui aiment frapper avant de réfléchir." ));
            allItems.Add(new Items ( 8621, "Cawotte Maudite", "Cette appétissante cawotte à l'aiw owdinaiwe mais elle sent le maléfice à plein museau. Wésistewez-vous à la tentation de la cwoquew à pleines dents ?" ));
            allItems.Add(new Items ( 8624, "Ballon Bleu Magique", "Contrairement aux contrefaçons que l'on trouve à la foire du Trool, ce ballon-ci sait au moins s'envoler avec grâce et aisance. Il est confectionné à partir d'une larve bleue, vidée encore remuante, puis gonflée par un Trool, selon la traditionnelle recette du grand mage Vahy Nez." ));
            allItems.Add(new Items ( 8625, "Ballon Vert Magique", "Ce ballon vert est le résultat assez étrange qui se produit lorsqu'une larve verte mange un champignon Luidegît. Notez qu'après ce repas les yeux de la larve sortent généralement de leur orbite et roulent au sol. Ils sont alors souvent ramassés par les bworks des environs, friands de ces mets pour leur apéritif. Ce ballon a inspiré ses célèbres recettes au grand mage Vahy Nez." ));
            allItems.Add(new Items ( 8626, "Nisitik Miditik", "Cittik ippitissintik nisitik ii ik'ik irdinirik mis illik sik lik milificik ii plinik misik. Risistirik-vis ik lik tintitinik dik lik grinitik ii plinik dints ?" ));
            allItems.Add(new Items ( 8627, "Flûte de Sarr Ys", "Confrère et ami du célèbre Guy-guy (proche parent du Chêne Mou), Sarr Ys est un troubadour dont les notes enchanteresses ont gravées leurs empruntes dans la mémoire d'Amakna. Malheureusement, ce brillant artiste est décédé tragiquement dans un accident de Koin Koin, lors d'une beuverie estivale." ));
            allItems.Add(new Items ( 8628, "Chapeau Leufère", "Cette coiffe procure assurance et témérité à celui qui la porte. Grâce à elle vous irez toujours de l'avant, en pensant être capable de réaliser tous les exploits. Pensez toutefois à l'enlever de temps en temps, sinon elle vous entraînera dans des situations inextricables !" ));
            allItems.Add(new Items ( 8629, "Chapeau Grave", "À cause de cette coiffe, les adversaires deviennent souvent muets comme une tombe. On n'ose demander à quoi est censé ressembler ce couvre-chef, on ne peut plus atypique. Mais ce n'est pas très grave. Les Srams ne sont guère susceptibles." ));
            allItems.Add(new Items ( 8630, "Bonnet Nufar", "Cette coiffe ressemble beaucoup au Sulik et au Piruk. Mais comme elle est très confortable et que les Xélors ne la quittent jamais, même pour dormir, elle est communément nommée Bonnet Nufar. Elle dégage une agréable odeur de nymphéa qui contribue à l'endormissement. En revanche, les ronflements qu'elle provoque sont aussi insupportables que les chants de crapaud lors de la saison des amours." ));
            allItems.Add(new Items ( 8631, "Coiffe Fère", "Grâce à la Coiffe Fère, les Eniripsas savent toujours comment agir en cas d'urgence. En effet, la liste des gestes de premiers secours y est discrètement inscrite dans la doublure. Ce couvre-chef leur donne un air doctoral, comme s'ils venaient de recevoir leur diplôme, alors que personne n'oserait arborer un chapeau aussi ridicule lors d'une cérémonie scolaire !" ));
            allItems.Add(new Items ( 8632, "Chapeau Tyron", "On aurait pu croire que cette coiffe possédait une jolie couleur orangée. C'est pas du tout le cas. De plus, cet accessoire de mode tient plus du masque que du chapeau. Alors pourquoi ces créateurs l'ont-ils nommé Chapeau Tyron ? Sûrement pour faire parler les bavards !" ));
            allItems.Add(new Items ( 8633, "Bonnet Spairance", "Les pointes acérées qui sortent de ce bonnet permettent non seulement de blesser l'adversaire mais aussi d'avoir plus de chance de se prendre la foudre sur le crâne par temps d'orage. Les Sacrieurs sont en effet une classe qui aime autant faire souffrir les autres qu'eux-mêmes. Une bonne odeur de chair grillée : rien de tel pour commencer la journée !" ));
            allItems.Add(new Items ( 8634, "Capuche Apin", "Grâce à cette coiffe, vous aurez vraiment l'air d'une bête à cornes. Nul doute que l'on se retourne pour admirer la très grosse paire qui orne votre tête. Cette ample capuche permet aussi de conserver au frais des aliments comme le foin pour Bouftou ou le pain pour Tofu." ));
            allItems.Add(new Items ( 8635, "Chapeau Lithique", "Cette coiffe a la propriété de faire dire au porteur des promesses qu'il oublie aussitôt. Est-ce parce qu'elle exerce une pression sur les zones de la mémoire ou sur celle du langage ? Nul ne le sait précisément. L'important est de ne prendre en compte que les paroles les moins extravagantes." ));
            allItems.Add(new Items ( 8636, "Coiffe de Robbie Capuche", "Robbie Capuche était un célèbre Crâ qui vivait dans les bois, d'où la couleur verte de sa coiffe pour se fondre dans le paysage. Il se prenait pour un justicier et passait tout son temps à dérober l'argent des riches au profit d'une bonne cause : la sienne." ));
            allItems.Add(new Items ( 8637, "Bonnet Toual", "En revêtant cette coiffe, le Féca accroît sa chance et celle de ses alliés. Si votre groupe est guidé par un Féca portant un Bonnet Toual, vous êtes sûr d'arriver à bon port et ce presque sans une égratignure. Mais l'odeur est si nauséabonde que cela en fait l'objet idéal pour se protéger de la veuve et de l'orphelin." ));
            allItems.Add(new Items ( 8638, "Chapeau Taufeu", "Ce couvre-chef sert aussi bien de protection contre les intempéries que d'accessoire de cuisine, pour mitonner des plats à la bonne température. L'agitation du chapeau permet d'apporter l'afflux d'air nécessaire à la bonne tenue du feu de cuisson. Et, comme tout le monde sait que le pot au feu est un plat qui se mijote pendant longtemps, on comprend mieux pourquoi les Enutrofs ont des poignets si musclés." ));
            allItems.Add(new Items ( 8639, "Cape Aurale", "La cape qui flotte au vent est indispensable pour vous procurer une aura de guerrier, surtout si vous tournez le dos à l'adversaire. Certains pensent qu'elle est renforcée par une plaque de métal qui permet de parer tous les coups sournois." ));
            allItems.Add(new Items ( 8640, "Cape Aillé", "Faite dans un tissu doux et résistant, cette cape donne aux Ecaflips l'impression qu'ils sont toujours des as, quoi qu'il arrive. Lorsqu'ils remportent la victoire, ils adorent se draper dans ce vêtement avant de réclamer leur récompense : « Et maintenant, t'as qu'à payer ! »" ));
            allItems.Add(new Items ( 8641, "Cape Routh", "Cette cape est si légère et si fine qu'elle s'envole à la moindre brise de vent, de n'importe quel vent. Pas toujours très pratique, surtout lorsque l'on veut se faire discret, après un repas composé de légumineuses et de diverses boissons fermentées." ));
            allItems.Add(new Items ( 8642, "Cape Huchon", "La couleur sombre de cette cape lui permet d'être en harmonie avec n'importe quel vêtement, quel que soit son coloris. Comme elle est très ample, elle permet aussi de protéger la tête et le bout du nez." ));
            allItems.Add(new Items ( 8643, "Cape Sulhit", "La Cape Sulhit se met délicatement sur les épaules afin de protéger son porteur des tendinites et de l'enraidissement des articulations. Vêtement aux vertus médicales, il est regardé avec envie par les Pandawas, qui passent beaucoup de temps à porter leur adversaire comme leur allié. Eux-aussi, ils aimeraient avoir un vêtement de ce type." ));
            allItems.Add(new Items ( 8644, "Cape Hitton", "Cet objet sert à cacher les surcharges pondérales des Sadidas. À force de dormir au lieu de faire de l'exercice physique, les personnages de cette classe développent une tendance à l'embonpoint. Si les Sadidettes possèdent des réserves anti-famine, avantageusement placées pour flatter les regards masculins, les Sadidas tentent plutôt de masquer les leurs grâce à cette cape." ));
            allItems.Add(new Items ( 8645, "Cape Ytale", "Ce vêtement joue un rôle primordial dans la prestance des Sacrieurs. Ils s'en servent comme bandage pour panser les plaies, comme serpillière pour essuyer le sang, comme descente de lit pour ne pas avoir froid aux pieds. On ne dira jamais à quel point les personnages de cette classe redoutent les sols gelés !" ));
            allItems.Add(new Items ( 8646, "Cape Lyne", "Cette douce cape est parfois employée par les Osamodettes coquettes en guise de couvre-chef aux larges bords. Les Osamodas mâles ne s'en servent que comme pèlerine. Grâce au dessin qui orne cet habit, cette classe de personnages a hérité d'une réputation de grande franchise. On ne les voit jamais rire sous cape car le rire est cousu dessus." ));
            allItems.Add(new Items ( 8647, "Cape Wéra", "Les Pandawas se servent de cette cape comme protection contre les intempéries et comme accessoire de danse. Ils la posent à terre, au centre d'un cercle de danseurs éméchés. Puis, ils commencent leur chorégraphie improvisée sans que le pied touche le sol en dehors de la zone délimitée par ce vêtement." ));
            allItems.Add(new Items ( 8648, "Cape Hulco", "La cape Hulco tire son origine du nom d'un Crâ atypique. Celui-ci possédait un sens de l'orientation très particulier. Il était capable de se perdre chez lui. Et pourtant ce n'était pas bien grand ! Son problème résidait dans une déformation des fonctions du cerveau : pour lui, tous les lieux ressemblent une vaste plage, entourée de cocotiers, baignée par la lumière chaude d'un soleil couchant." ));
            allItems.Add(new Items ( 8649, "Cape Hadosse", "Les couleurs chaudes et réconfortantes de cette cape peuvent entraîner son porteur dans un voyage imaginaire, dans des contrées ensoleillées, pleines de canyons creusés par l'érosion, et de décors rupestres taillées dans les roches et les falaises. C'est extrêmement utile pour oublier le froid et la grisaille par temps de pluie." ));
            allItems.Add(new Items ( 8650, "Cape Haharnum", "Les Enutrofs ont pris l'habitude de dessiner une carte sur leur cape, car ils vivent dans un tel foutoir qu'il leur est difficile de retrouver le chemin des toilettes. Cela est très problématique, surtout lorsque l'on est sujet à des envies aussi pressantes qu'incontrôlables !" ));
            allItems.Add(new Items ( 8651, "Sangle Hynère", "Cette magnifique sangle ajoute du prestige à la tenue des Iops. Certains prétendent que la croix centrale peut se transformer en une arme amovible, lorsque l'épée n'est pas à portée de main. C'est bien sûr une légende, car seuls les Srams seraient assez fourbes pour ce genre de pratique." ));
            allItems.Add(new Items ( 8652, "Ceinture Buhlent", "Lorsqu'un Ecaflip ne sait pas quoi faire, il dégaine son jeu de carte de sa ceinture et entame un solitaire. En combat, il se sert également de ses cartes en tant que projectiles. Il tire plus vite que son ombre et, après la victoire, il est toujours temps de faire une petite partie !" ));
            allItems.Add(new Items ( 8653, "Sangle Glinglin", "L'origine du nom de cette ceinture provient du bruit particulier qu'elle produit. L'entrechoc des os ornant la sangle empêche les Srams, même invisibles, de passer inaperçus. Aussi, on peut les entendre dire : « Surprendre quelqu'un avec la Sangle Glinglin ? Tu peux toujours attendre ! »." ));
            allItems.Add(new Items ( 8654, "Ceinture Lupine", "Le tic-tac incessant du système d'horlogerie de la Ceinture Lupine empêche les Xélors de s'endormir lorsque les combats s'éternisent. Il est donc vivement recommandé d'enlever cette ceinture avant d'aller se coucher." ));
            allItems.Add(new Items ( 8655, "Sangle Holon", "À défaut d'enrayer les pleurs, la sangle Holon permet de transporter du matériel médical. Inutile de faire jouer les violons de l'automne qui blessent les tympans d'une langueur monotone. Les diverses potions utiles pour soigner les maux de gorge et autres affections ORL s'y trouvent soigneusement rangées. En effet, un Eniripsa à la voix enrouée ne peut pas lancer correctement ses sorts. Lorsqu'il est aphone, il est aussi utile qu'une veste polaire en pleine canicule." ));
            allItems.Add(new Items ( 8656, "Ceinture Luthe", "Les raisons qui ont poussé les concepteurs de cet accessoire de mode à placer une tête de poupée près de l'entrejambe restent mystérieuses. Bien des théories ont été avancées, mais chacun sait que tout ceci n'est que du pipeau !" ));
            allItems.Add(new Items ( 8657, "Sangle Hans", "Guerriers d'une ancienne tribu réputée pour son goût du sang, les Hans seraient les premiers adorateurs de la déesse Sacrieur. Sous son apparence inoffensive, cette ceinture est en réalité un instrument de torture sophistiqué." ));
            allItems.Add(new Items ( 8658, "Ceinture Bodiézèle", "Les Osamodas ont la réputation d'être un peu impulsif. Ils ont tendance à démarrer au quart de tour. Cette ceinture aux multiples boucles leur sert à refréner cette spontanéité : « Avant d'allumer ta colère, et d'avoir les yeux injectés de sang, touche toutes les cornes de ta Ceinture Bodiézèle ! »" ));
            allItems.Add(new Items ( 8659, "Sangle Huée", "Cette ceinture aide les Pandawas à se tenir à peu près droit lorsqu'ils sont saouls, ce qui leur arrive souvent. Dans ces moments-là, ils ont tendance à se sentir englués au sol, alors que la gravité n'a aucun rapport avec cette sensation de pesanteur. Elle est bien trop occupée à assommer les scientifiques à coup de pommes." ));
            allItems.Add(new Items ( 8660, "Sangle Cible", "Ce ceinturon permet à la fois de retenir les vêtements tombants (pour ne pas effrayer les âmes sensibles) et d'offrir une mire pour les jeunes Crâ en phase d'apprentissage. En effet, on leur apprend à ne jamais tirer en dessous de la ceinture, mais juste au milieu." ));
            allItems.Add(new Items ( 8661, "Gaine Hage", "Prévoyants, les Fécas conservent des provisions de survie dans une petite sacoche. Celles-ci se composent essentiellement de pain en pierre de Craqueboule. C'est pourquoi cette nourriture dure si longtemps. La Ceinture Deucékurité arrime fermement la sacoche à la taille, car le choc lié à la chute de ce sac de nourriture fait encore trop de blessés graves, par négligence ou inconscience." ));
            allItems.Add(new Items ( 8662, "Ceinture Gessant", "La Ceinture Gessant est un accessoire de mode très utile auprès les Enutrofs mâles. Grâce à la carte qui y est cousue, ils peuvent cacher certains accidents de parcours, lorsqu'ils se sont perdus sur le chemin des toilettes." ));
            allItems.Add(new Items ( 8663, "Bottes Antrin", "Avec les bottes Antrin aux pieds, les Iops sont toujours les premiers à arriver sur le champ de bataille et à mettre de l'ambiance. Il faut dire que cette classe de personnages adore amuser la galerie par ses exploits guerriers. Mais elle n'est jamais prise au sérieux en raison de son intelligence inversement proportionnelle à sa force." ));
            allItems.Add(new Items ( 8664, "Charentaises à poils", "On dit que les Sadidas ne se sentent jamais seuls, car ils peuvent invoquer plein de poupées pour leur tenir compagnie. Vous aussi, vous ne souffrirez plus de solitude grâce à ces chaussures ! Non seulement vous pourrez prendre votre pied pour lui parler, mais en plus, grâce à ces charentaises à poils, personne ne pourra plus mettre votre jeu à nu. Une fois qu'on les a enfilées, on se sent vraiment bien, même si on a un peu honte de sortir avec." ));
            allItems.Add(new Items ( 8665, "Bottes Hox", "Imprégnées d'un produit repulpant à diffusion lente, ces bottes permettent d'améliorer le galbe des jambes et de lutter contre le teint naturellement cadavérique des Srams. Cette crème a des effets spectaculaires sur la plastique des Srams femelles, mais semble totalement inefficace sur les mâles. Serait-ce un problème d'hormone ?" ));
            allItems.Add(new Items ( 8666, "Botte Hairo", "Créées par un artiste Xélor, ces bottes devaient lui permettre de peindre plus rapidement. C'est le cas ! En revanche, elles ont tendance à déformer la vision et plus précisément le sens des distances. Avec ces bottes, tout paraît plus rond. Appréciés par les Sadidas, les tableaux de ce Xélor ont contribué à rendre populaires les courbes généreuses des femmes un peu fortes." ));
            allItems.Add(new Items ( 8667, "Botte Ulisme", "Grâce à ces mi-bottes, vous serez toujours protégés des toxines provenant du sol. On ne se méfie jamais assez des microbes et autres bestioles qui traînent. Selon certains observateurs, ces chaussures ressemblent plus à des pantoufles qu'à des bottes. Pourtant, les Eniripsas les nomment bien ainsi. Est-ce à cause de leur petite taille qu'ils voient les choses sous un angle si différent ?" ));
            allItems.Add(new Items ( 8668, "Bottes Hanik", "L'amour des plantes pousse les Sadidas à s'équiper des seules chaussures à reconnaissance florale. Grâce à leur système de détection, elles permettent à son porteur d'éviter d'écraser les plantes rares. Par contre, elles ne vous empêchent pas de marcher sur des excréments divers et variés, si vous ne regardez pas où vous mettez les pieds." ));
            allItems.Add(new Items ( 8669, "Tong Aclou", "Contrairement aux autres classes, les Sacrieurs aiment vivre avec une épine dans le pied plutôt que de l'enlever du talon. La douleur leur rappelle qu'ils sont vivants et leur donne la rage de massacrer leur ennemi. De plus les traces de sang laissées en chemin permettent aux Sacrieurs de ne jamais se perdre. Ils sont toujours capables de retourner sur leur pas, ce qui n'aurait pas été le cas s'ils avaient des chaussures de mauvaises qualité." ));
            allItems.Add(new Items ( 8670, "Bottes Swana", "Ces chaussures permettent aux Osamodas d'arpenter de longues distances à travers les continents, même les plus reculés. À part ça, on ne sait pas très bien à quoi elles peuvent servir. Certains estiment qu'il est possible d'en faire des nids à Tofus ou d'élégants vases pour mettre en valeur les fleurs des champs. Tout cela n'est guère convaincant. Bref, les Bottes Swana sont essentiellement des chaussures." ));
            allItems.Add(new Items ( 8671, "Relique du Corailleur Magistral", "Cette impressionnante relique vous permettra peut-être d'oublier tous vos soucis." ));
            allItems.Add(new Items ( 8672, "Relique de Gourlo le Terrible", "Cette impressionnante relique vous permettra peut-être d'oublier tous vos soucis." ));
            allItems.Add(new Items ( 8673, "Relique du Rasboul Majeur", "Cette impressionnante relique vous permettra peut-être d'oublier tous vos soucis." ));
            allItems.Add(new Items ( 8674, "Relique du Tynril", "Cette impressionnante relique vous permettra peut-être d'oublier tous vos soucis." ));
            allItems.Add(new Items ( 8675, "Relique du Kimbo", "Cette impressionnante relique vous permettra peut-être d'oublier tous vos soucis." ));
            allItems.Add(new Items ( 8676, "Epée qui Pète", "Vlad était tombé amoureux de cette magnifique épée. Il a participé à la plus terrible des courses de Dragokart afin de pouvoir enfin l'acheter. Malheureusement, il n'avait pas compris que cette épée renfermait une terrible malédiction..." ));
            allItems.Add(new Items ( 8677, "Ross", "Ce magnifique Lapino est le familier préféré du célèbre Maître Noir Jack. Ne vous fiez pas à son apparence, il peut se montrer redoutable en combat. Ce familier ne mange que des Cawottes et des plantes." ));
            allItems.Add(new Items ( 8678, "Chapeau de Satanusse", "Satanusse était jadis un patineur artistique traînant ses patins de Holiboul on Ice en patinoires de seconde zone. C'est en tombant sur le coccyx qu'il eut la vision qui allait changer sa vie : son destin était d'être le conducteur de Dragokart le plus doué de sa génération ! Un petit choc provoque parfois un grand déclic." ));
            allItems.Add(new Items ( 8679, "Fantôme Ross", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8680, "Ecaille poisseuse", "Cette écaille poisseuse et glissante permettait à son propriétaire de ne pas se faire attraper facilement par les aventuriers qui ont décidé de pêcher avec les dents. Malheureusement pour lui, elle ne le protégeait pas du tout contre un bon coup d'épée rouillée." ));
            allItems.Add(new Items ( 8681, "Peau de Raul Mops", "La vraie faiblesse du Raul Mops, c'est qu'il aime se badigeonner d'eau, de vin, ou même de vinaigre. Mais il aime avant tout se rouler dans des pots en verre autour d'un oignon. Encore une créature aux moeurs mystérieuses." ));
            allItems.Add(new Items ( 8682, "Peau de Mob l\'Eponge", "Cette peau peut être utilisée des deux côtés, le premier est doux et permet de laver les surfaces fragiles, l'autre côté est beaucoup plus rugueux, il permet par exemple de récurer les parties de son anatomie qui étaient restées inaccessibles." ));
            allItems.Add(new Items ( 8683, "Cadeau Walk", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8684, "Certificat de Mise en Chanil : Ross", "Ce certificat décrit les caractéristiques du familier Ross que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8685, "Certificat de Mise en Chanil : Bilby", "Ce certificat décrit les caractéristiques du familier Bilby que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8686, "Cadeau Ross", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8687, "Cadeau Bilby", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8688, "Transformation Fantôme Ross", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8691, "Marque de victoire.", "Vous avez vaincu le disciple aisément. Il vous a remis ce parchemin en attestant." ));
            allItems.Add(new Items ( 8692, "Formule de potion de transformation", "Ce recueil contient les théories de divers alchimistes sur la possibilité de se transformer en Wabbit. Comme quoi ils n'ont vraiment rien à faire." ));
            allItems.Add(new Items ( 8693, "Fëanor", "Grâce à ce familier, n'importe quelle personne du sexe opposé vous fera les yeux doux. Un conseil : évitez de triompher. Bien vite, vous vous apercevrez qu'en réalité, votre prétendant(e) est hypnotisé(e) par Fëanor. N'espérez pas en profiter : désormais votre soupirant(e) mange du Pichon cru en se frottant contre vos jambes... Côté glamour, on a connu mieux, pas vrai ? Ce familier ne mange que du poisson." ));
            allItems.Add(new Items ( 8694, "Piment confit", "Ce piment confit est enrobé de bonbon rouge surpuissant fondu. Ce mélange explosif entre la confiserie et le piment a permis à la dragodinde Bruyère Blonde, de gagner une des plus célèbres courses de Dragokart. Vous pouvez l'utiliser comme suppositoire si vous voulez avoir le feu aux fesses ou comme dragée si vous recherchez une haleine chargée." ));
            allItems.Add(new Items ( 8695, "L\'Épée Nice", "Comme disait Brice : « Ce n'est pas sa taille qui compte, mais la façon de s'en servir. »" ));
            allItems.Add(new Items ( 8699, "Coiffe du Tynril", "Avec cette coiffe sur le crâne, vous aurez toujours une tête en pétard, même si vous n'êtes pas en colère. Il faut dire que les écorces de la chevelure du Tynril frisent lorsque le temps est humide, ce qui fait de cet animal l'indicateur météorologique le plus sûr au monde. En revanche, il redoute le feu. Faites donc très attention aux flammes si vous ne voulez pas vous transformer en torche humaine." ));
            allItems.Add(new Items ( 8700, "Coquille Emeraude", "Cette coiffe semble avoir été fabriquée à partir d'un Dofus Emeraude brisé. Le Dofus n'est pas encore complètement vide, vous pourrez profiter de cette coiffe pour vous faire un shampooing aux oeufs." ));
            allItems.Add(new Items ( 8701, "Epée Choh", "Cette somptueuse épée appartenait à un illustre voleur, réputé pour se faire attraper à chaque fois qu'il commettait un délit." ));
            allItems.Add(new Items ( 8704, "Le Nahitse", "Si vous agitez vos deux petits grelots rapidement, vous pourrez presque jouer de la musique." ));
            allItems.Add(new Items ( 8706, "Fantôme Fëanor", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8707, "Certificat de Mise en Chanil : Fëanor", "Ce certificat décrit les caractéristiques du Fëanor que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8708, "Certificat de Mise en Chanil : Atouin", "Ce certificat décrit les caractéristiques d'Atouin que vous avez placée au chanil." ));
            allItems.Add(new Items ( 8709, "Transformation Fantôme Fëanor", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8711, "Cadeau Fëanor", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 8712, "Bout de Goldemort", "Bravo ! Vous avez réussi à dépouiller le cadavre de Goldemort d'une cuillerée de sa substance. Les vautours sont fiers de vous." ));
            allItems.Add(new Items ( 8713, "Getas Bernacle", "Dans certaines contrées, il semble que les Getas Bernacle sont lancées en guise d'insulte pour provoquer les duels. Même si cela peut prêter à sourire, ce n'est finalement pas plus stupide que de frapper l'adversaire avec une paire de gants. L'avantage des Getas Bernacle est d'ajouter une commotion olfactive au coup physique. Les Pandawas n'ont jamais été réputés pour l'hygiène de leurs pieds..." ));
            allItems.Add(new Items ( 8714, "Anneau Bhli", "Porter cet anneau est une marque honorifique. Cela signifie que vous êtes un digne fidèle de la déesse Crâ, la seule qui vous révèle comment bander plus vite afin de tirer efficacement." ));
            allItems.Add(new Items ( 8715, "Bague Houx", "La volubilité des Ecaflips est légendaire. Ils sont capables de vous sortir n'importe quel baratin pour vous faire jouer. Cette bague célèbre cette loquacité sans faille. Accessoirement, elle peut servir de dé de rechange et elle est également très pratique pour se gratter... à peu près partout." ));
            allItems.Add(new Items ( 8716, "Bague Héra", "Les Osamodettes adorent la bague Héra qui complète leur démarche féline d'une touche de grâce. Il faut dire qu'à force de côtoyer les monstres, elles finissent parfois par se comporter en vraies tigresses. Mais en réalité, elles sont aussi dévouées que des mères louves." ));
            allItems.Add(new Items ( 8717, "Bague Harre", "Les mauvaises langues prétendent que la bague Harre est l'accessoire de mode le plus inutile au monde. Les ailes sur le côté sont fort embarrassantes quand vous voulez chercher des mouchoirs dans vos poches. Or, comme les Iops n'ont ni mouchoir ni poche, tout va bien. De plus, elles permettent de déchiqueter un peu de chair à chaque fois qu'elles frôlent l'ennemi. C'est à se demander si ce n'est pas un bijou de Sacrieur." ));
            allItems.Add(new Items ( 8718, "Anneau Tassion", "Les mini boucliers de l'anneau Tassion servent de pense-bête aux Fécas qui peuvent y glisser des petits mots et des listes de choses à faire. Ce bijou est surnommé le bouclier anti-oubli. En revanche, il ne protège ni du bégaiement ni des lapsus." ));
            allItems.Add(new Items ( 8719, "Anneau Stalgik", "Grâce à cet objet, les Enutrofs peuvent à tout moment se remémorer les instants heureux. Un doux parfum s'en échappe. Il suffit pour cela de retirer la pelle qui y est fichée. Au préalable, il faut bien sûr y mettre l'odeur que l'on veut conserver en souvenir, et ce n'est pas toujours facile." ));
            allItems.Add(new Items ( 8720, "Anneau Zamour", "Cet objet a été donné par la déesse Eniripsa à ses fidèles en signe d'alliance spirituelle lors de la séance d'initiation aux sorts de la classe. Accessoirement, les Eniripsas peuvent aussi s'en servir comme bracelet mousse pour éponger le sang des alliés." ));
            allItems.Add(new Items ( 8721, "Anneau Raille", "Ce bijou a tendance à rendre les Pandawas un peu moqueurs, surtout après la troisième bière. Ils peuvent même aller jusqu'aux insultes. Mais ce n'est pas très important car, comme le dit le célèbre proverbe Pandawa, « Ventre imbibé n'a pas d'oreille »." ));
            allItems.Add(new Items ( 8722, "Anneau Hell", "Ce bijou à tête de mort était traditionnellement offert en cadeau, lors d'une fête récompensant tous les enfants Sram pour leur fourberie et leurs coups bas. Les autres classes ont tendance à déconsidérer cette pratique, qui rend les gamins trop agités. Ils deviennent alors infernaux." ));
            allItems.Add(new Items ( 8723, "Anneau Tilus", "L'anneau Tilus est le seul bijou qui donne l'heure et qui est parfaitement résistant à l'eau. Certains pensent qu'en agrandissant les plans de cet objet et en les modifiant, on pourrait en faire un véhicule se déplaçant sous l'eau. Où peuvent-ils aller chercher tout cela ?" ));
            allItems.Add(new Items ( 8724, "Anneau Pwal", "Contrairement à la rumeur, les Sadidas n'ont pas de poils dans la main. Non ! Ceux-ci sont en fait ajustés autour d'un superbe anneau, dont ils renforcent les effets bénéfiques : ils ne doivent en aucun cas être coupés. Grâce à l'Anneau Pwal, vous serez toujours parfait !" ));
            allItems.Add(new Items ( 8725, "Anneau Pitale", "Si vous voyez un personnage qui n'est pas Sacrieur mettre un Anneau Pitale, emmenez-le d'urgence en soins intensif. Ce bijou est en effet extrêmement blessant. En général, les gens perdent leur sang-froid devant le bain d'hémoglobine provoqué par cet anneau." ));
            allItems.Add(new Items ( 8726, "Chaussures Lepon-Davignon", "Ces chaussures permettent à son porteur de danser jusqu'au bout de la nuit, malgré les cors et les ampoules qu'elles occasionnent ! Elles bougent toutes seules au bruit de la musique et font des « bip bip » lorsqu'elles se trouvent à proximité de minerais précieux. C'est pourquoi les Enutrofs sont souvent appelés les « beat-machines »." ));
            allItems.Add(new Items ( 8727, "Bottes Antouche", "Lorsque les Fécas sont embarrassés et souhaitent détourner la discussion, ils évoquent toujours leurs bottes Antouche. Ils décrivent de quelle manière ces chaussures leur permettent d'arpenter tous types de terrains. Lorsque leurs alliés sont en grosse difficulté lors de combats, le confort de ces bottes leur permet de se ressaisir pour détourner la menace." ));
            allItems.Add(new Items ( 8728, "Bottes Deuradi", "Les multiples sangles entourant ces chaussures peuvent servir de corde de remplacement, lorsque celle de l'arc est rompue. Les jeunes femmes Crâ les emploient aussi pour faire des bottes de légumes en cuisine. Il faut croire qu'elles n'ont pas peur des odeurs de pieds." ));
            allItems.Add(new Items ( 8729, "Corail usé", "Se détachant du Corailleur par temps sec, le corail est bien joli à voir. Certains en font même des bijoux. D'autres les mangent. Mais ils sont rares et ne le font que lorsqu'ils le confondent avec des bâtons de crabes sourimis." ));
            allItems.Add(new Items ( 8730, "Coccyx du Corailleur", "Situé à un endroit stratégique, cet os permet au Corailleur de s'installer dignement. Maintenant qu'il est mort, vous pouvez toujours vous en servir pour asseoir votre autorité sur ses congénères." ));
            allItems.Add(new Items ( 8731, "Corail Magistral", "La seule différence entre le Corail usé et le Corail Magistral réside dans la taille et dans l'usage que l'on peut en faire. Mais, hélas, un amalgame de Coraux usés ne remplacera jamais un seul Corail Magistral." ));
            allItems.Add(new Items ( 8732, "Corail Kouraçao", "La couleur remarquable de ce corail vient de l'alimentation déréglée du Crustorail. Encore un qui a oublié de manger son quota de fruits et légumes !" ));
            allItems.Add(new Items ( 8733, "Corail Malibout", "La couleur remarquable de ce corail vient de l'alimentation déréglée du Crustorail. Encore un qui a oublié de manger son quota de fruits et légumes !" ));
            allItems.Add(new Items ( 8734, "Corail Passaoh", "La couleur remarquable de ce corail vient de l'alimentation déréglée du Crustorail. Encore un qui a oublié de manger son quota de fruits et légumes !" ));
            allItems.Add(new Items ( 8735, "Corail Morito", "La couleur remarquable de ce corail vient de l'alimentation déréglée du Crustorail. Encore un qui a oublié de manger son quota de fruits et légumes !" ));
            allItems.Add(new Items ( 8736, "Coeur de pierre poli", "La seule chose qui distingue un cœur de Craqueleur de celui d'un Craqueleur poli est la délicatesse avec laquelle l'objet peut s'écraser sur vos doigts de pieds quand vous vous y attendez le moins." ));
            allItems.Add(new Items ( 8737, "Boule polie", "De quelle partie de l'anatomie du Craqueboule peut bien venir cette boule ? Et pourquoi est-ce qu'on en retrouve souvent par paire ?" ));
            allItems.Add(new Items ( 8738, "Sacrum magistral", "Le Corailleur vous offrait un os de son postérieur. Le Corailleur Magistral vous permet de prendre possession d'une autre partie de son anatomie, mais à peu près dans le même secteur." ));
            allItems.Add(new Items ( 8739, "Humérus du Sparo", "Bien qu'on le nomme Humérus, rien n'indique que cet os vient vraiment du bras de Sparo. Comme tout son squelette a été broyé par les épées transperçant le tonneau, alors tout est un peu en miettes." ));
            allItems.Add(new Items ( 8740, "Chicot du Flib", "À votre place, je ne ramasserai pas cet objet provenant d'une bouche à l'hygiène plus que douteuse. Mais bon, c'est vous l'aventurier !" ));
            allItems.Add(new Items ( 8741, "Bec du Kido", "Le bec du Kido est si peu solide que les disciples d'Otomaï s'en servaient pour faire des flèches d'entraînement à la pointe émoussée pour ne pas faire de dégâts." ));
            allItems.Add(new Items ( 8744, "Pince de Crustorail", "Évitez de serrer la pince du Crustorail ! Vous risquez de perdre la main pour un simple geste de politesse." ));
            allItems.Add(new Items ( 8745, "Pétale Kouraçao", "La couleur de ce pétale témoigne de sa provenance douteuse. L'abus d'alcool chez les disciples d'Otomaï se perçoit même dans leurs créations." ));
            allItems.Add(new Items ( 8746, "Pétale Malibout", "La couleur de ce pétale témoigne de sa provenance douteuse. L'abus d'alcool chez les disciples d'Otomaï se perçoit même dans leurs créations." ));
            allItems.Add(new Items ( 8747, "Pétale Passaoh", "La couleur de ce pétale témoigne de sa provenance douteuse. L'abus d'alcool chez les disciples d'Otomaï se perçoit même dans leurs créations." ));
            allItems.Add(new Items ( 8748, "Pétale Morito", "La couleur de ce pétale témoigne de sa provenance douteuse. L'abus d'alcool chez les disciples d'Otomaï se perçoit même dans leurs créations." ));
            allItems.Add(new Items ( 8749, "Bulbe Kouraçao", "Le Palmifleur ne naît pas d'une graine mais d'un bulbe que l'on trempe dans l'alcool. Pourquoi ? Parce que !" ));
            allItems.Add(new Items ( 8750, "Bulbe Malibout", "Le Palmifleur ne naît pas d'une graine mais d'un bulbe que l'on trempe dans l'alcool. Pourquoi ? Parce que !" ));
            allItems.Add(new Items ( 8751, "Bulbe Passaoh", "Le Palmifleur ne naît pas d'une graine mais d'un bulbe que l'on trempe dans l'alcool. Pourquoi ? Parce que !" ));
            allItems.Add(new Items ( 8752, "Bulbe Morito", "Le Palmifleur ne naît pas d'une graine mais d'un bulbe que l'on trempe dans l'alcool. Pourquoi ? Parce que !" ));
            allItems.Add(new Items ( 8753, "Crinière fleurie", "Hybridation improbable entre des poils et des pétales de fleurs, cette crinière est doublement allergène. Veillez à rester à distance si vous êtes sensibles à ces deux éléments sous peine d'expulser votre cerveau en éternuant." ));
            allItems.Add(new Items ( 8754, "Barbe inexistante du Barbroussa", "Cette minuscule touffe de poils vous rappellera les vacances grâce à son odeur de sable, de produits solaires et d'alcool. Dommage qu'elle pue autant la poudre !" ));
            allItems.Add(new Items ( 8755, "Touffe rousse du Flib", "On n'a jamais su si la couleur de cette touffe était naturelle ou si elle était liée à un feu de broussaille intempestif." ));
            allItems.Add(new Items ( 8756, "Duvet du Kilibriss", "En raison de sa légèreté, le duvet du Kilibris est très recherché pour la confection de matelas. C'est pourquoi, en attendant la création d'une espèce de matelas vivants que l'on peut tuer lors de safari, les artisans chassent activement le Kilibris." ));
            allItems.Add(new Items ( 8757, "Maillot de corps de Barbroussa", "Besoin d'un répulsif contre les insectes ? Adoptez le Maillot de corps de Barbroussa ! Mais renoncez aussi à toute tentative de fréquentation humaine !" ));
            allItems.Add(new Items ( 8758, "Morceau de caleçon de Gourlo", "Il n'a jamais été possible de retrouver un caleçon de Gourlo intact. À force de jouer avec la poudre, il en vient toujours à y mettre le feu." ));
            allItems.Add(new Items ( 8759, "Coffret maudit du Flib", "Ce coffre a pris les formes de Flib, à moins que ce ne soit l'inverse... Dans tous les cas, ça reste un coffre maudit." ));
            allItems.Add(new Items ( 8760, "Foulard du Sparo", "Accessoire de mode indispensable été comme hiver, ce foulard vous donnera un look original, mais un peu kitsch. Grâce à sa couleur éclatante, vous êtes sûr d'exciter les bouftous mâles. Mais est-ce là votre intention ?" ));
            allItems.Add(new Items ( 8761, "Poudre explosive de Gourlo", "À défaut de se greffer un canon à la place du bras, Gourlo ne sort jamais sans son arme. Mais il n'est pas capable de tirer son coup très vite, car il lui faut à chaque fois faire sécher sa poudre, signe de sa mauvaise qualité." ));
            allItems.Add(new Items ( 8762, "Fragment de cerveau poli", "Grâce à ce fragment de cerveau, vous allez enfin apprendre les bonnes manières à la façon des Craqueleurs : broyage de main en guise de bonjour." ));
            allItems.Add(new Items ( 8763, "Moustache du Mufafah", "Plus fournie que celle des poissons chatons, la moustache du Mufafah peut servir à faire des pinceaux pour le peintre Marylork." ));
            allItems.Add(new Items ( 8764, "Sourcil du Rasboul majeur", "Afin de prouver qu'il est bien le Boss, le Rasboul majeur garde toujours les sourcils froncés. Même après la mort, ils le restent." ));
            allItems.Add(new Items ( 8765, "Bâton du Kilibriss", "Grâce à sa longueur, ce bâton va vous permettre d'atteindre tous les endroits difficiles d'accès et de vous gratter partout." ));
            allItems.Add(new Items ( 8766, "Plume de fesse du Kido", "Cette ressource a servi à créer l'une des plus grandes énigmes d'Otomaï : qu'est-ce qui est le plus lourd ? cent pods de plumes du Kido ou cent pods de Pyrute ?" ));
            allItems.Add(new Items ( 8767, "Aile du Bitouf des plaines", "Le Bitouf n'a jamais été connu pour ses performances en vol. Vu les ailes qu'il a, c'est normal." ));
            allItems.Add(new Items ( 8768, "Oreille déchirée de Pumbaa", "En collant cette oreille à la vôtre, vous pourrez entendre le galop du sanglier des plaines à la place du bruit de la mer." ));
            allItems.Add(new Items ( 8769, "Coco du Bitouf des plaines", "Dans ce Coco du Bitouf, il n'y a pas de lait, mais du jus de cerveau et parfois quelques neurones flottant en surface." ));
            allItems.Add(new Items ( 8770, "Ecorce du Tynril", "Cette écorce est curieusement recourbée, comme si on l'avait obtenue par le biais d'une râpe." ));
            allItems.Add(new Items ( 8771, "Ecorce de Floribonde", "Cette écorce est curieusement recourbée, comme si on l'avait obtenue par le biais d'une râpe." ));
            allItems.Add(new Items ( 8772, "Ecorce de Brouture", "Cette écorce est curieusement recourbée, comme si on l'avait obtenue par le biais d'une râpe." ));
            allItems.Add(new Items ( 8773, "Ecorce de Nerbe", "Cette écorce est curieusement recourbée, comme si on l'avait obtenue par le biais d'une râpe." ));
            allItems.Add(new Items ( 8774, "Ecorce de Fécorce", "Cette écorce est curieusement recourbée, comme si on l'avait obtenue par le biais d'une râpe." ));
            allItems.Add(new Items ( 8775, "Ecorce de Chiendent", "Cette écorce est curieusement recourbée, comme si on l'avait obtenue par le biais d'une râpe." ));
            allItems.Add(new Items ( 8776, "Coco du Bitouf sombre", "Dans ce Coco du Bitouf, il n'y a pas de lait, mais du jus de cerveau et parfois quelques neurones flottant en surface." ));
            allItems.Add(new Items ( 8777, "Pistil du Tynril", "Les fleurs correspondent aux organes sexuels des plantes. Le pistil faisant partie de la fleur, à vous d'en déduire ce que vous voulez." ));
            allItems.Add(new Items ( 8778, "Etamine de Floribonde", "Une étamine est l'organe mâle de la reproduction sexuée chez les végétaux. Eh oui, malgré les apparences, Floribonde serait de sexe masculin." ));
            allItems.Add(new Items ( 8779, "Carpelle de Brouture", "Les fleurs correspondent aux organes sexuels des plantes. Le pistil faisant partie de la fleur, et la carpelle étant un élément du pistil, à vous d'en déduire ce que vous voulez." ));
            allItems.Add(new Items ( 8780, "Sépale de Nerbe", "Cette partie du corps de Nerbe reste toujours verte. Est-ce dû à l'excès de chlorophylle ou à autre chose ?" ));
            allItems.Add(new Items ( 8781, "Calice de Fécorce", "Ce n'est pas en utilisant le calice de Fécorce que vous arriverez à boire quoi que ce soit. Malgré sa forme en coupe, elle est percée." ));
            allItems.Add(new Items ( 8782, "Bractée de Chiendent", "La Bractée de Chiendent correspond à une zone que la morale nous interdit de nommer." ));
            allItems.Add(new Items ( 8783, "Amygdales du Bitouf Sombre", "Une fois qu'on lui a retiré les amygdales, le Bitouf sombre a moins les boules. C'est normal, en général l'opération conduit à sa mort." ));
            allItems.Add(new Items ( 8784, "Talon d\'achille de l\'Abrakleur sombre", "Ce n'est pas en tirant une flèche dans son talon d'Achille que vous vaincrez l'Abrakleur sombre. Mais vous pouvez toujours essayer." ));
            allItems.Add(new Items ( 8785, "Graine de l\'Abrakleur sombre", "Avec beaucoup de patience, de temps, d'eau et d'engrais, vous parviendrez à faire pousser un Abrakleur dans votre jardin pour le combattre en guise d'entraînement. Mais il est plus simple d'aller les décimer sur l'île d'Otomaï" ));
            allItems.Add(new Items ( 8786, "Tresse du Poolay", "Cette tignasse rêche et épaisse peut servir de serpillère à défaut de cacher la calvitie. Mais il faudra auparavant la laver à de nombreuses reprises" ));
            allItems.Add(new Items ( 8787, "Cloaque du Poolay", "Non ce n'est pas répugnant, c'est la nature !" ));
            allItems.Add(new Items ( 8788, "Croupion du Bitouf aérien", "Ce croupion vous permettra de créer des objets pour ne plus avoir le cul à l'air." ));
            allItems.Add(new Items ( 8789, "Chaussette du Kimbo", "Malgré les apparences, Kimbo porte bien des chaussettes, mais pas aux pieds. Il préfère les rouler en boule et les placer à un endroit stratégique." ));
            allItems.Add(new Items ( 8790, "Rembourrage de Meupette", "La Nature ayant modestement pourvu la Meupette, celle-ci doit user d'artifice pour paraître esthétique. Il arrive qu'elle s'en serve aussi pour améliorer le moelleux de son lit." ));
            allItems.Add(new Items ( 8791, "Etoffe de Meupette", "Cette étoffe a une texture bizarre mais elle habille en un rien de temps n'importe quel monstre un peu pudique." ));
            allItems.Add(new Items ( 8792, "Coco du Bitouf aérien", "Dans ce Coco du Bitouf, il n'y a pas de lait, mais du jus de cerveau et parfois quelques neurones flottant en surface." ));
            allItems.Add(new Items ( 8793, "Coquille du Kaskargo", "Ronde et luisante à la fois, cette coquille a été lustrée avec de la bave pendant plusieurs siècles. Chacun son truc." ));
            allItems.Add(new Items ( 8795, "Perruque du Kimbo", "Pour masquer sa calvitie naissante, Kimbo met cette perruque ridicule. Heureusement que la noix de coco la cache en grande partie." ));
            allItems.Add(new Items ( 8796, "Noeud de l\'Abrakleur clair", "C'est à cause de cette ressource que beaucoup de personnes surnomment l'Abrakleur « tête de nœud »." ));
            allItems.Add(new Items ( 8797, "Souche de l\'Abrakleur clair", "Une souche différente des autres souches. Et pourtant elle tourne !" ));
            allItems.Add(new Items ( 8798, "Queue emplumée du Rasboul Majeur", "Les plumes du Rasboul sont collées sur son appendice caudale, une à une, année après année. Plus la queue est touffue, plus le Rasboul est vieux." ));
            allItems.Add(new Items ( 8799, "Poil d\'aisselle de Pumbaa", "Même s'il se les rase souvent, ses poils repoussent drus et gardent constamment une odeur rance. Comme quoi ce n'est pas la sueur qui est en cause." ));
            allItems.Add(new Items ( 8800, "Etoffe Zoth", "Faite dans une matière à la fois résistante et souple, cette étoffe s'adapte parfaitement à la silhouette des Zoths pour en cacher l'essentiel." ));
            allItems.Add(new Items ( 8801, "Etoffe de Ouassingue", "Cette étoffe comporte plus de trous qu'un gruyère, mais elle est pourtant capable d'absorber un maximum de déchets pour les régurgiter au moment le moins opportun." ));
            allItems.Add(new Items ( 8802, "Tibia du Guerrier Zoth", "Comme tous autres os du Guerrier Zoth, ce tibia a subi de multiples fractures. Il s'est parfaitement ressoudé et il est devenu plus solide. Comme quoi, s'il avait eu le don de la parole, lui aussi aurait pu dire : « Ce qui ne me tue pas, me rend plus fort »." ));
            allItems.Add(new Items ( 8803, "Rotule du Disciple Zoth", "À force de donner des coups de genou, la rotule du Disciple Zoth est devenu très résistante et a pris du volume.  Bientôt, il sera possible d'en faire des boules de pétanque." ));
            allItems.Add(new Items ( 8804, "Braguette du Maître Zoth", "La braguette du Maître Zoth est remarquable, elle a été spécialement étudiée pour ne pas déchirer les chaires. Les aventuriers qui ont été un jour traumatisés par leur braguette comprendront." ));
            allItems.Add(new Items ( 8805, "Mouchoir de la Gamine Zoth", "La Gamine Zoth ne se sert pas de ce mouchoir pour faire des adieux larmoyants, mais pour essuyer le sang de ses ennemis." ));
            allItems.Add(new Items ( 8806, "Ecusson du Sergent Zoth", "Donné par sa mère et brodé sur une étoffe, cet écusson est un cuisant souvenir d'enfance avant d'être un insigne militaire." ));
            allItems.Add(new Items ( 8807, "Jus de Ouassingue", "À moins d'être totalement saoul, impossible de confondre le jus de Ouassingue avec un quelconque liquide potable. L'odeur nauséabonde dissuaderait même un aveugle d'en prendre une gorgée." ));
            allItems.Add(new Items ( 8808, "Etoffe du Roissingue", "Cette étoffe comporte plus de trous qu'un gruyère, mais elle est pourtant capable d'absorber un maximum de déchets. Là où cette étoffe passe, la crasse trépasse." ));
            allItems.Add(new Items ( 8809, "Culotte à l\'envers du Roissingue", "Depuis l'habitude prise par le Roissingue Dagobert, tous les Roissingues portent la culotte à l'envers. L'important est que cette pratique ne leur retourne pas l'estomac." ));
            allItems.Add(new Items ( 8810, "Tourbe séchée de Tourbassingue", "Faite à partir de végétaux en décomposition, cette tourbe possède un pouvoir absorbant étonnant. Les Mama Bworks s'en servent pour régler un problème mensuel." ));
            allItems.Add(new Items ( 8811, "Faux menton du Bourbassingue", "Le Bourbassingue passe beaucoup de temps à prendre des bains de boue en espérant maigrir. Au lieu de perdre du poids, il a juste gagné un double menton pour harmoniser sa silhouette." ));
            allItems.Add(new Items ( 8812, "Encre du Kralamoure géant", "Visqueuse et bien couvrante, l'encre du Kralamoure peut servir à l'écriture comme à la peinture des murs d'une chambre de dépressif." ));
            allItems.Add(new Items ( 8813, "Ventouse du Kralamoure géant", "Vu la taille de cette ventouse, nul doute que l'adhérence doit être bonne et, quand on la retire, le bruit de succion est tonitruant." ));
            allItems.Add(new Items ( 8814, "Bourse de 5000 Kamas", "Cette bourse est très lourde, elle contient sûrement une vraie fortune." ));
            allItems.Add(new Items ( 8815, "Bourse de 1000 Kamas", "Cette bourse est très lourde, elle contient sûrement une vraie fortune. Un marchand sera ravi de vous l'échanger contre sa valeur en Kamas." ));
            allItems.Add(new Items ( 8816, "Cadeau Epée qui Pète", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
            allItems.Add(new Items ( 8817, "Cadeau Chapeau Satanusse", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
            allItems.Add(new Items ( 8818, "Cape Souveraine du Roissingue", "Constituée d'une étoffe de Roissingue, cette cape comporte des micro trous permettant une meilleure évacuation de la sueur en cas d'effort intense, comme lors de mêlées belliqueuses en zone marécageuse. Cette cape laisse passer l'humidité mais garde la chaleur. Ainsi, vous restez sec quelle que soit la situation. Dommage que l'odeur persistante de moisissure soit inaltérable." ));
            allItems.Add(new Items ( 8819, "Cape de la Ouassingue", "Moins performante que la Cape du Roissingue, cette cape présente néanmoins les mêmes avantages quant à l'évacuation de la sueur. Avec elle, vous allez pouvoir faire face à toutes les situations... Si vous arrivez à combattre en apnée, car vu l'odeur, il est vraiment impossible de ne pas défaillir." ));
            allItems.Add(new Items ( 8820, "Masque du Sergent Zoth", "Grâce à ce masque, vous affronterez tous les dangers sans aucune crainte ! C'est normal vu qu'il obstrue tout le champ de vision." ));
            allItems.Add(new Items ( 8821, "Coiffe du Disciple Zoth", "De loin, cette coiffe ressemble à un abat-jour avec deux trous pour les yeux. De près, aussi." ));
            allItems.Add(new Items ( 8822, "Masque de l\'Abrakleur clair", "Ce masque ne permet ni de prévenir ni de guérir la gueule de bois, mais il vous donne au moins l'occasion de la dissimuler de façon élégante." ));
            allItems.Add(new Items ( 8823, "Masque de l\'Abrakleur sombre", "Ce masque ne permet ni de prévenir ni de guérir la gueule de bois, mais il vous permet au moins de la dissimuler de façon élégante. La nuance sombre indique juste que vous êtes de mauvaise humeur et qu'il ne faut pas vous chauffer longtemps pour que vous lanciez à tout le monde une volée de bois vert." ));
            allItems.Add(new Items ( 8824, "Kaskargo", "Avec ça sur le crâne, vous aurez l'air d'un Kaskargo. Certes vous serez protégé des coups à la tête, mais est-ce une raison pour baver ainsi ? Vous n'avez pourtant pas besoin de ça pour vous déplacer !" ));
            allItems.Add(new Items ( 8825, "Masse du Corailleur Magistral", "Plus grande, plus lourde, plus puissante, cette masse s'abat plus bruyamment sur les ennemis. Mais curieusement, elle est plus légère et d'une prise en main plus agréable. Le Corailleur dont on s'est servi pour la confectionner n'était pas magistral pour rien." ));
            allItems.Add(new Items ( 8826, "Marteau de la Gamine Zoth", "Malgré sa petite taille, la Gamine Zoth arbore un immense marteau. C'est une façon comme une autre de renforcer la musculature. Pas besoin de mettre des poids aux pieds ou aux poignets pour augmenter la force : traîner le marteau est bien suffisant !" ));
            allItems.Add(new Items ( 8827, "Hache du Guerrier Zoth", "Le Guerrier Zoth a pris l'habitude de porter deux haches, car il est incapable de toucher le flanc gauche de son ennemi avec sa main droite. Problème de schéma corporel ou maladresse congénitale ? Difficile de se prononcer là-dessus. Mais avec ses deux haches, il fait feu de tout bois à défaut de s'en servir pour couper des rondins." ));
            allItems.Add(new Items ( 8828, "Perruque du Poolay", "Le Poolay est un volatile malhabile qui a autant de vivacité qu'une poule atteinte de grippe aviaire et une intelligence comparable à celle du Tofu. Autant dire qu'il était mal parti pour survivre. Pourtant certains semblent adorer sa curieuse chevelure évoquant une serpillère tressée en nattes par une ménagère teutonne. La nature humaine est décidément bien étrange." ));
            allItems.Add(new Items ( 8829, "Chapeau de Barbroussa", "Ce chapeau était suffisamment large pour dissimuler la tête et la barbe ridiculement peu fournie de Barbroussa. Mais il n'est pas capable de couvrir le crâne de quelqu'un ayant la grosse tête." ));
            allItems.Add(new Items ( 8832, "Bave du Kaskargo", "La bave du Kaskargo ne peut jamais atteindre le plumage du Bitouf, mais elle peut vous engluer au sol." ));
            allItems.Add(new Items ( 8833, "Masse du Corailleur", "Ce marteau tire sa jolie couleur des morceaux de Corailleur qui le composent. Contrairement à ce que l'on pourrait croire, il ne servait pas initialement à écrabouiller les Iops ou les ennemis. En réalité, il s'agit d'un objet d'artisanat laissé par une ancienne civilisation. Cette masse servait de heurtoir pour que les personnes ayant des problèmes d'audition puissent comprendre que quelqu'un frappe à la porte." ));
            allItems.Add(new Items ( 8834, "Branche de l\'Abrakleur sombre", "Cette branche servait à racler certaines parties du corps inaccessibles autrement. Mais rassurez-vous, la couleur sombre est d'origine. Aucun rapport avec les dépôts contenus dans les cavités les plus inaccessibles de l'Abrakleur." ));
            allItems.Add(new Items ( 8835, "Branche de l\'Abrakleur clair", "Cette branche servait à racler certaines parties du corps inaccessibles autrement. Ceci explique peut-être l'odeur étrange de cette branche." ));
            allItems.Add(new Items ( 8836, "Tige du Brouture", "Les Broutures se servent de leur tige pour se nettoyer les oreilles. Les sculpteurs de bâton préfèrent les employer comme arme. Comme quoi, les goûts et les couleurs, ça ne discute pas." ));
            allItems.Add(new Items ( 8837, "Bâton du Maître Zoth", "Le Maître Zoth est capable de vaincre n'importe qui avec son bâton. Aucun adversaire ne lui fait peur ! Et c'est bien normal. Vu qu'avec sa coiffe il ne voit rien, il ne peut pas être terrorisé par son ennemi. Cette technique bien pratique au combat est toutefois problématique lorsqu'il s'agit de prendre les repas. Ayant un champ de vision limité, le Maître Zoth a toujours eu quelques problèmes pour manger sans en mettre partout." ));
            allItems.Add(new Items ( 8838, "Rhizome du Tynril", "Telle la mauvaise herbe, le Tynril est capable de se reproduire à l'infini grâce à un simple bout de rhizome. C'est à partir de cette constatation que certains sculpteurs ont créé un bâton à base de Tynril. Ils espéraient pouvoir le reproduire facilement et à moindre frais pour le revendre au plus offrant. C'est raté." ));
            allItems.Add(new Items ( 8839, "Masque Vaudou Thierry", "Ce masque Vaudou sert essentiellement à masquer le visage des Kannibouls qui aiment la fronde (comme Thierry), mais qui ratent souvent leur cible. C'est pourquoi, ils sont toujours un peu verts de rage." ));
            allItems.Add(new Items ( 8840, "Masque Vaudou Jav", "Les Kannibouls Jav ont souvent le " ));
            allItems.Add(new Items ( 8841, "Masque Vaudou Sarbak", "À force de rire jaune car ils loupent leur objectif, certains Kanniboules ont peint leur masque de cette couleur. Ainsi, même lorsqu'ils rient de bon cœur, on a l'impression qu'ils se forcent." ));
            allItems.Add(new Items ( 8842, "Masque Vaudou Archer", "Les Kannibouls Archer n'arrivaient pas à se concentrer et à bien viser sauf lorsqu'ils étaient très en colère. Ainsi, pour être toujours sur de voir rouge, ils ont peint leur masque dans cette couleur." ));
            allItems.Add(new Items ( 8843, "Chapeau de Crocodaille Dandi", "Avec ce chapeau sur le crâne, le Crocodaille pense être à la pointe de la mode. Il faudrait que celle-ci soit vraiment bien émoussée pour ce que cela soit possible. Mais il vaut mieux éviter de lui faire remarquer son côté ridicule. Le Crocodaille est très susceptible..." ));
            allItems.Add(new Items ( 8844, "Capuche Souveraine du Roissingue", "Coiffe royale, cette capuche possède les mêmes vertus que la Cape du Roissingue. Ses micro-aérations contribuent à une meilleure évacuation de la sueur pour toujours garder la tête froide. Hélas, elle possède également les défauts de la cape : son odeur nauséabonde est si présente qu'on pourrait presque la découper au couteau." ));
            allItems.Add(new Items ( 8845, "Capuche de la Ouassingue", "Moins efficace que la Coiffe royale, cette capuche permet toutefois une très bonne évacuation de la sueur. Un peu trop bonne, en fait. Du coup, que l'on porte la capuche ou qu'on ne la porte pas ne fait aucune différence." ));
            allItems.Add(new Items ( 8846, "Chapeau de la Gamine Zoth", "Le Chapeau de la Gamine Zoth a la particularité de lui faire voir les choses en double. Ainsi, lorsqu'elle voit une personne de sa tribu, elle zoth zoth." ));
            allItems.Add(new Items ( 8847, "Casque du Guerrier Zoth", "Ce casque a les mêmes avantages que les autres coiffes Zoth : il protège des coups de soleil même en plein cagnard, il amortit les coups portés à la tête, et il empêche de prendre conscience du danger en restreignant le champ de vision de celui qui le porte. Bref, ceci est le couvre-chef idéal du guerrier." ));
            allItems.Add(new Items ( 8848, "Coiffe du Maître Zoth", "La coiffe du Maître Zoth possède l'immense avantage de restreindre le champ de vision de manière encore plus importante que les autres casques. C'est grâce à lui que l'on peut accéder à la fameuse technique de combat dite « de la fouine ». En gros, il suffit de frapper n'importe où en utilisant toutes ses forces. Étant ainsi totalement imprévisible, vous êtes sûr de vaincre l'adversaire (ou du moins le surprendre suffisamment pour lui donner des coups bien placés)." ));
            allItems.Add(new Items ( 8849, "Racine de Fécorce", "Ce bâton est très branché auprès des femmes troncs. Mais si vous préférez séduire d'autres types de femmes, cela est aussi possible (même si cela risque d'être moins efficace)." ));
            allItems.Add(new Items ( 8850, "Racine de Floribonde", "Si vous êtes superstitieux et que vous avez toujours besoin de toucher du bois, choisissez cette Racine de Floribonde ! D'un autre côté, vous pouvez aussi essayer d'être prudent, de ne pas scier la branche sur laquelle vous êtes assis et de ne surtout pas mettre le doigt entre l'arbre et l'écorce." ));
            allItems.Add(new Items ( 8851, "Worn Treasure Map", "Event Shika" ));
            allItems.Add(new Items ( 8854, "Crack Sparrow\'s Own Withered Hat", "Event Shika" ));
            allItems.Add(new Items ( 8855, "Bouclier Fi\'Squale", "Certes ce bouclier, vous assure une protection effective mais ne rêvez pas, il ne durera pas." ));
            allItems.Add(new Items ( 8856, "Ceinture Rasboulaire du Rasboul", "Lorsque le Rasboul a les boules, il porte sa ceinture Rasboulaire pour envoyer bouler tous les casse-boules." ));
            allItems.Add(new Items ( 8858, "Bottes Horchons", "Ces Bottes émettent un immonde bruit de succion à chaque pas, ce qui n'est pas sans vous rappeler le chant d'amour des Trools en rut. Un souvenir douloureux dont vous vous seriez bien passé." ));
            allItems.Add(new Items ( 8859, "Sceau Souverain du Roissingue", "Ce sceau désigne son possesseur comme étant le souverain incontesté de la Tourbière sans fond de l'île d'Otomaï. Difficile d'imaginer un royaume plus bucolique." ));
            allItems.Add(new Items ( 8860, "Alliance de Corail", "Cet anneau a été arraché au doigt encore chaud d'un jeune Corailleur fraichement massacré. Sa mère l'attendait pour le repas, jusqu'à ce qu'elle s'aperçoive que son rejeton venait d'être transformé en pierre tombale.  Même le corail a un coeur, et pas forcément de pierre." ));
            allItems.Add(new Items ( 8861, "Sandales Circulaires du Kimbo", "Seul le Kimbo et ses pieds totalement loufoques peuvent se sentir à l'aise dans ces sandales. En revanche il n'est pas le seul à pouvoir profiter de leur puissance. Attention tout de même de ne pas vous tromper de sens en les enfilant." ));
            allItems.Add(new Items ( 8862, "Ceinture Bitoufale de Prosper Youpla", "Prosper Youpla était persuadé de pouvoir voler grâce à cette ceinture ornée d'ailes de Bitouf. Ignorant que très peu de Bitoufs savent voler, il s'élançait du haut de l'arbre Hakam et s'écrasait systématiquement au sol, tant et si bien qu'il fût surnommé " ));
            allItems.Add(new Items ( 8863, "Zothulette", "Cette amulette est un vulgaire morceau de bois et de tissu déchiré, mais les Zoths croient dur comme fer à ses pouvoirs. Les pauvres fous !" ));
            allItems.Add(new Items ( 8864, "Kask\'Arc Go", "La corde de cet arc est faite de bave de Kaskargo, plus ou moins sèche, son élasticité est sans pareille, sa texture gluante et visqueuse en revanche rappelle... On ne sait pas trop ce que ça rappelle mais ça n'est pas agréable." ));
            allItems.Add(new Items ( 8865, "Anneau Skargo", "Sculpté dans une carapace de Kaskargo, cet anneau résiste à n'importe quoi ou n'importe qui." ));
            allItems.Add(new Items ( 8866, "Puissante Cape Fulgurante", "Tissée dans des étoffes de Zoths, cette cape possède un pouvoir extraordinaire dont l'origine reste encore inconnue." ));
            allItems.Add(new Items ( 8867, "Cape de la Meupette", "En portant cette cape, vous serez pris d'une furieuse envie de vinaigrette." ));
            allItems.Add(new Items ( 8868, "Poolache", "Le fait qu'un bec de Poolay orne cette arme ne sert absolument à rien, voire handicape l'utilisateur. Heureusement qu'elle est puissante." ));
            allItems.Add(new Items ( 8869, "Bottines en bois d\'abrakleur", "Ces bottes sont si lourdes qu'elles seraient plutôt à classer dans la catégorie des armes tellement les coups de pieds deviennent meurtriers." ));
            allItems.Add(new Items ( 8870, "Ceinture sombre en Abrakleur", "Taillée dans un Abrakleur sombre, cette ceinture n'est pas assez flexible pour permettre de bouger librement, mais au moins, elle protège bien." ));
            allItems.Add(new Items ( 8871, "Ceinture Claire en Abrakleur", "Taillée dans un Abrakleur clair, cette ceinture ne protège pas bien, mais au moins, elle est assez flexible pour bouger librement." ));
            allItems.Add(new Items ( 8872, "Palmano", "Ce bijou n'est pas très précieux, mais il a la taille idéale pour servir à doser le mourgale dans un bon belgrouth à la glaviouze." ));
            allItems.Add(new Items ( 8873, "Krustoture", "Enfin une bonne excuse de sentir la marée !" ));
            allItems.Add(new Items ( 8874, "Ouassulette", "Ce bijou essaye parfois de vous voler des aliments au passage pendant votre repas, ne le laissez pas pendre trop bas." ));
            allItems.Add(new Items ( 8876, "Voile d\'encre", "Cette cape étrange semble faite d'encre noire, mais étrangement, elle se comporte comme une étoffe et ne coule pas. En revanche elle tâche. Votre arrière train n'aura jamais été aussi sale." ));
            allItems.Add(new Items ( 8877, "Kralano", "Une fois enfilé, vous ne pourrez plus jamais l'enlever tellement ses ventouses sont efficaces." ));
            allItems.Add(new Items ( 8878, "Kidorteau", "Ce marteau n'est pas pratique, pas spécialement beau, pas très ergonomique, pas puissant, pas assez lourd, trop fragile, mal équilibré, il brille trop et sent mauvais. nMais il est coloré ! Enfin, un peu de couleurs dans ce monde de brutes." ));
            allItems.Add(new Items ( 8879, "Anneau Poli", "Cet anneau n'est malheureusement poli qu'à l'extérieur. On y enfile le doigt par un trou orné d'éclats de roche acérés. Recommandé pour les Sacrieurs." ));
            allItems.Add(new Items ( 8880, "Amufafah", "Ce collier donne une furieuse envie de rugir à son possesseur, simplement parce que les moustaches du Mufafah qui l'ornent lui chatouillent les tétons." ));
            allItems.Add(new Items ( 8881, "Anneau Solo", "Solo était un gentil vaurien, d'ailleurs il le disait lui-même. Ça ne l'a pas empêché de mourir pendu sur la place du village après avoir dérobé une petite culotte à la femme du chef. Solo n'était vraiment pas futé, mais gentil quand-même..." ));
            allItems.Add(new Items ( 8883, "Potion de Foyer de Guilde", "Cette potion vous permet de retourner dans votre foyer de guilde." ));
            allItems.Add(new Items ( 8885, "Fantôme Chacha Tigré", "Ce Chacha Tigré n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 8886, "Transformation Fantôme Chacha Tigré", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 8887, "Certificat de Mise en Chanil : Chacha Tigré", "Ce certificat décrit les caractéristiques du Chacha Tigré que vous avez placé au chanil." ));
            allItems.Add(new Items ( 8888, "Certificat de Mise en Chanil : Ecureuil Chenapan", "Ce certificat décrit les caractéristiques de l'Ecureuil Chenapan que vous avez placé au chanil." ));
