            allItems.Add(new Items ( 8913, "Potion Bulbique", "Cette potion est composée d'extraits de bulbes macérés dans de la purée de Kloug. Les alchimistes les plus audacieux utilisent de la farine de riz à la place de la purée de Kloug devenue introuvable depuis longtemps. Aussi improbable que cela puisse paraître, vous vous habituerez à son goût." ));
            allItems.Add(new Items ( 8914, "Fiche descriptive d\'un Bricoleur", "Cette fiche contient toutes les informations nécessaires pour retrouver le Bricoleur qui y est décrit." ));
            allItems.Add(new Items ( 8915, "Fiche du Bricoleur à remplir", "Cette fiche est faite pour y remplir toutes les informations nécessaires pour identifier un Bricoleur." ));
            allItems.Add(new Items ( 8916, "Ambre du Tynril", "Cette ambre n'est pas comme les autres. Si vous l'agitez de haut en bas, en fermant les yeux, et en dansant un tango rapide du Captain Chafer, vous pourrez peut-être la sentir vibrer et à briller, comme si une âme mystérieuse l'habitait. Accessoirement, vous aurez l'air d'un fou, mais d'un fou qui brille et qui vibre." ));
            allItems.Add(new Items ( 8917, "Clef de la Grotte Hesque", "Cette clef étrange vous permettra de rentrer dans la Grotte Hesque en un seul morceau." ));
            allItems.Add(new Items ( 8918, "Casque amélioré", "Ce casque amélioré permet en théorie de chevaucher les Scarafeuilles les plus rapides sans perdre sa perruque. Tout simplement inestimable." ));
            allItems.Add(new Items ( 8919, "Ailes en bois améliorées", "Ces ailes en bois améliorées ont été rembourrées et rendues plus légères grâce à des plumes et du tissu de qualité. L'armature étant toujours en bois, vous aurez tout de même du mal à ne pas vous écraser." ));
            allItems.Add(new Items ( 8922, "Bourse de kamas", "Cette bourse contient une importante somme de kamas. Elle est surement destinée à quelque pot-de-vin." ));
            allItems.Add(new Items ( 8923, "Sac de patates", "Un sac de patates, il faut vraiment ne pas savoir quoi faire de son temps pour se balader avec ça" ));
            allItems.Add(new Items ( 8924, "Arc à Chon", "C'est l'histoire d'un homme appelé Chon, il avait un phare. C'était le Phare à Chon." ));
            allItems.Add(new Items ( 8925, "Arc Oleptik", "Cet arc étrange a été sculpté par une Sadida pendant une phase de somnambulisme créatrice. Il en émane une envie de sommeil qui affecte autant le porteur que ses cibles." ));
            allItems.Add(new Items ( 8926, "Dague Adou", "Ces dagues sont parfaites pour pousser les ananas ou moudre le café. Certains s'en servent même pour taper des pommes et des poires." ));
            allItems.Add(new Items ( 8927, "Dagues Eudin", "Eudin était un Sram complètement fou, il volait les pauvres pour donner aux riches." ));
            allItems.Add(new Items ( 8928, "Dagues Ruik", "Ruik est meilleur buveur que combattant, et ça ne s'améliore pas au fil du temps. Il est quand même déconseillé de ressembler à un bouchon quand il porte ses dagues." ));
            allItems.Add(new Items ( 8929, "Kriss Toubal", "A ne pas confondre avec le célèbre bâton au nom semblable, ces lames provoquent des carnages bien plus violents." ));
            allItems.Add(new Items ( 8930, "Fuschia", "Plus d'un fier guerrier a refusé cette arme à cause de sa couleur, la majorité d'entre eux est morte sous sa lame." ));
            allItems.Add(new Items ( 8931, "Az\'tech", "Cette épée appartenait à un vieil Enutrof mégalomane, elle est en or massif, comme l'était son égo." ));
            allItems.Add(new Items ( 8932, "Kukri Kura", "Souriez ! Dites cheese ! Voilà, vous êtes coupé en deux." ));
            allItems.Add(new Items ( 8933, "Hache Sueur Rance", "Ourag'han était un vieux Pandawa au derme si doux qu'on l'appelait Peau Lisse. Son odeur en revanche était si forte qu'il a hérité d'un autre surnom. Il l'a transmis à sa fidèle hache." ));
            allItems.Add(new Items ( 8934, "Hache Harnée", "Fâché de s'être fait voler un grain de riz dans son assiette par un écureuil chenapan pendant ses vacances touristiques sur l'île de Pandala, Brouce Boulgour s'est servi de cette hache pour abattre chaque bambou au sommet duquel le voleur se réfugiait, afin de l'attraper. Les Pandawas du village ont dû le calmer avec la moitié de la réserve d'alcool de bambou pour éviter qu'il ne rase la forêt. Sacré Brouce." ));
            allItems.Add(new Items ( 8935, "la Bêche à Mel", "Jeune Iopette, Mel se voyait déjà vaincre des dragons. Elle jouait à combattre les rochers du jardin de sa mère avec une pelle en guise d'épée. La rocaille a appris à redouter l'ustensile." ));
            allItems.Add(new Items ( 8936, "Pelle Shattkitou", "Cette pelle est sauvage et tente de mordre son propriétaire à chaque utilisation. Elle émet des sons bizarres quand elle est contente et ne se calme qu'en présence de grelots." ));
            allItems.Add(new Items ( 8937, "Pelle Aigante", "Un Enutrof futé s'était fait pirate après avoir fait le constat qu'on ramasse plus de trésor sur un cadavre frais que dans une tombe moisie. Sa pelle rouge sang était son symbole et faisait trembler l'équipage des bateaux de toutes les mers." ));
