            allItems.Add(new Items ( 8985, "Eupéoh pour Dragoune et Tifoux", "En utilisant ce comprimé puissant, vous augmenterez les capacités de votre Dragoune ou de votre Tifoux de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 8986, "Eupéoh pour Bouloute et Fotome", "En utilisant ce comprimé puissant, vous augmenterez les capacités de votre Bouloute ou de votre Fotome de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 8987, "Eupéoh pour El Scarador et Croum", "En utilisant ce comprimé puissant, vous augmenterez les capacités de votre El Scarador ou de votre Croum de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 8988, "Eupéoh pour Abra Kadabra et Minimino", "En utilisant ce comprimé puissant, vous augmenterez les capacités de votre Abra Kadabra ou de votre Minimino de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 8989, "Casque du Bitouf Aérien", "Avec ce casque sur la tête, vous pouvez espérer vous élever dans les airs si vous tournez sur vous-même à la vitesse de l'éclair. Pour l'atterrissage, débrouillez-vous." ));
            allItems.Add(new Items ( 8990, "Prisme de conquête", "Cet objet magique en pierre rayonne dans toute la zone dans laquelle il est posé. Il permet de marquer votre territoire sans avoir besoin de gâcher votre urine. À utiliser sans modération si vous avez l'âme d'un conquérant. Attention, vous ne pouvez poser un prisme de conquête que si vous affichez clairement votre grade (mode joueur contre joueur activé), et si vous êtes dans une zone ne contenant aucun prisme. La zone dans laquelle vous voulez poser un prisme de conquête doit en outre être mitoyenne d'une zone devenue alliée depuis au moins cinq minutes." ));
            allItems.Add(new Items ( 8991, "Annolamour", "Cet anneau vous suce le doigt en permanence, il est gluant et froid, si vous supportez ça vous pourrez profiter de ses immenses pouvoirs." ));
            allItems.Add(new Items ( 8992, "Faux maudite du Saigneur Guerrier", "Cette lame fauche vos ennemis comme les blés, mais elle attise également la jalousie de vos alliés. Tel est le destin des puissants." ));
            allItems.Add(new Items ( 8993, "Epée Maudite du Saigneur Guerrier", "Cette épée dégage un pouvoir impressionnant, les carnages qu'elle augure sont de ceux restant dans les livres d'histoire. Mais son maniement reste étrangement hasardeux. Il existe peut-être quelqu'un capable de corriger cela." ));
            allItems.Add(new Items ( 8994, "Etoffe Corailleuse", "Cette étoffe humide est restée sous l'eau pendant plusieurs années. De petites particules de corail ont commencé à la recouvrir. Dommage qu'elle soit aussi rigide, elle aurait pu servir de mouchoir." ));
            allItems.Add(new Items ( 8995, "Etoffe de Gourlo le Terrible", "Autrefois utilisée comme pansement pour soigner les cloques de Gourlo le Terrible, cette étoffe est encore imbibée d'un liquide à l'odeur surprenante. Le plus étrange c'est que cela semble vous donner de l'appétit." ));
            allItems.Add(new Items ( 8996, "Peau du Rasboul Majeur", "Cette peau est plus douce que celle que l'on peut trouver sur les fesses d'une femelle Kilibriss. Ca vous fait une belle jambe." ));
            allItems.Add(new Items ( 8997, "Peau lacérée du Tynril", "Cette peau recouvrait sûrement un point sensible du Tynril. Le pauvre." ));
            allItems.Add(new Items ( 8998, "Pierre précieuse du Kimbo", "Pendant des années, le Kimbo a sucé cette pierre précieuse pour la rendre plus lisse et pouvoir l'introduire plus facilement dans ses narines. La nature est parfois difficile à comprendre." ));
            allItems.Add(new Items ( 9001, "Bouclier de Sidimote", "Ce bouclier est censé rappeler à vos adversaires que vous avez grandi dans les Landes de Sidimote. Il paraît que cela pourrait suffire à les effrayer." ));
            allItems.Add(new Items ( 9002, "Ftalmo", "Ce bouclier est recouvert de divers yeux, pour permettre à son porteur de fixer plus facilement son adversaire dans les yeux." ));
            allItems.Add(new Items ( 9003, "Bouclier Taverne", "Le tavernier de la brasserie Feubuk de Bonta devait certainement s'ennuyer fermement lorsqu'il eu soudainement l'idée d'équiper les videurs de sa taverne avec ces boucliers publicitaires." ));
            allItems.Add(new Items ( 9004, "Bouclier Akwadala", "Pour parer les coups de bourre ou les coups tordus, ce bouclier issu du village d'Akwadala devrait faire l'affaire." ));
            allItems.Add(new Items ( 9005, "Bouclier Ventaille", "Lorsque vos alliés commencent à s'essouffler ou même lorsque vous voulez dissiper les effets d'un Vent de Panique, ce bouclier peut vous sauver la vie, il suffit de l'agiter vigoureusement dans tous les sens, un peu comme si vous vouliez calmer un nourrisson un peu trop bruyant." ));
            allItems.Add(new Items ( 9006, "Bouclier du Sadida", "Ce bouclier servait à l'origine de tabouret aux Sadidas lorsque ces derniers devaient se reposer. Une fois les pieds repliés, il vous protègera de beaucoup d'attaques, mais pas des fous rires de vos adversaires." ));
            allItems.Add(new Items ( 9007, "Bouclier en Slip", "Cette création surprenante, est recouverte d'un Slip en Cuir du Chafer lancier, bouilli, nettoyé et détendu pour épouser les formes du bouclier, qui sont loin de rappeler celles du Chafer, bien plus avantageuses et aguichantes..." ));
            allItems.Add(new Items ( 9008, "Bouclier d\'Asse", "Ce splendide bouclier contient un échantillon d'eau de la Mer d'Asse, ce qui en plus de vous porter bonheur, pourra vous servir de gourde, si votre dulcinée ne vous accompagne pas durant vos combats." ));
            allItems.Add(new Items ( 9009, "Bouclier Abraknyde", "Caché derrière ce bouclier, les aventuriers les plus naïfs pourront vous prendre pour un Abraknyde et se sauver en courant. Les autres vous agresseront en pensant récupérer quelques ambres." ));
            allItems.Add(new Items ( 9010, "Bouclier Pandawa", "Idéal pour se faire discret sur Pandala, et pour se faire remarquer partout ailleurs, ce bouclier a surtout l'avantage d'être recouvert de poils de Pandawas, réputés pour ne jamais déclencher la moindre allergie. Vous pourrez donc sans crainte ranger ce bouclier directement dans votre slip en cuir." ));
            allItems.Add(new Items ( 9011, "Cadran de Feu", "Ce bouclier original vous protégera de l'élément Feu, et accessoirement, donnera l'heure à vos adversaires. Très utile pour les aider à chronométrer leur défaite." ));
            allItems.Add(new Items ( 9012, "Cadran d\'Air", "Ce bouclier original vous protègera de l'élément Air, et accessoirement, donnera l'heure à vos adversaires. Très utile pour les aider à chronométrer leur défaite." ));
            allItems.Add(new Items ( 9013, "Cadran de Terre", "Ce bouclier original vous protégera de l'élément Terre, et accessoirement, donnera l'heure à vos adversaires. Très utile pour les aider à chronométrer leur défaite." ));
            allItems.Add(new Items ( 9014, "Cadran d\'Eau", "Ce bouclier original vous protégera de l'élément Eau, et accessoirement, donnera l'heure à vos adversaires. Très utile pour les aider à chronométrer leur défaite." ));
            allItems.Add(new Items ( 9015, "Bouclier du Bûcheron", "Ce bouclier servait à l'origine aux bûcherons qui voulaient se protéger de la chute des arbres. Il a vite été détourné de son usage, et sert maintenant à se protéger des chutes des ennemis qui ont parfois une fâcheuse tendance à vous tomber dessus sans prévenir." ));
            allItems.Add(new Items ( 9016, "Bouclier du Mont Kipou", "Verdâtre et malodorant, ce bouclier n'en reste pas moins une protection de choix, lorsqu'il s'agît d'affronter les créatures du Mont Kipou." ));
            allItems.Add(new Items ( 9017, "Bouclier des Brigandins", "Si vous avez toujours rêvé d'être un vrai Brigandin, c'est le bouclier qu'il vous faut. Transmis de générations en générations, il vous permettra de détrousser sans prendre le moindre coup, les jeunes aventuriers d'Astrub." ));
            allItems.Add(new Items ( 9018, "Le Karde Coeur", "Un illustre aventurier collectionneur, a passé sa vie à chercher ce type de bouclier. On raconte qu'une fois quatre de ces boucliers réunis, on pouvait jouir d'une meilleure vie." ));
            allItems.Add(new Items ( 9019, "Bouclier du Bawbawe", "Tous les Wabbits ne sont pas de petites créatures inoffensives. En réalité, tous les Wabbits sont d'infâmes créatures, certains ont même des moeurs barbares et écrasent les Cawottes avec cet énorme bouclier, pour en faire du jus." ));
            allItems.Add(new Items ( 9020, "Derma Tho", "Ce bouclier en peau, risque de vous coller à la peau, tellement vous l'apprécierez. N'oubliez pas de soigner ses verrues et ses furoncles de temps en temps pour éviter qu'il ne s'abîme." ));
            allItems.Add(new Items ( 9021, "Bouclier du Captain Amakna", "Un jeune aventurier avait voulu s'enrôler dans la milice de Bonta pour défendre les opprimés. Réformé en raison de sa constitution trop faible, il fut tout de même contacté par un Alchimiste d'Amakna, déterminé à l'utiliser comme cobaye pour tester une de ses nouvelles potions magiques. L'effet fut immédiat, le jeune aventurier devint rapidement un véritable guerrier d'une force extraordinaire, mais aussi complètement stupide. Il avait en fait été transformé en Iop. Il se fit forger ce bouclier réputé indestructible, et le surnom de Captain Amakna (en hommage à l'illustre Captain Chafer qui disparut dans des circonstances bien mystérieuses) lui fut attribué pour l'éternité." ));
            allItems.Add(new Items ( 9022, "Kloume", "Cet énorme bouclier a jadis, permis au célèbre aventurier Kloume, de ne pas mourir écrasé sous le poids d'un Craqueleur qui était tombé amoureux de lui. L'histoire ne raconte pas s'il a survécu aux effets secondaires de sa relation passionnée avec le Craqueleur." ));
            allItems.Add(new Items ( 9023, "Bouclier Volant", "Aussi étrange que cela puisse paraître, on raconte que ce bouclier en bois et en poils, permet aux aventuriers les plus légers, de s'envoler quelques instants dans les airs, pour impressionner une dernière fois ses adversaires avant de s'écraser stupidement sur le sol." ));
            allItems.Add(new Items ( 9024, "Bouclier du Chouque", "Le Chouque se servait de ce bouclier pour se protéger des gros boulets qui lui étaient régulièrement envoyés. Les histoires les plus folles, racontent même que certains boulets étaient vivants." ));
            allItems.Add(new Items ( 9025, "Bouclier du Bouftou", "Assez inutile mais tout de même impressionnant grâce à l'odeur qu'il dégage, ce bouclier sera le compagnon de beaucoup de jeunes aventuriers." ));
            allItems.Add(new Items ( 9026, "Bouclier de Bowisse", "Ce bouclier est clairement un hommage à la cape du Bowisse, elle-même étant un hommage à une célèbre relique composée de trois fragments de forme triangulaire. Encore une histoire à dormir debout." ));
            allItems.Add(new Items ( 9027, "Bouclier du Craqueleur", "Ce bouclier en pierre est en fait une tête de Craqueleur écrasée par le pied d'un Minotoror. Une façon comme une autre de combiner grosse tête et odeurs de pieds." ));
            allItems.Add(new Items ( 9028, "Bouclier Terrdala", "Pour parer les coups de bourre ou les coups tordus, ce bouclier issu du village de Terrdala devrait faire l'affaire." ));
            allItems.Add(new Items ( 9029, "Bouclier Feudala", "Pour parer les coups de bourre ou les coups tordus, ce bouclier issu du village de Feudala devrait faire l'affaire." ));
            allItems.Add(new Items ( 9030, "Bouclier Aerdala", "Pour parer les coups de bourre ou les coups tordus, ce bouclier issu du village d'Aerdala devrait faire l'affaire." ));
            allItems.Add(new Items ( 9031, "Annobusé de Maître Jarbo", "En son temps, Maître Jarbo était très fort et il possédait richesses et femmes à foison. Mais il n'avait guère de mérite puisqu'il tirait tout son pouvoir de son joli anneau. Un jour, il traversa une route. Et PAF Maître Jarbo. Depuis, son anneau se transmet de main en main entre personnes peu scrupuleuses." ));
            allItems.Add(new Items ( 9035, "Potion d\'enclos de Guilde", "Cette potion vous permet de retourner dans votre enclos de guilde." ));
            allItems.Add(new Items ( 9036, "Potion du village des Zoths", "Cette potion vous permet de vous téléporter dans le village des Zoths, si ce dernier vous appartient." ));
            allItems.Add(new Items ( 9037, "Potion de découverte", "Cette potion permet de dévoiler les personnages invisibles de votre entourage." ));
            allItems.Add(new Items ( 9038, "Petite potion d\'agression", "Cette potion vous permet d'entrer dans une telle rage que vous attaquez automatiquement les ennemis qui s'approcheront trop près de vous." ));
            allItems.Add(new Items ( 9039, "Potion de rappel prismatique", "Cette potion vous permet de vous téléporter vers le prisme allié le plus proche." ));
            allItems.Add(new Items ( 9040, "Potion d\'agression Moyenne", "Cette potion vous permet d'entrer dans une telle rage que vous attaquez automatiquement les ennemis qui s'approcheront trop près de vous." ));
