            allItems.Add(new Items ( 9060, "Morceau de papier déchiré", "C'est une partie de lettre que quelqu'un a déchirée. On peut encore y lire : '... Comme convenu, je m'arrangerai pour que l'enquête sur ce marchand n'aboutisse pas. Venez me verser la somme prévue à l'endroit habituel où les femmes sont de charmante compagnie.' nLa lettre est signée X." ));
            allItems.Add(new Items ( 9061, "Exoram", "Exoram doit être escorté hors du camp gobelin et jusqu'au temple Sram." ));
            allItems.Add(new Items ( 9062, "Document sur Xephirès", "Ce document contient de nombreuses informations intéressantes sur l'individu nommé Xephirès. Notamment, on y apprend qu'il passe beaucoup de temps à la taverne du Pandawa Ivre." ));
            allItems.Add(new Items ( 9063, "Liste de Milone", "Cette liste absolument impressionnante vous a été remise par Milone le bijoutier. Elle décrit les objets nécessaires pour confectionner un somptueux joyaux." ));
            allItems.Add(new Items ( 9064, "Bave du Bouftou Royal", "Est-elle vraiment différente d'une bave ordinaire ? En tout cas, ça n'en a pas l'air." ));
            allItems.Add(new Items ( 9065, "Lettre pour Milone", "Cette lettre, écrite à la hâte, explique à Milone que le bijou est en danger. D'ailleurs, pourquoi perdez-vous du temps à la lire au lieu de vous précipiter chez lui ?" ));
            allItems.Add(new Items ( 9066, "Lettre du chef des voleurs", "Retrouvée sur un des voleurs que vous avez vaincus chez Milone : nMilone Corichel le bijoutier vient de créer un joyau de grande valeur. Dérobez-le par tous les moyens possibles. nVisiblement, les voleurs ont échoué et en plus au dos du message se trouve l'emplacement de leur repaire. Une petite visite de courtoisie s'impose, mais mieux vaut s'y rendre accompagné." ));
            allItems.Add(new Items ( 9067, "Lettre pour Xephirès", "Cette lettre d'Oto Mustam explique en détails le plan pour Xephirès." ));
            allItems.Add(new Items ( 9068, "Joyau de Milone", "Ce joyau est le résultat de vos efforts et de ceux de Milone le bijoutier. Il vaut assurément une petite fortune... pour ceux que ça peut intéresser." ));
            allItems.Add(new Items ( 9069, "Nouvelle lettre pour Xephirès", "Oto y décrit ses conditions pour la corruption de Xephirès." ));
            allItems.Add(new Items ( 9070, "Epée étrange", "Cette épée est vraiment inhabituelle. Vous n'en avez jamais vu de telle dans Bonta." ));
            allItems.Add(new Items ( 9071, "Notes du tavernier Feubuk", "Le tavernier Feubuk a reçu des Ecaflips louches qui ont prononcé le nom de Loudac." ));
            allItems.Add(new Items ( 9072, "Notes du tavernier Pinchaut", "Le tavernier Pinchaut a entendu des gardes parler de leurs problèmes avec une bande d'Ecaflips." ));
            allItems.Add(new Items ( 9073, "Insigne de Bandit Ecaflip", "Vous avez récupéré cet insigne sur les bandits Ecaflips des plaines de Cania." ));
            allItems.Add(new Items ( 9074, "Bijou d\'Amayiro", "Ce bijou servira à appâter le chef des bandits afin de le capturer." ));
            allItems.Add(new Items ( 9075, "Lettre du Xélor", "Cette lettre vous a été remise par l'intermédiaire de Marge la bijoutière : nAmenez le bijou à Ekusoyk Ubnan à la Taverne de la Chopenbois. Ce sera plus rapide pour nous et beaucoup plus lucratif pour vous." ));
            allItems.Add(new Items ( 9076, "Bijou d\'Amayiro (faux)", "Ce bijou ressemble à s'y méprendre à celui qu'Amayiro vous a envoyé chercher. D'ailleurs, il vaudrait mieux qu'il s'y méprenne, sinon..." ));
            allItems.Add(new Items ( 9077, "Crâne de Feca", "Le crâne de Feca fait un excellent presse-livres." ));
            allItems.Add(new Items ( 9078, "Crâne d\'Osamodas", "Un Tofu par-ci, un Bwork mage par-là : ce disciple d'Osamodas en a perdu la tête." ));
            allItems.Add(new Items ( 9079, "Crâne d\'Enutrof", "Ce trophée était déjà vieux avant de devenir un trophée." ));
            allItems.Add(new Items ( 9080, "Crâne de Sram", "Ce trophée était déjà morbide avant de devenir un trophée." ));
            allItems.Add(new Items ( 9081, "Crâne de Xélor", "Ce crâne est souvent confondu avec celui de la célèbre momie de Tut Enkarthon. Ne vous y trompez pas : ces bandelettes-ci ne sont pas en carton, mais en papier mâché." ));
            allItems.Add(new Items ( 9082, "Crâne d\'Ecaflip", "À défaut de jouer à pile ou face, on pourra jouer aux osselets." ));
            allItems.Add(new Items ( 9083, "Crâne d\'Eniripsa", "Ne soyez pas mauvaise langue : Non, Il ne s'agit pas d'un crâne de Piaf !" ));
            allItems.Add(new Items ( 9084, "Crâne de Iop", "Ce trophée sonne aussi creux que lorsqu'il était plein." ));
            allItems.Add(new Items ( 9085, "Crâne de Crâ", "On pourrait le croire sorti d'une fouille archéologique, ce crâne de Crâ mignon !" ));
            allItems.Add(new Items ( 9086, "Crâne de Sadida", "Qui eût cru qu'il y eut un crâne sous la touffe de cheveux ?" ));
            allItems.Add(new Items ( 9087, "Crâne de Sacrieur", "Ce sacrieur fait moins le fier sans ses milliers de points de vie." ));
            allItems.Add(new Items ( 9088, "Crâne de Pandawa", "On aurait pu croire qu'après la mise en bière le Pandawa aurait une gueule de bois." ));
            allItems.Add(new Items ( 9089, "Insigne de la patrouille de Bonta", "Vous avez récupéré cet insigne lors de la (très facile) bataille d'escarmouche contre Bonta." ));
            allItems.Add(new Items ( 9090, "Insigne de la patrouille de Brâkmar", "Vous avez récupéré cet insigne à grand peine lors de la bataille d'escarmouche contre Brâkmar." ));
            allItems.Add(new Items ( 9094, "Poison Bwork", "Ca ressemble davantage à de la gelée de menthe qu'à du poison. Les Bworks ne sont assurément pas très doués pour ça : ce qui est plus inquiétant, c'est que les espions de Brâkmar non plus, apparemment." ));
            allItems.Add(new Items ( 9096, "Tacticien", "Le tacticien a accepté de vous suivre jusqu'à votre chef." ));
            allItems.Add(new Items ( 9097, "Badge du puissant milicien", "Vous avez trouvé ce badge sur le milicien particulièrement redoutable qui a vaincu plusieurs guerriers de Brâkmar. Son équipement semblait très particulier." ));
            allItems.Add(new Items ( 9098, "Marteau de Nara", "Ce marteau indiquera à Oto que vous avez vaincu le forgeron mercenaire de Bonta." ));
            allItems.Add(new Items ( 9099, "Demande d\'aide d\'Amayiro", "Cette lettre, signée par Amayiro, est une demande d'aide officielle." ));
            allItems.Add(new Items ( 9100, "Caleçon de Lenglad", "Là, c'est sûr : Lenglad ne fera plus parler de lui avant un moment." ));
            allItems.Add(new Items ( 9101, "Caisse d\'outils de Nara", "Cette caisse contient les outils que Nara le forgeron a oubliés." ));
            allItems.Add(new Items ( 9103, "Plan d\'attaque", "Cette lettre, datée d'il y a une semaine, explique en détails le plan d'attaque contre Bonta. Il faut toutefois se méfier, car une note en fin de page indique que ce plan n'est pas encore confirmé et sujet à modification dans les prochains jours par l'officier Kapi Taigne. Par conséquent, vous devez découvrir ce qu'il en est à l'heure actuelle." ));
            allItems.Add(new Items ( 9104, "Insigne de la patrouille du Cimetière", "Vous avez récupéré cet insigne sur la patrouille brâkmarienne que vous avez attaquée au Cimetière des Torturés." ));
            allItems.Add(new Items ( 9105, "Lettre de Danathor", "Quelqu'un a glissé cette lettre dans votre sac pendant qu'Amayiro vous donnait la mission : nRécupérez les documents, mais apportez-les moi plutôt qu'à Amayiro. Vous serez récompensé. Danathor" ));
            allItems.Add(new Items ( 9106, "Documents secrets de Bonta", "Ces documents contiennent un bon nombre d'informations concernant les défenses et le système de Bonta. Mais à qui allez-vous les donner ?" ));
            allItems.Add(new Items ( 9107, "Consignes pour l\'espion de Bonta", "Pénètre dans la tour de Brâkmar à minuit et récupère tous les documents que tu trouveras au deuxième étage. Reviens ensuite à Bonta en passant par l'Est de la route. nNon, ce n'est pas VOUS qui devez le faire, c'était la consigne pour l'espion que vous devez sauver." ));
            allItems.Add(new Items ( 9108, "Documents brâkmariens secrets", "Ces documents contiennent de nombreuses informations sur la Corne Edfrit." ));
            allItems.Add(new Items ( 9109, "Bandana Troué", "Ce bandana en mauvais état est couvert de glaire, comme s'il avait servi de mouchoir à plusieurs générations de Wabbits. Il a sans nul doute appartenu à Turorte, en témoigne son odeur de hareng pourri subtilement camouflée par une odeur de cawotte. Pour sentir aussi mauvais après toutes ces années, il est indubitablement imprégné d'une puissante magie." ));
            allItems.Add(new Items ( 9110, "Corne Edfrit", "Vous avez déjà essayé de souffler dedans, mais il ne se passe rien de spécial..." ));
            allItems.Add(new Items ( 9112, "Consigne pour l\'espion brâkmarien", "Pénètre dans la tour de Bonta à minuit, égorge tous ceux qui sont sur ton chemin, et récupère tous les documents que tu trouveras au premier étage. Reviens ensuite à Brâkmar passant par la route. nNon, ce n'est pas VOUS qui devez le faire, c'était la consigne pour l'espion que vous devez retrouver." ));
            allItems.Add(new Items ( 9113, "Documents secrets volés", "Les documents que l'espion blessé a récupéré dans la tour de Bonta." ));
            allItems.Add(new Items ( 9114, "Documents secrets froissés", "Les documents que l'espion blessé a récupéré dans la tour de Bonta. Ils ont été un peu abimés pendant votre combat contre les forces de Bonta." ));
            allItems.Add(new Items ( 9115, "Salace fourrée au pollen", "Cette salace a été préparée dans le chaudron primordial ce qui lui confère des vertus particulières. Pour savoir lesquelles, il faudrait pouvoir y goûter, malheureusement c'est à Turorte qu'elle est destinée. La vie est parfois injuste." ));
            allItems.Add(new Items ( 9116, "Caisse d\'équipement", "Une arme, un casque, un bouclier : cette caisse contient un équipement rudimentaire mais efficace pour les guerriers." ));
            allItems.Add(new Items ( 9117, "Ougarteau", "Ougah a la tête dure, plus dure que n'importe qui, c'est pourquoi un marteau créé avec son crâne est idéal pour fracasser celui des autres." ));
            allItems.Add(new Items ( 9118, "Cadeau Event Shika", "(cadeau offert aux joueurs de Shika pour l'event, ne pas traduire)" ));
            allItems.Add(new Items ( 9119, "Cadeau Shika", "(cadeau pour les joueurs abonnés de Shika, ne pas traduire)" ));
            allItems.Add(new Items ( 9120, "Chapeau Shika", "(Chapeau pour les abonnés du serveur Shika, ne pas traduire)" ));
            allItems.Add(new Items ( 9121, "Cape Shika", "(cape destinée aux joueurs abonnés du serveur Shika, ne pas traduire)" ));
            allItems.Add(new Items ( 9122, "Blopanneau Griotte", "Une fois enfilé au bout d'un doigt, il n'est plus possible de se débarrasser de cet anneau. Plus vous y pensez, et plus il vous rappelle quelqu'un." ));
            allItems.Add(new Items ( 9123, "Blopanneau Reinette", "Une fois enfilé au bout d'un doigt, il n'est plus possible de se débarrasser de cet anneau. Plus vous y pensez, et plus il vous rappelle quelqu'un." ));
            allItems.Add(new Items ( 9124, "Blopanneau Coco", "Une fois enfilé au bout d'un doigt, il n'est plus possible de se débarrasser de cet anneau. Plus vous y pensez, et plus il vous rappelle quelqu'un." ));
            allItems.Add(new Items ( 9125, "Blopanneau Indigo", "Une fois enfilé au bout d'un doigt, il n'est plus possible de se débarrasser de cet anneau. Plus vous y pensez, et plus il vous rappelle quelqu'un." ));
            allItems.Add(new Items ( 9126, "Blopanneau Griotte Royal", "Kroula avait jadis tenté d'enfiler cet anneau autour de son gros orteil. Il espérait ainsi que ses ennemis daigneraient lui lécher les pieds dans l'espoir de récupérer quelques gouttes de jus de Blop Royal." ));
            allItems.Add(new Items ( 9127, "Blopanneau Reinette Royal", "Kroula avait jadis tenté d'enfiler cet anneau autour de son gros orteil. Il espérait ainsi que ses ennemis daigneraient lui lécher les pieds dans l'espoir de récupérer quelques gouttes de jus de Blop Royal." ));
            allItems.Add(new Items ( 9128, "Blopanneau Coco Royal", "Kroula avait jadis tenté d'enfiler cet anneau autour de son gros orteil. Il espérait ainsi que ses ennemis daigneraient lui lécher les pieds dans l'espoir de récupérer quelques gouttes de jus de Blop Royal." ));
            allItems.Add(new Items ( 9129, "Blopanneau Indigo Royal", "Kroula avait jadis tenté d'enfiler cet anneau autour de son gros orteil. Il espérait ainsi que ses ennemis daigneraient lui lécher les pieds dans l'espoir de récupérer quelques gouttes de jus de Blop Royal." ));
            allItems.Add(new Items ( 9130, "Ougamulette", "Cette réplique approximative du grand Ougah se porte autour du cou, avec un regard noir et une barbe correctement entretenue." ));
            allItems.Add(new Items ( 9131, "Anneau Colerette", "Colerette la jeune Crâette, a perdu la tête dans les bois de Litneg. La pauvre fille a reçu une hache lancée par Brouce Boulgoure, qui l'avait prise par méprise pour une fougère géante. Etre une belle plante ne paie pas toujours." ));
            allItems.Add(new Items ( 9132, "Anneau Chevelu", "Avec cet anneau, en plus d'avoir un cheveu sur la langue et un poil dans la main, vous pourrez vous vanter d'avoir des cheveux autour des doigts." ));
            allItems.Add(new Items ( 9133, "Alliance Boletée", "Si vos ennemis sont allergiques aux champignons, laissez-les vous croquer les doigts." ));
            allItems.Add(new Items ( 9134, "Arc à Lamelles", "Construit à la base pour servir de lattes pour les sommiers de lits, ces lamelles ont fini par être utilisées dans la fabrication de cet arc, qui vous permettra d'endormir vos ennemis pour l'éternité." ));
            allItems.Add(new Items ( 9135, "Baguetterelle", "A l'origine, il suffisait de souffler très fort dans cette trompette fongique, pour faire arriver le ménestrel Francisque Kabroule. Ce dernier se mettait alors à chanter, et tous les monstres mourraient sur place. Bon comme c'était un peu trop facile comme technique pour tuer les monstres, les dieux ont décidé de la boucher et de la transformer en une vulgaire baguette." ));
            allItems.Add(new Items ( 9136, "Bâton Champmanique", "Les trois pointes de ce bâton permettent de concentrer les résidus de plasmogrine présents dans la brise quadramentale. Mettez un peu de salive sur les pointes, orientez le bâton dans le sens des aiguilles d'une montre Xélor inversée, comptez jusqu'à soixante, ou essayez jusqu'à six si vous êtes Iop. Restez concentré, serrez les fesses, voilà vous avez une chance sur deux de vous prendre un coup de tonnerre, ou de transformer vos ennemis en Kirboules hirsutes." ));
            allItems.Add(new Items ( 9137, "Couteaux à Champignons", "Ces couteaux étaient autrefois utilisés pour retirer les mycoses qui parasitaient les pieds des aventuriers. On leur coupait les pieds juste au niveau du genou." ));
            allItems.Add(new Items ( 9138, "Hache à Lamelles", "Les différentes lamelles de cette hache permettent de couper très rapidement le saucisson. Un seul coup de hache permet de couper trois tranches. Pour tuer les ennemis, c'est un peu moins utile, sauf si vous avez l'habitude de les manger en tranches." ));
            allItems.Add(new Items ( 9139, "Bottines des sous-bois", "Ces bottines sont parfaites pour marcher dans les sous-bois, sans écraser les feuilles mortes. Attention cependant, leurs origines végétales les poussent inexorablement à écraser tout ce qui s'apparente de près ou de loin à du fumier." ));
            allItems.Add(new Items ( 9140, "Chaussons Pignons", "Ces chaussons sont recouverts de petites aérations qui contrairement aux idées reçues, ne permettent pas à vos pieds de mieux s'aérer, mais plutôt aux spores des champignons installés sur vos pieds, de s'enfuir rapidement pour aller parasiter d'autres aventuriers plus facilement." ));
            allItems.Add(new Items ( 9141, "Caprin", "Si cette cape n'était pas recouverte de moisissure, elle pourrait servir de nappe. Ce n'est pas grave, elle pourra toujours vous servir de linceul." ));
            allItems.Add(new Items ( 9142, "Capignon", "Cette cape était autrefois réservée aux meilleurs grimpeurs, aux aventuriers qui étaient les plus rapides pour escalader la montagne des Craqueleurs. Aujourd'hui elle sert surtout à se faire remarquer dans les soirées mondaines." ));
            allItems.Add(new Items ( 9143, "String Tue-Mouche", "Comme son nom l'indique, ce string est particulièrement efficace pour tuer les mouches un peu trop intéressées par votre anatomie. Il est déconseillé de l'arracher avec les dents, même si vous appréciez cela." ));
            allItems.Add(new Items ( 9144, "Ceinture Mycosine", "La meilleure façon de ne pas avoir de champignons dans l'entrejambe, c'est de les attirer ailleurs. Comme il n'y avait déjà plus de place dans vos chaussures pour loger d'autres champignons, vous pourrez utiliser cette ceinture, véritable substrat pour tout bon champignon digne de ce nom." ));
            allItems.Add(new Items ( 9145, "Tranche des Sous-bois", "Prenez un Orme, retirez-lui son écorce, laissez-la tremper dans une flaque, enfouissez-la dans la vase pendant plusieurs mois, recouvrez-là de spores de champignons, mettez-là sous votre lit pendant une semaine en la badigeonnant de graisse de Blop tous les soirs, et voilà vous avez une ceinture en écorce qui sent très mauvais. Alors heureux ?" ));
            allItems.Add(new Items ( 9146, "Ougature", "Sur l'étiquette de cette ceinture, on peut lire : " ));
            allItems.Add(new Items ( 9147, "Chapignon", "Prenez un Dora, recouvrez-le de bave de Bouftou et de la sueur de Iop. Laissez le tout moisir pendant quelques semaines, et voilà vous avec une réplique de Chapignon. Attention, ce chapeau donne parfois des hallucinations." ));
            allItems.Add(new Items ( 9148, "Blopanneau Multicolore Royal", "Plutôt que de réduire sa consommation de jus de Blop frelaté, Shika Ingalsse préféra continuer de faucher en état d'ébriété. C'est en titubant dans les champs qu'elle tomba sur cet étrange anneau. Malheureusement, cette histoire ne comporte qu'une seule chute." ));
            allItems.Add(new Items ( 9149, "Amublop Griotte", "Quand Lorkos voulut pénétrer dans le Labyrinthe du Minotoror, il n'hésita pas une seconde à équiper son Amublop. Personne n'a jamais su pourquoi." ));
            allItems.Add(new Items ( 9150, "Amublop Reinette", "Quand Lorkos voulut pénétrer dans le Labyrinthe du Minotoror, il n'hésita pas une seconde à équiper son Amublop. Personne n'a jamais su pourquoi." ));
            allItems.Add(new Items ( 9151, "Amublop Coco", "Quand Lorkos voulut pénétrer dans le Labyrinthe du Minotoror, il n'hésita pas une seconde à équiper son Amublop. Personne n'a jamais su pourquoi." ));
            allItems.Add(new Items ( 9152, "Amublop Indigo", "Quand Lorkos voulut pénétrer dans le Labyrinthe du Minotoror, il n'hésita pas une seconde à équiper son Amublop. Personne n'a jamais su pourquoi." ));
            allItems.Add(new Items ( 9153, "Amublop Griotte Royale", "Cette amulette coûte beaucoup plus cher à fabriquer qu'une Amublop classique. Mais il faut voir le bon côté des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d'Astrub." ));
            allItems.Add(new Items ( 9154, "Amublop Reinette Royale", "Cette amulette coûte beaucoup plus cher à fabriquer qu'une Amublop classique. Mais il faut voir le bon côté des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d'Astrub." ));
            allItems.Add(new Items ( 9155, "Amublop Coco Royale", "Cette amulette coûte beaucoup plus cher à fabriquer qu'une Amublop classique. Mais il faut voir le bon côté des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d'Astrub." ));
            allItems.Add(new Items ( 9156, "Amublop Indigo Royale", "Cette amulette coûte beaucoup plus cher à fabriquer qu'une Amublop classique. Mais il faut voir le bon côté des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d'Astrub." ));
            allItems.Add(new Items ( 9157, "Amublop Multicolore Royale", "Sans Additif ni colorant, cette amulette serait tout simplement transparente. Rassurez-vous, personne ne vous demandera de la manger." ));
            allItems.Add(new Items ( 9158, "Bloptes Griottes", "Inutile de râler parce que le nom de ces bottes est imprononçable, cette particularité vous sauvera la vie, quand un brigand de grand chemin vous demandera : " ));
            allItems.Add(new Items ( 9159, "Bloptes Reinette", "Inutile de râler parce que le nom de ces bottes est imprononçable, cette particularité vous sauvera la vie, quand un brigand de grand chemin vous demandera : " ));
            allItems.Add(new Items ( 9160, "Bloptes Coco", "Inutile de râler parce que le nom de ces bottes est imprononçable, cette particularité vous sauvera la vie, quand un brigand de grand chemin vous demandera : " ));
            allItems.Add(new Items ( 9161, "Bloptes Indigo", "Inutile de râler parce que le nom de ces bottes est imprononçable, cette particularité vous sauvera la vie, quand un brigand de grand chemin vous demandera : " ));
            allItems.Add(new Items ( 9162, "Bloptes Griotte Royales", "Prononcez dix-huit fois le mot " ));
            allItems.Add(new Items ( 9163, "Bloptes Reinette Royales", "Prononcez dix-huit fois le mot " ));
            allItems.Add(new Items ( 9164, "Bloptes Coco Royales", "Prononcez dix-huit fois le mot " ));
            allItems.Add(new Items ( 9165, "Bloptes Indigo Royales", "Prononcez dix-huit fois le mot " ));
            allItems.Add(new Items ( 9166, "Bloptes Multicolores Royales", "Avec ou sans rembourrage ces bottes restent étranges. Il est donc inutile de les rembourrer avec autre chose que vos pieds." ));
            allItems.Add(new Items ( 9167, "Blopture Griotte", "Derrière ce nom stupide, se cache en réalité une ceinture originale, dont on ne connaît pas vraiment les défauts, ni les qualités. On sait tout de même qu'elle est reconnue d'utilité publique grâce à la protection exceptionnelle qu'elle confère aux poignées d'amour." ));
            allItems.Add(new Items ( 9168, "Blopture Reinette", "Derrière ce nom stupide, se cache en réalité une ceinture originale, dont on ne connaît pas vraiment les défauts, ni les qualités. On sait tout de même qu'elle est reconnue d'utilité publique grâce à la protection exceptionnelle qu'elle confère aux poignées d'amour." ));
            allItems.Add(new Items ( 9169, "Blopture Coco", "Derrière ce nom stupide, se cache en réalité une ceinture originale, dont on ne connaît pas vraiment les défauts, ni les qualités. On sait tout de même qu'elle est reconnue d'utilité publique grâce à la protection exceptionnelle qu'elle confère aux poignées d'amour." ));
            allItems.Add(new Items ( 9170, "Blopture Indigo", "Derrière ce nom stupide, se cache en réalité une ceinture originale, dont on ne connaît pas vraiment les défauts, ni les qualités. On sait tout de même qu'elle est reconnue d'utilité publique grâce à la protection exceptionnelle qu'elle confère aux poignées d'amour." ));
            allItems.Add(new Items ( 9171, "Blopture Griotte Royale", "Dépourvue de boucle, cette ceinture peut tout de même faire très mal si vous vous en servez comme fouet sur un postérieur à la peau fragile. Les pointes en fer rouillées sont à commander séparément." ));
            allItems.Add(new Items ( 9172, "Blopture Reinette Royale", "Dépourvue de boucle, cette ceinture peut tout de même faire très mal si vous vous en servez comme fouet sur un postérieur à la peau fragile. Les pointes en fer rouillées sont à commander séparément." ));
            allItems.Add(new Items ( 9173, "Blopture Coco Royale", "Dépourvue de boucle, cette ceinture peut tout de même faire très mal si vous vous en servez comme fouet sur un postérieur à la peau fragile. Les pointes en fer rouillées sont à commander séparément." ));
            allItems.Add(new Items ( 9174, "Blopture Indigo Royale", "Dépourvue de boucle, cette ceinture peut tout de même faire très mal si vous vous en servez comme fouet sur un postérieur à la peau fragile. Les pointes en fer rouillées sont à commander séparément." ));
            allItems.Add(new Items ( 9175, "Blopture Multicolore Royale", "En serrant suffisamment fort cette ceinture, on peut réussir à bloquer entièrement l'appareil digestif de son propriétaire. Il ne reste ensuite plus qu'à trouver une utilité à cet acte profondément débile." ));
            allItems.Add(new Items ( 9176, "Lame Assacre", "Lorsque le Bworker commença à étriper lentement Francisque Kabroule, ce dernier se mit à chanter : " ));
            allItems.Add(new Items ( 9177, "Bracelet Ventré", "Les trois pointes de ce bracelet représentent les trois faiblesses du Bworker : sa soif de vengeance, sa soif de victoire, et sa soif tout court." ));
            allItems.Add(new Items ( 9178, "Anneau Bliteré", "Cet anneau est recouvert des mucosités du Bworker. Les petites pointes lui permettent de se curer le nez avec plus d'efficacité." ));
            allItems.Add(new Items ( 9179, "Amulette Ripage", "Un jour, le Captain Chafer rencontra le Bworker, il prit l'initiative de lui dire : " ));
            allItems.Add(new Items ( 9180, "Cape Erforée", "On raconte que le Bworker s'est cousu cette cape avec la peau des fesses de Rushu. Cette histoire aurait pu être crédible si le Bworker savait coudre." ));
            allItems.Add(new Items ( 9181, "Casque Harnage", "Ce magnifique casque renferme bien des secrets, pendant de longues années, lorsqu'il était le seul compagnon du Bworker, et lorsqu'il était encore vivant, le Bworker lui aurait confié toutes ses histoires de protections usagées. A force d'entendre ces atrocités, le casque a fini par mourir." ));
            allItems.Add(new Items ( 9182, "Bottes Répané", "Il y a bien longtemps, Danous Kouaide a voulu faire le malin et battre le Bworker dans un concours de blagues : " ));
            allItems.Add(new Items ( 9183, "Ceinture Toré", "Les deux clous de la ceinture se plantaient directement dans les poignées d'amour du Bworker. Lorsqu'il se trémoussait en faisant la danse du ventre pour distraire ses ennemis, on pouvait parfois apercevoir les coutures de ses sous-vêtements en peau de Kilibriss. L'érotisme exacerbé du Bworker a fait vaciller plus d'un ennemi. Sa lame s'est contentée de les achever." ));
            allItems.Add(new Items ( 9184, "Casque de Galgarion", "Malgré les apparences, ce casque ne vous permettra pas de mieux entendre... mais de voir plus loin ! En revanche, méfiez-vous des bourrasques inattendues car vous allez finir par vous envoler." ));
            allItems.Add(new Items ( 9185, "Coiffe de Goultard", "Cette coiffe ne vous rendra pas plus féroce et encore moins plus puissant... Et si vous croyez que les aventuriers un peu naïfs vont vous prendre pour Goultard vous vous fourrez le doigt dans l'œil ! Mais grâce à elle on vous verra arriver de loin, c'est déjà ça !" ));
            allItems.Add(new Items ( 9186, "Ceinture de Flash Gorgone", "On ne connait que peu de chose sur Flash Gorgone, hormis sa passion pour les vases en porcelaine. Une légende raconte que ce héros courait tellement vite que même les Tofus ne parvenaient pas à le rattraper. Malheureusement, à force d'être tout le temps pressé, il arriva un jour où il attacha mal sa ceinture... et s'empêtra les pieds dedans. Sa collection de porcelaine s'en souvient encore..." ));
            allItems.Add(new Items ( 9187, "Coiffe de Maître Noir Jack", "L'Eniripsa Maître Noir Jack a toujours été passionné par la couture. C'est ce qui l'a d'ailleurs conduit à devenir un expert en points de suture. Ami des tailleurs, il aurait conçu cette coiffure bicolore en s'inspirant de celle d'une tailleuse cruelle qui extermina jadis la tribu Ouginak des Maciens... Charmant, non ?" ));
            allItems.Add(new Items ( 9188, "Sombrero de Pépitox", "Ce sombrero est le seul vestige d'un sombre héros Xélor qui aurait soudainement disparu. La dernière fois qu'on l'aurait vu, il venait tout juste de chanter la sérénade à sa belle. On raconte que les frères de la jeune fille ne seraient pas étrangers à cette disparition, mais certains pensent qu'il aurait été emmené dans une dimension parallèle...  Le mystère reste entier..." ));
            allItems.Add(new Items ( 9189, "Cornes de XVII", "A force de vouloir encorner les autres aventuriers, c'est vous qui risquez d'avoir la tête comme une passoire ! En effet, les Cornes de XVII, le Démon de la dix-septième Heure, ont la faculté extraordinaire de vous faire oublier pratiquement tout... sauf peut-être l'heure du déjeuner !" ));
            allItems.Add(new Items ( 9190, "Coiffe de Crâ Cramé", "Voici une coiffe parfaitement adaptée aux Crâs qui n'ont pas peur de passer pour des abrutis. C'est vrai qu'en la portant vous n'aurez pas franchement l'air très intelligent. D'ailleurs ceux qui osent l'arborer sont généralement des Iops ou des aventuriers avec déjà quelques neurones en moins..." ));
            allItems.Add(new Items ( 9191, "Epée de Pym", "Cette épée semble avoir été arrachée à l'enseigne d'un honnête forgeron... Espérons qu'elle vous enseignera à frapper plus fort qu'avant ! Sinon il ne vous restera plus qu'à ouvrir votre propre échoppe." ));
            allItems.Add(new Items ( 9192, "Beignature", "Lorsque vous aurez équipé cette ceinture, vous vous apercevrez rapidement que vos hanches n'auront jamais été aussi alléchantes." ));
            allItems.Add(new Items ( 9193, "Tartamulette", "Si vous avez toujours rêvé de distribuer des tartes à vos amis, offrez-leur cette amulette." ));
            allItems.Add(new Items ( 9194, "Caskogato", "Autrefois, les poux vous dévoraient le crâne. Avec ce chapeau sur la tête, les Pious vous picoreront le crâne. Parfois, il vaut mieux rester chauve." ));
            allItems.Add(new Items ( 9195, "Capogato", "Une fois enroulée autour de fruits confits et recouverte de gelée royale, cette cape peut être dégustée en suppositoire." ));
            allItems.Add(new Items ( 9196, "Cape Paupayahn", "Cette cape confectionnée à partir d'énormes graines diffuse une odeur persistante d'aisselle de Bitouf aérien. Il paraît que ça permet d'exciter son entourage à moindre frais." ));
            allItems.Add(new Items ( 9197, "Chapeau Paupayahn", "Ce chapeau vous permettra d'étancher vos petites soifs. Le sucre est à commander séparément." ));
            allItems.Add(new Items ( 9198, "Anneau Paupayahn", "Lorsque votre eau ou votre bière n'a plus de goût, trempez-y cet anneau." ));
            allItems.Add(new Items ( 9199, "Bottes Paupayahn", "Voilà ce qui arrive lorsque l'on essaye de casser des tasses à coups de bottes." ));
            allItems.Add(new Items ( 9200, "Parchemin de Sort Invocation de Chaferfu", "(Double clic pour apprendre le sort) nCe parchemin vous permettra d'apprendre le sort d'invocation de Chaferfu." ));
            allItems.Add(new Items ( 9201, "Pawchemin de la Cawotte", "Ce pawchemin décwit une méthode simple en 101 leçons, qui vous pewmettwa d'appwendwe en autodidacte les bases de la fameuse, mais pas si secwète que ça, technique du Wa Wabbit." ));
            allItems.Add(new Items ( 9202, "L\'autre Annobusé de Maître Jarbo", "Même mort, Maître Jarbo continua à forger des artefact complètement déphasés avec l'équilibre cosmique du Monde. Quand il eut forgé son second anneau, il se dit avec tristesse que s'il l'avait eu plus tôt, il aurait pu traverser les routes sans risques. Un démon le lui vola un jour et le cacha dans le monde des vivants pour les aventuriers en mal de puissance." ));
            allItems.Add(new Items ( 9233, "Cape Obvijevan", "Cet objet polymorphe établit une relation de prédateur ou de symbiote avec les capes inanimées. (Vous devez nourrir cette cape vivante en lui donnant à manger d'autres capes. Vous pouvez lui donner un nouveau repas toutes les douze heures.)" ));
            allItems.Add(new Items ( 9234, "Chapeau Obvijevan", "Cet objet polymorphe établit une relation de prédateur ou de symbiote avec les couvres-chef inanimés. (Vous devez nourrir ce chapeau vivant en lui donnant à manger d'autres chapeaux. Vous pouvez lui donner un nouveau repas toutes les douze heures.)" ));
