            allItems.Add(new Items ( 9255, "Amulette Obvijevan", "Cet objet polymorphe établit une relation de prédateur ou de symbiote avec les amulettes inanimées. (Vous devez nourrir cette amulette vivante en lui donnant à manger d'autres amulettes. Vous pouvez lui donner un nouveau repas toutes les douze heures.)" ));
            allItems.Add(new Items ( 9256, "Anneau Obvijevan", "Cet objet polymorphe établit une relation de prédateur ou de symbiote avec les anneaux inanimés. (Vous devez nourrir cet anneau vivant en lui donnant à manger d'autres anneaux. Vous pouvez lui donner un nouveau repas toutes les douze heures.)" ));
            allItems.Add(new Items ( 9263, "Ecorce de Champaknyde", "Le Champaknyde est un Abraknyde parasité par des champignons sans scrupules qui s'incrustent jusque dans son écorce. De temps en temps en se grattant le postérieur, il arrache un morceau de mycélium purulent qui finira par donner vie à un magnifique champignon qui à son tour ira parasiter un Abraknyde." ));
            allItems.Add(new Items ( 9265, "Miteters", "Voici des Miteters que l'on trouve parfois sur la tête des Craqueleurs à l'hygiène douteuse." ));
            allItems.Add(new Items ( 9266, "Graisse de Gelée Royale", "Cette graisse a été récupérée sur une gelée royale. C'est un ingrédient essentiel entrant dans la composition d'une potion de paralysie." ));
            allItems.Add(new Items ( 9267, "Trompe de la Tromperelle", "Encore chargée de l'haleine putride de son ancien propriétaire, cette surprenante trompe peut servir à jouer de la musique tout en asphyxiant votre auditoire." ));
            allItems.Add(new Items ( 9269, "Langue du Champodonte", "Pour lécher les spores de ses compagnons, le Champodonte dispose d'une langue agile et incroyablement souple. C'est bien la seule chose que l'on puisse envier au Champodonte." ));
            allItems.Add(new Items ( 9277, "Oeil de Champmane", "Le Champmane a la fâcheuse habitude de vouloir se gratter les paupières avec son bâton à pointes. Parfois il perd un oeil. Quand il se gratte le cou, il lui arrive de perdre la tête." ));
            allItems.Add(new Items ( 9278, "Lamelle de Champbis", "Le Champbis possède deux pieds, deux bras, et deux têtes. Ce qui en fait une créature deux fois plus bête que les autres." ));
            allItems.Add(new Items ( 9279, "Bave de Champ à Gnons", "Gluante mais appétissante, cette bave sert parfois à huiler les armes, ou les corps des aventuriers qui aiment combattre nus dans les arènes." ));
