            allItems.Add(new Items ( 9366, "Tutu Rose", "Ce tutu rose sera parfait pour la représentation de fin d'année de votre petite dernière. Fabriqué en coton ancestral et en mousse de Mob l'éponge extensible, doux et confortable avec une jupe en froufrou, ce tutu sera peut-être le premier d'une future danseuse étoile." ));
            allItems.Add(new Items ( 9379, "Ballotin de chocolat", "Ce petit paquet est à offrir à celle ou celui que vous aimez. Si vous n'aimez personne, vous êtes autorisé à le manger." ));
            allItems.Add(new Items ( 9380, "Fée d\'artifice invocatrice de coeurs", "Cette Fée d'artifice originale vous permettra de répandre un peu d'amour dans ce monde de brutes, et un peu de rut dans ce monde d'amour." ));
            allItems.Add(new Items ( 9381, "Fleur de Gloutoblop", "Le Gloutoblop est tellement affamé, qu'il serait capable de manger ses propres fleurs. Heureusement, il trouve toujours un aventuriers imprudent à portée de dents." ));
            allItems.Add(new Items ( 9382, "Fleur de Blopignon", "Cette fleur vient d'être arrachée de la tête d'un Blopignon. Etant donné l'allure ridicule qu'elle lui donnait, il ne va pas s'en plaindre." ));
            allItems.Add(new Items ( 9383, "Racine de Tronkoblop", "Le Tronkoblop possède de longues racines, qui vont puiser dans le sol tout ce que les aventuriers peuvent y rejeter." ));
            allItems.Add(new Items ( 9384, "Fleur de Blop Griotte Royal", "Si cette fleur n'était pas royale, vous l'auriez sûrement jetée." ));
            allItems.Add(new Items ( 9385, "Fleur de Blop Coco Royal", "Si cette fleur n'était pas royale, vous l'auriez sûrement jetée." ));
            allItems.Add(new Items ( 9386, "Fleur de Blop Indigo Royal", "Si cette fleur n'était pas royale, vous l'auriez sûrement jetée." ));
            allItems.Add(new Items ( 9387, "Fleur de Blop Reinette Royal", "Si cette fleur n'était pas royale, vous l'auriez sûrement jetée." ));
            allItems.Add(new Items ( 9388, "Feuille de Blop Royal", "Une fois séchée, roulée, puis fumée, cette feuille peut déclencher chez les plus faibles, d'importantes hallucinations, et une incroyable envie de se soulager. Une consommation excessive entraîne une mort subite." ));
            allItems.Add(new Items ( 9389, "Feuille de Blop Multicolore Royal", "Cette feuille se nourrit de la matière grise du Blop Multicolore Royal. Heureusement qu'elle n'est pas trop gourmande." ));
            allItems.Add(new Items ( 9391, "Fleur de Blop Multicolore Royal", "Cette fleur était cachée à l'intérieur de la boîte crânienne du Blop Multicolore Royal. Elle lui servait à boucher ses trous de mémoire." ));
            allItems.Add(new Items ( 9394, "Chapblopignon", "Cette coiffe stylée donne la Mitcho classe à n'importe qui, ainsi qu'une odeur de sous-bois dont vos amis se passeraient bien." ));
            allItems.Add(new Items ( 9395, "Blopronne Royale", "Ce couvre-chef a été porté par Kuri en personne pendant plusieurs années, jusqu'à ce qu'elle décide d'arrêter de porter des coiffes démodées." ));
            allItems.Add(new Items ( 9396, "Le sceau des Zewouan", "En ancien langage Lenald, il se nomme « Zewouan Ling ».  nLa rumeur prétend qu'il aurait été forgé dans un volcan situé sous Pandala Feu, par Zauwon, le Wabbit obscur." ));
            allItems.Add(new Items ( 9401, "Peau de Bworker", "Cette peau a traversé toutes les guerres, elle en est sortie indemne. Les épreuves du temps l'ont à peine écorchée. Ca ne vous a pas empêché de lui faire la peau pour autant." ));
            allItems.Add(new Items ( 9402, "Pantoufrelons", "Les petites ailes accrochées à ces pantoufles permettent d'aérer les pieds malodorants. Attention, il est déconseillé d'utiliser ces pantoufles pour jouer au Boufball, le Bouftou pourrait ne pas apprécier de se faire piquer les fesses." ));
            allItems.Add(new Items ( 9403, "Masse O\'dard", "Les petites ailes accrochées à cette masse permettent de la rendre plus légère et plus facile à manier. Parfois, elle s'envolera même toute seule et vous frappera pendant votre sommeil, pour rire un peu." ));
            allItems.Add(new Items ( 9404, "Cape Kifébzz", "Le dard accroché au bout de cette cape, vous empêchera probablement de vous asseoir sans vous faire très mal au postérieur, mais personne n'osera vous botter les fesses." ));
            allItems.Add(new Items ( 9405, "Caskabeille", "En équipant ce chapeau vous prenez le risque de recevoir des coups de tapette à Moskitos sur les antennes." ));
            allItems.Add(new Items ( 9406, "Cochobits", "Ces oreilles de Porkass encore fraîches vous permettront de séduire facilement les Porkass que vous croiserez. Ne rigolez pas, ça pourrait finir par vous servir, on peut parfois se sentir bien seul dans la campagne d'Amakna." ));
            allItems.Add(new Items ( 9407, "Cape Bouchon", "Certains nomment cette cape la Tire-boucape, et vouent une haine farouche à l'encontre de ceux qui l'appellent autrement. Qu'importe, quel que soit le nom que vous donnerez à cette cape, elle ne vous sera d'aucune utilité pour déboucher les bouteilles." ));
            allItems.Add(new Items ( 9408, "Jambanneau", "Avec cet anneau, votre doigt fera de vous un véritable petit Porkass, ce qui ne devrait pas déplaire à vos amis les plus proches." ));
            allItems.Add(new Items ( 9409, "Ceinture Lardière", "Votre petite queue en tire-bouchon fera plus d'un jaloux parmi votre entourage." ));
            allItems.Add(new Items ( 9410, "Chapeau du bois de l\'est", "Moins à la mode que le Clint, mais beaucoup plus raffiné, ce chapeau vous permettra d'exacerber votre coquetterie sans limite." ));
            allItems.Add(new Items ( 9411, "Cape du bois de l\'est", "Cette cape protégera vos dos des coups de soleil. Attention elle ne descend pas jusqu'aux fesses chez tous les aventuriers." ));
            allItems.Add(new Items ( 9412, "Ceinture du bois de l\'est", "La fronde accrochée à cette ceinture est très bien accrochée. Tellement bien accrochée que vous ne pourrez pas la décrocher. Elle ne sert qu'à impressionner les petits bandits de grand chemin que vous croiserez." ));
            allItems.Add(new Items ( 9413, "Bottes du bois de l\'est", "Les éperons accrochés à ces bottes, permettent de lacérer le cuir de votre Dragodinde, pour pouvoir la dépecer plus rapidement lorsque vous aurez décidé de la manger." ));
            allItems.Add(new Items ( 9414, "Borsitrono", "Cet énorme citron vidé accueillera sans problème votre tête et le petit pépin qui se loge dans votre crâne." ));
            allItems.Add(new Items ( 9415, "Cape Grume", "Cette cape a été confectionnée à partir d'une multitude d'écorces d'agrumes diverses et variées. Elle est jolie, elle rafraîchit, mais elle ne brille pas dans le noir." ));
            allItems.Add(new Items ( 9416, "Anneau Zest", "Si votre eau n'a plus assez de goût, trempez-votre doigt avec cet anneau dans votre verre, agitez-le frénétiquement jusqu'à ce qu'il se décroche. Et voilà vous avez un délicieux cocktail à l'orange sanguine et au citron, ainsi qu'un doigt en moins. On ne peut pas tout avoir." ));
            allItems.Add(new Items ( 9417, "Pailleton", "Si vous avez rêvé de boire le sang de vos adversaires tout en les frappant vigoureusement, ce bâton est fait pour vous." ));
            allItems.Add(new Items ( 9418, "Coiffe Cristaline", "Le prochain qui tentera de vous coiffer les cheveux, risque de casser son peigne." ));
            allItems.Add(new Items ( 9451, "Cape Cristaline", "Cet impressionnant morceau de cristal peut servir de cape rigide. Elle ne vous rendra pas nécessairement plus fort, mais vous pouvez être certain que le prochain monstre qui tentera de vous dévorer, risque de s'étouffer en l'avalant." ));
            allItems.Add(new Items ( 9452, "Anneau Cristalin", "Avec cet anneau, votre doigt n'aura jamais été aussi éclatant." ));
            allItems.Add(new Items ( 9453, "Ceinture Cristaline", "Avec cette ceinture autour des hanches, plus personne n'osera vous les pincer de peur de se trancher les doigts." ));
            allItems.Add(new Items ( 9454, "Pompamulette", "Cette amulette pourra servir de bavoir pour les plus jeunes et les plus vieux." ));
            allItems.Add(new Items ( 9455, "Pompanneau", "Cet anneau peut être porté à un orteil si vous avez de mauvaises odeurs de pieds à cacher." ));
            allItems.Add(new Items ( 9456, "Pompocape", "Si vous estimez que votre postérieur n'a jamais eu l'attention qu'il mérite, il n'est pas trop tard pour revêtir cette longue cape qui le recouvrira et attirera tous les regards." ));
            allItems.Add(new Items ( 9457, "Pompocoiffe", "Cette coiffe dégage une odeur de rose démoniaque suffisamment forte pour faire fuir tous les poux qui vivent sur votre tête, et tous les parasites qui se font passer pour vos amis." ));
            allItems.Add(new Items ( 9459, "Liste de jouets du papa Nowel", "Le papa Nowel prend toujours soin des listes de jouets qui sont amenées par les enfants qu'il dévore.  nVous n'aviez pas encore écrit la vôtre, en voilà une." ));
            allItems.Add(new Items ( 9460, "Pomme de pik", "Voilà un Pokipik qui ne poussera jamais, vous n'avez pas honte ?" ));
            allItems.Add(new Items ( 9461, "Le Kim", "On dit que le Kimbo porte cette perruque en hommage à un bel éphèbe bronzé rencontré un jour sur son île, dont il appréciait le sourire aéré. Il la cache néanmoins sous sa coco parce qu'il la trouve quand même ridicule." ));
            allItems.Add(new Items ( 9463, "Amunite", "Ce bijou dégage une telle puissance qu'elle tuerait un Moskito à 30 pas, si son odeur n'empêchait pas ce dernier d'approcher à moins d'un kilomètre." ));
            allItems.Add(new Items ( 9464, "Kralamansion", "Cette effrayante amulette est longtemps restée cachée dans un manoir perdu. Autrefois vivante, elle voulait conquérir le monde. Finalement elle n'aura que votre cou. La pauvre." ));
            allItems.Add(new Items ( 9468, "Pelle Emélaka", "Cette pelle est dangereuse, elle volera votre vitalité, votre jeunesse, l'eau de votre corps et vos précieux gâtrools." ));
            allItems.Add(new Items ( 9469, "Epée Loponèze", "Cette épée construite à partir de fragments de mycoses, parasitera à merveille les intestins de vos ennemis." ));
            allItems.Add(new Items ( 9471, "Chapeau de la Sain Ballotin", "Ballotins, Ballotines, vous qui cherchez l'âme sœur, ce couvre-chef élégant et sobre sera votre signe de reconnaissance. En effet, ce chapeau magique ne peut être porté que par les aventuriers et les aventurières qui ne sont pas encore mariés. Vous pourrez ainsi facilement distinguer plus facilement celles et ceux dont le cœur n'est pas encore pris." ));
            allItems.Add(new Items ( 9472, "Shigekax Passion", "Réputé pour ses vertus aphrodisiaques, ce bonbon doux et sucré est idéal comme cadeau à offrir à l'être aimé." ));
            allItems.Add(new Items ( 9474, "Sublime Pierre d\'âme hasardeuse", "Cette rune offre une faible chance de capturer des âmes d'une puissance tout simplement incroyable." ));
            allItems.Add(new Items ( 9475, "Sublime Pierre d\'âme", "Cette rune offre une chance correcte de capturer des âmes d'une puissance tout simplement incroyable." ));
            allItems.Add(new Items ( 9476, "Sublime Pierre d\'âme heureuse", "Cette rune offre une bonne chance de capturer des âmes d'une puissance tout simplement incroyable." ));
            allItems.Add(new Items ( 9479, "Poshocoiffe", "Le clou planté dans cette coiffe permet de dégonfler la tête de ceux qui l'ont un peu trop grosse." ));
            allItems.Add(new Items ( 9480, "Poshocape", "Avec cette jolie cible brodée sur votre cape, vos ennemis n'auront aucune excuse s'ils vous ratent." ));
            allItems.Add(new Items ( 9481, "Poshoclier", "Le clou planté dans ce bouclier peut servir d'arme de corps à corps si vous voulez crever votre adversaire pour qu'il se dégonfle." ));
            allItems.Add(new Items ( 9482, "Cigaloclier", "Si vous avez peur d'effrayer vos amis avec votre tête de monstre, vous pourrez toujours vous cacher derrière ce bouclier pour ressembler à quelqu'un de sympathique." ));
            allItems.Add(new Items ( 9483, "Bouclier Boh-Bihn", "Ce bouclier était utilisé par les jeunes Iops qui y rangeaient les images offertes par leurs instituteurs pour les récompenser de leurs efforts. De temps en temps, ils s'amusaient à les faire défiler rapidement pour épater leurs camarades de classe." ));
