            allItems.Add(new Items ( 9566, "Bouclier Pararayon", "C'est dans les tavernes de Pandala que le bouclier Pararayon fut pour la première fois forgée.  Il est dit que ce bouclier, inspiré par la déesse Pandawa elle-même protégerait des effets du rayon cosmique. Réalité ou opération marketing de forgeurs de boucliers Pandawas, le mystère reste entier." ));
            allItems.Add(new Items ( 9567, "Coiffe du Black Wab", "Avec cette coiffe, vous pourrez facilement vous métamorphoser en vengeur masqué, et peut-être même en  mangeur vasqué. A vous de voir." ));
            allItems.Add(new Items ( 9569, "Cape du Black Wab", "Le petit pompon accroché à l'extrémité de cette cape permet de ramasser efficacement les poussières de votre maison, il est normalement assez petit pour passer sous les meubles. N'oubliez pas de l'enlever lorsque vous partez travailler aux champs." ));
