            allItems.Add(new Items ( 9594, "Bouloute du Parrain", "Ce bouloute a l'air tout à fait normal... Pourtant on dirait que sa laine vire au noir par endroits... Laissez-lui un peu de temps et vous pourriez bien avoir une surprise !" ));
            allItems.Add(new Items ( 9595, "Fantôme Bouloute du Parrain", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 9596, "Cadeau Bouloute du Parrain", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 9597, "Transformation Fantôme Bouloute du Parrain", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
