            allItems.Add(new Items ( 9916, "Parchemin de Sort Spécial Laisse spirituelle", "Ce parchemin permet d'apprendre le sort Laisse spirituelle. Il est accessible aux disciples d'Osamodas uniquement." ));
            allItems.Add(new Items ( 9917, "Parchemin de prime", "Ce parchemin désigne la cible." ));
            allItems.Add(new Items ( 9919, "Bague du hibou", "Prendre l'apparence d'un hibou." ));
            allItems.Add(new Items ( 9920, "Parchemin de prime accompli", "Ce parchemin prouve que la cible a été supprimée avec succès." ));
            allItems.Add(new Items ( 9921, "Bracelet du Tétounik", "Ce bracelet est censé briller, mais uniquement la nuit et dans les tavernes." ));
            allItems.Add(new Items ( 9922, "Ceinture du Tétounik", "Cette ceinture est dotée d'un mécanisme magnétique incroyablement puissant qui lui permet de se rester fermée sans utiliser la moindre boucle de ceinture. Attention, il est déconseillé de l'utiliser en même temps qu'un slip de plomb." ));
            allItems.Add(new Items ( 9923, "Chaussures du Tétounik", "Si vous êtes bien musclé, et que vous voulez danser comme un robot, ces chaussures sont faîtes pour vous. Attention, les lacets sont en option." ));
            allItems.Add(new Items ( 9924, "Corno fulgures", "La légende raconte que ces cornes servent de paratonnerre, concentrent la foudre pendant plusieurs heures puis projettent des éclairs contre tous les imprudents qui s'approchent un peu trop près. Une autre légende raconte qu'elles servent juste à vous faire passer pour un idiot." ));
            allItems.Add(new Items ( 9925, "Fulgu au poing", "Cet anneau est plus proche du gant ou de la moufle que de l'alliance mais qu'importe, plus personne ne vous serrera la main comme avant." ));
            allItems.Add(new Items ( 9926, "Hache Téreau hache", "Cette hache composée de deux lames permet de trancher deux têtes simultanément et donc de décapiter un huitième d'une hydre à seize têtes." ));
            allItems.Add(new Items ( 9927, "Golbottes", "Olcar portait ces bottes lorsqu'il aperçut du haut du mont Jifu d'étranges objets non identifiés traversant le ciel." ));
            allItems.Add(new Items ( 9928, "Bottes Siks Won Naïne", "Il vous faudra environ 12 heures pour nouer les lacets de ces bottes et 16 heures pour les dénouer. Si vous décidez de les enlever une fois par jour, vous prendrez donc du retard tous les jours, à moins de prendre de l'avance dès aujourd'hui pour les jours suivants." ));
            allItems.Add(new Items ( 9929, "Ceinture Siks Won Naïne", "Chez certains combattants, le talon d'Achille se situe au niveau du nombril. Avec cette ceinture autour des hanches, vos amis vous trouveront deux fois plus lourd, mais vous serez mieux protégé." ));
            allItems.Add(new Items ( 9930, "Masque Siks Won Naïne", "Avec ce masque sur la tête, vous ressemblerez de loin à une larve géante mutante, mais de près vous ressemblerez à un terrible combattant. Évitez les combats en distance." ));
            allItems.Add(new Items ( 9931, "Coiffe Mulet du Tétounik", "Nuque longue et crête de Pioute, cette coiffe vous ira comme un gant, surtout si vous l'enfilez ailleurs que sur vos mains." ));
            allItems.Add(new Items ( 9932, "Touffe de poils du Tavernier Bagrutte", "Cette touffe de poils poisseuse a été arrachée aux aisselles du Tavernier Bagrutte. L'odeur qui s'en dégage vous donne envie de courir et sauter en hurlant Groink ! Encore un trouble psychiatrique latent." ));
            allItems.Add(new Items ( 9933, "Cape Siks Won Naïne", "Les poils de Bitoufs cousus sur le haut de cette cape, protégeront efficacement votre nuque lorsque le public décidera de vous envoyer quelques chaises afin de vous signifier la médiocrité de vos prestations." ));
            allItems.Add(new Items ( 9934, "Clef de l\'enclos", "Les gobelins étant équipés d'outils rudimentaires, ils recyclent un maximum de choses et cet os ne fait pas exception à la règle. Qui aurait crû qu'un simple os permettait d'ouvrir leur enclos ? Pas vous en tout cas." ));
            allItems.Add(new Items ( 9935, "Corde d\'escalade", "Cette corde très solide en vogue chez les alpinistes en herbe peut aussi bien servir à monter qu'à descendre. Cependant, une vieille tradition amaknéenne consiste à y faire un noeud coulant au bout et à passer ce noeud autour du cou du chef du village, pour lui faire comprendre que ses décisions ne font pas l'unanimité." ));
            allItems.Add(new Items ( 9936, "Caskostar", "Ce casque est parfait si vous voulez ressembler à un méchant malgré votre visage de chérubin et vos boutons d'acné." ));
            allItems.Add(new Items ( 9937, "Le Chapleau", "Ce chapeau est livré avec une moustache postiche qui ne pousse pas, ce qui vous permettra d'économiser quelques séances chez le barbier." ));
            allItems.Add(new Items ( 9938, "Sabroh", "La lame de ce sabre a été trempée pendant 18 mois dans un concentré de purée de lucioles. C'est franchement ignoble, mais au moins il brille dans le noir." ));
            allItems.Add(new Items ( 9939, "La Chaplanne", "Cette canne souple en bambou a appartenu à un illustre vagabond aux manières raffinées." ));
            allItems.Add(new Items ( 9940, "Nacre brute", "Correctement polie, cette pierre peut donner une magnifique Nacre." ));
            allItems.Add(new Items ( 9941, "Nacre", "Composé de cristaux d'aragonite et de conchyoline, la nacre était inconnue jusqu'à la découverte de l'île d'Otomaï et des crustorails. C'est un élément très prisé en alchimie et joaillerie, bien que les recettes dans lesquelles il intervient soient jalousement tenues secrètes." ));
            allItems.Add(new Items ( 9942, "Anneau de la mort", "La construction de cet anneau, aussi connu sous le nom " ));
            allItems.Add(new Items ( 9943, "Les Chaplures", "Ces magnifiques chaussures sont recouvertes d'un vernis à base de... Personne ne sait réellement, mais tant qu'elles brillent tout le monde s'en moque." ));
            allItems.Add(new Items ( 9944, "La Chaplivate", "A la fois amulette et cravate, cet objet d'apparat permettra à vos proches de vous tenir en laisse facilement. Et vous aimerez ça." ));
            allItems.Add(new Items ( 9945, "Sac de Bouba le Prophète", "Bouba a toujours prétendu être un prophète, porteur d'un message qu'il a fini par lui-même oublier. reconverti pendant plusieurs années comme chasseur de primes, il a terminé sa triste carrière, digéré dans le ventre d'une créature des sables." ));
            allItems.Add(new Items ( 9946, "Cristal de Kain Dharyn", "Kain Dharyn était un chasseur de trésors réputé à travers tout Amakna. Parmi ses découvertes se trouvent des cristaux précieux aux propriétés surprenantes, dont la faculté de loucher à l'envers sans les mains. Kain cacha l'emplacement du gisement et aujourd'hui encore de nombreux Enutrofs sont à sa recherche." ));
            allItems.Add(new Items ( 9947, "Cristal de Kain Dharyn enchanté", "Ce Cristal de Kain Dharyn a été enchanté de manière à décupler l'avidité de ceux qui l'observent." ));
            allItems.Add(new Items ( 9948, "Tibia du chef des voleurs", "Avec un tibia en moins, le chef des voleurs va avoir du mal à marcher. Tant pis pour lui, ça lui servira de leçon." ));
            allItems.Add(new Items ( 9949, "Pâte à Tra Khben", "Cette pâte fut inventée par le célèbre Tra Khben, géologue spécialiste de l'écosystème et des moissons, précocement atteint d'arthrose dans le dos. Cette pâte avait comme particularité de raidir et durcir toute substance organique avec laquelle elle entrait en contact, ce qui permettait à Tra Khben de se tenir droit. Ce ne fut qu'après la mort de ce dernier qu'on trouva à la pâte un tout autre usage, très en vogue surtout chez les Enutrofs. Son seul inconvénient est l'odeur, ce qui explique l'haleine de certaines Enutrofettes." ));
            allItems.Add(new Items ( 9950, "Pierre de souillure bleue impure", "Correctement polie, cette pierre dévoilera son plein potentiel." ));
            allItems.Add(new Items ( 9951, "Pierre de souillure jaune impure", "Correctement polie, cette pierre dévoilera son plein potentiel." ));
            allItems.Add(new Items ( 9952, "Pierre de souillure verte impure", "Correctement polie, cette pierre dévoilera son plein potentiel." ));
            allItems.Add(new Items ( 9953, "Pierre de souillure bleue", "Cette pierre est capable de souiller une âme sans grande volonté, ce qui peut s'avérer efficace pour transformer des tofus en tofus maléfiques. Combinée à d'autres pierres de souillure, elle serait à même de donner un contrôle total sur les âmes souillées." ));
            allItems.Add(new Items ( 9954, "Pierre de souillure jaune", "Cette pierre est capable de souiller une âme sans grande volonté, ce qui peut s'avérer efficace pour transformer des tofus en tofus maléfiques. Combinée à d'autres pierres de souillure, elle serait à même de donner un contrôle total sur les âmes souillées." ));
            allItems.Add(new Items ( 9955, "Pierre de souillure verte", "Cette pierre est capable de souiller une âme sans grande volonté, ce qui peut s'avérer efficace pour transformer des tofus en tofus maléfiques. Combinée à d'autres pierres de souillure, elle serait à même de donner un contrôle total sur les âmes souillées." ));
            allItems.Add(new Items ( 9956, "Pierre des Âmes Souillées", "Cette pierre provient du Puits des Âmes Souillées. Grâce à elle, il est possible de contrôler les âmes des créatures piégées en son sein." ));
            allItems.Add(new Items ( 9958, "Caisse d\'équipement bontarien", "Cette caisse renferme le kit du parfait petit milicien. Si le contenu de cette caisse est de la même qualité que le contenant, il ne faudra pas s'étonner que la milice de Bonta soit vaincue en moins d'une journée." ));
            allItems.Add(new Items ( 9959, "Rapport d\'expertise", "Comme vous vous y attendiez, ce rapport d'expertise confirme la mauvaise qualité de l'équipement des miliciens bontariens. Cette nouvelle devrait ravir Oto Mustam." ));
            allItems.Add(new Items ( 9960, "Clefs de l\'entrepôt", "Ce bocal contient les restes de ce qui résidait jadis dans le ventre d'Ybur Effel. La bonne nouvelle, c'est qu'il contient également les clefs de l'entrepôt. Il ne vous reste qu'à y mettre la main pour les récupérer et surtout, n'oubliez pas de vous lécher les doigts après." ));
            allItems.Add(new Items ( 9961, "Marteau Martel", "Ce marteau de piètre qualité a été conçu pour leurrer les forgerons, en leur faisant croire qu'ils avaient entre leurs mains un marteau d'excellente qualité. Il est fortement déconseillé de s'en servir, même pour ne rien faire." ));
            allItems.Add(new Items ( 9962, "Caisse de minerai", "Vous avez volé cette caisse de minerai afin que Latem Sa ne puisse pas s'en servir pour forger les nouveaux équipements destinés aux miliciens de Bonta." ));
            allItems.Add(new Items ( 9963, "Marteau de Forgeron", "Vous avez dérobé ce marteau à Latem Sa et vous l'avez remplacé par un Marteau de Martel. Latem Sa est tellement incompétent qu'il ne devrait pas faire la différence." ));
            allItems.Add(new Items ( 9964, "Cadeau Dragodinde Kraméléhon", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite !" ));
            allItems.Add(new Items ( 9965, "Méthode d\'élevage canin", "Cette méthode, rédigée par le célèbre dresseur de chachas Riyou Tchevlakouk, explique comment dompter un Croc Gland pour en faire le meilleur ami de l'homme, mais pas de la femme." ));
            allItems.Add(new Items ( 9966, "Butin du convoi", "Vous avez obtenu ce butin en attaquant un convoi bontarien. Piétinor le bourrin, dans le bâtiment de la milice de Brâkmar, saura vous récompenser pour vos efforts. Avec de la chance, il acceptera même de vous laisser repartir en un seul morceau." ));
            allItems.Add(new Items ( 9967, "Comment préparer du Gloutoburg", "Laura Soho, serveuse à la Taverne d'Amakna, vous a écrit sur ce morceau de papier comment préparer une bonne bouteille de Gloutoburg à l'aide d'un alchimiste. n- 5 Fioles de Pandaburg. n- 1 Glouto Rhum. n- 10 Feuilles de Salace." ));
            allItems.Add(new Items ( 9968, "Bouteille de Gloutoburg", "Ce mélange d'alcool surpuissant arrache la langue, racle le palais, brûle l'oesophage et explose l'estomac avant de faire bouillir le cerveau. Autant dire que c'est une boisson pour les Pandawas, les vrais ! nIl est écrit en tout petit au fond de la bouteille : l'abus de boisson est dangereux pour la santé, à consommer avec modération sauf en cas de libation pour la déesse." ));
            allItems.Add(new Items ( 9969, "Salace de poissons empoisonnée", "Comme le dit le dicton : une odeur peut en cacher une autre. En effet, le parfum dégagé par cette salace de poissons cache un puissant poison qui, une fois ingurgité, vous fait régurgiter. Très efficace pour animer un mariage un peu mou ou jouer un tour à son chef de guilde préféré." ));
            allItems.Add(new Items ( 9970, "Recette de la Salace de poissons", "Gropinson Cruaulé a écrit la recette de la salace de poissons sur le sable de la plage et vous l'avez recopiée sur ce morceau de papier. n- Un poissonnier (le mettre dans le plat est optionnel). n- 2 Bars Rikains. n- 3 Bâton de crabe ***. n- 2 Citrons. n- 4 Feuilles de Salace. n- 1 Blopisier empoisonné (ça, c'est votre touche personnelle)." ));
            allItems.Add(new Items ( 9971, "Clefs de Mulic Bere", "Ces clefs appartiennent à Mulic Bere. Elles servent à ouvrir l'accès à sa forge spéciale qui lui permet, entre autre, de fabriquer des objets peu ordinaires pour des personnes très ordinaires telles que vous." ));
            allItems.Add(new Items ( 9972, "Marteau de Forgeur de Grattoir", "Ce marteau destiné à la forge a été conçu pour un usage précis, ce qui rend son utilisation difficile pour autre chose. Seule une personne désespérée ou marginale s'en servirait pour forger des armes." ));
            allItems.Add(new Items ( 9973, "Extrémité du Grattoir", "Cette extrémité de grattoir possède plusieurs articulations pour faciliter son emploi. Ses nombreux piquants illustrent parfaitement le vieux dicton amaknéen 'Qui s'y frotte s'y pique'." ));
            allItems.Add(new Items ( 9974, "Tailladeuse de Manche", "Si vous utilisez cette tailladeuse en combat, vous vous battrez comme un manche. Par contre, si vous vous en servez pour sculpter des manches, les tailleurs se retrouveront sur la paille, ce qui rendra les paysans mécontents." ));
            allItems.Add(new Items ( 9975, "Manche du Grattoir", "Seul, ce manche ne vous est d'aucune utilité. Il est possible d'y ajouter un grattoir à son extrémité, ce qui ne vous sera pas plus utile. Vous pouvez raisonnablement  en conclure que seul un esprit tordu saurait quoi en faire." ));
            allItems.Add(new Items ( 9976, "Grattoir à dos inachevé", "Ce grattoir à dos est presque parfait. Presque, mais pas totalement. Seul un forgeron de grand talent pourra l'achever, malheureusement vous n'en connaissez aucun." ));
            allItems.Add(new Items ( 9977, "Grattoir à dos Spentox", "Ce grattoir à dos couvert de pointes est articulé de manière à épouser la forme du corps et ainsi atteindre des endroits jusqu'alors inaccessibles. Ne rêvez pas, la beauté n'est pas un endroit." ));
            allItems.Add(new Items ( 9978, "Armée de Tiques de sanglier des plaines", "Plus grosses que les tiques de bouftou, les tiques de sanglier des plaines peuvent absorber jusqu'à cent fois leur poids en sang. De quoi faire pâlir d'envie les vampires et même le Dark Vlad en personne." ));
            allItems.Add(new Items ( 9979, "Philtre de Longévité", "L'alchimiste Nileza créa cette potion dans l'espoir qu'elle lui octroierait l'immortalité. Malheureusement, il mourut avant de pouvoir l'utiliser, la confondant avec le Sang de Likrone empoisonné qui se trouvait à côté." ));
            allItems.Add(new Items ( 9980, "Plat de Gra-vette", "Ce plat illustre parfaitement l'expression favorite du chevalier Kacador 'Le gras, c'est la vie'." ));
            allItems.Add(new Items ( 9981, "Farine Humée", "Cette farine fut utilisée par Xavier le Boulanger pour concevoir le pain servi à chacune des funérailles du célèbre ménestrel Francisque Kabroule." ));
            allItems.Add(new Items ( 9982, "Salade Houbée", "Le nom de cette salade est un hommage au jour de sa conception. En effet, elle fut servie pour la première fois lors de la cérémonie d'adoubement du chevalier Kacador. Comme toute nourriture destinée au chevalier, cette salade est déconseillée aux végétariens." ));
            allItems.Add(new Items ( 9983, "Pain Phecte", "Servi lors des funérailles du célèbre ménestrel Franscique Kabroule, le goût et l'odeur de ce pain sont si infects qu'ils réveilleraient un mort, ou tueraient un vivant. Ceci pourrait expliquer les nombreux retour à la vie du ménestrel Francisque Kabroule." ));
            allItems.Add(new Items ( 9984, "Liste des ingrédients d\'antan", "Voici la liste donnée par Oto Mustam : n- 1 Pain Phecte. n- 1 Salade Houbée. n- 1 Plat de Gra-vette. n- 1 Philtre de Longévité." ));
            allItems.Add(new Items ( 9985, "Recette du Pain Phecte", "- 2 Farines Humées. n- 5 Eaux. n- 2 Levures de Boulanger." ));
            allItems.Add(new Items ( 9986, "Recette de la Farine Humée", "- 1 Farine de Xavier le Boulanger. n- 1 Poudre d'Eniripsa. n- 4 Oignons." ));
            allItems.Add(new Items ( 9987, "Recette de la Salade Houbée", "- 5 Salades de Museau ***. n- 20 Pollens de Blop. n- 10 Bourgeons d'Abraknyde." ));
            allItems.Add(new Items ( 9988, "Recette du Plat de Gra-vette", "- 10 Beignets de Greu-vette ***. n- 5 Citrons. n- 10 Feuilles de Salace." ));
            allItems.Add(new Items ( 9989, "Recette du Philtre de Longévité", "- 2 Sangs de Likrone. n- 10 Potions de Vieillesse. n- 2 Potions de Souvenir." ));
            allItems.Add(new Items ( 9990, "Broche de Gueurle", "Cette broche vous a été remise par Gueurle pour avoir vaillamment défendu son convoi de marchandises. C'est également la marque de sa promesse de vous remercier personnellement lors de son retour à Bonta." ));
            allItems.Add(new Items ( 9993, "Sabine", "Vous devez emmener Sabine au bâtiment de la milice de Brâkmar." ));
            allItems.Add(new Items ( 9995, "Parchemin lié de mercenaire", "Ce parchemin permet de localiser sa cible afin de la traquer." ));
            allItems.Add(new Items ( 9996, "Lot de cent parchemins de mercenariat", "Ce lot permet de " ));
            allItems.Add(new Items ( 9997, "Clef du repaire des bandits", "Cette clef permet d'ouvrir la porte du repaire des bandits. Grâce à Grimpill, vous savez qu'un mécanisme ferme automatiquement la porte dès qu'une personne entre, c'est pourquoi chaque personne habilitée par les bandits possède un exemplaire de la clef." ));
            allItems.Add(new Items ( 9999, "Copie de la clef du repaire des bandits", "Cette copie de la clef du repaire des bandits Ecaflips semble sur le point de se briser. Il y a fort à parier que ça se produira lorsque vous voudrez l'utiliser. La mention 'A manier avec précaution' ne figurant pas sur l'emballage, vous en déduisez qu'il s'agit d'une contrefaçon, et vous avez raison !" ));
            allItems.Add(new Items ( 10000, "Touffe de poils de Grimpill", "Lorsque vous tenez cette touffe de poils dans votre main, vous avez l'impression qu'elle grouille d'activité. En regardant de plus près, votre impression se confirme. Ce n'est pas une touffe de poils mais une touffe de puces et de tiques géantes. En résumé, vous tenez un véritable écosystème dans le creux de votre main." ));
            allItems.Add(new Items ( 10001, "Lot de dix parchemins de mercenariat", "Ce lot permet de " ));
            allItems.Add(new Items ( 10002, "Parchemin vierge de mercenariat", "Ce parchemin permet de " ));
            allItems.Add(new Items ( 10003, "Gelée Sbihaine", "Seule Liroye Merline connaît la recette de cette gelée artificielle. Lorsqu'elle est combinée à du gazon humide, la gelée se durcit et épouse la forme de tout objet ayant été préalablement glissé à l'intérieur, ce qui rend l'usage de la Gelée Sbihaine très populaire pour la confection de moules." ));
            allItems.Add(new Items ( 10004, "L\'art d\'utiliser la Gelée Sbihaine", "Ce document rédigé par Liroye Merline explique mille et un usages de la Gelée Sbihaine. La partie qui vous intéresse est celle où elle explique comment créer des clefs à moindre frais : n- 1 Gelée Sbihaine. n- 1 Herbe Folle. n- 1 Bakélélite." ));
            allItems.Add(new Items ( 10005, "Documents confidentiels", "Une liste de documents confidentiels bontariens. On peut se demander comment de tels documents se sont retrouvés dans un repaire de bandits." ));
            allItems.Add(new Items ( 10006, "Rapport suspicieux", "Ce rapport fait peser de lourds soupçons sur vous. Il remet en cause votre loyauté envers Bonta et vous implique dans de nombreuses affaires louches. Seul un expert pourra vous dire s'il s'agit d'un document authentique." ));
            allItems.Add(new Items ( 10007, "Fiole de poison brâkmarien", "Cette fiole de poison brâkmarien préparée à l'ancienne porte la mention 'A conserver au chaud et à consommer très lentement après ouverture', suivie de la mention 'Ne pas avaler, sauf si vous êtes la victime ou Bontarien'. Décidément, les Brâkmariens ne plaisantent pas avec le poison." ));
            allItems.Add(new Items ( 10008, "Registre des clients", "Ce registre contient la liste de tous les clients de l'atelier des alchimistes de Brâkmar de ces deux dernières semaines. Le problème est que tout est écrit en Brâkmarien et que vous n'y comprenez rien." ));
            allItems.Add(new Items ( 10009, "Xephirès", "Vous devez amener le traître Xephirès auprès d'Amayiro. Il a des comptes à lui rendre." ));
            allItems.Add(new Items ( 10010, "Clef de la salle de jeu", "Cette clef permet l'accès à la salle de jeu de la Taverne du Chabrulé." ));
            allItems.Add(new Items ( 10011, "Coeur de Trape", "Ceci est le coeur encore palpitant de Trape. Qu'une disciple du dieu Sram n'ait opposée aucune résistance quand vous lui avez ôté la vie vous trouble encore, mais après réflexion, vous vous dites que le résultat aurait été le même et c'est le coeur léger que vous apportez ce coeur à votre chef adoré." ));
            allItems.Add(new Items ( 10012, "Coeur de Trape", "Ceci est le coeur encore palpitant de Trape. Qu'une disciple du dieu Sram n'ait opposée aucune résistance quand vous lui avez ôté la vie vous trouble encore, mais après réflexion, vous vous dites que le résultat aurait été le même et c'est le coeur léger que vous apportez ce coeur à votre chef adoré." ));
            allItems.Add(new Items ( 10013, "Coeur de Trape", "Ceci est le coeur encore palpitant de Trape. Qu'une disciple du dieu Sram n'ait opposée aucune résistance quand vous lui avez ôté la vie vous trouble encore, mais après réflexion, vous vous dites que le résultat aurait été le même et c'est le coeur léger que vous apportez ce coeur à votre chef adoré." ));
            allItems.Add(new Items ( 10014, "Chèque de Trape", "Ce chèque destiné à Oto Mustam a été préparé par Trape. Dommage qu'il ne s'agisse pas de Kamas sonnants et trébuchants sinon vous auriez pu vous servir discrètement." ));
            allItems.Add(new Items ( 10015, "Liste de courses d\'Oto Mustam", "Vous avez lamentablement échoué au test de malveillance et Oto Mustam veut vous le faire payer en vous obligeant à trouver les objets suivants : n- 1 Sac de Bronze. n- 1 Sac de Malt. n- 1 Sac de Fleurs de Chanvre." ));
            allItems.Add(new Items ( 10016, "Hache Léenne", "Cette hache à l'aspect rudimentaire semble appartenir à un autre âge. Sa qualité est si médiocre qu'il vous semble impossible de couper un Tronknyde avec." ));
            allItems.Add(new Items ( 10017, "Katana de Nara", "Ce katana a été forgé par Nara lors de sa mise à l'épreuve. Sa qualité est sans commune mesure avec celle de la Hache Léenne forgée par Latem Sa." ));
            allItems.Add(new Items ( 10018, "Schéma de la Pince à Linge", "Ce schéma est si clair qu'un enfant Iop pourrait peut-être le comprendre. Peut-être, car un Iop reste un Iop. Entre les mains d'un Bricoleur, ce schéma pourra même servir à fabriquer des objets. Si en plus le Bricoleur est un Iop, il se pourrait que vous assistiez à un miracle. nIl vous faut le présent schéma, 10 Planches en Bois de Kaliptus et 10 Aluminites." ));
            allItems.Add(new Items ( 10019, "Seau de Pinces à Linge", "Ce seau contient suffisamment de pinces à linge pour accrocher les sous-vêtements de toute la garnison d'Amakna sur les remparts. On peut également s'en servir pour faire sécher les peaux et les cuirs des monstres fraîchement tués ou de vos beaux-parents." ));
            allItems.Add(new Items ( 10020, "Poupée Malmenée", "Vous avez du mal à croire que cette poupée à l'allure déplorable et à l'odeur repoussante était autrefois un bwork mage. Puis vous vous dites que l'allure d'un bwork mage est repoussante et que son odeur est déplorable et l'histoire prend alors tout son sens." ));
            allItems.Add(new Items ( 10021, "Poupée Rafistolée", "Vous vous demandez si la poupée était mieux avant ou après avoir été rafistolée par Maskara. Une chose est certaine, seul un dieu pouvait rendre la poupée plus belle que son modèle d'origine. On comprend alors mieux pourquoi le bwork mage s'était enfermé dans une caverne et la raison de son mauvais caractère." ));
            allItems.Add(new Items ( 10022, "Poupée Enchantée", "Quel enchantement de recevoir cette poupée enchantée des mains de l'enchanteur qui l'a enchantée dans les Lacs enchantés. S'il était encore en vie, le ménestrel Francisque Kabroule vous l'aurait volontiers offerte en chantant." ));
            allItems.Add(new Items ( 10023, "Registre des rondes", "Contrairement à ce que son titre indique, ce registre ne parle pas des mamas bworks mais du parcours emprunté par les patrouilles brâkmariennes. Pour tendre une embuscade il y a l'embarras du choix." ));
            allItems.Add(new Items ( 10024, "Vieux Grimoire Bwork", "C'est le vieux grimoire d'un bwork mage des cavernes, ce qui signifie que les illustrations qu'il contient sont difficilement compréhensibles pour un bwork mage des cavernes moderne et davantage encore pour un bwork mage standard. Reste à espérer que Danathor lui trouvera une utilité. Ce serait dommage de s'être donné tant de mal pour rien, vous ne trouvez pas ?" ));
            allItems.Add(new Items ( 10025, "Fièvre du Gloutoburg", "Vous étiez prévenu : l'abus de Gloutoburg est dangereux pour la santé." ));
            allItems.Add(new Items ( 10026, "Recette de la salace de poissons", "Gropinson Cruaulé a écrit la recette de la salace de poissons sur le sable de la plage et vous l'avez recopiée sur ce morceau de papier. - Un poissonnier (le mettre dans le plat est optionnel). - 2 Bars Rikains. - 3 Bâton de crabe ***. - 2 Citrons. - 4 Feuilles de Salace. - 2 Vertgely (ça, c'est votre touche personnelle)." ));
            allItems.Add(new Items ( 10028, "Recette du Tissu de Rapiéçage", "Cette recette a été mise au point par le tailleur Sosh pour rapiécer les vêtements magiques. Les tailleurs expérimentés peuvent s'en servir pour créer un tissu à l'épreuve du temps et des intempéries. (action Coudre une cape) n- 5 Poils de Barbe de Bwork Mage. n- 5 Poils de Black Tiwabbit. n- 3 Poils de Barbe du Grand Pa Wabbit. n- 3 poils du Milimulou. n- 1 Fil de Soie." ));
            allItems.Add(new Items ( 10029, "Recette de la Teinture Orange Sanguine", "Cette recette a été mise au point par le célèbre alchimiste Uba Tessé. Nul ne l'a encore utilisée car Uba Tessé l'avait oubliée chez son ami Sosh, qui vous en a généreusement fait cadeau. n- 1 Teinture Magique de Rougeur. n- 1 Teinture Magique Orange. n- 10 Sangs de Bouftou. n- 5 Sardines Brillantes." ));
            allItems.Add(new Items ( 10030, "Tissu de Rapiéçage", "Ce tissu de synthèse inventé par le tailleur Sosh permet de rapiécer les vêtements magiques sans altérer leurs pouvoirs. On comprend pourquoi les tailleurs expérimentés le prisent autant." ));
            allItems.Add(new Items ( 10031, "Teinture Orange Sanguine", "Cette teinture magique inventée par Uba Tessé est extrêmement agressive, comme si elle était animée d'une volonté propre la poussant à faire le mal. Se pose alors la question de savoir pourquoi elle est aussi méchante." ));
            allItems.Add(new Items ( 10032, "Bandana Rapiécé", "Ce bandana est dans un bien meilleur état que lors de sa découverte. Il sent un peu moins la cawotte et un peu plus le poisson, signe qu'il ressemble plus à ce qu'il était autrefois. Turorte devrait apprécier de le récupérer en si bon état." ));
            allItems.Add(new Items ( 10033, "Ordre de mission", "Cet ordre de mission signé par Kaffra Kyper indique aux Brâkmariens qu'ils doivent remettre les documents à Seci Ves, en poste au pied de la Tour des Ordres de Brâkmar." ));
            allItems.Add(new Items ( 10034, "Liste du Mage Ax", "Liste des objets à apporter pour le rituel de Révélation : n- 5 Potions de Crachin. n- 10 Eaux. n- 10 Graines de Pandouille. n- 4 Poudres de Perlinpainpain. n- 2 Bois de Charme. n- 4 Potions de Courant d'Air. n- 2 Personnes de Confiance. n- PAS de soufre sur soi." ));
            allItems.Add(new Items ( 10035, "Résultat de la Révélation", "Le Mage Ax a écrit sur ce parchemin le résultat du rituel de Révélation. Pour utiliser la Corne Edfrit, il suffit de plonger sa main dedans et il en sort des bâtonnets de patate frits." ));
            allItems.Add(new Items ( 10036, "Fée lanceuse de Griffe Joueuse", "Alors qu'elle vivait dans la Tour Enfer Mâle, Mylaise avait commandé à un fameux artisan un grand nombre de fées d'artifices pour animer ses soirées. Aujourd'hui encore, elle en distribue parfois quand elle est de bon poil." ));
            allItems.Add(new Items ( 10042, "Fée lanceuse de Langue Rapeuse", "Alors qu'elle vivait dans la Tour Enfer Mâle, Mylaise avait commandé à un fameux artisan un grand nombre de fées d'artifices pour animer ses soirées. Aujourd'hui encore, elle en distribue parfois quand elle est de bon poil." ));
            allItems.Add(new Items ( 10043, "Le désir d\'être belle", "Une lotion de beauté très puissante vous a donné cette apparence... Vous voilà prête à faire fondre tous les matous ! Meow !" ));
            allItems.Add(new Items ( 10044, "Lotion de beauté de Mylaise", "Mylaise s'applique chaque jour cette lotion sur le corps pour rester jeune et belle... Essayez, vous verrez ! Cependant, le moindre effort en combat vous rendra votre allure décharnée et vos traits tirés... Ce n'est pas la lotion qui fait la Prêtresse de l'Amour." ));
            allItems.Add(new Items ( 10052, "Potion d\'oubli : \'Laisse spirituelle\'", "Cette potion permet d'oublier un niveau du sort spécial 'Laisse spirituelle'" ));
            allItems.Add(new Items ( 10054, "Anneau du Vampyre", "Cet anneau a trempé pendant trois ans dans de la fiente de chauve-souris. Mais il est magique." ));
            allItems.Add(new Items ( 10055, "Amulette du Vampyre", "Cette amulette est censée prévenir les Vampyres qui voudraient vous mordre dans le cou, qu'il est tout simplement inutile d'essayer de vous sucer le sang, puisque vous êtes vous-même un Vampyre." ));
            allItems.Add(new Items ( 10056, "Ceinture du Vampyre", "Sur le slogan publicitaire de cette ceinture vous pouvez lire " ));
            allItems.Add(new Items ( 10057, "Rune de chasse", "Souvenez-vous de ce que disait le très connu Lézain : " ));
            allItems.Add(new Items ( 10058, "Cape du Vampyre", "Bram avait raison, il faut toujours se méfier des personnes aux dents longues, surtout lorsqu'elles portent la cape du Vampyre." ));
            allItems.Add(new Items ( 10061, "Bottes du Vampyre", "On raconte qu'Elizabeth Bythora en personne a porté ces bottes. Ça vous fait une belle jambe." ));
            allItems.Add(new Items ( 10063, "Le désir d\'être belle", "Une lotion de beauté très puissante vous a donné cette apparence... Vous voilà prête à faire fondre tous les matous ! Meow !" ));
            allItems.Add(new Items ( 10073, "Bouclier hispanique", "Le mythique Dal-Uk-Wet, unificateur des royaumes de HispaÑia, avait un bouclier magique. Son arrière petit-fils, Sir-Dal, en fit forger quelques reproductions pour récompenser les meilleurs guerriers de HispaÑia. Ce bouclier est l'une de ces rares copies." ));
            allItems.Add(new Items ( 10074, "Lettre du chef des voleurs", "Retrouvée lors du combat contre les voleurs chez Milone : nMilone Corichel le bijoutier vient de créer un joyau de grande valeur. Dérobez-le par tous les moyens possibles. nHeureusement, Milone leur a remis un faux, et ce n'est pas grâce à vous. nPar contre, au dos du message se trouve l'emplacement de leur repaire. Une revanche s'impose, mais mieux vaut y aller accompagné." ));
            allItems.Add(new Items ( 10075, "Bouillie infâme", "Cette bouillie infâme est apparue comme par enchantement dans votre poche." ));
            allItems.Add(new Items ( 10076, "Bouclier hispanique unique", "Le mythique Dal-Uk-Wet, unificateur du royaume de HispaÑia, concentrait tout son pouvoir dans son bouclier, au grand dam de ses adversaires. Des années plus tard, Sir-Dal, son arrière petit-fils, en fit forger quelques reproductions pour récompenser les meilleurs guerriers de HispaÑia, mais celui-ci est l'original, l'unique, le plus puissant de tous !" ));
            allItems.Add(new Items ( 10077, "Terra Cogita", "Vous devez escorter Terra Cogita à plusieurs endroits avant de le présenter à Jerhyn Gholein." ));
            allItems.Add(new Items ( 10078, "Eni Hoube", "Vous devez escorter et protéger l'eni Hoube. Depuis qu'il vous accompagne, on dirait que le destin s'acharne contre vous." ));
            allItems.Add(new Items ( 10079, "Pain Trépide", "Très prisé par les Iops, ce pain rend courageux et téméraire à la fois, ce qui a déjà provoqué la mort prématurée de nombreux aventuriers. Xavier le Boulanger cessa d'en préparer lorsqu'une compagnie entière de troubadours chargea une meute de mulous enragés." ));
            allItems.Add(new Items ( 10080, "Message des renégats", "Ce message donne rendez-vous aux renégats à proximité du Donjon des Scarafeuilles. Vous devriez vous y rendre pour en savoir plus." ));
            allItems.Add(new Items ( 10081, "Message du chef des renégats", "Dans ce message, le chef des renégats leur demande de le rejoindre au sud du territoire des bandits. C'est pour vous l'occasion d'éliminer ce traître." ));
            allItems.Add(new Items ( 10082, "Cocktail Moritov", "Qu'obtient-on en pressant un pétale morito ? De la sève alcoolisée à base de nombreux cocktails, dont les disciples d'Otomaï se délectent lors des fêtes estivales. En effet, ce cocktail détonant saura mettre le feu à vos soirées, au propre comme au figuré." ));
            allItems.Add(new Items ( 10083, "Salace de poissons", "Comme le dit le dicton : une odeur peut en cacher une autre. En effet, le parfum dégagé par cette salace de poissons cache une forte odeur de pieds. A ne pas consommer en présence de Porkass en rut, sauf si vous êtes une cochonne." ));
            allItems.Add(new Items ( 10084, "Sifflet d\'appel", "Ce sifflet, conçu d'après la méthode d'élevage canin de Riyou Tchevlakouk, permet d'appeler les crocs glands domptés. En revanche, il ne permet pas de se faire passer pour le maître d'un croc gland qui ne vous appartient pas. Il marche aussi très bien avec les crocs glands sauvages." ));
            allItems.Add(new Items ( 10085, "Ordre d\'exécution.", "Les ordres sont clairs et nets, pas comme les caleçons du Minotot. Trouvez et abattez la cible désignée." ));
            allItems.Add(new Items ( 10086, "Canine de bandit des plaines", "La violence du combat contre les bandits des plaines était telle que l'un d'entre eux a perdu une canine." ));
            allItems.Add(new Items ( 10087, "Rendez-vous de Xarthan", "Ce papier rédigé par un Brâkmarien donne rendez-vous à Xarthan dans la Tour de Bonta après sa prochaine réunion. Cela signifie que le rendez-vous est peut-être déjà en cours." ));
            allItems.Add(new Items ( 10088, "Mouchoir usé", "Vous avez ramassé ce mouchoir lors de votre combat contre Xephirès. Ca vous fait un petit souvenir utile et agréable, surtout si vous avez le nez bouché ou êtes allergique aux poils de chacha." ));
            allItems.Add(new Items ( 10089, "Caisse de marchandises", "Xephirès vous a demandé de livrer cette caisse de marchandises au conseiller Boyowa, chez le Grandapan, en guise de bonne foi. Vous ignorez son contenu exact mais il s'agit sûrement d'objets de valeur." ));
            allItems.Add(new Items ( 10090, "Liste de fournitures de Nara", "Nara vous a fait une liste de ce dont il a besoin pour son nouvel atelier de forgeron : 20 Bois de Chêne, 1 Blague de Soufre, 30 Fers, 1 Kouartz, 5 Bakélélites et 10 Cuirs de Sanglier des Plaines." ));
            allItems.Add(new Items ( 10091, "Notes sur La malédiction d\'Erazal", "La malédiction d'Erazal indique comment ressusciter une âme contre sa volonté. Il vous faut 1 Sang de Phénix, 5 Poudres de Perlinpainpain et 1 Amulette de Lear, obtenue sur les cadavres des abraknes et des abraknes sombres." ));
            allItems.Add(new Items ( 10092, "Ordre d\'annulation.", "Cette lettre, datée d'il y a quelques heures seulement, indique que l'assaut sur Bonta a été annulé en raison de l'amélioration en équipement que leurs miliciens ont reçu, et de la capture du traître Xephirès." ));
            allItems.Add(new Items ( 10102, "Masque du Vampyre", "Ce masque vous protégera des rayons du soleil à défaut d'autre chose." ));
            allItems.Add(new Items ( 10103, "Lettre pour Fidji Merline", "Ebru Of El vous a confié cette lettre à remettre à Fidji Merline. Apparemment, il faudra suivre les instructions de la bricoleuse de Brâkmar." ));
            allItems.Add(new Items ( 10104, "Mangeoire trafiquée", "Cette mangeoire a été trafiquée pour cacher des aubergines et mieux vaut ne pas traverser Brâkmar sur une monture s'étant rassasiée dans cette mangeoire. En effet, les flatulences engendrées par la consommation excessive d'aubergines pourraient provoquer un incident détonant." ));
            allItems.Add(new Items ( 10105, "Patte couverte de poils", "Cette patte d'arakne couverte de poils d'arakne et de chacha est une arme redoutable si l'on doit faire face à Ledrob Terceséc." ));
            allItems.Add(new Items ( 10106, "Vampyrette", "Cette terrible créature prendra un malin plaisir à sucer le sang de vos amis, ou celui de vos chevilles si elles sont un peu trop grosses. Mais cela ne suffira pas à la divertir pour autant, elle aime par dessus tout que vous affrontiez avec elle des squelettes pour se repaître de leurs dépouilles. On vous avait bien dit qu'elle était terrible." ));
            allItems.Add(new Items ( 10107, "Chauffe-souris", "Ce familier a eu une enfance très difficile, il a hérité d'un nom franchement stupide. C'est peut-être ce qui explique pourquoi il est devenu un peu fou et n'a plus qu'une seule obsession dans la vie, tuer toutes les Ouassingues qu'il rencontre pour se nourrir de leurs dépouilles." ));
            allItems.Add(new Items ( 10108, "Fantôme de Vampyrette", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 10109, "Fantôme de Chauffe-souris", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 10118, "Croc Gland fraichement dompté", "Ce Croc Gland a été dressé selon la méthode d'élevage canin mise au point par le célèbre dresseur de chachas Riyou Tchevlakouk." ));
            allItems.Add(new Items ( 10119, "Goulyfié", "Vous vous sentez différent..." ));
            allItems.Add(new Items ( 10121, "Petit caillou", "Ce petit caillou sert à activer la magie des dalles pour atteindre l'esprit de l'eau. Son utilisation est des plus simples : il suffit de le poser sur une dalle et la magie opère d'elle-même. Simple et efficace. Même un Iop peut y arriver, s'il ne trouve aucun ennemi sur qui jeter le petit caillou avant." ));
            allItems.Add(new Items ( 10122, "Liste de Norb", "Norb vous a fait une liste des ressources à apporter à Nara. Il faut 2 Bakélélites, 10 Manganèses, 20 Bauxites, 15 Dolomites et 20 Bois d'Oliviolet." ));
            allItems.Add(new Items ( 10125, "Arc du bandit archer", "Cet arc appartenait à un ancien bandit archer qui s'en servait pour achever ses victimes à distance, afin de ne pas se salir. Il est imprégné d'une magie si puissante que vous risquez de vous transformer en bandit archer si vous l'équipez..." ));
            allItems.Add(new Items ( 10126, "Epée du bandit fine-lame", "Cette épée appartenait à un ancien bandit fine-lame, qui ne supportait pas de devoir poursuivre ses adversaires. Il commençait par leur couper les deux jambes et posait ses questions ensuite. Elle est imprégnée d'une magie si puissante que vous risquez de vous transformer en bandit fine-lame si vous l'équipez..." ));
            allItems.Add(new Items ( 10127, "Bâton du bandit baroudeur", "Ce bâton appartenait à un ancien bandit baroudeur, constamment ivre, il était incapable de faire les poches de ses victimes sans s'écrouler. Ce bâton lui permettait de tenir debout le temps qu'il termine son office. Il est imprégné d'une magie si puissante que vous risquez de vous transformer en bandit baroudeur si vous l'équipez." ));
            allItems.Add(new Items ( 10131, "Cadeau Vampyrette", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révéleront la présence d'une créature prête à vous sauter au cou. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier." ));
            allItems.Add(new Items ( 10132, "Cadeau Chauffe-souris", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature prête à vous sauter au cou. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier." ));
            allItems.Add(new Items ( 10133, "Baguette du bandit ensorceleur", "Cette baguette appartenait à un ancien bandit ensorceleur, qui s'en servait pour réduire en cendres les ennemis qu'il voulait fumer. Elle est imprégnée d'une magie si puissante que vous risquez de vous transformer en bandit ensorceleur si vous l'équipez..." ));
            allItems.Add(new Items ( 10134, "Certificat de mise en chanil : Vampyrette", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 10135, "Certificat de mise en chanil : Chauffe-souris", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
