            allItems.Add(new Items ( 10156, "Yugoufle", "Ces pantoufles sont plus confortables que des pantoufles ordinaires, mais vous ne saurez jamais exactement pourquoi. Peut-être que les milliers d'acariens masseurs qui y vivent y sont pour quelque chose." ));
            allItems.Add(new Items ( 10157, "Yugure", "Cette ceinture a été fabriquée à partir de chutes de ficelles de lin. Elle doit pouvoir servir à fermer les sacs poubelles." ));
