            allItems.Add(new Items ( 10168, "Chispottes", "Ponch cirait ses bottes tous les matins avant de partir en mission sur sa Dragodinde pour sauver la veuve et l'orphelin, mais surtout la veuve... Un sacré charmeur ce Ponch." ));
            allItems.Add(new Items ( 10169, "Chispouclier", "Ponch portait fièrement ce bouclier sur sa dragodinde lorsqu'il veillait à la sécurité des ruelles de Bonta." ));
            allItems.Add(new Items ( 10170, "Chispocasque", "Ce casque permet de se protéger très efficacement des chutes de dragodindes ou de passer pour un idiot sans en être un." ));
            allItems.Add(new Items ( 10171, "Mitaines de Chisp", "Ces mitaines en cuir sont parfaites pour donner des fessées déculottées." ));
            allItems.Add(new Items ( 10172, "Collertue", "Cette amulette peut nourrir une limace vorpale pendant deux jours, ou trois jours si elle l'accompagne d'une purée de votre tête." ));
            allItems.Add(new Items ( 10173, "Libottes", "La semelle de ces bottes est recouverte d'une bave collante et l'intérieur est rempli d'une bave glissante. A moins que ça ne soit l'inverse." ));
            allItems.Add(new Items ( 10174, "Limape", "Une bave gluante sort des pores de cette cape. Prenez un peu d'élan, allongez-vous sur le dos et laissez-vous glisser." ));
            allItems.Add(new Items ( 10175, "Limasque", "Ce masque gluant et baveux alimentera votre cuir chevelu en gomina de façon continue. Les deux yeux accrochés au masque peuvent être connectés à votre crâne si vous acceptez d'insérer les terminaisons nerveuses dans vos oreilles." ));
            allItems.Add(new Items ( 10180, "L\'Esteban", "Ce médaillon est censé servir de clé pour accéder à une mystérieuse cité oubliée." ));
            allItems.Add(new Items ( 10181, "Anneau solaire", "Cet anneau a été retrouvé dans une mystérieuse cité dont personne ne soupçonnait réellement l'existence jusqu'à ce que l'on commence à soupçonner son existence." ));
            allItems.Add(new Items ( 10182, "Bouclier solaire", "Ce bouclier absorbe les rayons du soleil et les renvoie directement dans le visage de vos adversaires. A défaut de les éblouir avec votre intelligence, vous les aveuglerez temporairement grâce à ce bouclier." ));
            allItems.Add(new Items ( 10183, "Masque solaire", "Ce masque captera les rayons de soleil et les concentrera sur votre visage pour que vous puissiez rester bronzé toute l'année, même si vous habitez au nord d'Amakna." ));
            allItems.Add(new Items ( 10184, "Masque de Noké", "Noké aimait beaucoup revêtir ce masque et se cacher dans les bois pour effrayer les promeneurs égarés. Il est mort de peur il y a bien longtemps en se regardant dans une flaque d'eau." ));
            allItems.Add(new Items ( 10185, "Cape de Noké", "Noké rêvait d'être un grand méchant loup. Même avec cette cape sur les épaules, il ne ressemblait qu'à un petit méchant." ));
            allItems.Add(new Items ( 10186, "Collier de Noké", "Cette amulette en dents de sanglier effraiera les sangliers et par extension votre belle-mère." ));
            allItems.Add(new Items ( 10187, "Dagues de Noké", "Ces dagues ont servi jadis à dépecer un sanglier belliqueux qui rêvait d'être un cerf." ));
            allItems.Add(new Items ( 10188, "Coiffe de la Denrée", "Cette coiffe fera de vous un excellent Wobot de protocole grâce à son dispositif unique qui permet de traduire à la volée les discussions des choux. C'est également un excellent casque insonorisant." ));
            allItems.Add(new Items ( 10189, "Oxano", "Cet anneau appartenait à un sabotier connu sous le surnom de " ));
            allItems.Add(new Items ( 10190, "Bottines de la Denrée", "Si vous décidez de planter des choux, à la mode de chez nous, pensez à équiper ces bottines conçues pour ne pas écraser les choux. N'essayez pas de les utiliser pour danser avec votre partenaire, elles ne permettent pas d'éviter les écrasements de pieds." ));
            allItems.Add(new Items ( 10191, "L\'oxolature", "Cette ceinture permet de contenir vos ballonnements après une indigestion de soupe aux choux." ));
            allItems.Add(new Items ( 10192, "Cadeau Cape Siks Won Naïne", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
