            allItems.Add(new Items ( 10207, "Trousseau de clefs", "On le dit doté de longs pênes très agiles. Cependant, ils s'épuisent vite et leurs magies ne se rechargent qu'au bout d'une semaine. Qu'elles soient à gorges ou à pompes, vous pourrez forcer toutes les serrures des donjons." ));
