            allItems.Add(new Items ( 10515, "Cadeau Couronne Koninginnedag", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
            allItems.Add(new Items ( 10516, "Transformation flammèche", "Voilà ce que c'est que de vouloir jouer avec le feu. Allez, ce n'est peut être pas si grave, prenez conseil auprès du Mage Ax." ));
            allItems.Add(new Items ( 10518, "Recette de la Corde d\'escalade", "Un bricoleur peut bricoler une Corde d'escalade à l'aide des ressources suivantes : 30 Chanvres, 5 Fils de Soie et 1 Fil Résistant." ));
            allItems.Add(new Items ( 10519, "Recette des Pierres de souillure impures", "Un alchimiste peut fabriquer des Pierres de souillure impures de différentes couleurs avec les ingrédients suivants : nBleue : 2 Gelées Bleutées, 1 Potion Ghetto Raide. nJaune : 2 Gelées Citrons, 1 Potion Ghetto Raide. nVerte : 2 Gelées à la Menthe, 1 Potion Ghetto Raide." ));
            allItems.Add(new Items ( 10520, "Recette des Pierres de souillure", "Un mineur peut polir des Pierres de souillure de différentes couleurs avec les ingrédients suivants : nBleue : 10 Pierres de souillure bleues impures, 10 Eaux. nJaune : 10 Pierres de souillure jaunes impures, 10 Eaux. nVerte : 10 Pierres de souillure vertes impures, 10 Eaux." ));
            allItems.Add(new Items ( 10544, "Gelutin", "Cette créature venue d'ailleurs a été dressée par son ancien maître pour ne manger que les monstres issus de la famille des Blops. C'est peut-être ce qui explique son apparence surprenante ou son haleine de Blop." ));
            allItems.Add(new Items ( 10552, "Yugonnet", "Cette coiffe surprenante protégera efficacement vos oreilles du froid et des mauvaises blagues de vos amis." ));
            allItems.Add(new Items ( 10553, "Chapeau de Tolot", "Seul un boufbowler peut avoir l'idée saugrenue de se déguiser en tofu pour impressionner son ennemi. A moins qu'il ne s'agisse de fendre l'air comme personne grâce à la forme fuselée de ce chapeau. Quoiqu'il en soit, vous ne passerez pas inaperçu." ));
            allItems.Add(new Items ( 10554, "Chapeau de Beneji", "Les lunettes intégrées dans ce casque vous permettront de voir rouge et de vous sentir plus boufton que jamais." ));
            allItems.Add(new Items ( 10555, "Bouclier du Real Boitar", "Cet imposant bouclier porte les traces de nombreux matches. D'apparence robuste, il n'est pourtant d'aucune utilité dans un vrai combat, si ce n'est pour impressionner vos ennemis ou faire ami-ami avec des bouftous." ));
            allItems.Add(new Items ( 10556, "Bouclier des Bouftons Rouges", "Cet imposant bouclier illustre parfaitement la fougue et l'insolence de la jeunesse. Tout dans l'apparence et rien dans le ventre. C'est le cas de le dire, il ne vous protégera de rien dans un vrai combat, sauf si votre adversaire vous attaque à coups de ballon." ));
            allItems.Add(new Items ( 10557, "Gantelet du Real Boitar", "Ce gantelet marqué par les années facilite la prise en main de ballon ou toute autre bestiole ronde recouverte de laine. Ce que vous en faites après ne regarde que vous." ));
            allItems.Add(new Items ( 10558, "Gantelet des Bouftons Rouges", "Ce gantelet permettra au jeune Chienchien fou que vous êtes de jouer à la baballe comme un grand boufbowler." ));
            allItems.Add(new Items ( 10559, "Ceinture de Tolot", "Initialement conçue pour absorber les chocs lors de matches de boufbowl musclés, cette ceinture pourra aussi vous servir de bouée si d'aventure vous souhaitez prendre un bain. Quelle drôle d'idée." ));
            allItems.Add(new Items ( 10560, "Ceinture de Mounu", "Cette ceinture montre à quel point le boufbowl n'est pas un sport de fillette, d'où les protections judicieusement placées sous la ceinture pour éviter tout coup bas un peu trop bas. Idéal pour assurer l'entraînement de petits Srams." ));
            allItems.Add(new Items ( 10561, "Bottes du Real Boitar", "Ces bottes ont vécu, et ça se sent. Avec elles, vous pourrez vous faire passer pour un grand boufbowler, et planter vos crampons dans le postérieur de quelques bouftous devant vos fans." ));
            allItems.Add(new Items ( 10562, "Bottes des Bouftons Rouges", "Ces bottes dans l'air du temps feront fureur auprès de tous les jeunes aventuriers fascinés par le boufbowl. Elles facilitent les chutes pour pouvoir plus facilement faire semblant d'avoir été poussé par un adversaire. Idéal pour apprendre les rudiments du sport." ));
            allItems.Add(new Items ( 10563, "Orbe reconstituant", "Cet objet permet de répartir ses points de caractéristiques. Les maîtres des temples de classe savent comment l'utiliser." ));
            allItems.Add(new Items ( 10564, "Paquet orbe reconstituant", "Ce paquet contient un orbe précieusement enveloppé." ));
            allItems.Add(new Items ( 10587, "Etoffe des Héros", "On dit que cette étoffe fut fabriquée par le célèbre Féca XxRamboPLxX. Il s'en servait comme bandeau qu'il se passait autour du front, ce qui ne manquait pas d'impressionner les jeunes fossoyeurs koalaks." ));
            allItems.Add(new Items ( 10597, "Fantôme de Gelutin", "Ce Gelutin n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 10598, "Cadeau Gelutin", "En posant votre oreille sur ce cadeau ou en le secouant vigoureusement, vous entendrez peut-être d'étranges bruits qui vous révèleront la présence d'une créature encore vivante. Ne perdez pas une seule seconde, ouvrez-le vite, vous y trouverez un superbe familier, des ressources pour le nourrir et des poudres d'Eniripsa pour le soigner." ));
            allItems.Add(new Items ( 10599, "Certificat de mise en chanil : Gelutin", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 10601, "Certificat de reconstitution", "Ce document atteste de la remise à zéro de vos caractéristiques." ));
            allItems.Add(new Items ( 10602, "Parchemin de Sort apprivoisement de monture", "Ce parchemin permet d'apprendre le sort apprivoisement de monture." ));
            allItems.Add(new Items ( 10603, "Parchemin de Sort  capture d\'âmes", "Ce parchemin permet d'apprendre le sort capture d'âmes." ));
            allItems.Add(new Items ( 10604, "Parchemin de Sort marteau de Moon", "Ce parchemin permet d'apprendre le sort marteau de Moon." ));
            allItems.Add(new Items ( 10605, "Parchemin de Sort  boomerang perfide", "Ce parchemin permet d'apprendre le sort boomerang perfide." ));
            allItems.Add(new Items ( 10606, "Gemme Spirituelle de Rat Blanc", "Gagnée en récompense de vos victoires en duel, cette gemme referme l'âme d'un monstre. A conserver à l'abri de la lumière dans un endroit sec. Secouer avant usage." ));
            allItems.Add(new Items ( 10608, "Potion vers Kralamoure Géant", "Cette potion vous mènera directement vers le monstre indiqué." ));
            allItems.Add(new Items ( 10613, "Rune Pa Pi", "Cette rune permet d'augmenter la puissance de ses pièges." ));
            allItems.Add(new Items ( 10615, "Rune Pa Pi Per", "Cette rune permet d'augmenter la puissance de ses pièges." ));
            allItems.Add(new Items ( 10616, "Rune Ra Pi Per", "Cette rune permet d'augmenter la puissance de ses pièges." ));
            allItems.Add(new Items ( 10618, "Rune Pa Do Per", "Cette rune permet d'augmenter les dommages causés à ses adversaires." ));
            allItems.Add(new Items ( 10619, "Rune Ra Do Per", "Cette rune permet d'augmenter les dommages causés à ses adversaires." ));
            allItems.Add(new Items ( 10621, "Ordres des Sacre", "Ce document déchiré liste les consignes des Sacre. Simples, celles-ci consistent en la traque et l'exécution d'une cible. Et surtout, ne pas retourner les voir avant dix minutes et ce, quelque soit le dénouement du combat." ));
            allItems.Add(new Items ( 10622, "Epée de Grüt", "Cette épée permettait à Grüt le Bwork de couper les noisettes de ses adversaires en deux, ce qui suffisait généralement pour les faire abandonner le moindre combat. Cette arme magique est encore imprégnée de la personnalité de Grüt. En l'équipant, vous pourrez peut-être l'incarner." ));
            allItems.Add(new Items ( 10623, "Amulette de Grüt", "Grüt le Bwork n'était pas très malin. Il était même complètement stupide. Heureusement, les méthodes d'enseignement ancestrales fonctionnaient aussi bien sur les enfants que sur les Bworks. Quelques coups de bâton bien mérités ont fini par avoir raison de sa stupidité et au fil des années, Grüt est devenu juste un peu bête." ));
            allItems.Add(new Items ( 10624, "Ceinture de Grüt", "Ceux qui ont eu à affronter Grüt le Bwork vous le diront, " ));
            allItems.Add(new Items ( 10625, "Bottes de Grüt", "Les bottes de Grüt dégagent une odeur tellement répugnante, qu'elles peuvent étouffer vos adversaires d'un simple coup de pied à la tête. Ces bottes ont été interdites en combat par la convention de Sufokia il y a très longtemps. Personne ne reviendra sur cette décision qui a permis d'éviter de nombreuses morts atroces." ));
            allItems.Add(new Items ( 10626, "Epée de Kocksis", "La vie d'un Chafer Fantassin n'est pas aussi facile que l'on pourrait l'imaginer. Mais comme personne n'a jamais réussi à imaginer comment vit un Chafer Fantassin, tout le monde s'en moque et s'imagine que sa vie est facile. C'est à cause de raisonnements aussi stupides que celui-ci, que le Chafer Fantassin est devenu un Chafer Fantassin. Cette arme magique est encore imprégnée de la personnalité de Kocksis. En l'équipant, vous pourrez peut-être l'incarner." ));
            allItems.Add(new Items ( 10627, "Anneau de Kocksis", "Kocksis le Chafer Fantassin avait de nombreux plaisirs plutôt malsains, comme manger des chips au vinaigre avec de la pâte à tartiner ou pire encore, manger des poils de Chacha. Nombreux sont ceux qui ont tenté de le ramener dans le droit chemin et se sont perdus sur le chemin du retour." ));
            allItems.Add(new Items ( 10628, "Bottes de Kocksis", "Rien de plus bruyant que le claquement des os sur un parquet en orme. Grâce à ces bottes, Kocksis le Chafer Fantassin pouvait danser l'Ankarmagniole toute la nuit sans craindre de réveiller les voisins, les morts, ni même les voisins morts." ));
            allItems.Add(new Items ( 10629, "Ceinture de Kocksis", "Kocksis le Chafer Fantassin avait deux rêves dans sa vie : gravir la montagne des Craqueleurs et devenir ménestrel. La vie fût très cruelle avec lui, puisqu'il dû se contenter de faire les premières parties des concerts de Francisque Kabroule. Parfois le sort s'acharne sur vous. Cette fois-ci il s'est acharné sur celui de Kocksis." ));
            allItems.Add(new Items ( 10630, "Bâton de Gobeuf", "Ce bâton ressemble étrangement à une lance, Gobeuf le Gobelin a fait monter une lame aiguisée au bout de ce bâton afin de pouvoir transpercer les œufs de Tofu à distance. Il est inutile de chercher une explication rationnelle derrière tout cela, les Gobelins ont tout simplement toujours eu des jeux étranges. Cette arme magique est encore imprégnée de la personnalité de Gobeuf. En l'équipant, vous pourrez peut-être l'incarner." ));
            allItems.Add(new Items ( 10631, "Amulette de Gobeuf", "Voici une des devises les plus populaires chez les Gobelins : " ));
            allItems.Add(new Items ( 10632, "Ceinture de Gobeuf", "Gobeuf le gobelin adorait gober les œufs en état de putréfaction avancée. Si son appareil digestif supportait sans problème cette fâcheuse habitude, les gens situés à proximité de son haleine putride tombaient comme des Mosquitos. Cette ceinture permettait à tous ses proches de se rattraper avant de s'écrouler lamentablement au sol." ));
            allItems.Add(new Items ( 10633, "Anneau de Gobeuf", "Gobeuf le Gobelin était passionné par le jardinage, surtout lorsqu'il s'agissait de donner des coups de râteau sur les imprudents qui s'approchaient de ses terres. Il a trouvé cet anneau en ratissant le doigt d'un imprudent qui avait marché sur un de ses pieds de Salace." ));
            allItems.Add(new Items ( 10634, "Arc de Laikteur", "Le Kanniboul Laikteur utilisait cette arme pour chasser ses proies et divertir ses enfants en leur envoyant quelques flèches dans les fesses lorsqu'il était de bonne humeur. Cette arme magique est encore imprégnée de l'âme du Kanniboul Laikteur. En l'équipant, vous pourrez peut-être parvenir à l'incarner." ));
            allItems.Add(new Items ( 10635, "Amulette de Laikteur", "Lorsque les deux petites boules accrochées à cette amulette s'entrechoquent, elles produisent un léger bruit de claquement qui vous permet d'être informé de l'arrivée d'un Kanniboul. Lorsque vous entendez ce bruit, il est généralement trop tard pour vous enfuir, mais il est encore temps de choisir à quelle sauce vous voulez être dévoré." ));
            allItems.Add(new Items ( 10636, "Anneau de Laikteur", "Cet anneau diffuse des épices afin d'aromatiser facilement le bouillon lorsqu'il est mélangé avec les mains. Ces épices sont si bonnes, qu'une fois cet anneau équipé, vous risquez de vous en mordre les doigts." ));
            allItems.Add(new Items ( 10637, "Bottes de Laikteur", "Le Kanniboul Laikteur a utilisé ces bottes pendant plusieurs années avant de les échanger contre un touriste de l'île de Moon. La faim justifie les moyens." ));
            allItems.Add(new Items ( 10638, "Epée de Rekto Topi", "Le Pandit Rekto Topi utilisait cette épée en frappant ses adversaires avec le plat de la lame, afin de les assommer sans les taillader. Un seul coup bien placé suffisait généralement pour les envoyer directement au tapis. Cette arme magique est encore imprégnée de l'âme du Pandit Rekto Topi. En l'équipant, vous pourrez peut-être parvenir à l'incarner." ));
            allItems.Add(new Items ( 10639, "Amulette de Rekto Topi", "Rekto avait un frère, appelé Verso Litaire. Il aimait voyager seul et explorer les tunnels sombres qui ne sentent pas très bon." ));
            allItems.Add(new Items ( 10640, "Ceinture de Rekto Topi", "Cette ceinture est censée être réversible mais personne n'a jamais osé utiliser l'autre face de peur de découvrir qu'elle n'est en fait pas réversible." ));
            allItems.Add(new Items ( 10641, "Bottes de Rekto Topi", "Rekto Topi a confectionné ces bottes lui-même, à partir de morceaux de bois et d'un peu de tissu. Elles ne ressemblent pas du tout à des bottes, mais qu'importe elles sont magiques tout de même." ));
            allItems.Add(new Items ( 10642, "Pioche de Grizou", "Grizou le Mineur Sombre a utilisé cette pioche pour creuser la célèbre mine sans fond de Kruup, dans laquelle le GAddie a vécu plusieurs années. Cette arme est encore imprégnée de l'âme de Grizou. En l'équipant, vous pourrez peut-être l'incarner." ));
            allItems.Add(new Items ( 10643, "Anneau de Grizou", "Cet anneau change de couleur lorsqu'il est trempé dans du jus de Chmoule bouillant. C'est sûrement ce qui explique pourquoi Grizou le Mineur Sombre tenait autant à cet anneau." ));
            allItems.Add(new Items ( 10644, "Bottes de Grizou", "La semelle magique de ces bottes permet de casser les blocs de charbon en envoyant les vibrations nécessaires à leur fragmentation. La notice indique qu'elles sont même capables de vibrer toutes seules dès qu'un son de Bouftonik est joué à proximité." ));
            allItems.Add(new Items ( 10645, "Ceinture de Grizou", "Cette ceinture renforcée à l'aide de nombreux métaux était assez solide pour supporter le poids des énormes bourses de Grizou le Mineur Sombre. Elle devrait convenir pour les bourses plus légères." ));
            allItems.Add(new Items ( 10647, "Cadeau Bottes du Real Boitar", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
