            allItems.Add(new Items ( 10657, "Chercheur d\'Ogrines", "Cet étrange familier est issu du croisement des meilleurs chercheurs de truffes de tout Amakna. Son énorme groin lui permet de repérer l'Ogrine, même lorsqu'elle est cachée sous l'eau. Otomaï aurait mis plusieurs années à obtenir cette espèce et l'on raconte qu'il l'utiliserait pour réparer une de ses erreurs, retrouver ce qu'il aurait égaré ou laissé s'enfuir. Personne ne sait... La recherche d'Ogrine implique un processus physiologique si complexe et coûteux en énergie, que cette créature est obligée de consommer les âmes des monstres pour grandir. Elle a été élevée sur les plages de l'île d'Otomaï et ne dévore que les âmes des créatures qui y vivent." ));
            allItems.Add(new Items ( 10658, "Fantôme de Chercheur d\'Ogrines", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 10659, "Oeuf du Chercheur d\'Ogrines", "Cet œuf étrange contient une petite créature magique. Gardez-le au chaud, il finira sûrement par éclore." ));
            allItems.Add(new Items ( 10660, "Cadeau Oeuf de Chercheur d\'Ogrines", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
            allItems.Add(new Items ( 10661, "Certificat de mise en chanil : Chercheur d\'Ogrines", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 10662, "Rune Pa Prospe", "Cette rune permet d'avoir plus de chance de trouver des objets rares." ));
            allItems.Add(new Items ( 10663, "Brioche à la viande de bouftou", "Il faut avoir l'estomac bien accroché pour avaler cette brioche. La viande de bouftou utilisée, pas toujours de première fraîcheur, donne une saveur proche des huîtres au chocolat. Un délice pour Bwork." ));
            allItems.Add(new Items ( 10664, "Abragland", "Très délicat comme tous les glands, il suffit de le frotter légèrement pour qu'il expulse l'abraknyde emprisonné en son sein." ));
            allItems.Add(new Items ( 10665, "Coffre de Jorbak", "Jorbak ayant perdu toute sa fortune, que peut bien contenir ce coffre étrange ? Ouvrez-le pour le découvrir." ));
            allItems.Add(new Items ( 10667, "Empreinte de Jorbak", "Vous avez réussi à noter quand sera visible l'esprit de Jorbak. Inutile de se précipiter." ));
            allItems.Add(new Items ( 10668, "Bonne fortune de Jorbak", "Pendant quelques temps, vous bénéficierez de la chance de Jorbak." ));
            allItems.Add(new Items ( 10669, "Potion de crapaud-mufle", "Ça vous apprendra à boire n'importe quoi !" ));
            allItems.Add(new Items ( 10670, "Parchemin de Persimol", "Simples à suivre comme une recette de cuisine Wabbit, les indications de ce parchemin révèlent un secret envoutant." ));
            allItems.Add(new Items ( 10673, "Potion de HmmMmmMmmm", "HmmMmmMmmm." ));
            allItems.Add(new Items ( 10674, "Mmmmm", "Mmmmm mm mmmmmm mmmmm, mmm mmm mm mmmmmm mmmmm mmmmmmm mmmmmm mmm mmmm'm mmmmm mmmm mmmmmmm." ));
            allItems.Add(new Items ( 10675, "Cadeau Dofusien", "Pas moyen de vous révéler le contenu de ce cadeau, mais sachez qu'il vous permettra à la fois de vous éclater et de faire la foire. Lorsque c'est jour de fête, il faut bien ça." ));
            allItems.Add(new Items ( 10676, "Double effet Kill Coos", "Votre prospection et votre sagesse sont augmentées pendant plusieurs combats." ));
            allItems.Add(new Items ( 10677, "Shigekax Kill Coos", "Retrouvez la saveur et tout le charme des sucreries d'antan, avec ce bonbon préparé de façon traditionnelle n(Ce bonbon augmente la prospection et la sagesse de 100 points pendant 20 combats)." ));
            allItems.Add(new Items ( 10678, "Voyager sans jamais perdre le Nord", "Arpagnagrobis a écrit cet ouvrage pour tous les aventuriers ne sachant point se servir de leur boussole et des zaaps." ));
            allItems.Add(new Items ( 10680, "Souvenir sanglant du Goultarminator", "Vous avez arraché ce coeur à l'Essence Maléfique de Goultard. Vous pourrez le poser sur votre bureau en guise de souvenir." ));
            allItems.Add(new Items ( 10681, "Transformation en Crapaud-mufle", "Bien fait !" ));
            allItems.Add(new Items ( 10682, "Le secret de Persimol", "Chance et bonheur vont de paire." ));
            allItems.Add(new Items ( 10685, "Casque du Roks Or", "Équipée de ce casque en or, votre tête sera rapidement mise à prix et vaudra son pesant d'or." ));
            allItems.Add(new Items ( 10686, "Cape du Roks Or", "Cette cape en tissu, recouverte d'une fine couche d'or est magique. Elle attire les regards et renvoie la lumière directement dans les pupilles des fourbes qui tenteraient de vous poignarder par derrière. Indispensable si vous avez des amis Srams." ));
            allItems.Add(new Items ( 10687, "Anneau du Roks Or", "Cet anneau est si lourd qu'il augmente considérablement l'inertie de votre poing quand vous distribuez des claques à vos adversaires. Lorsque vous aurez commencé à punir les manants, il vous sera quasiment impossible de vous arrêter." ));
            allItems.Add(new Items ( 10688, "Ceinture du Roks Or", "Cette ceinture est parfaite pour accrocher vos lingots d'or autour de votre taille, ils passeront inaperçus." ));
